<?phpclass CacheAction extends CommonAction{					public function cache_clean() {	 function delDirAndFile($path, $delDir = FALSE) {			if (is_array($path)) {				foreach ($path as $subPath)					delDirAndFile($subPath, $delDir);			}			if (is_dir($path)) {				$handle = opendir($path);				if ($handle) {					while (false !== ( $item = readdir($handle) )) {						if ($item != "." && $item != "..")							is_dir("$path/$item") ? delDirAndFile("$path/$item", $delDir) : unlink("$path/$item");						if($item=='.' || $item=='..' || $item=='Admin' || $item=='Index' ){echo '';}else{echo '删除文件'.$item.'成功<br>';}						//echo '删除文件'.$item.'成功<br>';					}					closedir($handle);					if ($delDir)						return rmdir($path);				}			} else {				if (file_exists($path)) {					return unlink($path);				} else {					return FALSE;				}			}			clearstatcache();		 }								echo "<span style='color: red;'>缓存清理中……</span><br/>";		$path ="APP/Runtime/Cache/";		delDirAndFile($path);		$URL=GROUP_NAME.'/Index/welcome';		echo "<span style='color: red;'>缓存清理完毕.</span>";	}	}?>