<?php/**后台首页控制器*/Class SiteinfoAction extends Action{	Public function index(){		$this->display();		}	Public function ajax(){		echo json_encode($_POST); 			}	Public function siteinfo(){				import('Class.Siteinfo',APP_PATH);		  $gifo = new get_gust_info();		 $IP=$gifo->Getaddress();		  $gifo->Getaddress[0][0];		 $win=array(         '操作系统'=>PHP_OS,         '运行环境'=>$_SERVER["SERVER_SOFTWARE"],		 '运行方式'=>php_sapi_name(),//运行方式         'ThinkPHP版本'=>THINK_VERSION.' [ <a href="http://thinkphp.cn" target="_blank">查看最新版本</a> ]',		 'MYSQL版本'=>"MySQL server version:".mysql_get_server_info(),//MYSQL版本         '上传附件限制'=>ini_get('upload_max_filesize'),         '执行时间限制'=>ini_get('max_execution_time').'秒',		 '服务器时间'=>date("Y年n月j日 H:i:s"),         '北京时间'=>gmdate("Y年n月j日 H:i:s",time()+8*3600),		 '浏览器'=>$gifo->GetBrowser(),//浏览器		 '真实地址'=>"本机IP:".$_SERVER["REMOTE_ADDR"]."&nbsp;&nbsp;".$IP[0][0]   .$IP[0][1]  .$IP[0][2].  "&nbsp;&nbsp;[".$IP[0][3]."]",//真实ip		 '语言'=>$_SERVER['HTTP_ACCEPT_LANGUAGE'],//语言         '服务器域名/IP'=>$_SERVER['SERVER_NAME'].' [ '.gethostbyname($_SERVER['SERVER_NAME']).' ]',		 '剩余空间'=>round((@disk_free_space(".")/(1024*1024)),2).'M',         'register_globals'=>get_cfg_var("register_globals")=="1" ? "ON" : "OFF",         'magic_quotes_gpc'=>(1===get_magic_quotes_gpc())?'YES':'NO',         'magic_quotes_runtime'=>(1===get_magic_quotes_runtime())?'YES':'NO',		 );		$this->assign('win',$win);		$info=M('config')->find();		$this->assign('info',$info);		$this->display();				}			}?>