<?php// 本类由系统自动生成，仅供测试用途class AboutAction extends CommonAction {    public function index(){		$cid=$_GET['cid'];		$sid=$_GET['sid'];		$this->eqid=$aid;		$this->cid=$cid;		$this->sid=$sid;		$this->cname=M('cate')->where("id='$cid'")->find();		$this->subname=M('cate')->where("id='$sid'")->find();		$this->sublist=M('cate')->where("pid='$cid'")->select();		$this->single=M('single')->where("kinds='$sid'")->find();			$this->display();    }	    public function lists(){		$cid=$_GET['cid'];		$aid=$_GET['aid'];		$this->eqid=$aid;		$this->cid=$cid;		$this->cname=M('cate')->where("id='$cid'")->find();		$this->subname=M('cate')->where("id='$aid'")->find();		$this->sublist=M('cate')->where("pid='$cid'")->select();				import('Class.Page',APP_PATH);		$m=M('article');		$where = "kinds='$aid'";		$p=getpage($m,$where,8);		$list=$m->field(true)->where($where)->order('id desc')->select();		$this->lists=$list;		$this->page=$p->show();			$this->display();    }	    public function video(){		$cid=$_GET['cid'];		$aid=$_GET['aid'];		$this->eqid=$aid;		$this->cid=$cid;		$this->cname=M('cate')->where("id='$cid'")->find();		$this->subname=M('cate')->where("id='$aid'")->find();		$this->sublist=M('cate')->where("pid='$cid'")->select();				import('Class.Page',APP_PATH);		$m=M('article');		$where = "kinds='$aid'";		$p=getpage($m,$where,9);		$list=$m->field(true)->where($where)->order('id desc')->select();		$this->lists=$list;		$this->page=$p->show();			$this->display();    }	    public function show(){		$cid=$_GET['cid'];		$aid=$_GET['aid'];		$this->eqid=$aid;		$this->cid=$cid;		$this->cname=M('cate')->where("id='$cid'")->find();		$this->subname=M('cate')->where("id='$aid'")->find();		$this->sublist=M('cate')->where("pid='$cid'")->select();				$id=$_GET['articleid'];		$this->detail=M('article')->where("id='$id'")->find();						//相关阅读		$this->read=M('article')->where("kinds='$aid'")->limit(8)->order("id DESC")->select();		//新闻资讯		$this->news=M('article')->where("kinds in(34,35,36,38,39)")->order("id DESC")->limit(8)->select();		//新闻资讯		$this->question=M('article')->where("kinds='43'")->limit(8)->order("id DESC")->select();				//上下篇		$up=M('article')->where("kinds='$aid' and id<'$id'")->order('id DESC')->LIMIT(1)->find();		if($up){			$this->upid=$up['id'];			$this->up=$up['title'];		}else{			$this->upend="没有了";		}		$down=M('article')->where("kinds='$aid' and id>'$id'")->order('id ASC')->LIMIT(1)->find();		if($down){			$this->downid=$down['id'];			$this->down=$down['title'];		}else{			$this->downend="没有了";		}					$this->display();    }	}?>