<?php	class CommonAction extends Action {        //这是thinkphp中自带的一个始使化方法，加载类的时候会首先调有和这个方法		public function _initialize(){		 header("Content-Type:text/html; charset=utf-8");		 	if(isMobile()){				//$this->redirect('m.qsn580.com');				header('Location:http://m.qsn580.com');			}					$vid=I('id',0,'intval');			$cid=I('cid',0,'intval');			$sid=I('sid',0,'intval');			$aid=I('aid',0,'intval');			$articleid=I('articleid',0,'intval');			$site=M('config')->find();			$this->url=$site['site_url'];			$this->site=$site;						//SEO标题			$fname=M('cate')->where("id='$cid'")->find();			$sname=M('cate')->where("id='$aid'")->find();			$tname=M('article')->where("id='$articleid'")->find();			$title=M('single')->where("kinds='$sid'")->find();			if($cid){				$line="_";			}			if($aid){				$line1="_";			}			if($articleid){				$line2="_";			}			if($sid){				$line3="_";			}						$this->title=$title['title'].$line3.$tname['title'].$line2.$sname['name'].$line1.$fname['name'].$line;			//栏目下面的图片			$this->pic=M('cate')->where("id='$cid'")->find();						$topad=M('ad')->where("pos='topad'")->order("id DESC")->find();			$this->topad="<a href='".$topad[url]."' target='_blank' class='topad'><img src='".$topad[pic]."'></a>";			$weixin=M('ad')->where("pos='weixin'")->order("id DESC")->find();			$this->weixin="<img src='".$site[site_url]."/".$weixin[pic]."'  width='144'>";			$this->yq=M('ad')->where("kinds='友情链接'")->order("id DESC")->select();			$this->qq=M('qq')->order("sort DESC")->select();			$this->set=M('qq_set')->where('id=1')->find();		 }	}		?>