<?php// 本类由系统自动生成，仅供测试用途class NewsAction extends CommonAction {    public function index(){		$cid=$_GET['cid'];		$aid=$_GET['aid'];		$this->eqid=$aid;		$this->cid=$cid;		$this->cname=M('cate')->where("id='$cid'")->find();		$this->subname=M('cate')->where("id='$aid'")->find();		$this->sublist=M('cate')->where("pid='$cid'")->select();				import('Class.Page',APP_PATH);		$m=M('article');		$where = "kinds='$aid'";		$p=getpage($m,$where,16);		$list=$m->field(true)->where($where)->order('id desc')->select();		$this->lists=$list;		$this->page=$p->show();	$this->display();    }	}?>