<?php	class CommonAction extends Action {        //这是thinkphp中自带的一个始使化方法，加载类的时候会首先调有和这个方法		public function _initialize(){			header('Content-Type: text/html; charset=utf-8');			$config=M('config')->where("id=1")->find();			$this->site=$config;			if(false===isMobile()){				echo "请使用手机访问";die();			}			//nav		import('Class.Cate',APP_PATH);		$nav=M('cate')->where("nav='1'")->order('sort ASC')->select();		$this->nav=Cate::navtree($nav,'child','0');		$logo=M('link')->where("id=65")->find();		$this->logo=imgpath($logo['image'],3);		}			};		?>