INSERT INTO `think_article` VALUES ('349', '24', '感恩励志端午节——学员个人才艺展示<视频>', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://static.video.qq.com/TPout.swf?vid=p0160tucz11&auto=0\" width=\"650\" height=\"520\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20151214/1450056057135468.jpg\" title=\"文艺展示\" alt=\"文艺展示\" width=\"648\" height=\"354\" style=\"width: 648px; height: 354px;\" border=\"0\" vspace=\"0\"/></a></p>', '1', null, '1435412104', 'mingyu');
INSERT INTO `think_article` VALUES ('350', '24', '青少年励志成长教育：舞蹈开班仪式<视频>', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://static.video.qq.com/TPout.swf?vid=z01614yobih&auto=0\" width=\"650\" height=\"520\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20151214/1450055919132839.jpg\" title=\"特长班的舞蹈\" alt=\"特长班的舞蹈\" width=\"650\" height=\"353\" style=\"width: 650px; height: 353px;\" border=\"0\" vspace=\"0\"/></a></p>', '1', null, '1435757529', 'mingyu');
INSERT INTO `think_article` VALUES ('351', '24', '夏令营学员湘西“手牵手”反差式教育扶贫献爱心活动启动中', '', '', '', '夏令营，夏令营活动，军事夏令营，青少年夏令营，励志夏令营，素质夏令营，教育问题孩子学校', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439434596830943.jpg\" title=\"准备出发湘西\" alt=\"准备出发湘西\" width=\"301\" height=\"182\" style=\"width: 301px; height: 182px;\" border=\"0\" vspace=\"0\"/><img src=\"/ueditor/php/upload/image/20150813/1439434907104197.jpg\" title=\"出发湘西反差式教育\" width=\"308\" height=\"182\" style=\"width: 308px; height: 182px;\" border=\"0\" vspace=\"0\" alt=\"出发湘西反差式教育\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439434910557519.jpg\" style=\"width: 298px; height: 202px;\" title=\"出发湘西\" width=\"298\" height=\"202\" border=\"0\" vspace=\"0\" alt=\"出发湘西\"/><img src=\"/ueditor/php/upload/image/20150813/1439434910694575.jpg\" title=\"夏令营活动\" width=\"313\" height=\"202\" style=\"width: 313px; height: 202px;\" border=\"0\" vspace=\"0\" alt=\"夏令营活动\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439434913330656.jpg\" style=\"width: 483px; height: 311px;\" title=\"湘西夏令营活动进行中\" width=\"483\" height=\"311\" border=\"0\" vspace=\"0\" alt=\"湘西夏令营活动进行中\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;<span style=\"font-size: 16px; color: rgb(0, 112, 192);\"> 湖南青少年励志教育培训学校2015年夏令营学员出发湘西手牵手“反差式”教育扶贫献爱心活动正在出发中，夏令营学员信心满满，兴高采烈地备好装备整队待发，向往着美丽的湘西。</span></span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">&nbsp; 本次活动主要目的是：夏令营旅游拓展、感恩励志教育、市农反差体验教育、生活体验、历史文化的了解与学习、发泄式心理教育、扶贫献爱心活动、抗压实践训练、吃苦耐劳训练、抗打击训练等等，我们是在玩中学，学中玩，边玩边学，快乐成长，励志成才！！！</span></p>', '1', null, '1438657515', 'mingyu');
INSERT INTO `think_article` VALUES ('352', '24', '夏令营学员湘西扶贫活动到达目的地，自己动手做饭！', '', '', '', '夏令营，夏令营活动，军事夏令营，青少年夏令营，励志夏令营，素质夏令营，教育问题孩子学校', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439450630101890.jpg\" style=\"width: 337px; height: 199px;\" title=\"吃的材火饭\" width=\"337\" height=\"199\" border=\"0\" vspace=\"0\" alt=\"吃的材火饭\"/><img src=\"/ueditor/php/upload/image/20150813/1439450630780381.jpg\" title=\"切的是绿色食材\" width=\"322\" height=\"199\" style=\"width: 322px; height: 199px;\" border=\"0\" vspace=\"0\" alt=\"切的是绿色食材\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439450633897255.jpg\" style=\"width: 338px; height: 192px;\" title=\"等待的是开饭\" width=\"338\" height=\"192\" border=\"0\" vspace=\"0\" alt=\"等待的是开饭\"/><img src=\"/ueditor/php/upload/image/20150813/1439450633905216.jpg\" title=\"训练的是自控能力\" width=\"321\" height=\"191\" style=\"width: 321px; height: 191px;\" border=\"0\" vspace=\"0\" alt=\"训练的是自控能力\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439450645135141.jpg\" style=\"width: 538px; height: 267px;\" title=\"这叫真食！\" width=\"538\" height=\"267\" border=\"0\" vspace=\"0\" alt=\"这叫真食！\"/></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp;<span style=\"color: rgb(0, 112, 192); font-size: 18px;\"> 孩子的成长，不需要有多聪明、多高多帅或国家栋梁之才，而是需要孩子能否正常的健康、快乐地成长！在不断长大的过程中，涉及的不仅仅是成长，更多涉及的是生活与生存！</span></span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 在本次湘西夏令营活动中，让他们了解生活，学会生活，懂得生活！</span></p>', '1', null, '1438698515', 'mingyu');
INSERT INTO `think_article` VALUES ('353', '24', '湘西夏令营学员下河洗漱，品味纯真的自然！', '', '', '', '', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439452873133790.jpg\" style=\"width: 324px; height: 196px;\" title=\"湘西夏令营学员下河洗漱，品味纯真的自然！\" width=\"324\" height=\"196\" border=\"0\" vspace=\"0\" alt=\"湘西夏令营学员下河洗漱，品味纯真的自然！\"/><img src=\"/ueditor/php/upload/image/20150813/1439452877733003.jpg\" title=\"夏令营学员河边洗漱\" width=\"330\" height=\"195\" style=\"width: 330px; height: 195px;\" border=\"0\" vspace=\"0\" alt=\"夏令营学员河边洗漱\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439452883491982.jpg\" style=\"width: 320px; height: 183px;\" title=\"纯净的自然，纯真的自己\" width=\"320\" height=\"183\" border=\"0\" vspace=\"0\" alt=\"纯净的自然，纯真的自己\"/><img src=\"/ueditor/php/upload/image/20150813/1439452887196181.jpg\" title=\"我们下水，融入自然\" width=\"333\" height=\"184\" style=\"width: 333px; height: 184px;\" border=\"0\" vspace=\"0\" alt=\"我们下水，融入自然\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150813/1439452888227595.jpg\" style=\"width: 310px; height: 190px;\" title=\"洗心革面\" width=\"310\" height=\"190\" border=\"0\" vspace=\"0\" alt=\"洗心革面\"/><img src=\"/ueditor/php/upload/image/20150813/1439452902117205.jpg\" title=\"洗去污垢，牵出友谊！\" width=\"344\" height=\"191\" style=\"width: 344px; height: 191px;\" border=\"0\" vspace=\"0\" alt=\"洗去污垢，牵出友谊！\"/></p><p><span style=\"font-size: 18px;\">&nbsp;&nbsp;<span style=\"color: rgb(0, 112, 192);\">青少年教育夏令营学员湘西反差式教育活动清晨河边洗漱，给予现实的亲切问候，触摸大自然的心灵，为自己洗心革面，重唤人生！</span></span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 2015湘西夏令营活动让孩子问候自然，牵手自然，融入自然，品味自然。让纯真的自然洗去自己的迷茫与无知，洗去惰性与依赖，换来纯真与美丽的人生。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 在我们青少年励志教育学校老师的带领下，夏令营的学员们井然有序的跟着领队老师下河洗漱，很多从没有下过河水的城市孩子有点好奇，一到河边就玩起了水戏。清澈见底的河水给我们一个清凉的问候---要安全而快乐地玩着。<img src=\"http://img.baidu.com/hi/jx2/j_0063.gif\"/></span></p>', '1', null, '1438747556', 'mingyu');
INSERT INTO `think_article` VALUES ('354', '24', '夏令营学员湘西拓展活动游玩“太阳岛”', '', '', '', '夏令营，夏令营活动，军事夏令营，青少年夏令营，励志夏令营，素质夏令营，教育问题孩子学校', '湖南青少年励志教育2015夏令营学员湘西反差式教育“手牵手”扶贫献爱心活动火热进行中，2015夏令营以学中玩，玩中学的“边玩边学”模式开展，让学员们在这美好的环境中找到自我，在玩中寻求快乐，在快乐中领悟知识，从而达到边玩边学的完美效果。', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"http://www.841990053.com/ueditor/php/upload/image/20150813/1439459085397183.jpg\" title=\"出发太阳岛，游湘西\" width=\"320\" height=\"192\" border=\"0\" vspace=\"0\" alt=\"出发太阳岛，游湘西\" style=\"width: 320px; height: 192px;\"/></a><a href=\"http://www.841990053.com/contact.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150813/1439459085681881.jpg\" title=\"太阳岛上游泳\" width=\"324\" height=\"191\" border=\"0\" vspace=\"0\" alt=\"太阳岛上游泳\" style=\"width: 324px; height: 191px;\"/></a><a href=\"http://841990053.com/about.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150813/1439459085780424.jpg\" title=\"太阳岛上生火做饭\" width=\"321\" height=\"195\" border=\"0\" vspace=\"0\" alt=\"太阳岛上生火做饭\" style=\"width: 321px; height: 195px;\"/></a><a href=\"http://841990053.com/study.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150813/1439459088945494.jpg\" title=\"太阳岛上共享美食\" width=\"324\" height=\"195\" border=\"0\" vspace=\"0\" alt=\"太阳岛上共享美食\" style=\"width: 324px; height: 195px;\"/></a><a href=\"http://841990053.com/study.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150813/1439459089111985.jpg\" title=\"太阳岛上烤玉米\" width=\"319\" height=\"198\" border=\"0\" vspace=\"0\" alt=\"太阳岛上烤玉米\" style=\"width: 319px; height: 198px;\"/></a><a href=\"http://841990053.com/case.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150813/1439459091232285.jpg\" title=\"太阳岛上吃零食\" width=\"326\" height=\"198\" border=\"0\" vspace=\"0\" alt=\"太阳岛上吃零食\" style=\"width: 326px; height: 198px;\"/></a><br/></p><p>&nbsp; <span style=\"color: rgb(0, 112, 192); font-size: 18px;\">湖南青少年励志教育2015夏令营学员湘西反差式教育“手牵手”扶贫献爱心活动火热进行中，2015夏令营以学中玩，玩中学的“边玩边学”模式开展，让学员们在这美好的环境中找到自我，在玩中寻求快乐，在快乐中领悟知识，从而达到边玩边学的完美效果。</span></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\"><a href=\"http://www.841990053.com/zhaos.php?cid=49\" target=\"_blank\" title=\"夏令营招生\"><img src=\"/ueditor/php/upload/image/20151010/1444443268782662.jpg\" title=\"假期您孩子在干嘛？学好的吗？\" alt=\"假期您孩子在干嘛？学好的吗？\" width=\"590\" height=\"168\" style=\"width: 590px; height: 168px;\" border=\"0\" vspace=\"0\"/></a></span></p>', '1', null, '1438786021', 'mingyu');
INSERT INTO `think_article` VALUES ('355', '3', '心在学，学在心中！', '', '', '../Uploads/1439723636.jpg', '青少年教育所，青少年心理健康教育，湖南青少年叛逆教育，如何戒网瘾', '叛逆厌学孩子在于心理思想的浮动产生的迷茫选择，幼稚的心灵做出异常的行为。湖南青少年励志教育学校注重孩子心理疏导，以认知引导的人性化教育方针牵引着孩子走向正轨，行于正道。让道理说服孩子的心思，以认知让孩子明辨是非，使良好的思想带着孩子成长，随励志让孩子成才！', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150816/1439722742855127.jpg\" title=\"心在学\" alt=\"心在学\" width=\"471\" height=\"272\" style=\"width: 471px; height: 272px;\" border=\"0\" vspace=\"0\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150816/1439722788123965.jpg\" title=\"学在心中\" alt=\"学在心中\" width=\"472\" height=\"267\" style=\"width: 472px; height: 267px;\" border=\"0\" vspace=\"0\"/></p><p>&nbsp;<span style=\"color: rgb(0, 112, 192); font-size: 18px;\"> 叛逆厌学孩子在于心理思想的浮动产生的迷茫选择，幼稚的心灵做出异常的行为。湖南青少年励志教育学校注重孩子心理疏导，以认知引导的人性化教育方针牵引着孩子走向正轨，行于正道。让道理说服孩子的心思，以认知让孩子明辨是非，使良好的思想带着孩子成长，随励志让孩子成才！</span></p>', '1', null, '1438858926', 'mingyu');
INSERT INTO `think_article` VALUES ('356', '24', '青少年教育：夏令营学员湘西活动游土司遗址', '', '', '', '长沙青少年教育学校，叛逆孩子教育学校，青少年叛逆教育机构', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=356\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439729766524944.jpg\" style=\"width: 331px; height: 166px;\" title=\"老司城土司遗址\" width=\"331\" height=\"166\" border=\"0\" vspace=\"0\" alt=\"老司城土司遗址\"/></a><a href=\"http://www.841990053.com/show.php?cid=24&id=356\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439729766811019.jpg\" title=\"湘西老司城遗址\" width=\"311\" height=\"166\" border=\"0\" vspace=\"0\" alt=\"湘西老司城遗址\" style=\"width: 311px; height: 166px;\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439729766196310.jpg\" style=\"width: 323px; height: 177px;\" title=\"游湘西土司城\" width=\"323\" height=\"177\" border=\"0\" vspace=\"0\" alt=\"游湘西土司城\"/></a><a href=\"http://www.841990053.com/about.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439729766264258.jpg\" title=\"土司古墓碑坊\" width=\"319\" height=\"177\" style=\"width: 319px; height: 177px;\" border=\"0\" vspace=\"0\" alt=\"土司古墓碑坊\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=356\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439729766139577.jpg\" style=\"width: 320px; height: 185px;\" title=\"土司古墓\" width=\"320\" height=\"185\" border=\"0\" vspace=\"0\" alt=\"土司古墓\"/></a><a href=\"http://www.841990053.com/about.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439729766121956.jpg\" title=\"进发土司遗址城\" width=\"323\" height=\"185\" style=\"width: 323px; height: 185px;\" border=\"0\" vspace=\"0\" alt=\"进发土司遗址城\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439729767950916.jpg\" style=\"width: 321px; height: 181px;\" title=\"土司遗址祖师殿\" width=\"321\" height=\"181\" border=\"0\" vspace=\"0\" alt=\"土司遗址祖师殿\"/></a><a href=\"http://www.841990053.com/about.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439732340718157.jpg\" title=\"土王桥\" alt=\"土王桥\" width=\"320\" height=\"181\" style=\"width: 320px; height: 181px;\" border=\"0\" vspace=\"0\"/></a></p><p>&nbsp; <span style=\"color: rgb(0, 112, 192); font-size: 18px;\">湖南青少年励志教育学校夏令营学员在学校夏令营中心老师的带领下，步入了湘西土司城遗址，参观了土司悠久的历史文化和风土人情，深深地体会了土司文化的传统风味。一位衡阳戒网瘾学员突然说：我听我爷爷说他以前在这里生活过，还跟过这边的土匪上山打猎，每天都能够吃上“野味”。让我们情不自禁地流下了口水，只能往山上往往去，想念着湘西腊肉。</span><img src=\"http://img.baidu.com/hi/jx2/j_0057.gif\"/></p>', '1', null, '1438866164', 'mingyu');
INSERT INTO `think_article` VALUES ('357', '24', '感恩励志教育：夏令营学员出游芙蓉古镇', '', '', '', '', '', '<p><br/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150816/1439731221344383.jpg\" style=\"width: 322px; height: 183px;\" title=\"芙蓉古镇\" width=\"322\" height=\"183\" border=\"0\" vspace=\"0\" alt=\"芙蓉古镇\"/><img src=\"/ueditor/php/upload/image/20150816/1439731221904700.jpg\" title=\"土王司\" width=\"332\" height=\"183\" border=\"0\" vspace=\"0\" alt=\"土王司\" style=\"width: 332px; height: 183px;\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150816/1439731221135287.jpg\" style=\"width: 330px; height: 180px;\" title=\"摆手堂\" width=\"330\" height=\"180\" border=\"0\" vspace=\"0\" alt=\"摆手堂\"/><img src=\"/ueditor/php/upload/image/20150816/1439731221634804.jpg\" title=\"抗战功臣\" width=\"324\" height=\"180\" style=\"width: 324px; height: 180px;\" border=\"0\" vspace=\"0\" alt=\"抗战功臣\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150816/1439731221106559.jpg\" style=\"width: 328px; height: 188px;\" title=\"湘西土匪纪实\" width=\"328\" height=\"188\" border=\"0\" vspace=\"0\" alt=\"湘西土匪纪实\"/><img src=\"/ueditor/php/upload/image/20150816/1439731222133424.jpg\" title=\"原始人部落\" width=\"326\" height=\"189\" style=\"width: 326px; height: 189px;\" border=\"0\" vspace=\"0\" alt=\"原始人部落\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150816/1439731222605586.jpg\" style=\"width: 336px; height: 199px;\" title=\"芙蓉镇留念\" width=\"336\" height=\"199\" border=\"0\" vspace=\"0\" alt=\"芙蓉镇留念\"/><img src=\"/ueditor/php/upload/image/20150816/1439731222618797.jpg\" title=\"土司王府\" width=\"318\" height=\"199\" style=\"width: 318px; height: 199px;\" border=\"0\" vspace=\"0\" alt=\"土司王府\"/></p>', '1', null, '1439040951', 'mingyu');
INSERT INTO `think_article` VALUES ('358', '24', '感恩励志教育：夏令营湘西扶贫献爱心活动', '', '', '', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=358\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439733212854740.jpg\" style=\"width: 322px; height: 189px;\" title=\"扶贫活动\" width=\"322\" height=\"189\" border=\"0\" vspace=\"0\" alt=\"扶贫活动\"/></a><a href=\"http://www.841990053.com/show.php?cid=24&id=358\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439733212119658.jpg\" title=\"扶贫爱心送到家\" width=\"344\" height=\"189\" style=\"width: 344px; height: 189px;\" border=\"0\" vspace=\"0\" alt=\"扶贫爱心送到家\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439733212879053.jpg\" style=\"width: 326px; height: 199px;\" title=\"扶贫活动进行中\" width=\"326\" height=\"199\" border=\"0\" vspace=\"0\" alt=\"扶贫活动进行中\"/></a><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439733212811966.jpg\" title=\"1439733212811966.jpg\" width=\"339\" height=\"200\" style=\"width: 339px; height: 200px;\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=358\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439733212123707.jpg\" style=\"width: 328px; height: 198px;\" title=\"感恩励志献爱心活动\" width=\"328\" height=\"198\" border=\"0\" vspace=\"0\" alt=\"感恩励志献爱心活动\"/></a><a href=\"http://www.841990053.com/show.php?cid=24&id=358\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150816/1439733212698707.jpg\" title=\"感恩励志教育扶贫活动\" width=\"338\" height=\"199\" style=\"width: 338px; height: 199px;\" border=\"0\" vspace=\"0\" alt=\"感恩励志教育扶贫活动\"/></a></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">一份爱心挽救一个家庭，一点奉献造福一方社会。湖南青少年励志教育学校感恩励志教育课程中让孩子学习的不仅仅是理论知识，更是注重现实社会的实践拓展活动，只有在实践中才能让孩子看到真理、学会真理、明白真理和使用真理。我们的教育不是纸上谈兵，而是理论实践相结合，把理论装在大脑，让实践来证实它的存在。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 夏令营虽然短暂，但是却让孩子平时无法学到的文化和真理，更是无法实现的梦想。但是在湖南青少年励志教育学校，一切理论与梦想都是尽可能地让它变成现实，就在两手之间。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 本次夏令营校外实践拓展活动于湖南湘西吉首张家界等地区展开，由学校出资扶贫献爱心的所需物品与资金，通过学生奉献的形式完成。让学校的爱心教育事业悄然无声地散发民间，让文明和感恩透过每一位孩子的身心，美好的意念留在每一位学生的脑海里，让他们学会知恩、感恩、报恩和孝顺。</span></p>', '1', null, '1439128689', 'mingyu');
INSERT INTO `think_article` VALUES ('359', '24', '青少年教育：游芙蓉古镇，赏阅摆手舞<视频>', '', '', '', '夏令营，夏令营活动，军事夏令营，青少年夏令营，励志夏令营，素质夏令营，教育问题孩子学校', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://static.video.qq.com/TPout.swf?vid=g0162nkk8s7&auto=0\" width=\"600\" height=\"500\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150818/1439860994759467.jpg\" title=\"游芙蓉古镇，赏阅摆手舞\" alt=\"游芙蓉古镇，赏阅摆手舞\" width=\"600\" height=\"354\" style=\"width: 600px; height: 354px;\" border=\"0\" vspace=\"0\"/></p>', '1', null, '1439040289', 'mingyu');
INSERT INTO `think_article` VALUES ('360', '24', '夏令营校外活动凯旋而归，全体师生校门迎接', '', '', '', '夏令营，夏令营活动，军事夏令营，青少年夏令营，励志夏令营，素质夏令营，教育问题孩子学校', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150818/1439866376834859.jpg\" style=\"width: 299px; height: 165px;\" title=\"学生拉彩旗校门等待\" width=\"299\" height=\"165\" border=\"0\" vspace=\"0\" alt=\"学生拉彩旗校门等待\"/><img src=\"/ueditor/php/upload/image/20150818/1439866376280265.jpg\" title=\"夏令营校外活动学员归来\" width=\"301\" height=\"165\" style=\"width: 301px; height: 165px;\" border=\"0\" vspace=\"0\" alt=\"夏令营校外活动学员归来\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150818/1439866376241623.jpg\" style=\"width: 299px; height: 168px;\" title=\"全校热烈欢迎夏令营学员归来\" width=\"299\" height=\"168\" border=\"0\" vspace=\"0\" alt=\"全校热烈欢迎夏令营学员归来\"/><img src=\"/ueditor/php/upload/image/20150818/1439866376831804.jpg\" title=\"夏令营学员凯旋而归\" width=\"299\" height=\"168\" style=\"width: 299px; height: 168px;\" border=\"0\" vspace=\"0\" alt=\"夏令营学员凯旋而归\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150818/1439866377997476.jpg\" style=\"width: 303px; height: 183px;\" title=\"收获满满，精神抖擞\" width=\"303\" height=\"183\" border=\"0\" vspace=\"0\" alt=\"收获满满，精神抖擞\"/><img src=\"/ueditor/php/upload/image/20150818/1439866377140830.jpg\" title=\"接受全校师生的赞礼\" width=\"296\" height=\"181\" style=\"width: 296px; height: 181px;\" border=\"0\" vspace=\"0\" alt=\"接受全校师生的赞礼\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150818/1439866377907196.jpg\" style=\"width: 315px; height: 177px;\" title=\"夏令营校外实践拓展圆满结束！\" width=\"315\" height=\"177\" border=\"0\" vspace=\"0\" alt=\"夏令营校外实践拓展圆满结束！\"/></p><p>&nbsp;<span style=\"color: rgb(0, 112, 192); font-size: 18px;\"> 本次夏令营活动按原计划开展时间为半个月的校外实践拓展、参观学习、农村生活体验、走访平困户、扶贫献爱心等活动，行走路线为长沙---益阳---常德---张家界---吉首---湘西其他市区、县级城镇及农村等，涉及的夏令营学生有上网成瘾（戒除网瘾）、好吃懒做（生活教育）、奢侈消费（价值观教育）、亲情淡漠（亲子教育）、以自我为中心（感恩励志教育）、早恋厌学（厌学早恋教育）等等不同类型问题青少年参加。在夏令营活动中心老师的组织下各项活动获得圆满成功，让全体师生满载而归。</span></p>', '1', null, '1439298063', 'mingyu');
INSERT INTO `think_article` VALUES ('361', '5', '梁老师》心理文化辅导', '', '', '../Uploads/1444962403.jpg', '湖南青少年戒网瘾励志教育学校，叛逆早恋教育学校，青少年励志戒网瘾学校，孩子叛逆厌学教育学校，湖南戒网瘾特训学校，长沙岳阳叛逆孩子教育学校，2015湖南长沙夏令营，叛逆孩子特训学校，特训戒网瘾学校', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20151016/1444962373123712.jpg\" title=\"心理辅导老师\" alt=\"心理辅导老师\" width=\"238\" height=\"283\" style=\"width: 238px; height: 283px;\" border=\"0\" vspace=\"0\"/></p><p><span style=\"font-family: Simsun; line-height: 25px; background-color: rgb(242, 242, 242);\"></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">梁琳</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 心理老师兼文化辅导。毕业于长沙民政学院心理系教育心理学专业，对青少年心理特点、辅导技术有着自己个性化的辅导教育技巧。擅长团体辅导，其自创的“我们一起握握手”“亲子沟通座椅”“校园寻宝&quot;等团体辅导游戏激发青少年心理能量，培养团队归属感、团队意识协作精神等方面有明显效果；她综合运用宣泄疗法体验心理疗法、认知疗法等引导青少年自我成长。</span></p>', '1', null, '1426818762', 'mingyu');
INSERT INTO `think_article` VALUES ('362', '5', '黄老师》德育心理辅导', '', '', '../Uploads/1440038460.jpg', '湖南青少年戒网瘾励志教育学校，叛逆早恋教育学校，哪里有戒网瘾学校，孩子叛逆不听话教育学校，长沙戒网瘾学校，长沙岳阳叛逆孩子教育学校，2015湖南长沙励志成长夏令营，湖南长沙特训学校', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150820/1440038320126565.jpg\" title=\"黄老师\" alt=\"黄老师\" width=\"272\" height=\"301\" style=\"width: 272px; height: 301px;\" border=\"0\" vspace=\"0\"/></p><p><span style=\"padding: 0px; margin: 0px; font-size: 18px; color: rgb(0, 112, 192);\">黄满</span></p><p><span style=\"padding: 0px; margin: 0px; font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 德育兼心理老师，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">曾于中国人民解放军河南军区75223部队教导大队服役四年，四级士官，大专学历，中共党员。 期间，多次获得“优秀班长”、“十佳带兵骨干”等荣誉称号。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">于老师军事素质过硬，带训经验丰富。动作指导，耐心细致；奖惩分明，严守原则。经过他训练的学生，纪律性、荣誉感都较强，具有良好的团队合作意识。</span></p>', '1', null, '1429530060', 'mingyu');
INSERT INTO `think_article` VALUES ('363', '3', '夏令营学员校外活动归来', '', '', '../Uploads/1440816916.jpg', '叛逆反抗孩子教育学校，管家叛逆厌学孩子机构', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=3&id=363\" target=\"_self\"><img src=\"http://www.841990053.com/ueditor/php/upload/image/20150829/1440816762111435.jpg\" title=\"校外拓展活动归来\" alt=\"校外拓展活动归来\" width=\"474\" height=\"281\" border=\"0\" vspace=\"0\" style=\"text-align: center; white-space: normal; width: 474px; height: 281px;\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150829/1440816716842261.jpg\" title=\"夏令营活动\" alt=\"夏令营活动\" width=\"474\" height=\"273\" style=\"width: 474px; height: 273px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/about.php\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150829/1440816792395462.jpg\" title=\"夏令营学员活动归来\" alt=\"夏令营学员活动归来\" width=\"473\" height=\"292\" style=\"width: 473px; height: 292px;\" border=\"0\" vspace=\"0\"/></a></p>', '1', null, '1439988875', 'mingyu');
INSERT INTO `think_article` VALUES ('365', '20', '一个娄底独生子的叛逆蜕变', '', 'http://www.841990053.com/case.php?cid=20', '../Uploads/1484642340.jpg', '叛逆厌学辍学教育学校，管教叛逆孩子的学校，娄底特训学校', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=20\" target=\"_self\" title=\"管教叛逆孩子的学校\"><img src=\"/ueditor/php/upload/image/20170117/1484642240136044.jpg\" title=\"叛逆孩子学校\" alt=\"叛逆孩子学校\" width=\"304\" height=\"185\" style=\"width: 304px; height: 185px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">娄底青少年管教学校，叛逆孩子特训中心</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 熊周，男，16岁，来自湖南娄底市，2014年1月17号入校；是家里的独生子，亲人都是比较关爱的，但是出现溺爱现象，自从他上了初中后出现叛逆行为，通过学校老师的反应，说他经常迟到逃课到外面玩，结交一些不良社会混混为好友，甚至称兄道弟的，还有拉帮结派打架现象等，使得学习情况不堪设想，父母怎么也没想到会出现的这种问题，感觉非常揪心。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题解析</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">湖南娄底叛逆青少年孩子特殊教育学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 由于熊周是家里的独生子，其父母及亲人都比较关爱，甚至出现溺爱情况，让他感觉到自己成了无事不从，无人不拥的“皇太子”，以为自己所需和所求的都会得到满足，所遇到的事情总会有人帮忙处理和解决，过着衣食无忧的“皇帝日子”。长期生活在这种安逸的环境中使得他自身的思想意识能力不断出现偏差现象，明辨是非能力下降，不知道什么是好是坏，无论是非黑白，先做了再说，反正有人扛着。再加上处于成长的迷茫期，思想观念相对较弱，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">控制能力差，遇事容易冲动，感情用事，不考虑后果。由于亲人的过于关爱，百事百依，缺乏思想上和生活上的约束，使得他放纵知如等导致了严重的后果问题。</span><span style=\"color: rgb(0, 112, 192); font-size: 16px;\">&nbsp;</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">长株潭娄底怀化问题少年孩子心理辅导机构</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.加强对其心理思想进行正确的疏导，改变对生活和事物的观念及看法；</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.对其父母进行相应的说教，建议其改变对孩子成长的管教理念，适度关爱；</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3.对其本人灌输一些良好的学习和生活的思想与理念，纠正其偏差思想，增强其明辨是非能力；</span></p><p><span style=\"color:#0070c0;font-size:18px\">4.进行基本素质教育、感恩励志教育、自控能力训练、挫折打击教育训练、生活体验、生活实践拓展等等全面化举辅导；</span></p><p><span style=\"color:#0070c0;font-size:18px\">5.加强文化辅导，培养其学习及生活兴趣，增强他自信心，提升责任感等。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">教育效果</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">娄底厌学辍学叛逆早恋孩子管教所</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 回家后不再过着“皇太子”的生活，也不再百事依父母的行为，不再迟到逃学，每天在家听父母，在学校听老师的乖孩子。现在在家能也够懂得尊重他人，明辨是非能力得到提升，自控能力加强，不再是那么的情绪化和冲动派了，现在不论做什么都会有所思考后再行事的原则让其父母非常满意。</span><br/></p>', '1', null, '1435664202', 'mingyu');
INSERT INTO `think_article` VALUES ('366', '8', '造成青少年游戏瘾及危害有哪些？', '', '', '', '戒网瘾学校，正规戒网瘾机构，湖南戒网瘾学校，青少年戒网瘾，孩子网瘾怎么办', '特别是浪费孩子的宝贵时间-学习。青少年期的孩子的主要任务就不是学习，就其自然的成长和生活规律来说也是要学习的，因为是处于一无所知的成长期，思想意识和心理道德都没有成熟，不能独立的完成自身的生活问题和社会生存问题，所以上网成瘾影响到了孩子的宝贵成长学习时间。', '<p><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">一、青少年游戏瘾的危害有哪些？</span></strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\"><br/></span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　1.特别是浪费孩子的宝贵时间-学习。青少年期的孩子的主要任务就不是学习，就其自然的成长和生活规律来说也是要学习的，因为是处于一无所知的成长期，思想意识和心理道德都没有成熟，不能独立的完成自身的生活问题和社会生存问题，所以上网成瘾影响到了孩子的宝贵成长学习时间。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　2.网络成瘾严重的危害及其身心健康。众所周知，电子计算机本身就是自带有辐射的散发特性，由于青少年期孩子各个肢体和思想细胞等处于脆弱的发育成长期，免疫功能极差，无法抵挡电子计算机散发的辐射危害。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 3.给父母带及家庭来着巨大的生活压力。首先上网所需的硬件、软件及通信等装备就不是免费的，光这些就让父母流尽了热血；再次就是给其身心造成了二次伤害后更是让父母心撕裂费，在此又是增添了巨大的保养资金等消费。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　4</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">.造成偏差的思想定格。因为网络是全球化的，好的坏的都融入了一体，本来思想不成熟，明辨是非不强的青少年很容易被各种新颖的东西带进误区，如：暴力游戏、色情影视及文书、反动派思想组织、传销洗脑等等社会危害性禁区，一旦误入一些不良禁区，便将无法脱身，思想被锁定更新---洗脑，造成了偏差的思想及行为，即使回家也是习惯性的“唱反调”，很难纠正。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 5.影响正常的生活规律。人类的生活都是有基本的规律活动期的，比如：会按时吃饭、按时睡觉、按时起床、按时学习等等一系列的循坏活动都是正常的生活规律。那么当孩子上网成瘾之后，这些正常的生活活动规律既被颠覆（如正常的吃饭时间他不会吃饭，到睡觉时间他不睡觉等），这样久而久之，他自身的生活活动规律就会受到影响，甚至危及到生命健康等。</span></p><p><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">二、造成</span></strong><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">青少年</span></strong><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">游戏瘾的原因和解决建议</span></strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　1.受父母或家庭的情感问题影响所致。情感既是家庭其他成员之间的感情或者是家庭成员与其自身的感情问题出现了某种异议时，他的心灵将会受到打击或者其他影响，因而青少年期的孩子又想渴望得到这些感情的充实便会寻找其他途径来弥补，既是网络。友很多网瘾青少年都是因为家庭情感缺失才造成了他们去网络世界中寻找发泄的对象或者感情的弥补等，就是为了寻找现实中得不到的社会认同和成就感而陷身网络之中而形成了网瘾。当然也有些时候孩子会为了吸引父母注意力，防止父母冲突，故意做出一些让父母担心的事情，转移家庭矛盾的焦点等所为的，但这一部分的存在是极少的。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 解决建议：要戒除青少年期孩子的游戏瘾问题，那么重点是在于家长要尽快改善父母之间的关系和改善家庭关系及环境等。同样也要从孩子成长的角度教给他们采取积极主动的心态，将家庭生活的一些不良因素合理化，也有助于这个问题的处理和解决，最关键还是以沟通来化解，以和谐来造福。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　2.受某些学校的管理和教学模式影响所致。目前我国的教育政策都是存在一定的漏洞，很多时候在于生活和学习的人为割裂而造成了青少年对学习的枯燥无味感，都是因为学校教育体制的僵化给恶化了青少年孩子的厌学情绪，从而偏向与网络的虚拟化，可以为所欲为的发泄。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 解决建议：当您的孩因上网问题而不去上学或者不想学习的时候您就应该为之敲起警钟了，要让孩子更多地去接触生活，寻找一些较好的东西来转移他的上网意念（比如：假期送他们参加一些有意义的青少年活动或者体验生活等）。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　3.还会受到学习压力的影响所致。学校或者家长施加过多的学习压力使孩子们产生了逃避心理，于是躲到网络世界中去。因为现实给予的是压力，所以对于成长期的幼稚青少年不懂是非，既是寻求网络的冲击，消遣内心的压抑，甚至玩起暴力游戏，学习一些不正当的东西来抗拒现实，因而网络成为了他们的“主食”。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp;处理建议：关键还是在于家长心态的调整和亲子关系，要用平常心去面对孩子，适度把握轻重，既不过分严厉，也不过于迁就宽容，要让孩子明白一个“度”的道理，同时也会理解父母的所为。</span></p>', '1', null, '1438347814', 'mingyu');
INSERT INTO `think_article` VALUES ('367', '38', '这些无视孩子人格的话建议您千万别说', '', '', '', '孩子是最敏感的，当他感受到父母的轻蔑时，很快就会自我否定，将自己想得一无是处。更可怕的是，他会强烈地感觉到父母不爱自己。这不再只是挫伤自尊心和勇气的问题，而是无情地吹灭了孩子幼小心灵中全部希望的火苗。', '孩子是最敏感的，当他感受到父母的轻蔑时，很快就会自我否定，将自己想得一无是处。更可怕的是，他会强烈地感觉到父母不爱自己。这不再只是挫伤自尊心和勇气的问题，而是无情地吹灭了孩子幼小心灵中全部希望的火苗。', '<p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 孩子是最敏感的，当他感受到父母的轻蔑时，很快就会自我否定，将自己想得一无是处。更可怕的是，他会强烈地感觉到父母不爱自己。这不再只是挫伤自尊心和勇气的问题，而是无情地吹灭了孩子幼小心灵中全部希望的火苗。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;1、你怎么这么傻</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;父母：“这已经讲到第几遍了？你怎么就一直听不懂呢？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;孩子：“这道题好难，我完全不明白为什么要这样做。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;父母：“到底难在哪儿了？这种题你同龄的孩子都会做了。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">孩子：“我好像没有数学天分……”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;父母：“你怎么总这么傻！这么简单的问题都不会做，以后到底想怎样啊？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;如果列出对孩子的禁语名单，这句话肯定名列榜首。这无疑是无视孩子人格的话中最具代表性的话语。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;在你无视孩子的人格时，也就是将所有美好的事物都踩在脚下时。孩子的自尊心、自我效能感、自信心、主导性动机、成就动机等，所有能给孩子带来光明前途的力量，都被父母无情地踩在了脚下。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;令人惊叹的是，我们平时很容易说出无视孩子人格的话而不自知。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;“你这个没用的！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;“你这孩子怎么这样啊？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;“你这种孩子谁会喜欢啊？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">“真是无可救药！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;“你这样的孩子，当初就不该生下来。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\"> &nbsp;“我怎么会生下你，真是活受罪啊。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;“真是冤家啊！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;“你还吃什么饭哪！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;请你扪心自问，上面的话有没有跟孩子说过。哪怕只说过一句，你也要好好反省一下自己了。因为这些话都会给孩子内心带来极大的伤害。而这俨然是另一种虐待。不只是身体上的虐待才叫虐待。父母这种践踏孩子人格、毁掉孩子梦想、玷污孩子未来的行为反而是更恶毒的虐待。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;有时，孩子的行为的确像个傻瓜。你可能觉得很郁闷，甚至有些寒心，终于有一天，你忍无可忍，对孩子说出了上面的话。可是，事情已经这样了，你说再多批判孩子人格的话也无济于事。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">板上钉钉的事并不是通过你的抱怨就能改变的。其实，这些话对孩子来说并没有任何好处，反而真的可能把孩子变成傻子，从而把他推向无可救药的深渊。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">2、我要是你就不这样做</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;父母：“要选课外辅导班了，你想好选什么科目了吗？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;孩子：“想好了。我要报足球和手工课。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;父母：“上次你不就是报的那两个吗？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">孩子：“我觉得继续以前做过的会更好一些。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;父母：“我要是你的话，就不会重复选同样的课。只有学习新知识，才会更有意思啊！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;父母经常给子女提出一些忠告，“如果我是你就会这样做”。他们站在父母的立场，想用自己的人生经验给孩子指出一条相对安全便捷的道路。当然，他们也会小心翼翼，不想让这样的谈话伤害到孩子，还希望能对孩子起到正确的引导作用。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;但事实上，这话本身就表达了一种谴责的态度，而谴责的对象就是孩子的想法和他们的行为。其实，这句话只强调了父母自己的想法，而没有充分尊重孩子的个性和意见，所以只会让孩子产生心理负担和抗拒感。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;无论如何，父母应该先肯定孩子。比如说，从某方面来说，孩子的选择也有其可取之处。然后，父母再换个说法，比如，“如果是妈妈，可能会那样做”，“爸爸觉得这样做好像也不错”，等等，用这种方式来表达父母的想法。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;有些孩子在父母给出建议后，会拼命想证明那是错的，而最常见的方式就是孩子故意做不好。其实，他们是想通过这种方式证明父母不够严谨的话其实是错的，从而达到自己的报复目的。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">有时，孩子也会做出一些极端行为来切断自己与父母之间的互动。可能很多人都曾有过这种逆反心理和行动，一两次没关系，但如果反复出现，就可能会出现无可挽回的局面。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">当你的孩子有这种逆反征兆时，父母千万不要再怪罪孩子的行为了，而是有必要先自我反省一下，是不是自己的某些话和行为伤害到了孩子，从而让他出现了过激行为。而你说过的话中，很可能就是“要是我就不这样做”这句话伤害到了孩子。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">3、我倒要看看你能做多好</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;妈妈：“妈妈不是跟你说了，不要看那本书，要读这本书，然后写读书心得吗？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;孩子：“那本书没意思。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;妈妈：“就算没意思你也要看，写这种书的感想感悟才更容易得奖。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">孩子：“可是，那本书真的很无趣，我根本没话可写啊。我自己会看着办的，妈妈你就别管了！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">妈妈：“行，随便你！我倒要看看，你到底能做成什么样，走着瞧！</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;有一次，我在电梯里看到这样让人难以置信的一幕：一个妈妈手里拿着的应该是孩子的试卷，她完全不顾外人在场，对孩子大声吼道：</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;“你考得可真好！真搞不懂你脑袋里到底装的是什么，竟然能考出这样的分数！你将来还想做医生？你以为医生是什么人都能当的吗？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;其实，即便是母子两人单独谈话，父母都不应该说出这种话，更何况还当着那么多人的面，这位妈妈竟然毫无顾忌地大吼大叫，平常母子之间的对话模式可想而知。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;而这个妈妈说这些不堪入耳的话时，孩子只是默默地站在一旁抠手指。看来，对于这种唠叨，孩子也已经有了抵抗力。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;父母当着孩子的面发火，或者无视孩子的存在，其实最终是为了强调自己的立场和权利。这种父母有一个特征，那就是有明显的要与孩子竞争的心理企图。当孩子不在自己的掌控范围之内时，就会单方面地开始指责孩子。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;在这种父母的“教育”下长大的孩子，不会把父母当成是支持和帮助自己的引导者，而是要通过吵架或竞争去打败的对手。他们当然无法信赖自己的父母，有的只是厌恶和怨恨。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;他们很肯定，自己的父母既没有意愿帮自己解开心结，也没有能力化解自己的困境。当这种想法变成习惯后，孩子就会在内心对父母生出厌恶之情。如此一来，本该是和睦相处、互相爱护的人，却变成了相互厌倦和无法依赖的对象了。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">4、你连这个都不懂</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;孩子：“爸爸，飓风是什么？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;爸爸：“怎么突然问到这个问题啊？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;孩子：“刚才爸爸看新闻时我也在旁边听到了，说美国有飓风登陆，死了很多人。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;爸爸：“你连飓风是什么都不知道？所以说让你平时多看看书啊！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">孩子没学过的东西当然不知道，即便是学过的东西，孩子也会很容易忘记。如果父母平时因为一点小事就责备孩子“怎么连这个也不懂”“你到底知道什么”等，孩子会真的认为自己无能。而从父母那里体会到的被瞧不起的滋味，会让孩子陷入被孤立的茫然之中。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;事实上，父母说这句话主要是因为他懒得给孩子讲解知识，或是嫌孩子麻烦。但是，孩子肯定不明白父母的这种心理，他们只会感到父母懒得教自己，生活中失去了一位很好的老师，而这会让他们非常失望。如果这种情况太频繁，就会给孩子留下很坏的印象——啊！爸爸妈妈不只是不愿意教我知识，而且还觉得我很烦。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;而孩子之所以问一些很简单的问题，主要是想确认一下自己获取的知识是不是正确，同时也想确认一下父母对自己的爱。所以，孩子才会反复问一些很简单的常识性问题，直到确认父母真的爱自己为止，否则他们不会罢休。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;有时，父母也会被孩子的问题难住，出于尴尬而恼羞成怒，对孩子吼道：“你连这个都不懂？”在孩子的世界里，比起父母答不上自己的问题，父母厌烦自己提问题的语气更让他受伤。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">所以，当孩子问问题时，父母一定要有耐心，不要总摆出一副不耐烦的样子打发孩子。如果父母也有不懂的地方，就要直接告诉孩子自己也不懂，然后和孩子一起寻找答案，这才是正确的做法。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;5、你问这个干什么</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">孩子：“妈妈，谁寄来的快递啊？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">妈妈：“妈妈在网上买的。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">孩子：“买的什么？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">妈妈：“就是一些妈妈需要的东西。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">孩子：“到底是什么啊？我打开看看行吗？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;妈妈：“你知道这个干什么？别管这个，干你自己的事儿去！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;什么事都想插一脚的孩子，的确有些折磨人。父母在烦闷的时候会不自觉地大发雷霆，这也是人之常情。但再生气也不能用“你问这个干吗？”“有工夫问这个，不如去学习”之类的话来刺伤孩子，父母一定要学会控制自己的情绪。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;被其他人无视的感觉真的很糟糕，这一点无论是对大人来说还是对孩子来说都是一样的。而且，孩子的内心很脆弱，他们还不能像大人一样很好地调试自己。当他们被外人，尤其是被父母无视时，孩子会认为父母不爱自己，内心受到很大打击。所以，父母讲话时一定要格外注意。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;当孩子开始大事小事都要插手时，你一定要先了解一下孩子为何那么做，真正的目的是什么。孩子只是想知道一个答案？还是想借机和爸爸妈妈聊天？这一点你必须先弄清楚。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">如果是前者，你就要给他明确的答案，解决他的困惑。如果是后者，那父母就需要跟孩子进行一次深入交流，满足他的沟通欲望了。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;这两者有一个共同点，那就是，都需要父母亲切地给予说明。当然，教孩子自行解决问题的方法更好。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;比如，孩子特别喜欢某个网站，每天都缠着你要上那个网站，那就不如干脆抽出时间，教孩子用电脑进行网站搜索的方法。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;或者，父母还可以跟孩子制订这样的规则，有想知道的事情，在来问爸爸妈妈之前，先问一下哥哥姐姐，如果还是得不到答案就去翻书，若实在是自己的能力解决不了的问题，就再来询问爸爸妈妈。但父母需要注意的是，在这种情况下，若孩子再来问你问题，你一定要用十二分的热情给予解答。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;6、你就不觉得羞愧吗</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;妈妈：“你都多大了，连自己的行李都不会收拾吗？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;孩子：“我总是会遗漏啊，你让我怎么办？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">妈妈：“到底要让妈妈照顾你到什么时候啊！你就不觉得羞愧吗？”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">孩子：“我以后会注意的。”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">妈妈：“你就只会说。只说不做，让妈妈怎么相信你！”</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">心理学家将人们在做错事后感受到的消极情感主要分为羞愧和内疚两种。羞愧是对自己的全盘否定，而内疚则是对自己某种行为的消极评价。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">羞愧感让一个人对自己进行了全盘否定和谴责，他们过度在意别人对自己的看法。所以，当一个人觉得羞愧时，内心就会严重萎缩，觉得自己一无是处、渺小，整个人变得无精打采。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;相反，内疚的对象不是对本人，内疚是针对自己所做的某种行为的自我谴责，还有因此而产生的紧张、自责、后悔等。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span><wbr/><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;虽说这两种情绪都是消极情绪，但从对孩子的破坏性上来说，羞愧给人带来的痛苦要远远超过内疚。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;所以，父母应尽量避免让孩子对自己的行为产生过度羞愧感，因为这在很多方面都会给孩子造成不良影响。有时候，父母是有口无心，说是让孩子“有点羞耻心吧”，可并不是真的那么想的，他们的初衷是让孩子审视自己行为的不足，可孩子感受到的却只是那种说不出的羞愧感和茫然无措。</span></p>', '1', null, '1438440172', 'mingyu');
INSERT INTO `think_article` VALUES ('368', '19', '江西“唐少爷”叛逆厌学的逆转', '', 'http://www.841990053.com/case.php?cid=20', '../Uploads/1456997441.jpg', '叛逆教育，厌学早恋教育，江西戒网瘾学校，江西特训学校', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=20\" target=\"_self\" title=\"少年戒网瘾学校\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20160303/1456997397526063.jpg\" title=\"网瘾少年在戒网瘾学校生活体验\" alt=\"网瘾少年在戒网瘾学校生活体验\" width=\"259\" height=\"148\" style=\"width: 259px; height: 148px;\" border=\"0\" vspace=\"0\"/></span></strong></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"color: rgb(255, 255, 255);\">江西叛逆厌学青少年教育特训学校</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><br style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; text-align: justify; text-indent: 32px; white-space: normal;\"/>&nbsp; 唐少杰，男，17岁，来自江西新余市，2013年9月11号入校。上高中后上网逐渐增多，学习兴趣慢慢降低，由于被其父母发现上网过度，在家采取了各种手段既然无法阻挡他的上网意念；但是更没想到的是到了开学报名的时候父母给了学费既然不去报名上学，而是拿着报名费到外面上网去了。眼看孩子的关键转折期就是高中了，心急如焚，采用多种手段无效后在江西找不到<a href=\"http://www.841990053.com/study.php?cid=8\" target=\"_self\" title=\"戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">合适的学校</span></a>，最后求助于我校（<a href=\"http://www.841990053.com\" target=\"_self\" title=\"戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">湖南特训学校</span></a>)</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">，直接从网吧里带了过来。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><strong>问题解析</strong></span><span style=\"color: rgb(255, 255, 255);\">江西南昌宜春问题孩子戒网瘾学校</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><br style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; text-align: justify; text-indent: 32px; white-space: normal;\"/>&nbsp; 由于家境条件还可以，独苗子“唐少爷”接受的宠爱有加，父母舍不得吃半点苦头，总是担心孩子吃不饱穿不暖，使其养成了习惯性的依赖，在心理及行为上也是从容无度，让“唐少爷”更是放纵知如，想干嘛就干嘛，只知道“有事一声吼”，父母来解决。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 毕竟父母比较关爱，接受的教育也是比较早的，在初中之前学习等各个方面都还不错，虽然小问题也有，但是还能听其父母的话。但是进了高中之后莫名的变换了角色，逆反情绪增强，抗拒行为明显，父母的油盐撒不进。在营地辅导的前期，通过生活中对唐少杰的细微观察陪伴和课程中呈现的状态了解到，唐少杰其实非常聪明，目前主要的症结在于他内心对自己的不信任和对高考的恐惧焦虑，自己给自己增添内心压抑，对生活和学习缺乏信心。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">辅导情况</span></strong><span style=\"color: rgb(255, 255, 255);\">江西新余九江叛逆孩子戒网瘾中心</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 唐少杰刚来学校对师生和校园环境多是有所陌生，特意安排了专业的心理老师专门针对他进行一周的一对一辅导，慢慢地打开他的心结。同时在心理咨询和辅导过程也找到了他其他问题的核心根源，让接下来的辅导工作漏出了头绪，使我们的心理辅导工作更有了针对性安排。在针对自我部分和情绪调节部分做了非常大的工作，最终在所有老师和唐少杰自己共同努力之下扭转了他对网络的看法，同时也积极调整了他自己学习和生活的状态。通过我们的学习兴趣和学习动能的培训后，摸清了他的学习情况，针对性的寻找出他的学习兴趣点，并一同探讨研究更有效的学习方法，让他有归回了学习之路，成绩也慢慢回升。<br style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; text-align: justify; text-indent: 32px; white-space: normal;\"/><strong>转化效果</strong></span><span style=\"color: rgb(255, 255, 255);\">江西叛逆孩子早恋厌学教育机构特殊教育学校</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><br style=\"margin: 0px; padding: 0px; font-family: 微软雅黑; text-align: justify; text-indent: 32px; white-space: normal;\"/>&nbsp; 通过我校的专业疏导，让他的心结得到敞开，也给他指明了前进的方向---学习。“唐少爷”回去后让家庭氛围有着明显的转变，充满了阳光和快乐；同时使得父母之间的关系也有了明显的改善，不再像以前那么的吵闹和忧伤，这给唐少杰的学习提供了一个舒适的家庭环境，端正了他的生活态度。因唐少杰基础扎实以及学习方法的调整，最终唐少杰在高考中以优异的成绩考取了南方大学，其父母流泪感激。有时候还会主动跟我们学校老师联系，将学校的趣闻我们共同分享，讲诉他的成功经历。</span></p>', '1', null, '1440933356', 'mingyu');
INSERT INTO `think_article` VALUES ('369', '8', '问题纠正学校 ：帮助孩子纠正不良问题应该注意的方法', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 第一，家长应该改变一下对孩子教育的错误方式方法。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">大部分家长的教育方式和方法太过于单一，要么就是溺爱和放纵，让孩子成了“皇帝”，天天供奉，最后导致了孩子的性格内向而不成熟，使得孩子的独立处理问题能力下降，当遇到问题时不能合理应的对外界事物和处理问题；要么就对孩子实行严加看管，将孩子关在家中或时刻跟踪，不能出门也不能玩。因而，建议家长要学会适度教育，不能过度的施压和暴力或极度的溺爱和放纵，让孩子能感觉到有良好的教育引导的同时也会感觉设有一定的限制性的控制，让孩子在学习中能够懂得节制。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 第二，家长应该多花时间跟孩子沟通交流，了解孩子的生活情况和成长需求，要创造有利于孩子成长的环境，并能满足孩子正常的生活、学习、娱乐及成长等方面的需要。家长在生活指导和思想的教育中要时刻更新观念，并提高对网络的认识，对社会的了解，不能因孩子上网就“一刀斩”。要把握孩子上网的质、量、度，培养出孩子的自制能力，综合性的提示和指导，学会有利的上网，但不能“断网”。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 第三，家长应该学会善用网络的正确知识来引导孩子的正确上网，指导向有利于学习和生活的网络方向去上网，让孩子及家庭生活否能的都良好的提升。而且家长要学会上网、懂网，如果家长自己不会上网也不懂网，那么就不能正确的引导孩子良好的上网，也不能督促孩子健康的上网，最后难以控制。因而家长还应该注意发现孩子上网中所碰到的问题难题，并在上网过程中能及时的与其沟通交流，一起制定有利的上网措施，建造健康上网环境。在这同时家长还可以在电脑上设置防火墙，防止孩子受到不良文化和信息的影响，能让孩子在上网中也能够预知问题和解决问题，也能理解父母的良苦用心，不会造成其他的不良后果问题，使得家庭生活美满、和睦，孩子健康、快乐的成长。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 第四，家长应该健康上网，给孩子树立一个良好的健康榜样。父母是孩子最好的老师，最亲切的情感，您的好与坏将决定着孩子的成长标杆。其实孩子在成长过程中出现的种种问题都是与其父母和家庭坏境是息息相关的，正是因为孩子模仿和学习出来的样品，也就成了问题的复印机。当您能够有一个好的样品摆放在孩子面前的时候，复制出来的就是好的事物，当您所摆放的样品是赝品的话，孩子复制出来的肯定也是不好的。所以您想要孩子学好做好，就需要您在孩子的面前是好样的。<span style=\"font-size: 18px; color: rgb(255, 255, 255);\">中国戒网瘾学校</span></span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 第五，以身作则，带动孩子离开网络世界，寻找现实的事物替换无聊的上网借口，走出不良网络区域。孩子的成长离不开他人的督促和引导，更多的是父母。不少家长下班或者忙完事务就会坐下来点击网络按钮，甚至做出不良之举，让旁边的孩子身受影响，在无意中孩子学会了模仿，抓住了家长的把柄，找到了自己上网的借口。如果您是坐下来带着孩子看看书，写写字，练练其他什么等，孩子还会这样吗？当然就不会了，当您良好的习惯把孩子带成了生活习惯，孩子的网瘾就不会存在了。</span></p>', '1', null, '1430479297', 'mingyu');
INSERT INTO `think_article` VALUES ('370', '25', '2名14岁少女沉迷手游 为买“宝物”8000元卖初夜', '', '', '', '', '', '<p style=\"margin-bottom: 0px; padding: 0px 0px 15px; line-height: 30px; text-indent: 2em; font-family: 宋体, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 18px;\">中国台湾网9月7日讯 据台媒报道，上周有初中少女为了手游密技被骗失身，未料新北市又有2名初三女学生沉迷手机游戏，为购买虚拟宝物，竟透过通讯软件援交，以8000元（新台币，下同）出卖自己初夜。警方接获摩铁报案当场逮捕恶狼，得知少女行径直呼：“为了游戏竟出卖自己的初夜，太离谱了！”</span></p><p style=\"margin-bottom: 0px; padding: 0px 0px 15px; line-height: 30px; text-indent: 2em; font-family: 宋体, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"text-indent: 2em; font-size: 18px;\">警方调查，刚升上初三的少女小慈（14岁，化名）跷家寄住在女同学小君（14岁，化名）家中，两人因缺零用钱储值手游点数，竟透过通讯软件认识的黄姓男子（37岁），以8000元出卖两人的身体。</span></p><p style=\"margin-bottom: 0px; padding: 0px 0px 15px; line-height: 30px; text-indent: 2em; font-family: 宋体, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 18px;\">警方指出，小慈与小君沉迷手游，小君曾因缺钱储值向家人要钱遭拒，她为了购买“虚拟宝物”，透过通讯软件向从未见过面的黄嫌诉苦，一星期后决定以4500元代价，和黄嫌上摩铁交易。</span></p><p style=\"margin-bottom: 0px; padding: 0px 0px 15px; line-height: 30px; text-indent: 2em; font-family: 宋体, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 18px;\">小君日前准备独自赴约援交，小慈发现小君找到解决钱的方法，再三拜托让她加入赚钱的行列，小君禁不起哀求，决定带着小慈一起，随即拿起手机与黄嫌议价，最终以8000元成交。</span></p><p style=\"margin-bottom: 0px; padding: 0px 0px 15px; line-height: 30px; text-indent: 2em; font-family: 宋体, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-size: 18px;\">警方表示，黄嫌开车将小慈、小君载往摩铁交易。摩铁业者发现三人年纪相距过大，且两名少女稚气未脱，察觉有异报警。警方到场后得知少女行径直摇头，直呼两人价值观如此偏差。2少女却呆坐警局，完全不了解自己犯了什么错，社工介入后已将两人辅导安置，黄嫌则被依妨害性自主等罪嫌移送。</span></p>', '0', null, '1441625714', 'mingyu');
INSERT INTO `think_article` VALUES ('371', '33', '益阳家长：我想让孩子留你们那继续读书可以吗？是否有文化班？', '', '', '', '文化教育，文化辅导，文化补习，学历教育', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;&nbsp;<span style=\"font-size: 36px; color: rgb(0, 112, 192);\">问：</span>因为我们孩子还小，并且才上了初中，我们想改变孩子问题后让他继续读书学习，不能落下了文化知识；现在就是不知道孩子来了您们学校后会不会影响到孩子的学习问题，是否可以留您们那边继续读书？有文化班吗？</span></p><p><span style=\"font-size: 36px; color: rgb(0, 112, 192);\">&nbsp;答：</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">可以的。我校设有一对一文化辅导班、小班文化辅导和大班文化授课等文化班，安排有专业的文化老师进行精心辅导，同步提上孩子文化问题，改善孩子学习心态。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 我校同时与当地全日制公办学历制学校合作，可以把学籍转来就读，参加同步升学考试，并有同等的录取和学习待遇！</span></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_blank\" title=\"厌学教育\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20150908/1441714268862830.jpg\" title=\"文化学习\" alt=\"文化学习\" width=\"595\" height=\"324\" style=\"width: 595px; height: 324px;\" border=\"0\" vspace=\"0\"/></span></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_blank\" title=\"厌学辍学教育学校\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20150908/1441714440402476.jpg\" title=\"文化辅导，励志教育\" alt=\"文化辅导，励志教育\" width=\"595\" height=\"325\" style=\"width: 595px; height: 325px;\" border=\"0\" vspace=\"0\"/></span></a></p>', '1', null, '1436357478', 'mingyu');
INSERT INTO `think_article` VALUES ('372', '25', '开学啦！这些校园诈骗要注意', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">不法分子瞄准在校学生及家长</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　随着各大中小学陆续开学，针对在校学生及学生家长的诈骗犯罪也会随之上升。对此，苏州警方梳理近年来的典型案例，分析相应诈骗手法，并作出提示，希望引起学生及家长的注意，谨防上当受骗。<br/>　　冒充校方人员<br/>　　这是专门针对学生家长的诈骗，不法分子通过群发短信，以“退还部分学杂费、发放教育补贴、发放助学贷款金、孩子受伤急需手术费、考证交钱”等名义，要求家长将钱款汇至指定账号。家长们在收到相关信息，特别是孩子受伤、出意外等信息后，往往会特别紧张，很容易上当受骗。<br/>　　在此特别提醒，家长遇到此类情况一定要“多核实、缓汇款”，收到请求汇款的电话和短信后，尽量与自己的孩子取得直接联系，多做核实，切莫轻信，更不要直接汇款。此外，家长最好能预留孩子班主任或者同学的联系方式，接到此类短信、电话后，可向他们求证是否属实。<br/>　　网上购物<br/>　　近年来网购已成为时兴的购物手段，但网上购物诈骗一直也是网络诈骗案件中的多发高发类型。<br/>　　大多数情况下，卖家会以“低价”吸引买家，在交易过程中会以系统失败、冻结激活、支付失败、验证失败、链接错误、已发货等多种理由，要求买家多次支付或骗取买家的个人信息、银行卡号、密码等，最终达到诈骗的目的。<br/>　　警方提醒，网上购物一定要在正规网站进行，购物聊天一定要用正规网站的客户端，不要随意点击、登陆对方发过来的链接，进入购物网站时尽量核实网站域名。对方要求转账、要求透露银行卡密码、身份证号等个人信息时，一定要提高警惕，切勿转账汇款。<br/>　　上门推销<br/>　　刚开学，学生会有相当多的事务需要办理，如激活饭卡、买电话卡、订报刊杂志等，不法分子往往利用新生对周围环境的不熟悉，以“代办”、“团购”、“折扣”等名义上门推销，诈骗学生钱财。<br/>　　警方提醒，绝不要轻易相信上门推销，要注意查看推销人员的证件，随时与保安、宿管等人员联系，必要时报警求助。<br/>　　“精英”培训<br/>　　现今，除了学校的正常课程外，还会有大量的社会培训机构。部分不正规的机构打着“精英教育”、“成功教育”的旗号，骗学生参加所谓的培训，收取高额的培训费。或有不法分子冒充正规培训机构工作人员，以“折扣价”吸引学生参加培训，进而收取费用，之后携款逃走。<br/>　　警方提醒，切莫急功近利，对于过度宣扬“短期快速成功”的培训机构一定要提高警惕。参加培训一定要仔细选择有资质、有口碑的培训机构，缴纳相关学费前可认真查看其资质验证材料，可提前在网络搜索该机构的相关信息，尽量多作比较。(来自360新闻）</span></p>', '1', null, '1442146619', 'mingyu');
INSERT INTO `think_article` VALUES ('373', '38', '孩子的千差万别，是因为孩子背后有千差万别的家长', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; <span style=\"font-size: 36px; color: rgb(0, 112, 192);\">家长</span>：我们该做怎样的家长？是望子成龙拔苗助长？还是听之任之撒手放羊？或者通情达理有良好教养为人处事绅士礼貌有同理心可以设身处地为他人着想？？</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　所以教育孩子不仅取决于我们做家长的能力，也取决于我们做家长的态度！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　养儿育女18年，我们慢慢的摸索出了为人父母我们家的小方法，我和老公都很明确：我们喜欢孩子自然健康，积极上进，有自己独立人格，懂得强调自我的同时也照顾他人感受！往小里说就是孩子喜欢学校和学习，能做好自己的本职工作，能和他人友好相处，懂得社会界限，肯分享肯帮助别人</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　本着这个大纲，作为家长我们在小心调整着放手／观望／管理／等待／信任等。。。。的分寸和尺度！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　一：孩子回家第一件事就是做作业：</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　看似简单，但果能做到会对孩子管理自己尤其管理自己时间有着深远的影响！很多孩子回家后磨蹭着上厕所或者不紧不慢的吃东西，或者先看会儿电视，或者先和小朋友玩儿。。。家长应该帮助孩子实现学校的要求，让孩子从态度和行动上都能认识到：回家第一件事做作业是最重要的事情，这条没做好，其他的都要靠后！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　二：集中精力做作业：</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　要做到像在课堂一样专心：喝水，上厕所，听电话，跑开，吃东西等，都属于没集中精力的不当行为！尤其家长，在边上可以安静的陪伴，最好看看书或做些家务，但是别玩手机，也别突然插话问问题，或者盯着孩子哪个字母没写好蓦然的来一巴掌。。。。像早上上课老师要求的一样：孩子不要分神开小差，家长也不要破坏课堂纪律！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　关于鼓励孩子好好写作业，在刚开始培养习惯阶段，家长可以动用一些相对有效的奖励方法，例如哥哥经常需要写好作业去朋友家或朋友来我家玩儿，好，最有效的提醒方式就是：“写好作业就可以去朋友家玩儿了！” 或者家长提议：“写好作业我们可以一起骑自行车去吃饭”！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　三：关于检查作业：</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　哥哥一年级时他的加减法妈妈还很老实的一道道题都默算一下，后来妹妹一年级时，她的老师说：“孩子的家庭作业允许有错误，家长也不用帮孩子订正每一道题，让孩子学会自己检查！”得，解放了妈妈的一项工作，所以妹妹三个月没到，妈妈不仅不再盯她写作业，也不再帮她检查作业了！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　四：关于孩子做作业磨蹭：</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　例如老师留的作业是半个小时完成的，那么父母就可以最多宽泛10分钟左右的时间，允许一开始写字慢的孩子慢慢的完成作业，但这个慢只是写的相对慢，不可以磨洋工，或者做白日梦在那里梦游，而且这个慢也要努力帮助孩子在一两周内赶上平均作业速度！快而准确的写作业，每个孩子都可以做到。。稍微训练下即可！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　另外，孩子磨蹭是因为不喜欢写作业，或者动力不足，孩子喜欢的事情他会跑得比谁都快！所以家长得帮助孩子找到写作业的积极动力！例如童一哥哥每次做作业都最先做数学，甜妞现在是最喜欢先做好手工或写画的作业，而妈妈小时做作业最喜欢先做好语文，从这里就可以看出每个人都会迅速完成自己最喜欢的作业：)</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　五：关于孩子忘记带作业本／书本等回来，导致没有办法完成作业的</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　德国是课本基本不带回家，所以偶尔发生过忘记带书回来没办法完成作业的情况，首先帮助孩子学会不要忘记，例如在学校里抄写作业时，抄到德语就检查德语书和作业本是否放进了书包，抄到数学就检查相应的数学课本和作业本！还有个办法就是回家后还可以给同学打电话借书或者复印作业！童一忘记的次数印象里不会超过三次吧，甜妞是几乎一次也没忘记过！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　六：关于作业质量：</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　作业潦草和认真，其实差别很大！所以一开始家长得盯孩子要认真的写作业，例如字母的写法，数字的写法，包括读课文等，家长不要吹毛求疵，但是得让孩子最大努力的认真对待！这个度可以根据孩子情况灵活把握，我们家童一哥哥是追求完美型，一点写不好就在那里擦啊擦的，那么妈妈就经常给他减压说差不多就行了，一个字母潦草问题不大！甜妞是属于有些大条的女生，她写字比较大气，涂色也会追求速度，那么妈妈就会要求严格些！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　七：关于孩子自己的事情自己用心：</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　这是个整体的锻炼，学习写作业都是孩子自己的事情，所以原则上妈妈帮助孩子养成良好学习习惯以后，这些孩子自己的事情就可以放手了！例如哥哥现在三年级，回家做好作业他不仅可以自己安排时间和小朋友互访，也可以决定这个周末怎么度过，或者他怎么支配他的零用钱等，爸爸妈妈都不用插手！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　父母要学会放手，对孩子要有信心，要有信任孩子的勇气，或者说要有胆量和孩子一起承受：孩子犯错／跑偏／跑慢等。。。的后果，孩子犯错并不可怕，更重要的是孩子自己能认识到错误并且学会自己纠正或改错的方法！长长的人生道路，他得自己强大！</span></p><p><br/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　所以说：前提如果孩子的智商没任何问题，那么不管孩子性情如何，都对孩子要有个“学习成绩不用最拔尖，但基本的80分得做到！”的态度， 明确告诉孩子，作为一个学生，学习就是你的工作，你如果连80分都做不到，就说明你的工作比较差，爸妈需要帮助你一起寻找差的原因！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　世界上没有一个不爱学习的孩子，只是有很多学习方法不对或者老师家长给了太多不当评价的孩子！智商正常的孩子，那么都应该能轻松面对正常的学校教育，家长要保护孩子一起对抗学校或老师的超负荷打压，但是家长也要鼓励孩子爱上学校／爱上老师／爱上轻松学习！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　其实打的基础很关键，重点就是整体态度，孩子对学校对老师对学校包括对作业，都需要有个正确态度，这样孩子学的轻松开心，老师和家长也不累！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　八：就是要和孩子一起摸索到好的学习方法：</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　什么是好的学习方法？每个孩子不同每个学科不同所以来讲好的学习方法也很不同，但是和做人一样，学习的好方法也有个通用的判别标准：主观情绪愉悦，有兴趣，有动力，有挑战，有目标，有征服感，有成就感，有幸福感！</span></p>', '1', null, '1436791161', 'mingyu');
INSERT INTO `think_article` VALUES ('374', '19', '湘西的他因成绩太好而沉迷了游戏', '', 'http://www.841990053.com/case.php?cid=19', '../Uploads/1484642844.jpg', '湖南戒网瘾学校，戒网瘾中心，戒网瘾机构', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=19\" target=\"_self\" title=\"戒网瘾学校的戒网瘾学校\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20170117/1484642662391976.jpg\" title=\"戒网瘾学校 戒网瘾学员\" alt=\"戒网瘾学校 戒网瘾学员\" width=\"252\" height=\"143\" style=\"width: 252px; height: 143px;\" border=\"0\" vspace=\"0\"/></span></strong></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 涂高峰，男，16岁，存在问题是因<span style=\"color: rgb(0, 112, 192); text-decoration: underline; font-size: 18px;\"><a href=\"http://www.841990053.com/study.php?cid=8\" target=\"_self\" title=\"戒网瘾\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\">上网沉迷</a>游戏</span>而影响了学业，来自湖南湘西，2013年9月入校，经过一百多天的教育辅导，效果得到良好的转变。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 说起来是个不可思议的原因：是因为在此在考上高中之前成绩太好，一直名列前茅，使得考上重点高中的成绩依旧位居榜首，正巧当局学校为迎接优秀新生准备奖励了奖品，涂高峰既是优秀新生榜首，获得了优秀一等奖——笔记本电脑一台和相应的奖学金。但是万万没有想到的是，就因这次的优秀奖励却把涂高峰带离了学习良园，走进了虚拟的网络世界——<a href=\"http://www.841990053.com/study.php?cid=8\" target=\"_self\" title=\"网瘾\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">网瘾</span></a>。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 由于涂高峰成绩一直优秀，很多要求都能够顺利得到父母的同意和满足，此次因考上重点高中获得学校奖励的笔记本电脑更是激动，便满足了涂高峰买无线网卡和随身携带上网的要求，让他获得了更多接触网络得机会。开始是以携带方便学习为由，后来在学习压力增大和乏味无聊的学习时间，便加入了同学的游戏队列，此后越玩越猛，甚至到了无游戏不吃饭的境界。使之学习成绩不断下滑，这种罕见情况让其父母大吃一惊，通过各种途径及手段都无法控制住“涂少爷”的上网成瘾问题，可以说他的网瘾是无人可挡的。其父母见况不妙，担心孩子的网瘾毁于一旦，学业将是半途而废，无奈之下上网搜索了<a href=\"http://www.841990053.com\" target=\"_self\" title=\"正规戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">湖南正规问题少年纠正学校</span></a>一词找到了我们<span style=\"color: rgb(0, 112, 192); text-decoration: underline; font-size: 18px;\"><a href=\"http://www.841990053.com\" target=\"_self\" title=\"戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\">青少年励志教育学校</a></span></span><span style=\"color: rgb(0, 112, 192); font-size: 18px; text-decoration: none;\">。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px; text-decoration: none;\">挽回了“涂少爷”的美好前途。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案及情况</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 1.对于“涂少爷”对<span style=\"color: rgb(0, 112, 192); text-decoration: underline; font-size: 18px;\"><a href=\"http://www.841990053.com\" target=\"_self\" title=\"网瘾\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\">沉迷</a>游戏</span>的偏执心态，我们特意开展了相关针对性的心理辅导，疏导其网络游戏思想的引诱力，缓解其网络游戏对生活所带来的内心压力，让从内心了解网络游戏玩成瘾后所带来的影响，提升其自我克制能力去战胜游戏魔力。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 2.针对涂高峰的问题给予了相应的特殊课程，主要是网络的基本知识和应用技术知识，以及网络的利与弊等等网络专业课程，让他深刻的了解虚拟的网络世界，同时也提升了他的电子计算机专业知识。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 3.增强对其的日常生活内务自理要求，转移和疏散他的“网瘾牵引力”，提升其自理能力。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 4.适当安排生活实践体验活动，认识了解“虚与实，真与假”的区别，增强他的明辨是非能力懂得明辨是非。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 5.参与基本军事动作训练，提升体质，增强体能，改善其上网成瘾所带来对身体的不良影响，提高基本动作素质。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 6.跟进文化课程，加强未来课程的预习和目前课程的学习，避免文化落伍，与学校课程并肩前进。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 7.对其父母做出相应的管教思想指导，避免过度给予孩子溺爱产生的不良后果，给予相应的管教方式方法，改善其家庭及亲子关系等。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">转化效果</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 因其本性良好，思想敏捷，学习快，反应能力强等特点，对自己的所作所为有着深刻的认识，并作出良好的改变效应，配合学校的辅导安排，其问题快速得到改善。从我“正规戒网瘾学校”毕业回家后继续步入了良好的学习状态，不再随身携带电脑，主动把电脑交给父母保管，只需在完成学习任务或查询资料时候触摸电脑。因我“<a href=\"http://www.841990053.com/study.php?cid=8\" target=\"_self\" title=\"戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">正规叛逆问题孩子纠正辅导学校</span></a>”的<a href=\"http://www.841990053.com\" target=\"_self\" title=\"正规戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">游戏瘾问题专业课程</span></a>让他掌握了良好的电子计算机技术技能，在高二时期参加了学校电子计算机技能竞赛中获得了优异的成绩，还特意电话联系我“<a href=\"http://www.841990053.com\" target=\"_self\" title=\"湖南戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题少年纠正学校</span></a>”老师致敬，深感激动！在此也祝贺他学业有成，创建他美好的未来！</span></p>', '1', null, '1439477141', 'mingyu');
INSERT INTO `think_article` VALUES ('375', '19', '宁乡叛逆早恋的少女在迷途中折返', '', 'http://www.841990053.com/case.php?cid=19', '../Uploads/1484643020.jpg', '宁乡戒网瘾学校，湖南戒网瘾学校，宁乡特训学校', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=19\" target=\"_self\" title=\"戒网瘾机构在湖南戒网瘾学校\"><img src=\"/ueditor/php/upload/image/20170117/1484642894330613.jpg\" title=\"网瘾少女在戒网瘾学校\" alt=\"网瘾少女在戒网瘾学校\" width=\"286\" height=\"163\" style=\"width: 286px; height: 163px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 肖雨露，女，16岁，来自湖南长沙宁乡县，2014年6月21日入校，因叛逆沉迷游戏早恋厌学辍学等问题送来我校教育辅导，为期六个月的心理认知教育，该生转变良好，现在家读书，无不良反应。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概述</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 小露上了初中二年级开始上网成瘾，之后结交了一些网友，同时也结交一些辍学的社会青少年朋友，开始步入了恋爱生涯。平时要么整天上网跟所为的男朋友聊天，要么就是外出玩耍几天不回家，是偏向于网恋情况，对学习开始心不在焉，可去也可不去的情绪，最后就直接逃课辍学了。她除了上网聊天交友以外，慢慢的被所为的朋友带她玩起网络游戏，像什么英雄联盟等网络团队游戏。后来玩游戏不仅仅在精神上越来越投入，而且还花钱去买什么游戏装备，甚至可以不吃饭也要玩，这让她身体消瘦不堪，精神疲惫和生活规律的颠倒，同时也引起了近视。父母长期发现她小露情况不对了才慢慢关注，但是也阻止不了她问题的蔓延，在无奈之下通过网络搜索“<a href=\"http://www.841990053.com/case.php\" target=\"_blank\" title=\"湖南戒网瘾学校，正规戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">湖南问题纠正学校</span></a>”找到我们了，并当天就把她带来了学校，这才让她的问题在此消除。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">教育情况与效果</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 由于小露游戏瘾比较重，再加上又偏向于网恋早恋情况，我们首先就是采取针对其几大问题的心理疏导，稳定她的情绪，慢慢地让她了解自己所做所为的利与弊，从内心认识到自己存在的不良问题，这是我们工作的第一步。但是由于小露的游戏瘾较重，初期的开导工作也比较难，他除了网络的游戏意识，其他的基本上不想的。但是通过学校的封闭隔离式的教学管理，让他无法接触到网络，这让他上网意识慢慢减弱，开始从事老师的辅导安排；良好的网络知识课程逐渐的展开，让她明白了网络与游戏的真正意义，学会了自我克制和放弃心理。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 通过基本的军事行为训练，让他的体质和意志力有着明显的提升，打破了她游戏瘾的生活规律，调整到了正常作息规律上来。生活教育让她学会自己的事自己做，自己的任务自己完成，培养她的自尊、自爱、自立、自理、自强等作风，做出她应有的成长事迹。</span></p>', '1', null, '1442237124', 'mingyu');
INSERT INTO `think_article` VALUES ('376', '19', '湖北武汉游戏少女比男生更疯狂', '', 'http://www.841990053.com/case.php?cid=19', '../Uploads/1484643293.jpg', '武汉戒网瘾学校，武汉特训学校，武汉戒网瘾中心', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=19\" target=\"_self\" title=\"戒网瘾学校的生活\"><img src=\"/ueditor/php/upload/image/20170117/1484643158344426.jpg\" title=\"戒网瘾学校的女生\" alt=\"戒网瘾学校的女生\" width=\"297\" height=\"181\" style=\"width: 297px; height: 181px;\" border=\"0\" vspace=\"0\"/></a></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\"><strong>基本信息</strong></span><span style=\"color: rgb(255, 255, 255);\">武汉戒网瘾学校特训学校，问题少年学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 黎欣，女，17岁，来自湖北武汉市，存在叛逆、沉迷网络游戏、厌学等问题，2014年5月入校，进行为期六个月的心理人教育，该生转变良好，现已考上大学，无不良反应。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题简述</span></strong><span style=\"color: rgb(255, 255, 255);\">武汉叛逆孩子教育学校，厌学少年教育</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 上了高中后沉迷网络游戏，心思整天就是呆在网络的世界里，一开口就是网游的东西，让身边人对她有所反感，因而更多的生活就是在虚拟的网络世界里度过。班级里不少同学因为比较爱学习，从不玩游戏，然而她却总是找同学攀谈游戏，没有共同话题的他们慢慢地不理会，导致了小欣朋友稀少，只能自我欣赏。有一次在一节课堂上，老师在讲课，而她坐着打瞌睡，让人出乎意料的是，她既然做起了网游戏的美梦，手在桌子上不断敲打，同时嘴里不停地说出打杀话语，让全班同学和老师惊讶不已。就那节课后，被老师罚了她搞一周的班级卫生，然而不服的她产生了厌学情绪，断断续续的上了几节课，不想去就干脆到街上网吧上网，等到了下课时间就回家。起初其父母并不知道她存在这样的不良行为问题，后来实在没办法的班主任联系了其父母，把问题揪了出来。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导情况</span></strong><span style=\"color: rgb(255, 255, 255);\">武汉特殊教育学校，叛逆青少年教育</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 像她这样的问题大部分都是男生，而她一个少女却产生这样的问题，其实主要也是一种心态的影响。对此我们首先也是要对其进行良好的心理疏导和解说，缓解她情绪的同时也让她明白自己的过错，并且引导她加以改正。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 由于她对网络游戏比较依赖，让他了解网络游戏是关键，通过游戏的解说让她清楚地明白游戏只是一种临时的消遣活动，而且每一场游戏的结局是一样的，那就是“竹篮打水一场空”，没有一点实际上的意义，毕竟它是虚幻的，不是真实的。只有让她明白了游戏的头尾经过和结果，才会产生自我的克制意识，从而慢慢达到较少上网效应。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">转变效果</span></strong><span style=\"color: rgb(255, 255, 255);\">武汉叛逆不听话孩子的管教所</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 现在的她自控能力比较强，偶尔会上网，但是已经没有了网游戏的依赖倾向，平时就看看电影和小说之类的。由于游戏瘾的戒除，她的生活规律变得比较正常，做事很有时间观念，不过早也不会延误。因为之前耽误了不少学习，一下子无法跟上，再加上对基础的学习没什么兴趣，为了让她能够有一技之长，我们推荐她上了五年制大大专学习会计专业。现在她没课的时候就会去做兼职赚钱，学会了吃苦喝坚强，理解了父母的不易，并能够自我独立。</span></p>', '1', null, '1439978185', 'mingyu');
INSERT INTO `think_article` VALUES ('377', '20', '广西叛逆少女什么都敢做，父母下跪求乖', '', 'http://www.841990053.com/case.php?cid=20', '../Uploads/1484643512.jpg', '叛逆孩子教育学校，孩子怕你怎么办，教育叛逆孩子的学校，青少年叛逆教育，青少年管教所', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=20\" target=\"_self\" title=\"叛逆孩子管教学校\"><img src=\"/ueditor/php/upload/image/20170117/1484643392400410.jpg\" title=\"叛逆少女教育学校\" alt=\"叛逆少女教育学校\" width=\"266\" height=\"156\" style=\"width: 266px; height: 156px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">广东叛逆青少年孩子励志教育学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 曾晓丽，女，14岁，来自广西桂林，存在严重叛逆偷摸行为，2013年7月入校辅导，转变良好，被写入学校教育成功案例档案。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong><span style=\"color: rgb(255, 255, 255); font-size: 10px;\">广东广西少年管教所，特训学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 晓丽自幼就胆大好动，不论是熟人还是陌生的事物都会习惯性的去触摸一下，养成了好奇心理及行为。随着晓丽逐渐长大和防备与警惕心理的增强，对身边人说的每一句话都会进行自我判断和思考，稍微自我感觉不太对或者有刺激性质的都会给予反驳，随之养成了以自我为中心的思想。由于父母工作和家务都比较繁忙，没什么时间陪伴其成长，更是没空闲跟她闹折腾，贪玩叛逆调皮的晓丽让父母很是无奈，不仅影响到家事和工作，也是影响了正常的生活，为了能够获得安宁和幸福的生活，其父母曾经向晓丽下跪，求她别再折腾父母和家庭，好好听话和学习，但是却没能得到晓丽的认可，反而觉得自己高大无上，跟父母提出了各种条件，成为了家庭的地主。在一个偶然的机会听到朋友说有管教这种叛逆孩子的学校才找到我们青少年励志教育学校，从此改变了一家的命运。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导情况与效果</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">中国南方励志教育学校，青少年心理辅导中心</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 叛逆不听话是青少年孩子最难管教的问题之一，针对于这类问题，我校的心理专家组通过教育商议会议研究出特殊的教育方法，既能让孩子听话又能够让她心甘情愿的去做改变，并且反思自己的缺点与不足，重新做人。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 这一类叛逆青少年孩子最缺乏的就是吃苦，没有一点吃苦能力和生活的意志力，无法明白父母的艰辛与生活的艰难。学校通过生活教育的历练，让她们提升认知力，了解现实生活情境，学会克制和理解父母，学会知恩感恩，学会励志成长。</span></p>', '1', null, '1431433641', 'mingyu');
INSERT INTO `think_article` VALUES ('378', '20', '安徽合肥少年叛逆了全家人都怕他', '', 'http://www.841990053.com/case.php?cid=20', '../Uploads/1484643665.jpg', '安徽戒网瘾学校，安徽特训学校，网瘾叛逆教育，叛逆厌学教育，青少年叛逆教育学校，教育叛逆孩子的学校', '金逸自安徽，存在叛逆厌学网瘾等综合问题，2013年11月入校，进行为期六个月的在校辅导，转变效果良好，被写入成功案例档案。', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=20\" target=\"_self\" title=\"叛逆少年管教中心特训学校\"><img src=\"/ueditor/php/upload/image/20170117/1484643552887960.jpg\" title=\"叛逆孩子学校\" alt=\"叛逆孩子学校\" width=\"295\" height=\"181\" style=\"width: 295px; height: 181px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"color: rgb(255, 255, 255);\">安徽合肥叛逆青少年教育学校，特训学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 金逸，男，17岁，来自安徽合肥，存在叛逆厌学网瘾等综合问题，2013年10月入校，进行为期六个月的在校辅导，转变效果良好，被写入成功案例档案。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong><span style=\"color: rgb(255, 255, 255);\">合肥戒网瘾学校，问题少年管教所</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 他主要是因<a href=\"http://www.841990053.com/study.php?cid=8\" target=\"_blank\" title=\"网瘾教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">上网成瘾</span></a>而带来这严重的叛逆情况，同时也引起了厌学等其他一系列问题，综合问题比较严重。开始是由于上网影响了正常的生活和学习，父母看不下去了也只好运用打骂手段进行解决，但是没想到的是不但没有取得效果，更是使得问题严重化。处于青春叛逆期的金逸存受不良父母的唠叨和打骂，就产生了叛逆情绪及行为，开始跟父母作对来解决自己的不服心理。在经过长期的亲子较量之后却没有让他问题得到解决，父母身心疲惫，也是所作所为没办法，当然也在担心金逸的暴力行为会毁掉这个美好的家庭，因而全家都在为金逸操心，心神不宁。叛逆的金逸却把家里闹得沸沸扬扬，被其父母的朋友知道后才给予了建议，找到了我们<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"叛逆青少年教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">青少年叛逆教育学校</span></a>，终止了家庭的噩梦。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">教育简述</span></strong><span style=\"color: rgb(255, 255, 255);\">安徽特训学校戒网瘾学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 针对于金逸的一系列问题我们找出了根本原因：首先是因叛逆引起的网瘾，之后上网成瘾就影响了学习，成绩直线下降，怕别人嘲笑就产生了厌学情绪不上学了。这一系列问题进行阶段性的问题辅导及矫正教育。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.首先是我们的心理辅导工作，疏导其心理思想，了解他的个人想法和对问题的阐述，稳定他的情绪波动，缓解他的叛逆情绪。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.进行网络教育，让他正确地认识网络真正意义上的文化，了解网络的利与弊和使用情况；让他缓解网络的依赖，重新调整自己状态。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3.对于他的叛逆问题给予针对性的心理疏导、行为矫正和生活实践教育三位一体同步进行，在心理得到缓解的同时行为上跟着调整，端正其姿态；并且加强生活实践教育受于他劳动的艰辛，思考父母的不易，让他学会理解和包容，懂得励志与感恩。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4.由于上网叛逆等问题影响了他的学业，在他不良问题得到疏导和矫正的同时，也要培养他的学习兴趣，让他明白知识的重要性，并且能够主动学习，接受学校的文化辅导，弥补过去的文化知识，赶上当前要学的知识，让他在文体上同步提升等</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">转化效果</span></strong><span style=\"color: rgb(255, 255, 255);\">安徽问题青少年心理辅导学校，行为矫正中心</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 经过学校的毕业测试和平时的表现和抓变情况，最终他以八十九分的优异成绩获得毕业回家，并且亲手签订了离校协议书，表示自己的已经转变良好，回家一会克制自己，好好听父母的话，认真做好自己力所能及的事情，不会有第二次返校可能。其父母当场流出了激动的泪水，深深地把孩子搂在怀里，是长大后的第一个拥抱！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 回家后的金逸果然表现良好，跟父母的关系有着逆天的变化，从没有半点逆反情绪，但是也会说出自己的想法和要求，也能够得到父母的理解和支持，家庭突然间变得和谐而幸福。他每天都还能按我们学校的标准作息之间作息和学习，上学也从没有迟到过，成绩在各个科目都还不错，就是理科少差一点，但是总体成绩都是班级中上等，也获得老师的赞同和认可。</span></p>', '1', null, '1435876119', 'mingyu');
INSERT INTO `think_article` VALUES ('379', '41', '{多动症行为矫正}多动症少女得到良好的控制', '', '', '', '多动症少女得到良好的控制', '多动症少女得到良好的控制', '<p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 杨丹，女，12岁，来自湖南岳阳平江县，存在多动症等问题影响正常的生活和学习而来到我校矫正，2013年7月入校，转变效果良好，多动问题得到控制，言行正常，生活规范，无不良反应。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 从小就存在多动情况，因时间长养成了习惯，“多动症”表现越来越明显，影响着正常的生活和学习。其多动的原因主要是自控能力比较弱，很多东西都是无法自我克制，特别是在动作上。平时在家也是心神不宁，坐着总是坐不住，东转转西转转；站着也站不住，手脚无法平静，眼神到处漫游。有一次家里来了客人，杨丹总是在客人旁边转来转去，动作做个不停，让客人感觉不耐烦就走了，这才引起了其父母的关注。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">教育方案</span></strong></p><p><span style=\"color:#0070c0;font-size:18px\">1.针对其问题进行心理疏导，年轻她的敏感心理多种，缓解她活跃的思想活动，用心理暗示她减少动作的发生。</span></p><p><span style=\"color:#0070c0;font-size:18px\">2.<span style=\"color: rgb(0, 112, 192); font-size: large;\">对于多动症的问题孩子，主要是以规范动作来提升自控能力，以严格的军事动作来训练其行为，控制她的不良动作。</span></span></p><p><span style=\"color:#0070c0;font-size:18px\"><span style=\"color: rgb(0, 112, 192); font-size: large;\">3.以生活体验和实践课程来转移她的不良动作行为，让她把动作放在做一些有意义的事情上来。</span></span></p><p><span style=\"color:#0070c0;font-size:18px\"><span style=\"color: rgb(0, 112, 192); font-size: large;\">4.通过相关文化的学习来克制其肢体的运动，比如练字等，可以提升她的耐力和自控能力。</span></span></p><p><span style=\"color:#0070c0;font-size:18px\"><span style=\"color: rgb(0, 112, 192); font-size: large;\">5.加强她的文化补习和辅导，让她在转变问题的同时也能够在文化知识上一起进步，避免文化落伍情况。</span></span></p><p><span style=\"color:#0070c0;font-size:18px\"><span style=\"color: rgb(0, 112, 192); font-size: large;\">6.其他<a href=\"http://www.841990053.com/class.php?cid=51\" target=\"_blank\" title=\"法制教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: large;\">法制教育</span></a>和<a href=\"http://www.841990053.com/center.php?cid=17\" target=\"_blank\" title=\"感恩励志教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: large;\">感恩励志教育</span></a>等课程一并开展学习，增强她的阅历和相关知识的学习，使其在各个领域过得提升等。</span></span></p><p><strong><span style=\"color:#0070c0;font-size:18px\"><span style=\"color: rgb(0, 112, 192); font-size: large;\">转化效果</span></span></strong></p><p><span style=\"color:#0070c0;font-size:18px\"><span style=\"color: rgb(0, 112, 192); font-size: large;\">&nbsp; 通过一百多天的严格训练和学习，她的转变情况比较理想，不仅仅在动作上达到标准和规范，而且在体质、文学、兴趣特长等各个方面都得到了很好的提升。现在的她坐有坐像，站有站像，行为规矩不乱动的良好肢体习惯，让家人都感到无比的惊讶和喜悦，回去还特意为小丹的转变设宴请客，也得到了她亲朋好友的祝福和肯定。</span></span></p>', '1', null, '1441945884', 'mingyu');
INSERT INTO `think_article` VALUES ('380', '41', '{多动症行为矫正}一位江西多动症少年是成功转变', '', 'http://www.841990053.com', '', '多动症孩子的管教方式，教育叛逆孩子的学校，多动症教育，多动症怎么办', '学校是专业针对在成长路上出现“上网成瘾、厌学、叛逆、早恋、行为异常、亲情淡漠、自卑自闭、离家出走、暴力趋向、好吃懒做、赌博涉毒”等问题的青少年进行心理辅导、行为纠正、习惯养成、实践拓展、励志国学、生活体验、兴趣培养、文化辅导等综合性励志教育机构。是以“心理疏导、行为督导、思想引导、生活指导、文化辅导”等人性化全方位教育，让每一位孩子都能在“心、理、德、智、文、武、法”等各个领域获得全面提升和发展', '<p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 张兵，男，13岁，来自江西赣州，存在多动症、自控能力差等问题，2013年8月入校，转变效果良好，现无反弹迹象。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 张兵比较好玩，从小跟着爷爷奶奶生活，总是被爷爷带着玩，习惯性的玩手势动作，长大后还是无法摆脱手势动作的影响，养成了习惯，时不时地自然搞个动作来，无法自控。长大上学后依然如此，而且动作越来越大，让身边的人感觉也越来越明显，不论是表情、声音、体型、动作都能搞怪，让人惊讶又可笑，都说他是个“怪孩子”。有时候在家里他也时不时地动弹一些小动作，没有正常的宁静，总是晃来晃去的，甚至在严肃的场合中也会东张西望，无法克制自己。由于多次被看着不耐烦的老师把在校的多动情况告诉了家长，再加上其父母亲在家也是发现了不太正常，才下定决心送来我们<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"多动症教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">青少年励志教育学校</span></a>进行行为上的矫正，最终不负众望的他成功转变回家。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">矫正情况</span></strong></p><p><span style=\"color:#0070c0;font-size:18px\">1.由于张兵主要是存在多动症和自控能力差等问题，我校对其情况安排基本的军事动作训练，提升他的自控能力和耐性，让他在严肃的军事动作训练中逐渐减少不规范的动作，从而慢慢减低多动问题。</span></p><p><span style=\"color:#0070c0;font-size:18px\">2.加强对其的心理辅导，缓解他敏感的动作意识；实施音乐静心疗法，放松其活跃的神经系统；进行催眠疗法，让其身心得到放松和休息，减少身体的活动性。</span></p><p><span style=\"color:#0070c0;font-size:18px\">3.参与生活教育课程，通过实践的体验和操作来充实他的体表运动，减少他空闲而多动情况。</span></p><p><span style=\"color:#0070c0;font-size:18px\">4.对其进行文体训练与学习，通过练字来规范其肢体动作，以端正的态度引导其耐心和自控力的提升。</span></p><p><span style=\"color:#0070c0;font-size:18px\">5.</span><span style=\"color: rgb(0, 112, 192); font-size: large;\">针对其学习情况给予补习和文化辅导，避免文化知识的退步，同时也跟进文化学习和升级，培养他的学习兴趣，减少做出无聊的动作。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: large;\">6.感恩励志、法制教育、特长培训等全能课程同步开展，在辅助其问题矫正的同时也会得打很好的学习和发展。</span></p><p><strong><span style=\"color: rgb(0, 112, 192); font-size: large;\">矫正效果</span></strong></p><p><span style=\"color: rgb(0, 112, 192); font-size: large;\">&nbsp; 经过严格的军事动作规范训练，他的多动行为得到很好的克制，坐、站、行、舞等各个项目轻松自如，自控能力有着明显的提高。虽然在矫正过程中有点艰辛，但他还是完成这项艰巨的任务，并且还是很成功的达成了父母的愿望，其父母感激流涕。他父母告诉我们：张兵现在在家基本上没有多动行为，转变得很明显，像换了个人一样，突然间我们都不太习惯了，特别的他爷爷；他在家有事没事都会自己站军姿，他说站军姿就不会有动作了，而且感觉很有帅气。现在学校老师也说他变了，不再是从前的张兵了，大家都很喜欢。我们听到这些音讯，感觉到无比的自豪，也在无形中拥有了成就感。</span></p>', '1', null, '1438685775', 'mingyu');
INSERT INTO `think_article` VALUES ('381', '41', '{自卑心理辅导}岳阳自卑内向的少年走向“阳光人”', '', '', '', '自卑内向的少年走向“阳光人”', '自卑内向的少年走向“阳光人”', '<p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"color: rgb(255, 255, 255);\">岳阳厌学辍学孩子教育学校，特殊教育管教机构</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 陈亮，男，12岁，来自岳阳汨罗，性格内向，有点自卑，2013年5月入校，留校辅导八个月；通过心理疏导和实践拓展训练，转变效果明显，能自信主动跟他人交流，其父母满意。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong><span style=\"color: rgb(255, 255, 255);\">岳阳问题青少年教育学校，戒网瘾中心</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 主要是因为性格内向而导致了自卑，缺乏自信心，意志力较弱。陈亮从小就比较胆小，由于其父母表现比较凶狠，母亲比较祥和，使得陈亮从小就时刻跟随着母亲走动，离不开半刻，见了陌生人就脑袋钻井母亲的怀抱，久而久之，这种行为养成了习惯，长大了还是有所放不开，胆小如鼠，便把自己包裹起来，住进了“世外桃源”的世界。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 陈亮上学都是要其母亲接送，甚至还有陪读，让其母亲无法脱身，把时间都寄托在了他的身上，让其父母感到无奈。家人都是以为孩子还小是这样子的，就一直陪着，但是时间一晃就过了十几年，看着孩子不但没有改善反而有些严重，恋母情结较重，遇事胆怯，总是依赖父母无法自立。眼看着陈亮长大需要自己上学，也对他的未来担忧，便在网上搜索<a href=\"http://www.841990053.com\" target=\"_self\" title=\"孩子自卑自闭教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">孩子自卑自闭</span></a>一词寻到了我校，在咨询了解后决定送来辅导。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导情况及效果</span></strong><span style=\"color: rgb(255, 255, 255);\">岳阳戒网瘾学校，特训学校，叛逆教育</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 像陈亮这种问题的孩子比较少，加上自卑自闭无法正常沟通交流，给了我们极大的辅导压力。但是通过我们特殊教育学校的专家组合力相助，制定了专业针对性的心理辅导方案和流程，破解了陈亮的心理症结，让他的自闭心理慢慢地得到松解。由开始不说话转变为愿意开口说话之后转为主动说话，这一过程经历了一段难熬的日子，心理老师一天二十四小时跟随做相关的思想疏导和生活上的指导，对于长期处于自卑自闭性格内向的陈亮来说，也是个极大的挑战项目，不习惯有人在自己身旁唠叨，在无奈之下开口说话。在这慢慢的历程中把他被动的习惯转为主动，是一百八十度的大转弯，让其父母都是意想不到的效果。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 经过八个月的漫长疏导，陈亮的问题得到明显的转变，比以前开朗了很多，看起来阳光帅气，并能够主动跟他人打招呼，也愿意跟他人沟通交流，这是从未有过的状况。回家后每天都能够跟父母进行交谈，有事没事也会打声招呼，让其父母放心了不少，同时也感觉到他们的家庭幸福了很多。现在在学校里继续学历文化学习，成绩还算可以，也交上了几个朋友，而且他的转变也得到了原来老师的认可，让同学们对其快目相看。这不仅仅是外界人对他转变的认可，同时也是给予了他自己增添了自信心，鼓励自己健康成长。</span></p>', '1', null, '1426341731', 'mingyu');
INSERT INTO `think_article` VALUES ('382', '21', '怀化少女厌学辍学去打工后返校', '', 'http://www.841990053.com/case.php?cid=21', '../Uploads/1484644328.jpg', '怀化特训学校，怀化戒网瘾学校，怀化叛逆孩子学校', '怀化特训学校戒网瘾学校叛逆孩子管教学校，怀化问题孩子心理辅导中心18874250661湖南青少年励志教育学校', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=21\" target=\"_self\" title=\"怀化特训学校\"><img src=\"/ueditor/php/upload/image/20170117/1484644170118630.jpg\" title=\"怀化特训学校\" alt=\"怀化特训学校\" width=\"256\" height=\"161\" style=\"width: 256px; height: 161px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 李圆圆，女，14岁，来自湖南怀化市，存在的问题是厌学辍学后离家出走跟辍学同学外出打工，2014年9月入校；转变效果良好，因其年龄较小，但是也辍学了一段时间，续读有点困难，因此我们给予推荐了五年制大专院校就读，学习护理专业。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 由于从小家庭也不是很好，圆圆觉得读书也是一种奢侈的生活，听着外面务工的人都能赚些钱，抵不住谣言诱惑的她心存杂念，对学习的心思慢慢淡化，辍学的举动也开始降临。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">初二下学期才开学不久，看到少了个同学，听说也是辍学去打工了，这让她的辍学思想吃下了一颗定心丸。两个星期后她联系上在外打工的同学便隐瞒着父母悄悄外出，直到学校老师发现几天不上学的圆圆便是给其家里打了电话才得知情况，父母还是以为顽皮的她可能是在同学朋友家里玩的，进行多方咨询后才联系上了已经到外面打工的她。但是初次外出的她却不愿回来读书，表示再呆一段时间再说。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 转眼间半年过去了，有一天她主动给家里打了电话，说上班好累，想回家，但没说几句就哭了，这让其父母突然心酸；但是过了一段时间还没有回到家，心急的父母只好亲自去把她带回。找到了她却不愿意回来，还不愿意读书，为了好玩就是跟外面朋友借钱，自己吃不了苦而没赚到钱，让父母很担忧她的未来。虽然找到了却无法管教，只好求助于我们“<a href=\"http://www.841990053.com\" target=\"_self\" title=\"厌学辍学教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">厌学辍学教育学校</span></a>”进行转化教育，才挽回了她美好的前途。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">教育情况及效果</span></strong></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 1.通过针对性的心理疏导，让圆圆明白了知识的重要性，表示愿意继续好好读书，建造自己美好的未来；2.对她进行了学习兴趣的培养，提高了她的学习兴趣，让她学会学习，懂得学习，并会学以致用的效果；3.通过基本的军事训练，规范了她的不良行为，矫正了其不规律的生活状态，把她引导向积极健康的成长道路；4.通过文化辅导让她学习成绩不断提高，把学过的遗忘知识弥补了回来，赶上了知识的同步学习；5.回家后听从父母的安排继续好好读书，学习成绩处于中等，学习态度有了明显的改善，认真、积极、向上的学习和生活心态，基本上无不良问题反映，其明辨是非和自控能力增强，家长满意！</span></p>', '1', null, '1436874657', 'mingyu');
INSERT INTO `think_article` VALUES ('383', '20', '湖北叛逆少年打父母后离家出走', '', 'http://www.841990053.com/case.php?cid=20', '../Uploads/1484644112.jpg', '湖北叛逆少年教育，孩子打父母怎么办，叛逆孩子离家出走，孩子叛逆不回家，叛逆孩子教育学校', '湖北戒网瘾学校特训学校湖北最好的叛逆孩子管教学校心理辅导中心18874250661湖南青少年励志教育学校', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=20\" target=\"_self\" title=\"叛逆孩子特训学校\"><img src=\"/ueditor/php/upload/image/20170117/1484643954569783.jpg\" title=\"叛逆孩子特训学校\" alt=\"叛逆孩子特训学校\" width=\"296\" height=\"174\" style=\"width: 296px; height: 174px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"color: rgb(255, 255, 255);\">湖北戒网瘾学校，特训学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 胡明，男，16岁，来湖北宜昌；</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">存在叛逆暴力倾向，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">2014年7月入校，在校辅导时间为期八个月，主要针对行为调整和心理疏导；转变效果明显，现在于高中学习，成绩良好，无不良反应。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong><span style=\"color: rgb(255, 255, 255);\">湖北武汉戒网瘾学校，叛逆孩子教育学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 小明的叛逆情绪与行为表现越来越明显，特别是假期时间段反应强烈，父母无法言行。平时主要是情绪方面的逆反，其父母以为是小明每天学习比较辛苦，就采取理解的包容心态，较少跟小明啰嗦。但是放假了回家小明的叛逆表现不减反增，而且其父母也给予强烈的反应，说是平时上学读书可以理解，但是两派不同的想法开始敌对起来，事态愈演愈烈。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 因父母的放纵时段导致了无法收官等结局，也给了小明放肆从容的理由。有时候父母是担心孩子没事了会玩耍出格而产生不良问题，但小明却不能理解，幼稚的思想也抵不住娱乐的诱惑。不仅仅是在情绪上表现逆反，而且会在行为上大打出手，甚至对父母动起手脚的恶性行为，使其父母心灰意冷。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其父母的朋友得知小明有这种问题后给予了一些建议，也从朋友那得知有我们“<a href=\"http://www.841990053.com/study.php?cid=9\" target=\"_self\" title=\"叛逆孩子教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">叛逆孩子特训学校</span></a>”情况，决定把小明送来我们“<a href=\"http://www.841990053.com/study.php?cid=9\" target=\"_self\" title=\"叛逆教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">叛逆孩子教育学校</span></a>来进行教育辅导，改变其不良思想及行为，希望孩子听话并好好读书学习，将来做个有用的人。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">辅导概况</span></strong><span style=\"color: rgb(255, 255, 255);\">武汉宜昌叛逆孩子教育机构，特殊教育学校</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 1.根据其父母的描述和小明的实际问题情况，我们主要还是开展对其针对性的心理疏导课程，开导他的不良思想心态，顺其叛逆意识。</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 2.介于小明的强烈叛逆行为给予相应的行为素质教育，参与基本军事素质训练，改善其不良行为习惯，提升自我控制能力，戒除不良恶习。</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 3.适当参与生活实践体验活动，了解显示生活问题，学会自理，懂得生活自理，理解父母的艰辛，学会感恩报恩，学会励志成长。</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 4.加入文化辅导班，补习学会知识，提升学习兴趣，复习现学习文才，跟进文化进度，提高文学知识。</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 5.授予其他课程辅导，提升综合文化素质，树立良好的世界观、人生观、价值观，定予理想的奋斗目标等。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">转变效果</span></strong><span style=\"color: rgb(255, 255, 255);\">湖北十堰叛逆孩子戒网瘾学校</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 经过八月的针对性教育辅导，理顺了他思想行为，纠正了其不良恶习，得到了很好的转变。毕业回去后亲子之间感情良好，不再出现逆反情绪及不良的叛逆行为，其自控能力得到了较好的提升。现在小明学习成绩优秀，服从能力较好，主动性强，力所能及的事都会自己坚持完成，给他的美好未来树立了很好的标杆。</span></p>', '1', null, '1439380337', 'mingyu');
INSERT INTO `think_article` VALUES ('384', '22', '永州少女早恋被骗父母全城翻个遍', '', '', '../Uploads/1463035366.jpg', '叛逆早恋，厌学早恋，早恋辍学，早恋教育，早恋教育学校', '其思想幼稚敏捷，性格外向，比较阳光，但是好奇心强，比较天真，容易受外界影响，自我控制能力较弱，意志力差等。', '<p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">湖南长沙永州问题孩子教育学校，叛逆早恋教育机构</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 黄钰，女，16岁，早恋辍学，来自湖南永州市，2013年4月9日入校，辅导期为六个月，转变良好无反弹。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题解析</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">永州孩子叛逆了怎么办，有管教叛逆孩子的学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其思想幼稚敏捷，性格外向，比较阳光，但是好奇心强，比较天真，容易受外界影响，自我控制能力较弱，意志力差等。因而在学校期间抵不住其他同学甜言蜜语的诱惑，最终在迷茫中与<a href=\"http://www.841990053.com/study.php?cid=11\" target=\"_self\" title=\"早恋教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">恋爱</span></a>挂上了钩，天真幼稚的她很快就被他人玩弄在手，脱离了学习目标，对于学习也开始慢慢地淡化在眼前，被恋爱诱惑后开始逃课逃学，出校后被不良的同学跟混迹社会人士勾结，把她带到一些娱乐场所吃喝玩乐，社会朋友一条链的介绍，最终被带进了“皇宫”。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 通过学校老师的反应后其父母才得至孩子不在学校读书了，而是像失踪一样不见踪影，其父母心急如焚，到处寻找，翻遍了整个城市。最终通过其同学提供的线索才找到了她，看着她精神疲惫恍惚，瘦弱不堪，其父母痛苦无奈。带回家后担心看不住她便找到了我们“<a href=\"http://www.841990053.com/study.php?cid=11\" target=\"_self\" title=\"早恋教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">早恋教育学校</span></a>”对其进行良好的思想教育，并送来学校进行修身养性，重新做人。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">叛逆青少年在永州问题孩子教育学校毕业不厌学了</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 1.就其迷茫的早恋给予针对性的心理辅导，疏导其对恋爱的好奇心和迷茫性的选择，改善其不良心态；</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 2.加强身体训练，参与基本军事动作与素质训练，增强体质，提高基本素质，转化不良行为；</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 3.开展早恋课程，授予恋爱知识，了解早恋的利与弊，增强自我防范意识，学会自我保护；</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 4.就其迷茫的思想和好奇心问题，加强对其应用心理的指导，转变其执着的偏执心态，让她学会明辨是非，提升自我控制能力；</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 5.加强她的文化补习，改善其学习兴趣，赶上文化的同步学习，避免在文化课程中落伍等。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">转化效果</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">戒网瘾学员在永州青少年戒网瘾学校改变了</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 首先，黄钰的体质得到了明显的改善，达到了健康成长标准；再次，黄钰的<a href=\"http://www.841990053.com\" target=\"_self\" title=\"恋爱早恋教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">恋爱思想</span></a>意识得到了深刻的认知，再加上经历过不少的痛苦，对恋爱也有了正常人的正确认识，表示除了结婚外不再顺便谈恋爱，会好好读书学习，考上一个好的大学，继续完成自己的“大学梦”；第三，黄钰的好奇心得到了很好的改善，不再那么的迷茫选择，而是再三考虑后才执行，学会了明辨是非，增强了自我控制能力；第四，当她在此回到学校读书后感觉到无比的新鲜，对学习产生了浓厚的兴趣，成了班级三好学生，成绩优异！</span></p>', '1', null, '1439632558', 'mingyu');
INSERT INTO `think_article` VALUES ('385', '19', '浙江少年被同学带入“游戏圈”', '', 'http://www.841990053.com/case.php?cid=19', '../Uploads/1484643871.jpg', '浙江戒网瘾中心，浙江戒网瘾学校，正规戒网瘾机构，湖南长沙戒网瘾，最好的戒网瘾学校', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=19\" target=\"_self\" title=\"孩子沉迷网络游戏怎么办\"><img src=\"/ueditor/php/upload/image/20170117/1484643719896524.jpg\" title=\"孩子沉迷网络游戏怎么办\" alt=\"孩子沉迷网络游戏怎么办\" width=\"258\" height=\"164\" style=\"width: 258px; height: 164px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">基本信息</span></strong></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 郭姚，女，十五岁，沉迷网络游戏、叛逆、厌学、贪玩等等不良问题，来自浙江温州，2013年9月11日入校，转变良好，现在上学中，无不良反应。</span></p><p><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">问题概况 </span></strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 因家庭条件较好，过早的接触电脑并学会上网，在无聊时间与同学聊天加入了网游队列，被“网瘾魔力”套住了双脚无法自拔。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">郭姚和其他一般沉迷网络游戏学生的不同之处就是不是她自己误入<a href=\"http://www.841990053.com\" target=\"_self\" title=\"戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">游戏</span></a>圈，而是在无聊时间与其他同学聊天被带到 网络游戏圈内，开始是个“游戏盲”的郭姚被同学一步一步深入其中，最终抵不住网络游戏的刺激诱惑的郭姚自己也踏上游戏平台，开始了自己的游戏生涯。从那以后，郭姚的上网频率越来越大，看书时间反而越来越少，学习成绩也随之下滑（相当于给学习兴趣装了个滑板，只往抵触滑行，无法高处走）。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 其父母发现情况后便是打骂交加，但却是阻碍无果，同时采取断网断电的残忍手段，使得郭姚的叛逆情绪狂起，打砸东西后邀请同学组队外出网吧上网，甚至有时候夜不归宿。让其母亲接近崩溃边界，原本是乖孩子却想不到今天出现的悲剧后果。在无奈之下其父亲借用周末休息时间便打开电脑查询个究竟，搜索了<a href=\"http://www.841990053.com\" target=\"_self\" title=\"戒网瘾\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">青少年励志成长教育学校</span></a>和<a href=\"http://www.841990053.com\" target=\"_self\" title=\"叛逆教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">叛逆孩子教育学校</span></a>等相关词语找到我了我们“<a href=\"http://www.841990053.com\" target=\"_self\" title=\"青少年问题教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">青少年问题教育学校</span></a>”后讲诉了郭姚的详细情况，通过我们青少年戒网瘾学校成功将其拉回正道，改变了她一生的命运。</span></p><p><strong><span style=\"color:#0070c0;font-size:18px\">辅导方案概况</span></strong></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 由于家庭条件较好，父母对她的满足度过高，总是担心她吃不饱穿不暖，平时的吃喝玩乐都是任由郭姚自己选择，久而久之，父母的条件开始在郭姚面前淡化，即使说了条件也是虚为乌有，这给了郭姚的任性逃之夭夭，无人可挡了。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 针对郭姚的问题特点，我们<a href=\"http://www.841990053.com/study.php?cid=8\" target=\"_self\" title=\"网瘾戒除\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">问题纠正学校</span></a>心理辅导专家给予制定了相应的辅导方案：1.疏通郭姚的沉迷思想，淡化上网意识；2.加强网络知识学习，重新认识网络的利与弊，正确用网；3.培养学习兴趣，对学过知识重新复习，对现在课程的辅导和未来课程的预习；3.适当参与军事基本素质训练，提升思想及动作素质，改善不良心态与行为；4体验生活实践，让其明白生活的艰辛和收获的来之不易，学会生活和懂得生活；5.接受感恩励志指导，树立正确的世界观、人生观、价值观，懂得感恩，学会励志；6.对其父母实施家教培训，改善其对孩子的管教方式方法，加强亲子沟通等。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\"><strong>转化效果</strong><br/>&nbsp; 先在家较少上网，能够听从父母安排，学习比以前积极很多，成绩不断上升；经过我们励志戒网瘾学校的生活实践体验和军事训练，让她变得勤快很多，平时在家主动帮父母完成各种家务，也学会了做饭（回家的第一天就是为父母做一次饭表示过错，让父母激动流泪，并拥抱大哭了一场）。在一个周末时间，其父母带着她返来我<a href=\"http://www.841990053.com/study.php?cid=8\" target=\"_self\" title=\"励志戒网瘾学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">励志成长教育学校</span></a>看望师生，还带了礼物和锦旗表示感谢！看到她的成功脱离游戏瘾，我们都为她给予鼓励，愿她再接再厉，完成她艰巨的学业。</span></p>', '1', null, '1436786381', 'mingyu');
INSERT INTO `think_article` VALUES ('386', '5', '黄老师》心理文化辅导', '', '', '../Uploads/1442804360.jpg', '心理咨询，心理辅导，问题孩子教育学校，戒网瘾学校，特训学校', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20150921/1442803537177247.jpg\" title=\"黄老师心理老师\" alt=\"黄老师心理老师\" width=\"239\" height=\"268\" style=\"width: 239px; height: 268px;\" border=\"0\" vspace=\"0\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">黄静波</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 心理老师兼任文化辅导班主任。来自贵州省遵义市，2010年加入中国共产党，成都大学心理应用学专业毕业，国家注册心理咨询师，来自多彩贵州遵义市，曾参与四川汶川地震留守儿童少管所支教心理辅导工作多年，获得“优秀支教老师”称号，看着坚强的孩子们长大读书后依依不舍地转入我校参与青少年健康成长教育工作。责任心强的他视生如友，精心辅导，亲切关怀，师生和睦，效果显著。</span></p>', '1', null, '1440161960', 'mingyu');
INSERT INTO `think_article` VALUES ('387', '3', '美术特长班的成效，看得见的效果', '', '', '../Uploads/1442826793.jpg', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/about.php?cid=3\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150921/1442826568140799.jpg\" title=\"美术特长生的随手绘画成品\" alt=\"美术特长生的随手绘画成品\" width=\"433\" height=\"270\" style=\"width: 433px; height: 270px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20150921/1442826594380099.jpg\" title=\"特长班成效\" alt=\"特长班成效\" width=\"430\" height=\"433\" style=\"width: 430px; height: 433px;\" border=\"0\" vspace=\"0\"/></a></p>', '1', null, '1440148393', 'mingyu');
INSERT INTO `think_article` VALUES ('388', '40', '法制教育：青少年犯罪的心理特征', '', '', '', '法制教育，青少年犯罪教育，青少年心理特征，问题青少年教育学校', '一半以上的犯罪青少年存在“以自我为中心”的价值观念，过分看重自身价值，在认识和处理事物时总是首先想到自己，认为个人利益高于一切，自私自利思想膨胀，要求别人以“我”为中心，为“我”服务，争强好胜，一意孤行，最终自我迷失以致跌入泥潭。', '<p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">一、<a href=\"http://www.841990053.com\" target=\"_self\" title=\"青少年励志教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\">青少年</a>犯罪的心理特征</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">1、以自我为中心，社会责任感薄弱</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 一半以上的犯罪青少年存在“以自我为中心”的<a href=\"http://www.841990053.com/about.php\" target=\"_self\" title=\"问题青少年教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\">价值观念</a>，过分看重自身价值，在认识和处理事物时总是首先想到自己，认为个人利益高于一切，自私自利思想膨胀，要求别人以“我”为中心，为“我”服务，争强好胜，一意孤行，最终自我迷失以致跌入泥潭。同时青少年罪犯一般家庭和社会情感发育不良，大多数人不了解社会主义道德规范的要求，对国家、对人民、对集体、对他人缺乏基本的责任感和道义感，在犯罪时没有顾及亲人和他人的感受，社会责任感极其薄弱。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">2、犯罪呈现突发性和情绪化</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 青少年无论在生理还是心理上都处于过渡时期，是由本能的生物人向理智的社会人发展过程中的一个阶段。某些青少年在社会化过程中没有完全形成健全的人格和正确的世界观、人生观、价值观，受到错误认识因素的影响，往往在某种因素的诱发和刺激下，或一时的感情冲动下突然犯罪。这种突发性的行为反映了青少年情感易冲动，不善于自我约束，自我控制。同时，<a href=\"http://www.841990053.com\" target=\"_self\" title=\"青少年教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\">青少年情绪化</a>强烈，想干什么就干什么，遇事冲动，不顾后果。一旦遇到挫折，缺乏理智的青少年会迅速外化为违法犯罪行为，危害社会。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">3、犯罪类型呈现团伙化</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 根据调查得知，团伙犯罪在目前我国青少年犯罪案件中占据大约60%以上的比例，且有逐年上升的趋势。青少年在成长期间，喜欢与兴趣、性格、目的等方面一致的人一起从事各种活动，在违法犯罪领域则体现为共同作案。团伙作案成为<a href=\"http://www.841990053.com\" target=\"_self\" title=\"问题青少年教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\">青少年犯罪</a>的主要形式，且人数越来越多，组织越来越严密。既有一些气味相投的青少年一拍即合形成的临时性纠合群体，又有具有严密组织和具体作案目的的犯罪团伙。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">4、犯罪手段残忍，不计后果</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 青少年处于个人社会化的重要时期，精力旺盛但极易冲动，收黄色书刊、淫秽音像制品、暴力文化的影响，同时也为在团伙中彰显自己的能力、提高自己的地位，作案往往心狠手辣、不择手段，在犯罪活动中非常疯狂，追求感官刺激，不计后果，不会考虑给他人造成的痛苦和给社会造成的损失。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">5、犯罪心理的报复性和犯罪心态的逆反性</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 青少年社会阅历浅，心智不稳定，情感冲动。对于缺乏理智的青少年，一旦受到不公正的待遇或因处理不及时、无法理解处理结果，就会采用报复手段对付他人。同时青少年渴望人格的独立自由，希望获得别人的尊重，对他人和社会的管束感到反感，特别是因不良行为被<a href=\"http://www.841990053.com/about.php\" target=\"_self\" title=\"管教叛逆孩子的学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\">管教</a>时，内心形成强烈的逆反情绪，轻则反抗，重则走上违法犯罪道路。</span></p>', '1', null, '1436615314', 'mingyu');
INSERT INTO `think_article` VALUES ('389', '40', '法制教育：青少年犯罪心理特征的原因分析', '', '', '', '青少年教育学校，青少年犯罪心理，青少年犯罪原因', '青少年心理发育并未成熟，加上社会阅历浅，辨别是非能力有限，在商品经济和外来文化的冲击下，一些不良风气在潜移默化中腐蚀着青少年的心灵。拜金主义、享乐主义、哥们儿义气为许多青少年所推崇。', '<p style=\"font-size: 16px; white-space: normal;\"><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">一、认知缺陷<br/></span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">1、社会认知缺陷</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 青少年心理发育并未成熟，加上社会阅历浅，辨别是非能力有限，在商品经济和外来文化的冲击下，一些不良风气在潜移默化中腐蚀着青少年的心灵。拜金主义、享乐主义、哥们儿义气为许多青少年所推崇。在这些错误人生观的支配下，青少年的行为极易触犯法律。同时一些青少年法制观念淡薄。据统计，在某少管所被调查的383名少年犯中，毫无法制观念的为359名（占总数的93、7%）。罪犯青少年往往缺乏法律知识，对自己的行为是否触犯法律全然不知，头脑中没有守法的观念，无法用法律意识来指导和约束自己的行为。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">2、他人认知缺陷</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 青少年处于青春叛逆期，缺乏理智分析能力，常常是非颠倒，善恶不分。一方面，他们常以批判的眼光来审视师长的教诲，抵触年长者的思想观念。当思想和行为受到外界干涉和压抑时，就可能激起其强烈反感，甚至表现出反抗行为。另一方面，青少年独立意识增强，自主空间拓展，从众心理使“志同道合”的青少年纠合在一起，形成不良的社会交往。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">3、个体认知缺陷</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 因心理较生理发展相对滞后，青少年无法客观的评价身体里那个慢慢觉醒的“自我”。美国著名心理学家埃里克森认为，如果青少年在成长过程中未形成社会认可的角色，或者他们自己觉得无法达到他人要求的水平，那么他们就可能选择消极的自我认同。形成消极自我认同的青少年可能会从犯罪团伙的同伴那里找到支持，这会进一步强化他们的消极自我认同。在埃里克森看来，青少年犯罪时建立自我认同的一种尝试，即使这种自我认同时一种消极的自我认同。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">二、需求因素</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 需求是在一定的生活条件下，有机体对客体事物的需求。美国心理学家马斯洛把人的需求分为五个层次，从低到高分别是：生理需求、安全需求、归属与爱的需求、尊重需求、自我实现的需求。心理研究证明，个人需求无论是合理的还是不合理的，如果得不到满足就会产生挫折感。一方面，社会上一些人过着花天酒地的日子，这些高消费对青少年极具诱惑力，使一些意志薄弱的青少年只追求物质，讲吃比穿，一旦某种需求受客观条件限制而难以得到满足就会不择手段，以非法目的捞取钱财。另一方面，青少年思维活跃，<a href=\"http://www.841990053.com/about.php\" target=\"_self\" title=\"好奇心教育\" style=\"color: rgb(0, 112, 192);\">好奇心</a>和求知欲强烈而<a href=\"http://www.841990053.com/about.php\" target=\"_self\" title=\"叛逆孩子教育学校\" style=\"color: rgb(0, 112, 192);\">明辨是非能力差</a>，大众传媒中一些庸俗、不健康的内容极易侵蚀青少年的心灵。日积月累的心灵荼毒令很多青少年的精神需求趋于庸俗、低级，为犯罪起了推波助澜的作用。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">三、青少年的犯罪动机因素</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 犯罪动机是指推动犯罪人实施犯罪行为的内部驱动力。青少年犯罪的动机因素常常表现为情景性、情绪性、易变性三个方面。情景性是指青少年极易为某种情景直接引诱而形成犯罪动机，且一旦动机形成，就会在头脑中迅速蔓延到每一根神经，一触即发。从某种意义上讲，青少年犯罪动机的形成往往不经过预谋和事先策划，从外界刺激到产生犯罪动机，具有突发性。情绪性是指青少年犯罪主体动机构成的主要因素是情绪和情感，其动机的产生、实施和转化均带有浓厚的情感和情绪色彩。青少年情绪反应速度快，强度高，一点外部刺激可能引起莫名的情绪爆发。他们的恶劣行为有时根本没有什么明确的目的，只是为了追求心理上的满足和情感体验上的愉悦。易变性是指青少年犯罪主体在实施某一犯罪行为时，由于客观情况的变化造成主体情绪发生变化而产生新的犯罪动机。在犯罪动机转化为实施行为的过程中，如果遇到阻力，使犯罪的初始动机未得逞，<a href=\"http://www.841990053.com/about.php\" target=\"_self\" title=\"感情用事的青少年教育\" style=\"color: rgb(0, 112, 192);\">感情用事的青少年</a>可能会因激情难以而迁怒于他人，另寻报复、发泄对象。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">四、青少年问题的情感因素</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">1、独立意向</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 随着青少年生理的发育，智力的发展和知识的积累，生产了强烈的独立倾向。一方面他们希望摆脱成人对他们的约束，渴望独立自主；另一方面，由于他们在思想上无法跨越特定的生理阶段，经济上难以自给自足，在遇到复杂问题时还需要成人的帮助。因此独立性与依赖性的矛盾进一步激发他们的独立意向。如果不能正确处理这一矛盾，处处同成人相对抗，就极易引发犯罪行为。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">2、情绪不稳定，易冲动</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">青少年时期虽然神经系统逐步发育完善，但这个时期中枢神经系统的兴奋和抑制过程还很不平衡，大脑皮层兴奋占优势，情感对于理智，感觉灵敏，反应迅速情绪波动大，所以一旦受到某些挫折或刺激，引起感情冲动，由于缺乏理智和控制力，就会激情爆发，意气用事，产生犯罪行为。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">3、冷漠、残酷</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">我国正处于市场经济时期，竞争不断加剧，贫富差距拉大，使青少年在内心和社会之间造成紧张的压力。一些青少年对自己的未来和社会失去信心，形成冷漠、不与人交往的性格。一些青少年在自我封闭的心灵空间里形成变态人格，破坏欲极强，对光明美好的事物持敌视态度，以毁灭他人的希望来弥补自己得不到的缺憾，常把自己的快乐建立在别人的痛苦之上。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">五、青少年成的意志因素</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 青少年时期由于缺乏人生的锤炼与磨难，意志力比较薄弱，分辨是非能力低，其处世的无知性和盲目性很难抵挡来自社会各方面的影响，经不起诱惑，容易被别人拉拢利用，从而走上犯罪的道路。同时由于普遍存在的侥幸心理和强烈的逆反心理，使青少年实施犯罪的意志力顽强，不达目的不罢休，从而在犯罪的泥潭中越陷越深，不能自拔。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">六、个性因素</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">1、智力水平总体偏低</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 智力是人类认识客观事物和解决实际问题的能力。我国心理工作者应用智力测验测量119名罪犯青少年的智商，结果表明，全体人数中没有一个智力优秀的，智力水平总体偏低，中下和极差的比例偏大。</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">2、神经气质类型的影响</span></p><p style=\"font-size: 16px; white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 前苏联生理学家巴普洛夫吧神经气质分为四类：强、平衡、灵活型（活泼型），强、平衡、不灵活型（镇静型），强、不平衡型（兴奋型），弱型（抑制型）。气质类型本身无好坏之分，在犯罪心理形成的过程中，气质不能其发动或制止作用，但气质可能影响犯罪者选择犯罪行为类型、犯罪手段和方式。在青少年犯罪中，强型气质的青少年冲动、果断、灵活，往往易实施暴力犯罪；安静型气质的青少年一般机敏、细心、沉着，易实施盗窃犯罪：弱型气质的青少年胆小、孤僻、悲观，易实施非暴力犯罪。</span></p>', '1', null, '1436783029', 'mingyu');
INSERT INTO `think_article` VALUES ('390', '40', '​法制教育：做好预防青少年犯罪问题工作', '', '', '', '青少年犯罪预防，教育青少年问题，叛逆青少年励志教育学校，问题青少年教育学校', '青少年犯罪心理的形成是一个由量变到质变的发展过程。面对青少年犯罪的心理特点，化解青少年成长发育过程中的心理冲突，培养青少年健康的心理，及时消除其人格障碍，是一项刻不容缓的工作。青少年的人格、心理、行为习惯并未成熟，如果加以有效的引导，可以从根本上预防青少年犯罪', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 16px;\"> </span><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">&nbsp; 青少年犯罪心理的形成是一个由量变到质变的发展过程。面对<a href=\"http://www.841990053.com\" target=\"_self\" title=\"励志成长教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">青少年犯罪</span></a>的心理特点，化解青少年成长发育过程中的心理冲突，培养青少年健康的心理，及时消除其人格障碍，是一项刻不容缓的工作。青少年的人格、心理、行为习惯并未成熟，如果加以有效的引导，可以从根本上预防青少年犯罪。<br/></span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">一、净化社会环境，构建家庭、学校、社会三位一体的青少年犯罪预防工作网络</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 家庭是青少年的第一<a href=\"http://www.841990053.com\" target=\"_self\" title=\"法制课堂\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">课堂</span></a>，父母是孩子的第一任老师，良好的家庭环境可以使青少年形成健康的心理。家长应多与子女沟通交流，及时了解子女心理、情绪变化，以科学的方法教育子女，做孩子的朋友，让孩子在家长的教育中感受到爱和温暖。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 学校是青少年从家庭走向社会的中间环节，是预防青少年犯罪的主要阵地。学校应努力改变传统的评价体系，积极推进素质教育，尊重每一个学生的个性发展，让学生感受到集体的温暖，避免因心理扭曲和人格缺陷引发的犯罪事件。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 很多青少年犯罪是因为受到社会上某些不良风气的影响所致，因此，要想预防青少年犯罪就必须净化社会环境，加强社会环境治理力度，限制大众传媒中不健康内容的出现，消除青少年模仿犯罪行为的根源。宣传先进人物事迹，为青少年树立优秀榜样；宣传良知，弘扬正气，为青少年心理健康创造良好的社会氛围。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">二、加强人生观和价值观教育，提高青少年道德素质</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 错误的人生观、价值观导致错误的言论和行为。青少年时期是人生观、价值观逐步形成的时期，因此引导青少年树立正确的人生观、价值观至关重要。不仅要告诉青少年什么是对的，什么是错的；什么是道德的，什么是不道德的，还要加强思想道德教育和法制教育，使青少年养成良好的思想道德品质的同时使其懂法、守法。同时要客观而科学地向青少年传授和讲解性知识，通过科学知识的教育，打破他们对性的神秘感，使青少年能够从伦理道德意义上去把握自身的性行为，用道德意识去调节心理和性行为。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">三、开展心理辅导与咨询工作，及时化解不良情绪</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 青少年在学习和生活中遇到困难时易产生不良情绪，导致<a href=\"http://www.841990053.com\" target=\"_self\" title=\"情绪教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">情绪障碍</span></a>，当这些不良情绪得不到及时的化解，一旦遇到外界不良刺激或不良诱因，容易导致犯罪行为的发生。通过心理辅导与咨询，由心理医生接待青少年来访，为青少年提供疏导服务和信息帮助，使青少年更多地了解自身的生理和心理发展中的问题，及时化解不良情绪，正视并解决这些问题，减少青少年情绪障碍的产生。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">四、通过不同的方式对青少年进行挫折教育</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 所谓<a href=\"http://www.841990053.com\" target=\"_self\" title=\"挫折教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">挫折教育</span></a>是指为了提高青少年克服在遇到挫折时情绪低落，意志消沉的能力，在事情发生前向青少年灌输振作精神，鼓舞斗志的理论或事先拟置特定的环境让青少年置身其中以增强青少年的心理适应能力的教育方式。它是针对青少年心理不成熟、承受能力弱的一种教育方式。青少年心理尚未发育成熟，如果不能有效抵制外界消极因素，就会出现各种心理问题，挫伤积极性，情绪低落，丧失对前途美好的憧憬。如果这些心理问题不及时加以<a href=\"http://www.841990053.com\" target=\"_self\" title=\"心理疏导\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">疏导</span></a>、转化，就有可能形成一种犯罪动机。所以要告诉青少年成功和失败都是财富；接纳青少年宣泄挫折，使其消除压抑；帮助他们正确看待挫折，面对挫折泰然应对，不要因为挫折而否定自己的能力；创设情景，让他们感受挫折，领略生活的艰辛和社会的复杂，锻炼他们承受挫折的能力。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">五、引导青少年建立良好的人际关系</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 人际关系的实质是人与人之间的心理关系。如果青少年生活在不和谐的人际关系氛围中，容易产生挫折感、孤独感，对社会冷漠不满，甚至有攻击行为。为此，第一，青少年要改变社交观念，把社交看作扩展视野、沟通心灵的必要方法，而不是把社交看作是浪费时间，耽误学习。第二，多渠道交往，扩大社交范围。利用郊游、联欢等加强交往实践，对学业、思想有所启发的人都要乐于接触。第三，注意筛选交往对象。多和对自己有正面积极影响的人沟通学习，拒绝与社会不良人员交往。同时在人际交往中要学会宽以待人，<a href=\"http://www.841990053.com\" target=\"_self\" title=\"换位思考\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">换位思考</span></a>，克制忍让，形成立体的良好的人际交往。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">六、培养青少年健康成熟的人格</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 违法犯罪青少年大都具有不成熟的<a href=\"http://www.841990053.com\" target=\"_self\" title=\"人格教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">人格特征</span></a>。人格发展不成熟加剧了犯罪青少年的身心矛盾，导致犯罪行为的发生。要彻底预防青少年犯罪。必须培养青少年健康成熟的人格。在生活和学习中，让青少年热爱生活，言行一致，自尊自爱，尊重并乐于帮助他人，克服焦虑不安和痛苦，保持心理稳定，使自己的性格、情感和行为能符合社会的行为规范，更好地适应社会的发展。同时通过寻找个人自尊自信的支点、创造个人成功记录等方式来培养其自尊自信的良好品质。通过提高青少年的认知能力，使其较全面、正确地了解自己，引导青少年保持个人自尊自信的正确方向。</span></p>', '1', null, '1437539845', 'mingyu');
INSERT INTO `think_article` VALUES ('392', '9', '叛逆教育：青少年叛逆期男孩教育方法？', '', '', '', '叛逆期男孩教育方法，叛逆孩子教育学校，青春叛逆期，叛逆期的孩子怎么教育', '每一位孩子都越过不了的时期段——青春叛逆期，这个阶段是令父母最为头疼的时期，一般的手段无法管教自己的孩子，溺爱不行，打骂也不行，哄着也哭，给他也不小的逆反表现。无论做什么都是与我们正常事态相反的做法，这是个孩子成长的一个过渡期——青春叛逆期。   ', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 每一位孩子都越过不了的时期段——青春叛逆期，这个阶段是令父母最为头疼的时期，一般的手段无法管教自己的孩子，溺爱不行，打骂也不行，哄着也哭，给他也不小的逆反表现。无论做什么都是与我们正常事态相反的做法，这是个孩子成长的一个过渡期——青春叛逆期。<br/></span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 在这个成长期的孩子由于思想及心智都未发育完全，处于思想萌芽期，就像一个气球在胀大的时候一样，需要一种能量来补给才能正常不断地成长。孩子更是需要如此，需要一种健康、积极、向上的正能量来不断地补给，从而才能满足他的成长需求，但是孩子的这种成长需求不是单一的，而是多当面的。但是，孩子的成长也跟气球在充气一样，必须适度，不能迅速和过量，因为像气球一样充气过快或者过量它就会爆炸，所有的工作将毁于一旦。孩子也是如此，当您迅速的满足时，孩子就不会懂得珍惜，以为您就是他的个人仓库，想要就有；当您过量的满足时，孩子就会觉得自己是“玉皇大帝”享不尽的荣华富贵，让孩子产生了一种奢侈的消费观念，便是铺张浪费。一旦到这种地步，往往会发现孩子出现很多不良问题，简单的说教和打骂更是让孩子变本加厉，后果不堪设想，父母甚是头疼。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 特别是男孩子，一旦叛逆起来更可怕，由于孩子不同于女孩，胆子大，调子高，什么事都能做得出。尤其是在叛逆青春期的男孩子，家长特别要注意留心管教，耐心引导，杜绝打骂等暴力管教方式。对于叛逆期的孩子，方面必须培养自己的耐力和意志力，管教叛逆的孩子不是一下子的事，而是长期的陪伴和教导。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 叛逆孩子的教育，和谐平等是关键。因为叛逆的孩子心理思想比较高傲，一般都会以自我为中心，排斥别人的歧义，特别是自己的父母亲。所以作为父母亲，要注重和谐平等的管教方式方法，避免跟孩子的思想产生较大的分歧，一良好的心理思想慢慢地引导孩子，以兴趣牵引回正。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp;如果您是用着长辈或父母等高等的身份与孩子谈条件，他们都是有所回避或者是逆反的，因为孩子是聪明的，知道长辈的身份也阅历是高的，但是会认为长辈的很多思想和思维都没有跟得上社会潮流，跟自己的想法的需求是相反或不符合的，因而会产生排斥心态。如果您是运用平等和谐的身份语气跟孩子交谈，结果是截然不同的，至少能让孩子感觉到自己是在成长和变化了，让他产生一定的反思意识和做出良好的表现；当然在平等和谐的情况下孩子也是愿意沟通交谈的，不会产生叛逆和排斥心理。</span></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=9&id=392\" target=\"_self\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20151112/1447319145142263.jpg\" title=\"平等，可以营造幸福！\" alt=\"平等，可以营造幸福！\" width=\"613\" height=\"110\" style=\"width: 613px; height: 110px;\" border=\"0\" vspace=\"0\"/></span></a></p>', '1', null, '1442404453', 'mingyu');
INSERT INTO `think_article` VALUES ('393', '8', '问题纠正学校：孩子沉迷虚拟事物会带来很多其他的严重问题', '', '', '', '专业的戒网瘾学校，湖南正规戒网瘾学校，青少年戒网瘾学校', '网络，是当今的发展盛世，是不可少的互动工具。众所周知，网络是一种良好的互联通讯工具，有利也有弊，但是对于青少年来说，更多的是学坏比学好的更容易更精彩。各位家长应该有所了解，自己的孩子平时的上网除了游戏还是玩游戏，严重地影响了他们的正常学习和生活。而懂得利用这良好的网络来进行学习和竞技深造的孩子却寥寥无几，然而却把美好的青春及时间白白地浪费掉了。', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 网络，是当今的发展盛世，是不可少的互动工具。众所周知，网络是一种良好的互联通讯工具，有利也有弊，但是对于青少年来说，更多的是学坏比学好的更容易更精彩。各位家长应该有所了解，自己的孩子平时的上网除了游戏还是玩游戏，严重地影响了他们的正常学习和生活。而懂得利用这良好的网络来进行学习和竞技深造的孩子却寥寥无几，然而却把美好的青春及时间白白地浪费掉了。但这常见而平凡的问题却被各个父母以小事视而不见，让孩子越战越勇，称霸了一方网络，不断地晋级深造美好的名誉。然而，这些让孩子感觉到欢快的虚拟的网络游戏绑定了孩子的一切，养成了网络的依赖，无法自拔。时间越久，网瘾越重，对孩子的生活和学习以及成长都是带来这严重的影响，问题不断增加，甚至严重的网瘾青少年会毁掉一个幸福的家庭。那么仅仅是上网成瘾会带来哪些严重问题呢？下面我们来例举一二。</span><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">湖南长沙正规戒网瘾学校-戒网瘾中心-戒网瘾机构</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">一、<span style=\"font-size: 18px; color: rgb(255, 255, 255);\">网瘾的</span>孩子会产生厌学辍学问题</span></strong><span style=\"color: rgb(255, 255, 255); font-size: 10px;\">湖南戒网瘾学校/江西戒网瘾学校-湖北戒网瘾学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 一旦孩子上网成瘾，他整个人的心思完全投入虚拟的网络世界里。那么也就会是学习成绩不断下滑，然而在班级和学校的学习排名就会一落千丈，在这过程中，变化如此明显的孩子就会受到同学和他人的关注，如果孩子努力无果，将会被嘲笑和鄙视，自我感觉就是伤了自尊心和淘汰感，因而产生厌学情绪辍学行为。</span><span style=\"color: rgb(255, 255, 255); font-size: 10px;\">最好的戒网瘾机构戒网瘾学校</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">二、<span style=\"font-size: 18px; color: rgb(255, 255, 255);\">网瘾</span>孩子的生活规律会被颠倒</span></strong><span style=\"color: rgb(255, 255, 255); font-size: 10px;\">长沙戒网瘾学校，株洲戒网瘾学校，湘潭戒网瘾学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 一般上网成瘾的孩子会把时间给忽略掉，生活也随之随意，没有一个正常作息和生活规律。常见的问题就是黑白颠倒，白天睡觉晚上上网通宵，导致了第二天起不来，早餐没得吃，午餐起不来，然而下午起来就是抱着电脑不放，形成了一个恶性循环的颠覆规律。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">三、<span style=\"font-size: 18px; color: rgb(255, 255, 255);\">网瘾</span>孩子的身心健康严重受到影响</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">浏阳戒网瘾学校，岳阳戒网瘾学校，汨罗平江戒网瘾学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 由于生活规律的颠倒，使得一日三餐也跟着混乱，睡眠不足和营养不良，身体会一日不如一日，消瘦不堪，身心影响严重。近几年发生较多的就是不少青少年萃死在网吧里或电脑前，这些都是因长期上网引起的生活规律失调，睡眠不足和营养不良，导致身体无法存受而休克情况。</span><span style=\"color: rgb(255, 255, 255); font-size: 10px;\">戒除网瘾最好的学校机构</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">四、<span style=\"font-size: 18px; color: rgb(255, 255, 255);\">网瘾</span>会影响到家庭的和睦与发展</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">娄底戒网瘾学戒除网瘾最哈校，怀化戒网瘾学校，常德戒网瘾学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 很多青少年上网成瘾导致一个幸福的家庭四分五裂，妻离子散，一发不可收拾。如果是孩子上网成瘾会引起父母之间的管教纠纷，在为孩子管教问题争吵不休，严重的便会产生父母离婚等问题。不少孩子上网成瘾后无视家庭和身边人的任何情况，只顾着自己虚拟的玩乐，有些甚至家庭出现了重大变故也一无所知，最后成了“留守儿童”。孩子是家庭的重要组成部分，如果孩子不融入家庭的分子里面，那么仅仅是父母两人就已经不是家庭，更是幸福和谐家庭了。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 因此，孩子的问题是不能小视也不能忽视。对于孩子的问题，需要及时发现、高度重视、给予教育，让问题以大化小，把小化了，这样您的孩子一样可以变得优秀。随其自然，只是问题的恶化，而不是自动好转，就像轮子一样越转越磨，就会变小变烂；而孩子问题就像在雪地里翻滚的雪球，越滚就会越大，当翻滚到极限的时候就是四分五裂、粉身碎骨，将变得一无所有。</span><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">仁佳戒网瘾学校机构-仁佳特训学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; <span style=\"font-size: 18px; color: rgb(0, 176, 80);\">您孩子的问题还小吗？是否也在犹豫？找湖南青少年励志成长教育培训学校》<a href=\"http://www.841990053.com\" target=\"_self\" style=\"color: rgb(0, 176, 80); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 176, 80);\">www.841990053.com</span></a></span></span><span style=\"color: rgb(0, 176, 80);\"> &nbsp; </span><span style=\"color: rgb(0, 176, 80);\">微电：188 74250 661 &nbsp;潘老师</span></p>', '1', null, '1442404692', 'mingyu');
INSERT INTO `think_article` VALUES ('394', '24', '青少年教育学校：欢乐过中秋 文艺烧烤真会玩', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 今天是我们中国一年一度的传统节日——中秋节。中秋节有悠久的历史，和其它传统节日一样，也是慢慢发展形成的，古代帝王有春天祭日，秋天祭月的礼制，早在《周礼》一书中，已有“中秋”一词的记载。后来贵族和文人学士也仿效起来，在中秋时节，对着天上又亮又圆一轮皓月，观赏祭拜，寄托情怀，这种习俗就这样传到民间，形成一个传统的活动，一直到了唐代，这种祭月的风俗更为人们重视，中秋节才成为固定的节日，《唐书·太宗记》记载有“八月十五中秋节”，这个节日盛行于宋朝，至明清时，已与元旦齐名，成为我国的主要节日之一。&nbsp;<br/></span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 我们青少年励志成长教育学校同祖国人民一道，一起庆祝这美好的节日，今天早上，原本是安排在晚上的中秋文艺大晚会突然进行一次一百八十度的大转弯，调整到今天上午进行。由于大家都感觉天气有点异常，像是会下雨的阴暗天气，为了让我们的文艺表现有着精彩的展示，让更多的观众欣赏我们青少年励志学员的精彩文艺培训效果。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 本次文艺节目由特长班老师督导，统一让学生自行安排排练，培养出了一批优秀的策划员、指导员、演员及特长才艺等学员，让他们学会自导自演的自我顽强精神，学会自理，懂得励志，明确目标的良好思想。</span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20151009/1444381010111951.jpg\" title=\"中秋文艺演出会\" alt=\"中秋文艺演出会\" width=\"356\" height=\"199\" style=\"width: 356px; height: 199px;\" border=\"0\" vspace=\"0\"/><img src=\"/ueditor/php/upload/image/20151009/1444381041347696.jpg\" title=\"过中秋，吃好玩好，快乐成长\" alt=\"过中秋，吃好玩好，快乐成长\" width=\"327\" height=\"199\" style=\"width: 327px; height: 199px;\" border=\"0\" vspace=\"0\"/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20151009/1444381118563716.jpg\" title=\"看到了吗，自己动手，丰衣足食\" alt=\"看到了吗，自己动手，丰衣足食\" width=\"356\" height=\"198\" style=\"width: 356px; height: 198px;\" border=\"0\" vspace=\"0\"/><img src=\"/ueditor/php/upload/image/20151009/1444381186816614.jpg\" title=\"中秋烧烤活动\" alt=\"中秋烧烤活动\" width=\"327\" height=\"198\" style=\"width: 327px; height: 198px;\" border=\"0\" vspace=\"0\"/></span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 晴朗的上午，文艺活动在中秋佳节中热闹响起，学员的精彩文艺展出不断，部分学生家长也趁美好佳节来校看望，也参入了我们热闹的活动之中。在这美好的节日中，吃喝玩乐享不尽。上午月饼零食使劲吃，中餐鱼肉佳肴用心品，下午烧烤还是口水直流。这美好佳节，我们吃得开心，玩得快乐，您来了吗？<img src=\"http://img.baidu.com/hi/youa/y_0017.gif\"/></span></p>', '1', null, '1443353292', 'mingyu');
INSERT INTO `think_article` VALUES ('395', '40', '自卑自闭的问题青少年父母怎么管教？', '', '', '', '自卑自闭的问题青少年父母怎么管教？', '自卑自闭的问题青少年父母怎么管教？', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 很多父母以为孩子有点自卑自卑总是认为孩子是性格内向的小问题，其实，孩子的自卑自卑问题不仅仅是性格内向的问题，而是孩子存在一定的心理障碍疾病，不是简单的情绪化小事。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 根据统计，大部分自卑自闭孩子确实是因性格内向而引起的孤僻症，也就是说，孩子长期处于性格孤僻的自我生活圈里，会导致与外界的正常沟通交流分隔开了，久而久之便养成了习惯，习惯便成了自然。但是也有部分自卑自闭的孩子是因为后天的心理障碍引起的，比如某些孩子后天的正常生活和学习中，没有得到良好的思想引导和同情的援助，处处碰壁，做什么都不如人，这样一来使得心理会产生一种落魄感和自责自弃，慢慢地会躲避或逃离，把自己的圈子不断缩小，最后成了孤僻一人，形成了自卑自闭问题。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 对于这一类的孩子，作为父母，特别要注意相关的管教方式方法，避免错入误区，给孩子及家庭带来不良的后果，特别是孩子本身的影响。那么应该如何管教呢？湖南青少年教育给您几个建议：</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.获取沟通条件</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 人的生活重于沟通，是成长的垫脚石。对于自卑自闭的孩子来说，沟通更是解决问题的根本。但是自卑自闭的孩子正好是难以沟通的对象，那么必须寻找出孩子感兴趣的东西来做沟通的导线，牵引孩子开口说话，愿意交流。慢慢地从孩子的兴趣话题淡入正常的生活话题，方可谈条件。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.了解孩子的个人想法</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 自卑自闭孩子的个性特征比较明显，个人的想法也是比较特别的，我们必须想办法了解到孩子的个人想法，才能有引导和解决问题的方案。但是要了解到孩子的真实想法是需要有耐心的，慢慢地从基本的沟通中获取资料，从生活中关注其特点。只要了解到孩子的个人想法及特点，您就知道应该怎么做了。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3.必须给予真情的关爱</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 一般自卑自闭的孩子主要是缺乏安全感和他人的关爱，才是把自己圈起来，封闭在自己的世界里。像这样的孩子，您必须给予充分的关爱，让孩子感觉到有人是真心的在爱他、关注他和认可他。当孩子得到这些基本的尊严后会慢慢地从自己的世界里走出来，步入现实的生活中来。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4.及时关注孩子成长动向，适当伸出援手</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 当发现孩子在生活或学习上遇到什么问题时候必须及时关注，看孩子是在面对还是逃避，是运用了些什么方式方法。但要是发现孩子遇到问题自己不敢面对的时候就要及时鼓励，要是孩子敢面对而无法解决的时候要及时伸出援手，帮助孩子一把，并且教导孩子之后遇到问题应该要如何面对和解决。这样不仅拉近了亲情，更是孩子的最佳伙伴和坚强的后盾，让孩子在成长过程中不胆怯、不退步、勇敢面对，增强他的自信心。</span></p>', '1', null, '1434547489', 'mingyu');
INSERT INTO `think_article` VALUES ('396', '33', '南京家长：请问孩子自卑自闭带有点抑郁症能辅导好吗？', '', '', '', '问答，心理辅导学校，自卑自闭教育，抑郁症教育，问题青少年教育学校', '请问孩子自卑自闭带有点抑郁症能辅导好吗？我们之前曾经带孩子去了医院检查，医生说我孩子有抑郁症，不知道是真的假的，我问这到底是什么样的病怎么治疗，他就是说', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; <span style=\"font-size: 36px; color: rgb(0, 112, 192);\">问</span>：请问孩子自卑自闭带有点抑郁症能辅导好吗？我们之前曾经带孩子去了医院检查，医生说我孩子有抑郁症，不知道是真的假的，我问这到底是什么样的病怎么治疗，他就是说一种精神病开药吃就好，感觉好像哪里不太对劲，我个人觉得应该是一种心理问题而已，怎么说是精神问题？还是开药吃就好，这让我不知道如何是好。看到你们学校是专业教育问题青少年的不知道你们是否能够转变还像我这样孩子的问题呢？也是开药了吗？还是以心理辅导转变的？</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; <span style=\"font-size: 36px; color: rgb(0, 112, 192);\">答</span>：感谢对湖南青少年教育学校的大力支持和认可!像您孩子这样的问题我们是可以转变好的。首先声明我们学校是专业针对不良问题的青少年进行以心理疏导为主的认知教育和行为矫正的学校；二、我们是做教育的学校，而不是医院，不是以开药来治疗孩子问题的；三、我们是以心理认知疏导的青少年教育学校，杜绝打骂体罚教育，实施24小时网络高清远程监控的透明化教学管理，您可以随时随地视察您孩子和学校动态。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; <span style=\"font-size: 18px; color: rgb(0, 176, 80);\">欢迎您随时来校实地考察与了解，您的满意就是我们的办学宗旨！学校招生网：</span><a href=\"http://www.841990053.com\" _src=\"http://www.841990053.com\" style=\"color: rgb(0, 176, 80); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 176, 80);\">www.841990053.com</span></a><span style=\"font-size: 18px; color: rgb(0, 176, 80);\">&nbsp; &nbsp;联系微信电话：188 74250 661（学校招生办主任：潘老师）</span></span></p>', '1', null, '1434625775', 'mingyu');
INSERT INTO `think_article` VALUES ('397', '25', '南京虐童案养母:我不是虎妈 只想孩子品质好?', '', '', '', '叛逆教育学校，青少年叛逆教育，叛逆孩子教育学校', '', '<p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">昨天，“南京虐童案”在南京市浦口区人民法院开审。9岁男童施某某的养母李征琴承认打了孩子，但否认犯罪，她对指控其故意伤害罪很有异议，称“是出于教育的目的，我心里不是想伤害人(孩子)”。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　庭审中，伤情鉴定是否客观成为控辩双方的争论焦点。此外，双方还就收养手续是否涉嫌违法进行辩论，李征琴夫妇也被曝各自都有儿女，但却凭借一份“无子女证明”领养了施某某的事实。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　昨天的庭审进行了12个多小时，一直到晚上10点才休庭，今天上午9点将继续开庭审理。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\"><strong style=\"padding: 0px; margin: 0px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0);\">被控涉嫌故意伤害罪</strong></span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　昨天9点30分，该案正式开庭。48岁的李征琴被两名女法警带到法庭。她戴着眼镜，身穿绿色真丝裙，外套一件黑色针织衫。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　公诉人宣读了起诉书。据检方指控，2013年6月，李征琴与其丈夫施某通过安徽省来安县民政局办理了收养施某某的手续，并将其带回南京家中抚养。2015年3月31日晚，李征琴认为施某某考试作弊、未完成课外阅读作业且说谎，在家中先后使用抓痒耙、跳绳对施某某进行抽打，造成施某某体表分布较广泛的挫伤。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　　经南京市公安局物证鉴定所鉴定，施某某躯干、四肢等部位挫伤面积为体表面积的10%，其所受损伤程度已构成轻伤一级。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　　案发后，李征琴于2015年4月4日，经公安机关电话通知后，到案接受调查，并如实供述了自己的主要罪行。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　　检方认为，李征琴作为养母，因家庭教育方式不当，故意伤害施某某的身体，且造成其损伤程度达到轻伤一级的严重后果，构成故意伤害罪，应该依法追究其刑事责任。但鉴于李征琴在案发后认识到了自己的错误，取得了施某某及其亲生父母的谅解，并达成了和解协议，此外，李征琴及时到案并如实供述了自己的主要罪行，故建议对其从宽处罚。检方向法庭提出，根据该案的犯罪事实、性质、情节以及被告人案发后的表现，充分考虑儿童利益最大化原则，以及施某某的身心健康及未来成长的实际，对李征琴作出公正的判决。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　<strong style=\"padding: 0px; margin: 0px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0);\">打孩子是因其说谎话</strong></span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　“我没有犯罪，对指控我故意伤害很有异议”，对于检方的指控，李征琴不认罪。她称，自己当天确实打了孩子，但没有打得那么重，也不可能构成轻伤。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　讲到当天打孩子的原因，李征琴称，主要是因为孩子当天向她撒了谎，而且孩子一直有撒谎的毛病，她担心孩子以后会学坏。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　李征琴说，现年9岁的施某某到她家已有3年。施某某在6岁前没有上过幼儿园，没有接受过教育，形成了很多不良习惯，比如撒谎、思想不能集中、不能与成年人进行交流等等。“很多正常孩子应该会的东西他都不会，连一加一都不会”，李征琴说，平时她都很有耐心教施某某，这次打施某某是因为他说谎。“说谎的习惯涉及到孩子长大后的品行问题”，李征琴说，施某某经常撒谎，考试抄别人的，她和丈夫教了施某某3年，都没有成效。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　按照李征琴的说法，事发当天，施某某告诉她自己考试得了第一名。但后来她了解到，施某某考试时抄了同桌的卷子，反复追问下，施某某才承认，“我这次气是因为他不仅撒谎，还拿撒谎的结果向我炫耀，我觉得他的谎言往严重的方向发展”。李征琴认为，自己是出于教育孩子的目的才动了手，而且这是她第一次打施某某，当时她想“是不是打他一下，可以让他把坏习惯改掉，心里并没有想伤害他”。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\"><strong style=\"padding: 0px; margin: 0px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0);\">当庭演示打孩子过程</strong></span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　法庭上，在回答公诉人对于打孩子过程的讯问时，李征琴表示，当时自己先用了竹制的挠痒耙，打了施某某的腿。此后，她让施某某去写作业，施某某拿作业时拿出了跳绳，她就将跳绳拿了过来。“我演示一下，当时拿出来是这样折好的，”李征琴边说边演示着，“我抢过来，就是顺手抓过来的，就这样往他身上刷，刷一次就是8道，3下就是24道。”李征琴强调说，自己并没有打很多下，大概就刷了二三次。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　李征琴说，当时自己打了施某某多长时间，已经记不清了，但她认为力度并不重，而且当时是4月份，施某某还穿着棉衣。“跳绳摆在这，手有多重我知道，他还穿着棉的衣服，我打他能有多重，他是我的孩子，我还能往死里打？”李征琴说。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 10px; border: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 32px; color: rgb(57, 57, 57); font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192);\">　李征琴表示，此后她因为生气，也没有去检查施某某的伤。在被通知去往派出所，看到承办民警出示的施某某伤情照片，她吓了一跳，“我看了这么严重啊，我第一次看，不敢相信”，李征琴称，她看到孩子伤情很惊讶，不能理解自己仅用跳绳打了几下，施某某又穿了衣服，怎么会这样，“我孩子我带了3年，就算是一只狗养了3年也有感情的，如果我知道孩子受伤，我一定会带他去看的”。（</span><a href=\"http://news.xinhuanet.com/politics/2015-09/29/c_128277049.htm\" target=\"_blank\" title=\"亲子教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">来自360新闻</span></a><span style=\"color: rgb(0, 112, 192);\">）</span></p>', '1', null, '1443484509', 'mingyu');
INSERT INTO `think_article` VALUES ('398', '33', '郴州家长：把一个叛逆不上学的孩子送进特殊教育学校好吗？', '', '', '', '叛逆孩子教育学校，戒网瘾学校，长沙问题叛逆青少年教育中心', '', '<p>&nbsp; <span style=\"font-size: 36px; color: rgb(0, 112, 192);\">问</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">：有一位家长疑问：请问把一个叛逆不上学的孩子送进特殊教育学校好吗？</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">家长自述：因为我总觉得，孩子本来就叛逆不听话的，连我们父母都说的话孩子都不听，何况是送进特殊教育学校的陌生环境中呢，并且都是陌生人，说的话孩子怎么会听？在一个就是孩子本来就不愿意去学习了，把他关在学校里怎么会学习呢？难道是强制让他学习吗？这个我很是不理解的，学校会有什么特殊教育方式吗？</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; <span style=\"font-size: 36px; color: rgb(0, 112, 192);\">答</span>：我们的特殊教育是有着特殊的教育方式方法摈弃是保证教育效果的。孩子的不良问题就相当于孩子生病，生什么病就会有对应的治疗药物，俗话说“对症下药”。那么我们特殊教育学校也是同理，孩子存在什么问题就会有相应的教育辅导手段和方式方法，针对性的戒除孩子不良问题，待孩子问题好转之后我们还会灌输一些良好的思想及文化知识，让孩子在成长过程中不再迷茫，树立这良好的奋斗目标，全面提升和发展。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 湖南青少年特殊教育学校是以心理认识教育为主，行为矫正为辅，其他各个方面全方位同步提升的全能化教育学校，让您的孩子不仅仅是转化好了不良问题，同时在文化、生活、自理、法制、感恩励志、素质素养、特长培训、兴趣培养、劳动体验、实践拓展、户外学习、挫折打击教育、抗压训练、武术学习、军事训练等等各种教育一样不少，还您一个好孩子！</span></p>', '1', null, '1434724329', 'mingyu');
INSERT INTO `think_article` VALUES ('399', '25', '叛逆学校如何教育孩子 社会各界议论纷纷', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 由于叛逆孩子是属于青少年当中最难以管教的问题孩子之一，说理不听，打骂无果，让很多家长束手无策，但是叛逆孩子教育学校是如何教育的呢？他们的教育会有效果吗？那么带着疑问一起走访<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"青少年教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">湖南青少年励志教育学校</span></a>了解个究竟吧！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 2015年8月25日 湖南都市在线 通过走访咨询得知：湖南青少年励志教育学校已成立办学多年，经过了一条艰难的办学历程，同时也积累了丰富的教学经验，办学思路和模式可以说是全国唯一的，是现代人性化全能化教育的特殊教育学校。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 学校实施现代化的电子化教学管理手段，配设有<a href=\"http://www.841990053.com/about.php?cid=55\" target=\"_blank\" title=\"网络远程监控系统\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">高清网络远程监控系统</span></a>，并且24小时面向学生家长开放<a href=\"http://www.841990053.com/about.php?cid=55\" target=\"_blank\" title=\"网络远程监控\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">高清网络远程监控摄像头</span></a>，学生家长可以随时随地运用电脑或者手机APP都可以看到孩子在校的动态，是个比较透明化的青少年教育学校。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 以前听说青少年特训学校多少暴力教学的“黑学校”，但是这次走访得知，大部分确实都是让人感到惊讶的军训化模式的压制性特训学校，学生都是由退伍的文盲青年教官看管，不听话就打，全是以军训来强制教育这些所谓的问题孩子；饭吃不饱，水没得喝，厕所都不能上的特训学校让人闻心丧胆，而家长却一无所知，被忽悠得满怀希望。然而相对来说，湖南青少年励志教育学校却是走着现代化的科学教育模式，透明化、人性化、全能化的全新教学理念，是我们意想不到的常规青少年教育学校。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 走进湖南青少年励志教育学校没有一点压抑感，轻松自如，看着学生都是穿着校服可以自由走动玩耍和学习，没有看到所为的“教官”。经过访问得知，学校没有教官，而是设有德育老师，都是司法警官学院毕业的或者是高学历的大学生任教，而不是“文盲的暴力军人”。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 学校多媒体教室的电脑既然无密码，但是却没有学生偷上网，这是让我们感觉到最惊讶的事件之一。学校招收有不少的“网瘾青少年”，但是却可以让这些“网魔”自由玩耍不会上网，是各位家长和社会人士都会觉得不可思议的。经过采访学生得知：学校管教有松有严，是以心理辅导为主的，通过老师的引导，我们都觉得有理，不能自己跟自己过不去，必须提升我们的自控能力，为了自己的将来和尽快毕业回家，所以即使没老师看管我们也会自觉遵守规则，不会随意去偷上网。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 这充分地体现了学校得教学效果，通过基本得心理认知教育就可以让这些所谓的“问题青少年”得以好转，很快就变成乖孩子，让我们欣慰不已。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 走进宿舍，一排排整齐的洗漱工具和床铺，看到床上个个都是清一色的“豆腐被子”——叠成豆腐块形状，是我们正常生活无法做到的耐心活。听学生说，在学校所有关于到自己生活的事务均有我们自己亲手完成，起初会有着老师指导引导，等学会后我们就自己动手去完成，这让我们都学会了自理，可以说是我们从这里出去后是无所不能的，正常的生活技巧我们都可以学会，也可以独立生活。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 青少年的健康成长离不开良好的教育引导，像这样的<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"少年教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">青少年教育学校</span></a>为家长分担了很大的压力，并且解决了孩子的管教问题，同时也是解决了孩子的文化知识技能等问题，能够让他们健康成长，励志成人成才。（都市记者：ZHANG08)</span></p>', '1', null, '1440587122', 'mingyu');
INSERT INTO `think_article` VALUES ('400', '40', '青少年教育：孩子“记仇” 家长怎么办？', '', '', '', '', '', '<p><span style=\"color: rgb(0, 112, 192);\">张女士最近很犯愁，上四年级的儿子东东跟她成仇人了。起因是她翻了儿子的书包，偷看了儿子与同学来往所写的小纸条。儿子发现后，至今2个星期过去了，仍对张女士绷着脸不说话。张女士心里害怕了，儿子咋这么记仇呢？有什么办法可以重新修复亲密的母子关系？</span></p><p><span style=\"color: rgb(0, 112, 192);\"><strong>心理分析</strong></span></p><p><span style=\"color: rgb(0, 112, 192);\">　东东的记仇源于他受伤的心理感受转化为愤怒的情绪。对东东来说，他处在学龄期向青春期转化的阶段，身高、体重等都在成长，他内心需要一种独立性来支持这种“长大”的感受。东东和他的同龄人会把交往的重心从家庭逐渐转向学校更大的范围，也会从同伴交往中得到新的乐趣，比如分享更多的趣事、谈一谈他们对长辈这些权威的反抗、交流一下学习的苦恼、从朋友那里得到秘密和支持等，这都是他们成长的精神养料。东东他们会用独特的方式来传递这些信息，比如说悄悄话、写小纸条、碰头扎堆开小会……对东东来说，这些活动是他在同龄人获得认可，有一定位置和价值的体现。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　孩子记家长的仇，原因就在于家长没有尊重孩子成长的心理空间，任意穿越，导致了亲子心理边界出现了冲突。孩子没有办法拒绝家长的强硬入侵，因为他们不得不在养育上依赖父母。他们反抗的方式就是表达不满，用生气来宣布自己不被尊重，无法支配自己的思想。从这种意义上说，孩子不是想要恶化亲子关系，他们是想得到一种心理上的认同感，认同他也需要独立和自由。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　那么，该如何化解这种儿童的仇恨，家长需要从以下几个方面努力：</span></p><p><span style=\"color: rgb(0, 112, 192);\">　一、学习儿童的心理发展知识。家长如果真想了解自己的孩子，与其费心地去探查孩子的行踪，不如主动学习一些儿童心理发展的知识，了解一下此阶段儿童心理发展的需要和特点，再对照自家孩子的状况，用真诚的态度去观察孩子的变化。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　二、真诚地给孩子道歉。其实孩子从娘胎里一落地，在建立依恋关系的同时，也在逐渐与家长进行身心分离。有的家长总想与孩子紧紧地绑在一起，认为这样才是温暖和永恒的亲子关系，他们对这种关系的需要程度，远远高于儿童，他们甚至以爱和关心的名义束缚着孩子，置于个人的羽翼之下才觉得心安。对这样的家长，如果希望儿童心理发育正常，就需要放手。承认先前教育方式的不妥，给孩子真诚地道歉，赋予孩子一定的独立权限，忍受这种分离带来的心理上的不舍。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　三、用诚信去交换孩子的发展信息。家长如果真想了解孩子，不妨转换角色身份，一定程度地去掉家长这个角色，去发展做孩子的好朋友这一新的角色。丢掉需要在孩子面前保持权威的角色压力，轻松地与孩子交朋友。家长也可以分享一下自己在儿童期的感受，说一说自己过去的趣事、糗事、痛苦和烦恼，再多给孩子一些心理空间和等待的时间，孩子必定感受到这份善意，他们会逐渐转变对家长的态度。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　亲子关系的记仇，是因为缺乏信任和接纳的结果。家长如不先建立关系巩固的亲子关系，只想着了解孩子的一举一动，将对的知识灌输给孩子，这种强行的心理哺乳，肯定会招致儿童的拒绝，孩子一定会像吐奶一样悉数吐出。溶解亲子冲突，化解儿童的失落和愤怒情绪，就是在疗愈儿童被粗暴对待的心理需求。</span></p>', '1', null, '1443604658', 'mingyu');
INSERT INTO `think_article` VALUES ('401', '29', '浙江唐家长：很高兴我当初的选择是对的！', '', '', '', '', '', '<p><span style=\"font-size: 18px;\">&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\"> 现在看到孩子有这样的转变，我很高兴我当初的选择是对的！孩子从入校到现在一切都很好，并且转变得比较快，这<span style=\"font-size: 18px;\">是我意想不到的。</span></span></span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 刚开始没有把孩子送过来之前，在网上查询了很多学校信息，看得眼花缭乱，不知道哪个好，最终我选择湖南青少年励志教育是没错的，我很高兴！也是值得我庆祝的事。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 在网上看了很多学校的各个说得天花乱坠，但是查了相关资料后才发现大部分都是有着“黑故事”的学校，并且多次被新闻曝光，让我心惊胆战，怕毁了孩子和我们的希望。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 看了湖南<a href=\"http://www.841990053.com/about.php\" target=\"_blank\" title=\"励志教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">青少年励志教育</span></a>学校写的都是简单直白的真实情况，并且通过去学校考察后发现基本上无差异，让我一下子放松了很多。是正规的教育学校，有统一的校服、规律的学习课程和生活规划，标准的配套都比较齐全，可以满足孩子正常、健康成长的需求，让我感受到了“爱的教育”。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其实我考察了励志教育学校后还去了其他几所学校也看了一下，发现只有湖南青少年励志教育学校的模式是唯一的，其他学校都是统一的军装，听说是什么迷彩服，还有那些老师一部分也是穿得像特种兵一样，看起来凶狠扼杀，感觉像是走进了监狱一样，还不让我接触学生，这让我心理带着很大的怀疑（因为在网上看到出事的学校跟这样是一样的），最后我选择了<a href=\"http://www.841990053.com\" target=\"_blank\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">湖南青少年励志教育学校</span></a>是我值得庆祝的，学校的<a href=\"http://www.841990053.com/about.php\" target=\"_blank\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">教育服务</span></a>、教育模式、教育效果等都是我很满意的，关键也是我还从入校到现在没有受到一点打骂体罚，而且转变得比较快比较好。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其实这样写主要是表示对学校的感谢和支持，同时也是让更多的家长有所了解，让我们给孩子选择一个美好的未来和希望！</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">在此也非常感谢学校各位老师得精心辅导，谢谢您们！您辛苦了！！！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><br/></span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 唐xx妈妈</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2016年8月23日</span></p>', '1', null, '1474197210', 'mingyu');
INSERT INTO `think_article` VALUES ('402', '52', '青少年教育学校：小孩子要从小就要养成良好的行为规范', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><span style=\"font-size: 18px; font-family: simsun; line-height: 28px; background-color: rgb(255, 255, 255);\">&nbsp; 行动规范教育首要应放在言行举止行动造就、文化礼貌行动造就、优越性格个性造就、遵照社会公德性为造就等几个方面，使孩子形成慷慨、热情、礼貌、该让等优越的个性性格特性。例如在言行举止行动造就上，就要要求孩子岂论坐立行都要有规则，也便是人们凡是所说的坐有坐相，站有站相，这不仅可以使孩子形成优越的举止习气，对孩子身材的康健发育也是需要的:要求孩子措辞要吐字清晰、叙说工作要井井有条、速率不急不慢:和他人措辞耍讲文化礼貌，不说脏话、粗话等。</span><span style=\"font-size: 18px; background-color: rgb(255, 255, 255); font-family: simsun; line-height: 28px;\">要求孩子在与火伴来往时要学会推让，要尊敬怙恃、尊长和先生，不自私、率性，在公收场合要讲文化礼貌，遵照公共道德，爱惜公物，不乱扔放弃物，不随地吐痰等。对孩子进行行动规范教育是一项耐烦过细的工作，优越行动习气的形成是需要大人们有目标、有方案、有步奏的，颠末历久的造就和教育，经由过程孩子历久的生涯理论造就能力形成的。</span></span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 112, 192); font-family: simsun; font-size: 18px; line-height: 28px;\">&nbsp; 一样平常来说，4岁的孩子正处于行动相对不屈衡期，在这一时期孩子每每表示出不守规则，过火活跃，性格喜怒无常等。在行动上男孩子一样平常容易有粗野的表示，如爱踢人、打人、破损器械或动辄就乱发牌气，基跳如雷等，而女孩子则表示为执拗、率性、做率没有耐性，爱哭、爱发性格，给人一种弗成理喻的表示等，在言语的使用上，也表示为孩子们之间时常产生争持，包含说一些粗鄙的话，爱说诳言、爱吹法螺，谩骂、自诩等。这种征象是因为孩子神经体系的发育尚未成熟。脑神经细胞发育还很优柔，对孩子躯体或四肢的安排能力较弱，但孩子的自立见识猛烈，这种盼望把握更多技巧，更多介入成人社会的欲望和短缺安排能力的矛后，以及孩子的自制力较弱，就形成了孩子情绪上的不往定和生理上的不屈街，天然而然地使孩子有了一种需要渲泄的生理需求，因而就有了以上不守规则的表示，跟着孩子春秋的增加，神经体系发育的成熟，孩子的自制力会垂垂增强，到了4岁半摆布这种表示便开端逐渐收效，孩子将不像以前那样不守规则，过火活跃，喜怒无常。他们开端比拟可以或许忍耐挫护，热情于促进本身的技巧，对各类游戏和进修都有兴致。</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 112, 192); font-family: simsun; font-size: 18px; line-height: 28px;\">&nbsp; 到了5岁的时刻，一样平常孩子都表示得可霏、宁静、友善，乐意与火伴相处，怙恃和幼儿园的西席是他们最相信的人，他们喜欢靠近扮如、和处们一路运动、一路办事，屈服她们的指令，违抗她们的教诲。依据孩子神经体系父育的这种特色，在对孩子进行行动规范救育和造就时，对付4岁摆布孩子的不守规则，大人们必定要有耐烦，不要将孩子发展进程中的正常行动当作是恶劣，对他们进行过多、过度的扣资对批许，但也要加以引异和束缚，赞助他们安全的度过这一不屈街期。不要错过了对孩子进行行动规范范教育的最佳时期，捉住各类时初机对孩子进行造就和教育。</span></p>', '1', null, '1441202370', 'mingyu');
INSERT INTO `think_article` VALUES ('403', '38', '犯错是教育子女的最佳时机，孩子闯祸后该怎么办？', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 孩子犯错了，家长未免会气急败坏地冒出一连串质问。但孩子大脑发育还不完善，信息量太大，反而会让他们无所适从，再加上看到父母愤怒的脸色心里害怕，更说不出话来。所以，在孩子闯祸后，可以先问他8个问题，有助于帮孩子自己解决问题。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　我们不妨假设一个场景：波波跟表弟在爷爷的卧室玩耍，突然传来一声响。波波爸爸冲进来，看到爷爷的紫砂壶摔到地上了，两个孩子面面相觑地站在那里。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 1.“发生什么事情了”。遇到问题，家长常会下意识地做判断，如指责自家孩子：“一定是你把茶壶打破的”。另外，孩子看问题的视角跟大人不一样，在大人看来是错事，在孩子眼里却可能是对的。若不听孩子说明，很可能会冤枉他。因此，家长要先问孩子事情的前因后果，并换位思考。这样即使是孩子错了，他也会因为有机会辩解而甘心认错。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　2.“你的感觉如何”。爸爸问过上一个问题，波波低着头，小声说：“弟弟刚刚推了我一下，所以茶壶打破了。”孩子犯错后，家长要做的第一件事，是关注他的内心感受。孩子有时不是故意的，往往内心已经受到冲击，他或许早已心怀内疚，只是不好意思表达出来。而心理学研究表明，当一个人情绪激动时，很难接收外界信息，别人说什么他都听不进去。此时，家长不妨问问孩子的感受，并引导他说出来，孩子的情绪会平复许多，才有可能冷静思考，并吸取教训。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　3.“你想要怎样”。问过感觉后，波波突然大哭起来，怎么劝都不说话。此时，父母就要问问孩子究竟想怎样了。孩子的内心没有那么复杂，如果他认识到错误，最多会不好意思认错。而拒不认错或大哭大闹，往往代表他有某种目的没有实现。家长直截了当地问出来，效果更好。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　4.“你觉得我该怎么做”。对于上个问题，波波回答：“又不是我一个人的错，干嘛光说我一个人！”当孩子说出自己的目的或真实想法时，不论是什么，家长都不要立刻评判。不管孩子的要求是否合理，都冷静地问他：“你觉得我该怎么做？”。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">5.“这样做有用吗”。波波说：“弟弟推了我，我才打破茶壶的，你也说他几句啊！”此时，不管孩子提出什么意见，都要让他自己审视这样做是否有意义。其实，大多数孩子都知道事情的意义。比如波波，他并非要让弟弟挨批评，而是想告诉家长，并不是自己一个人的错。假如孩子的认知有误区，家长也可借机与他谈谈，增进沟通，但要避免说教，只陈述事实即可。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　6.“你决定这样做吗”。跟孩子确认是否真的要这样做。孩子多半会选择自己认为最合理的方式。比如波波可能会说：“我也有错，不该跟弟弟打闹，你别说他了，是我错了。”但即使孩子的选择不是大人所期望的，也要尊重孩子的决定。言而有信的家长才能给孩子树立好榜样。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　7.“结果有没有如你所愿”。假如波波对弟弟不依不饶，爸爸可以对弟弟说：“波波的确不对，但你推了他，才打破了爷爷的茶壶，所以下次要注意，别在房间里打闹。”然后扭头问波波：“我说弟弟了，你觉得怎样？有没有如你所愿？”孩子达到目的后，往往就能冷静思考。这也是一个教育的好时机。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　8.“下次碰见相似的情形，你会怎么做”。孩子此时一般能做到自省，比如波波会保证，以后会用于承认错误，而不是找人一起分担，努力做个男子汉，教育的目的也就实现了。</span></p>', '1', null, '1443794537', 'mingyu');
INSERT INTO `think_article` VALUES ('404', '38', '亲子教育：怎样才能进行科学的隔辈教育？', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 因为中国的经济固然在倏地的增加，但实在人均收入到今朝来说还不够抱负；何况如今履行方案生养很长光阴了，让家庭的布局曾经发生了很大的变迁，本来是几个孩子养一对白叟，如今是两小我养四个白叟，家庭的生涯压力就无形的增加了。是以，在中国生了孩子的妈妈们，能上班的还得去上班。教育孩子的工作，天然就落在了上一辈人身上了。然则在隔辈教育上也发生了分歧的概念，有人以为隔辈教育不错，也有人则持相反的概念。那么，到底怎么能力进行科学的隔辈教育呢？起首从教育孩子的履历上看，祖辈更具履历和人生经历，更有耐烦的去造就孩子。可以排除年青怙恃的后顾之忧，更能将精神专一于奇迹之中。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 当然，儿孙绕膝这种天伦之乐的画面，也会给白叟家的心态带来更大的裨益。当然在环抱这隔辈教育也发生了一些新的社会问题，例如，宠爱孩子；观念破旧，不克不迭与当今的社会接轨；形成亲子情感隔膜，致使孩子与爷爷奶奶的情感更康复。同时，因为白叟都很宠爱孩子，可以说从早到晚都在环抱着孩子转，乃至是上茅厕、穿衣、用饭等等，白叟都邑竭尽所能为孩子提供各类各样的效劳。白叟各方面临孩子的宠爱，也催生了孩子各类有理无理的要求，康复比什么薯条、饮料、酸奶之类的器械，想要的时刻白叟就必需去买，不然非要哭闹弗成。以是，许多白叟为了“市欢”本身的孙子，不得不惯着，长光阴如许下去，就会是孩子养成野蛮不讲理的个性。对其未来的成长极其晦气。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 常言道，人无完人，金无足赤。是在说这个天下上没有一小我是完人，他多若干少都邑有些毛病，孩子更是如斯；因为孩子如今可以说照样一张白纸，还没有传染上什么坏习气，假如这个时刻孩子呈现了一些坏的苗头，而家人不予实时禁止，反而助其成长。光阴久了孩子就会养成如许的坏习气，李天一便是个很康复的例子，据李双江的战友杨洪基老师说，曾有一次三四岁的李天一和他爸爸打骂，当众就对李双江又踢又打。其时因为李天一还小，李双江也没在意，只是感到孩子还小不懂事，等长大了就天然不会如许了。谁知，长大了不只开车大人，而且还犯下强奸罪被判刑十年。由此可见，孩子可以爱，但不克不迭宠；更不克不迭眼看着孩子犯差错而不予以改正，不然日后必成大患。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 总结：依据中国今朝的现实国情来看，要想避开隔辈教育那是不现实的；然则在进行隔辈教育的时刻，必定要记住不克不迭太宠孩子了，也不克不迭事事都满意孩子的要求，要学会对孩子说不，而且白叟还要增强自身的进修，增强自身的文明，能力真正到达与时俱进，孩子能力获得更康复的教育。</span></p>', '1', null, '1435845863', 'mingyu');
INSERT INTO `think_article` VALUES ('405', '9', '叛逆教育：叛逆期男孩教育方法', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 作为家长，最为头疼的就是<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"叛逆教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">管教孩子</span></a>了，特别是男孩子。众所周知，一般男孩子相对来说，比女孩子难得管教，何况是叛逆的青少年期男孩子。叛逆期的男孩子需要特殊的教育方式方法方能得到完美解决，可以避免激发孩子的叛逆情绪及叛逆行为，使得亲子和谐，造福家庭和社会。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.对于叛逆孩子要注意言行举止</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 一个人的言行举止是个人本身的好坏表现，作为家长需要以身作则，以良好的一面引导孩子走向健康成长的阶梯。对于<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"叛逆特训学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">叛逆的男孩子</span></a>，要注意孩子的心思动向，明白孩子在您面前是想做什么？并且能够及时上演孩子需要的角色，这样就能够与孩子之间建立成一座良好的沟通桥梁。很多孩子不愿意听从父母的，主要是父母所演的角色无法得到孩子的认同，所以让模仿性极强的男孩子来说，就是他的天敌，甚至会做出意想不到的叛逆行为，一发不可收拾。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.与对叛逆孩子要寻找出他的个人观点</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 作为孩子身外作用的影响，必须要了解到孩子的个人性格特点和思想观念，方能有较好的方式方法去影响他，从而改变到他。由于<a href=\"http://www.841990053.com/index.php\" target=\"_blank\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">叛逆</span></a>还的观点跟正常人的思想观念是有所偏差的，特别是个性比较好强，脾气相对来说也是比较犟的。那么就需要找出孩子的弱点和认可观念，方能与孩子取得一个沟通的平台，有着共同的语言说话，从而也才能慢慢的去引导他的不良思想及行为。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3.叛逆孩子不喜欢低头</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 对于叛逆的孩子来说，个性是比较好强的，不会在别人面前低头。那么这样的孩子，您作为父母就应该改变一下身份，不能总是以为自己的孩子的主人，管教孩子是理所当然的。必须吧身份降级至于孩子身份平等阶段，以朋友的形式交往，以亲人的身份交谈。要让孩子感觉到有一个公平、公正、平等的安全感，才能有谈判的机会，这也是您取得引导和教育的机会。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 总之，对于出现了叛逆情绪和行为的孩子，杜绝运用传统的打骂体罚手段来教育，良好的沟通是解决问题的根本。一般叛逆孩子多数是不会听父母及亲人的言语，更是不会服从。因为父母及亲人是孩子最贴心的关照，是有着切割不开的血缘关系。那么孩子正式会利用这种关系来做自己为所欲为的支持力量，知道怎么做自己的亲人不会对自己怎么样，使得父母亲拿孩子没办法。在这样的情况之下，建议父母果断采取措施，也就是征求第三方的专业人员机构（比如我们青少年励志教育学校，青少年教育服务中心等）来帮助解决。</span></p>', '1', null, '1438521330', 'mingyu');
INSERT INTO `think_article` VALUES ('406', '25', '早恋教育：16岁少女产女 同龄男友“蒸发”：称孩子随便处理', '', '', '', '', '', '<p>&nbsp;东南网10月8日讯(海峡都市报记者陈钟兰)16岁的广东少女小燕(化名)，父母双亡，在懵懂无知的年纪，与同龄男友私奔，在福州产下一名女婴，而孩子出生后，男友却“失踪”了，这让小燕陷入了困境。</p><p>　　昨日海都报A02版报道了此事，引起强烈反响，各大网站纷纷转载，不少市民给小燕送去了奶粉、尿布等婴儿用品，二十多位市民表示想收养这孩子。</p><p>　　6日下午开始，海都记者根据小燕提供的男友电话以及微信，一直给对方打电话、发短信、发微信，但直至昨晚10点，对方都未回应。小燕给男友打电话、发微信，对方也不理睬。直到昨日，小燕才相信自己被抛弃了，“我很难受，现在很迷茫，不知道该怎么办。”</p><p>　对于小燕目前的状况，昨日20多位市民拨打本报热线，表示想收养她的孩子，其中还有一些江苏、天津、北京的读者。北京的李女士称，自己是做生意的，经济条件还算宽裕，结婚13年了，始终怀不上孩子，“希望能收养这个女婴。”<br/></p><p>　　对此，福建元一律师事务所的佘雷律师说，“无论是小燕想把新生儿送养，还是热心人士想要收养，这都不是一件简单的事情。”新生儿的父母健在，又是未成年人，若经济条件确实困难，无力抚养新生儿，决定送养，这除了两个未成年父母同意外，还需要双方的监护人同意，才能送给有收养条件的家庭收养，并要到民政局办理等相关法律手续。</p><p>　　佘雷说，小燕和她的男友，虽是未成年人，但作为孩子的父母，对孩子负有不可推卸的抚养责任，考虑到他们经济上还不能独立，所以双方的监护人也有责任帮助抚养新生儿。</p><p>　　“男方逃避责任，虽够不上犯罪，但可追究其民事责任。”佘雷表示，小燕作为孩子的母亲，可以向法院提起民事诉讼，要求男方及其监护人承担相应的抚养义务。</p><p>　　新闻回顾：</p><p>　　16岁少女福州产女 同龄男友：孩子随便处理</p><p>　　东南网10月7日讯(海峡都市报记者 陈钟兰/文 关铭荣/图) 在福州晋安区远洋村，一间昏暗的民房里，16岁的少女小燕(化名)，正小口喝着鲫鱼汤。身旁的木板床上，一件连体衣里裹着一个刚出生几天的女婴。女婴睡得正香，小脚突然踢腾了一下，小燕赶忙放下汤碗，轻拍着女儿安抚她。</p><p>　　小燕的姨妈覃国红看着这一幕，扭头抹眼泪。自10月3日小燕的女儿出生后，她的眼泪就没停过。</p><p>　　交男友姨妈不同意 跟着男友私奔数月</p><p>　　今年32岁的覃国红和小燕的老家在广东江门市开平。因母亲早逝，父亲几年前出海打鱼时遇故身亡，小燕大部分时间和覃国红一起生活。“怪我平时忙着干活，没照顾好她，才让她走到今天”，覃国红带着哭腔说，小燕初中没读完就辍学了，去年中秋，小燕带着一个与她同龄的男孩回家，说是她男友，希望自己同意他们在一起。覃国红震惊得不知所措，忍着怒气留男孩住了几天，好言劝了一番，但小燕不为所动。第三天晚上，小燕和男朋友跑了，直到过年才回家。</p><p>　　小燕回家后，覃国红气得打了她一顿，并禁止她和男友联系，结果发现两人仍通过QQ、微信等保持联系。今年端午节，覃国红带着小燕到福州投奔男友，希望借此分开二人，到福州后却发现小燕怀孕了，且胎儿已成形。男方的信息，小燕怎么都不肯说，直到宝宝出生男方也没露面。</p><p>　　覃国红说，小燕是被抛弃，被骗了，“给她男朋友打了几十个电话，就接通一次。他说自己没钱，也不肯让我跟他父母沟通。问他孩子怎么办，他让我们随便处理！”</p><p>　　少女说出月子后就打工养宝宝</p><p>　　采访的两个小时里，小燕大部分时间都低着头不说话。对于男友的事情，她不肯多说，她觉得男朋友没抛弃她，怕姨妈骂才不敢出现，“我想让他父母知道，然后帮我们带宝宝，我们就去打工赚钱养宝宝。”</p><p>　　但当记者问，如果真如她姨妈所说该怎么办时，小燕沉默了半晌，说等出了月子就去打工，赚钱养宝宝，“我姨妈说要把宝宝送人，可我不想。”</p><p>　　“我也舍不得，这么说是为了逼她说出男友家的地址，去找他家大人商量。”覃国红说，她会尽力抚养孩子，但就怕有心无力。因为小燕还小，不会照顾宝宝，她得在家帮忙，全靠在小吃店打工的男友养家，“如果最后真撑不下去，也只能送人了。”</p><p>　　还是少女，便为人母，其间有她的懵懂无知，也有大人的教养疏忽，但新生命总是无辜的。（来自360新闻）</p>', '1', null, '1444278333', 'mingyu');
INSERT INTO `think_article` VALUES ('407', '24', '透明人性化教学：学校第二套高清网络远程视频系统正式启动运行', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 不良青少年励志成长教育学校一直秉承着“良心办学，爱心教育”理念领横青少年教育行业，提倡“杜绝打骂，不体罚的”以心理认知教育为主的人性化教育方针，是全国首家实施分类分校教育辅导，24小时面对家长开放网络远程视频的青少年励志成长教育学校，让家长随时随地可以视察孩子及学校的一切动态。让家长放心，让学生安心，我们更省心！！！</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20151010/1444431992136316.jpeg\" style=\"width: 303px; height: 159px;\" title=\"高清网络远程监控---厨房\" width=\"303\" height=\"159\" border=\"0\" vspace=\"0\" alt=\"高清网络远程监控---厨房\"/><img src=\"/ueditor/php/upload/image/20151010/1444431992115527.jpeg\" title=\"远程监控---学生在教室洽谈\" width=\"304\" height=\"158\" style=\"width: 304px; height: 158px;\" border=\"0\" vspace=\"0\" alt=\"远程监控---学生在教室洽谈\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20151010/1444431992977940.jpeg\" style=\"width: 304px; height: 180px;\" title=\"远程监控---正在上文化课程\" width=\"304\" height=\"180\" border=\"0\" vspace=\"0\" alt=\"远程监控---正在上文化课程\"/><img src=\"/ueditor/php/upload/image/20151010/1444431992704559.jpeg\" title=\"学生在吃饭---食堂\" width=\"305\" height=\"181\" style=\"width: 305px; height: 181px;\" border=\"0\" vspace=\"0\" alt=\"学生在吃饭---食堂\"/></p><p>&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\"> 我们青少年教育学校第二套高清网络远程视频系统比前一套像素更高，画面更清新，功能更加强大。是无线网络的高清摄像机，带有拍照、摄像、录音和对讲等功能，家长可以随时随时视察孩子和学校的一切动态，同时还可以听到孩子和老师等所说的话语。如果家长想记录孩子的成长历程，可以拍照截图或者录像摄影等留在自己手机或电脑里面，随时翻看孩子在学校的点点滴滴，让您看得见、听得着、留得住的孩子成长全过程。</span></p>', '1', null, '1444398215', 'mingyu');
INSERT INTO `think_article` VALUES ('408', '20', '上海叛逆早恋少女的成长故事', '', 'http://www.841990053.com/case.php?cid=21', '../Uploads/1463032419.JPG', '上海叛逆孩子教育学校，湖南正规戒网瘾学校，上海厌学辍学教育，上海早恋教育，少年行走学校，青少年教育学校，叛逆教育，素质教育，叛逆孩子特训学校，湖南戒网瘾学校，湖南正规特训学校，戒网瘾学校，心理教育', '上海上海存在叛逆厌学早恋等问题，2011年3月被父母送来入校，经过我们学校的教育辅导，转变效果良好，现在家上学。', '<p style=\"text-align: center;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><a href=\"http://www.841990053.com\" target=\"_blank\" title=\"叛逆厌学早恋教育学校\"><img src=\"/ueditor/php/upload/image/20160512/1463032378786190.jpg\" title=\"叛逆孩子教育中心\" alt=\"叛逆孩子教育中心\" width=\"244\" height=\"153\" style=\"width: 244px; height: 153px;\" border=\"0\" vspace=\"0\"/></a></span></p><p><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">基本信息</span></strong><span style=\"color: rgb(255, 255, 255);\">上海浙江叛逆网瘾少年孩子教育学校</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 周雨，女，15岁，来自上海市，存在叛逆厌学早恋等问题，2011年3月被父母送来入校，经过我们学校的教育辅导，转变效果良好，现在家上学。</span><br/></p><p><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">问题概况</span></strong><span style=\"color: rgb(255, 255, 255);\">叛逆厌学早恋戒网瘾学校在上海问题少年学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其实周雨从小就与父母生活在一起的，但是父母忙于工作和家庭事务，根本无法给予充足的关照，只是满足了钱等物质上的需求。慢慢的，周雨在发育成长过程中觉得无聊和无助，只好在学校里交一些朋友来陪伴，但是朋友越来越多，什么类型都有，吃喝玩乐天天有，让周雨觉得有了快乐和自由，除了问父母拿钱之外不会多问一句话，放学回家的时间越来越晚，在家吃饭的次数也越来越少。但是其父母以为是给她钱就不会有事，然而也对周雨没有过多的顾虑，周雨也玩得不亦乐乎，开始把学习落在一边不理不睬，成绩也随之玩起了滑板游戏，直往低处滑行。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 长期跟随朋友一起玩耍的她被卷入了恋爱游戏，每天都是三五成群在一起，出现了迟到旷课情况，把上学当成了无所谓的游戏。有一次带着朋友一起回家拿什么东西时恰好被其父母看到，这时他们才对周雨有所疑心，便叫周雨前来询问，但是没想到却得到周雨一句惊人的语句：你管我？这时候其父母也惊了，不知道要说什么。就这样，叛逆行为就开始了。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">教育简述</span></strong><span style=\"color: rgb(255, 255, 255);\">上海青少年叛逆早恋教育戒网瘾中心</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.感恩励志教育：针对周雨这样的问题，首先对其进行心理疏导，对于父母的繁忙要给予包容和理解，要明白这个美好的家庭是繁忙通过繁忙的劳务所得，而不是祖传的；就包括你自己的文化知识和未来都是需要你自己学习和争取才能拥有，因而你必须好好读书，用心学习，争取建造你自己一个美好的未来。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.早恋教育：周雨你年龄还很小，发育成长都是未完全，未达恋爱时期而恋爱对你自己的身心、学习、生活以及你的家庭都是有着很大的影响，更是耽误了你美好的前程；对于早恋的危害及影响你要参与相关课程的学习，加强对恋爱关系问题的认识和了解，学会自我克制和自我保护，不要再让父母为你担心。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3.生活教育：生活自理是一个人是否成长的标志，那么通过学校的生活教育课程引导她学会生活，学会自理，学会自立、懂得自强；由于父母常常忙于工作，当在家没事的时候要学会帮助父母分担一些力所能及的家务，比如洗衣、做饭、搞卫生等等，这样不仅仅能够分担父母劳务，更是锻炼自己和充实自己。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4.兴趣培养和文化辅导：周雨厌学是因为玩而落下了学习心思，导致了成绩下降而失去信心，对此必须把她的学习兴趣提升起来，并加强文化辅导，跟进她的学习步伐。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">5.法制、特长、素质等全能课程同步开展，让周雨调整自己的不良行为的同时能够在其他各个方面得到辅助提升和发展，在学习和生活的道路不再是那么的单一和迷茫，给予多一点生活学习的正能量，让她健康快乐地成长。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">转化效果</span></strong><span style=\"color: rgb(255, 255, 255);\">上海叛逆厌学孩子教育学校，特训学校</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; </span></strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">由于她的接受能力较强，唔觉性比较高，短短几个月的时间，她策底明白和认识到自己的错误，并且基本上改正良好。周雨在学校比较积极，善于思考和学习，经过心理老师的疏导和引导，她立马就会去思索和努力，完善自己的言行德礼。明白父母的不易，学会了包容和理解，通过亲自教育的沟通，改善了他们的亲子感情；生活的教育提升了她的自理能力和独立性，在自己力所能及的范围内一般是不会让别人帮忙的。现在的她脾气变得比较温和，没有以前那么的暴躁和冲动，学会了忍耐和思考，改变了她交际情况。回家后又继续了她的文学之旅，在各个项目的表现都比较理想。</span></p>', '1', null, '1439202749', 'mingyu');
INSERT INTO `think_article` VALUES ('409', '21', '邵阳蒋同学的厌学早恋回归路', '', 'http://www.841990053.com/case.php?cid=21', '../Uploads/1444469665.jpg', '邵阳叛逆教育学校，湖南厌学辍学教育，邵阳特训学校，邵阳戒网瘾学校，邵阳青少年励志教育学校', '', '<p><strong><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">&nbsp; 蒋萌，女，15岁，来自湖南邵阳市邵东，2012年3月16日入校。因存在厌学辍学和叛逆等问题被父母发现而来进行的转化教育，其本性良好而领悟较快，转变效果明显，现回家继续读书表现良好，无不良问题反映。&nbsp;</span></p><p><strong><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 主要原因是小萌的家庭条件比较优越而父母工作繁忙所导致的。其父母只是一味地满足了小萌的物质需求而忽略小萌的精神需求，对小萌缺乏真正的关爱和陪伴，更是缺乏良好的教育引导，使得小萌把注意力转移到社会上，就去寻求异性朋友带给她的心灵抚慰，满足精神需求，才会陷入早恋的泥潭，同时也走上了厌学辍学的道路。还有就是其家庭教育有点欠缺，父母的管教方式方法不当，管教的思想观念落跟不上孩子的成长需求，对孩子的学习、生活、生理和心理发育等健全人格等方面进行教育和引导，而且没有重视对小萌正确的人生观、价值观、世界观的培养，使得孩子的</span><a href=\"http://www.841990053.com\" target=\"_self\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">思想观念</span></a><span style=\"color: rgb(0, 112, 192);\">也随之偏差，很容易被不良的思想和形形色色的社会人士给诱惑而走进误区，从而产生了脱离正常成长轨道，出现了厌学辍学早恋叛逆等等一系列不良问题，如果不及时发现和采取措施，将会是越陷越深，无法挽回。但是对于小萌情况还是得到了很好的解决，也是不可忽视的父母功劳，挽救了她的家庭和未来。</span></p><p><strong><span style=\"color: rgb(0, 112, 192);\">辅导方案</span></strong></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">1.通过心理咨询辅导了解她的真实情况，并让她表达出自己内心的真实想法和做法，让后以正确的思想其引导，让她认识到她之前的一些所作所为的错误是不应该的。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">2.实施换位思考教育法，让她深刻反思自己和换位思考父母，认识到自己的错误的同时也要了解父母的用心良苦。让她意识到自己之前的行为是多么的令他们失望和痛心，并且学会感恩励志，给变自己的过错。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">3.以德育教育让她树立起正确的道德观、人生观和价值观，理解当今社会中文化知识的重要性和自己不学习对自身所带来的影响。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">4.通过<a href=\"http://www.841990053.com\" target=\"_self\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">赏识教育</span></a>鼓励和培养她的学习兴趣，激发她的内在潜能，挖掘出其兴趣爱好和特长，并且加以培养和指导，让他在学习中寻找快乐感和成就感，避免厌倦。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">5.为了让她能够继续接起文化学习的重任，我们对她开展文化补习和文化的同步辅导，提升她的学习兴趣的同时也要让她的学习成绩提高起来，使其不再是个文盲少女。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">6.对其家长做出<a href=\"http://www.841990053.com\" target=\"_self\" title=\"管教方式方法\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">家教</span></a>调整建议：多关注孩子成长需求，陪伴孩子成长；并且平时要多跟孩子进行交流沟通和鼓励，增强亲子感情；多了解孩子的内心世界和真实的想法，给予正确的引导和帮助；当发现孩子出现错误时不要一味着责怪和排斥，多鼓励和引导，增强孩子的自信心的同时也避免了激发孩子的叛逆情绪。<br/><strong>转化效果</strong></span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">&nbsp; 一百多天的辅导和引导和学校老师的不懈努力，小萌也不负众望，成功地从问题少女的泥潭中突围而出，清洗了“问题孩子”的污垢。来我们<a href=\"http://www.841990053.com\" target=\"_self\" title=\"厌学辍学教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">青少年教育学校</span></a>后也是完成了她的华丽蜕变，性格明显变好，温和有礼，与同学、老师的关系都特别的好，课余时间经常看到她跟同学在一起有说有笑，碰到老师也会很有礼貌的问好；生活自理能力、自控能力都明显增强，与以前相比，更能理性地去看待问题，不再偏激、固执己见，完全不顾父母的感受，对自己的人生有了新的想法，小萌很喜欢画画，她决定回去好好读书，努力考上个理想的大学，学习艺术相关的专业，去追求自己的梦想。</span></p>', '1', null, '1439206465', 'mingyu');
INSERT INTO `think_article` VALUES ('410', '22', '岳阳汨罗少女因早恋引来多种问题', '', 'http://www.841990053.com/case.php?cid=22', '../Uploads/1484645474.jpg', '岳阳特训学校，岳阳戒网瘾学校，岳阳叛逆少年管教学校', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=22\" target=\"_self\" title=\"早恋孩子心理辅导中心\"><img src=\"/ueditor/php/upload/image/20170117/1484645314110234.jpg\" title=\"孩子早恋了怎么办\" alt=\"孩子早恋了怎么办\" width=\"271\" height=\"167\" style=\"width: 271px; height: 167px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"color: rgb(255, 255, 255);\">岳阳特训戒网瘾学校，叛逆厌学早恋教育学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 胡静姚，女，14岁，来自岳阳汨罗市，2014年11月12日入校，因存在早恋厌学离家出走等问题严重影响了其自己及家人的正常生活和学习而被父母送来我们早恋教育学校进行矫正与辅导，问题得到了成功的转变，现在已回去读书，在家表现良好。<br/></span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"></span></p><p><span style=\"color:#0070c0;font-size:18px\"><strong>问题概况与教育方案</strong></span><span style=\"color: rgb(255, 255, 255);\">叛逆孩子管教机构，特训教育学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1．静姚的性格本质并不坏，她心存善良，开朗大方，能够理解和关心他人，但是对父母亲却比较叛逆和不听话。像这样的问题我们首先要对她进行相应的心理疏导，让她能够理性的认识和理解父母对她的关爱，用她善良的本质散发在有利于自己成长上来，促进家庭和睦幸福。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2．关于早恋的问题上我们首先进行适当的心理干预和相关的知识的授课指导，让她明白早恋不仅对自己的身心造成伤害，而且会严重地影响到自己的生活和学习以及家庭问和负担。通过正确的恋爱知识的认识和理解，缓解其对恋爱的向往心理，提升她的自我控制能力。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3．静姚的问题离不开父母及家庭的影响，因而我们必须促进静姚与其父母亲加强沟通交流，增强亲子之间的感情，让她有问题学会跟父母沟通解决，避免积压在自己的内心深处，减少她的心理压抑。同时。也希望其父母能够正确地认识到自己在教育方式和对待孩子态度上所造成的影响，并在管教孩子的方式方法上适当做出调整，避免孩子引起其他的不良问题。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4.她的厌学情绪是因为受到其他同学的恋爱影响而导致的，再加上静姚自身自控能力较差，抵不住身边其他无色的诱惑，便以好奇的心理去尝试不应有的举动。对于这样的问题，我们还是进行其心理的疏导，缓解她的幼稚心态，让她明辨是非，自我克制。再进行相关知识的教育和认识和法制教育，让她了解未成年恋爱的影响，养成自我提示和控制。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">5.由于厌学影响了她文化知识的学习，为了让她能够在文化的社会中不再落伍，我们对其开始文化补习和辅导，挽救她的文化生命。让她在学习过去知识的同时也跟进现在应该学习的课程，等回家后继续跟进文化大革命的队伍中。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">6.她的幼稚思想及行为让在很多方面都有所欠缺，因此我们的生活教育、感恩励志教育、法制教育、国学教育等全能课程同步进行，增强她从未有过的阅历和知识。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"> <strong>转化效果</strong></span><span style=\"color: rgb(255, 255, 255);\">岳阳汨罗问题青少年特训学校戒网瘾机构</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 静姚来校前一直酷爱声乐和能歌善舞的少女，通过学校的特长培训和赏识教育，鼓励她继续学习自己的特长爱好，自己亲手创造自己的美好人生。这也是给她培养一个很好的成长目标，并且她一直是我校文艺活动的积极分子，成功策划和开展了多次文艺活动，得到了家人和学校的大力支持，给了她足够的鼓励和信心。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">毕业回家后便以全新的姿态投入到了学习的重任中去，在学习上她有着很大的兴趣，<span style=\"color: rgb(0, 112, 192); font-size: 18px;\">成绩不断提升，拿到班级里多种优秀奖项。</span></span></p>', '1', null, '1442148346', 'mingyu');
INSERT INTO `think_article` VALUES ('411', '8', '为什么孩子沉迷游戏带去医院检查是精神分裂症？', '', '', '', '戒网瘾学校，上网成瘾，戒除网瘾，青少年戒网瘾学校，戒网瘾中心', '我们都有一种自我医疗的意识存在，一感觉到自己或者亲人有点不适就会立马去医院检查或者是自己配药吃。这一来，医院为了顺应个人心理需求，满足检查人的治病心理，便会想方设法给你查出病来，而是没有认可科学依据的。这是究竟是什么原因呢？', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 我们都有一种自我医疗的意识存在，一感觉到自己或者亲人有点不适就会立马去医院检查或者是自己配药吃。这一来，医院为了顺应个人心理需求，满足检查人的治病心理，便会想方设法给你查出病来，而是没有认可科学依据的。这是究竟是什么原因呢？</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">一、为了经济利益</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.当代的社会经济条件游荡不断，没有一个相对稳定的经济利益与收入，那么对于医疗或者其他行业来讲，生存和维持都是存在着很大的经济压力的，而已为了缓解相关问题，他们只好做出背叛人性的选择，先捞一把再说。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.不论是企业利益还是个人利益，都是与其做法有着相对应的挂钩的。因而现在众所周知，企业都会有业绩与利益挂钩，个人更是以经济利益为主，这导致了有需要帮助的问题（网瘾等）青少年找到了医疗机构或医院的时候，他们（医生）往往会给您一个无中生有的神奇魔法诊断结果——那是病？</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">二、为了销售任务</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.医疗的主要方式就是服药，那药物的销售是他们医疗人员的首要任务。如果药物大量存库，将无利可图，在一个就是与期限接近，无法销售。那么在这种情况下，他们为了销售完这些要药物就必须找到有需要这些药品的对象，然而您刚好带着孩子去检查，证是他们的捕猎对象了。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.为了完成医疗病例指标，他们将会到处寻找病例对象来弥补空缺。因为他们个人的医疗病例指标是跟着他们的利益挂钩，如果本月的基本指标不达标，那么将会影响个人利益，那么您正好是他们等来的一个指标之一。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">三、背叛了医德仁义</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.当遇到一个没有良心的医务人员，他不是明目张胆挑衅你，而是跟你寻刺暗斗。因而往往当你找到他们帮忙的时候也就是成为了他们行事的机会，不论你有病没病，他们的结论还是病。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.没有医德的人，他们为了炫耀自己的专业和职位，可能会在你的面前逞能。这也就是找不到真实和科学的病源根据时候就会胡编乱辑，找一个接近你证怀疑的结果，从而也证明了他的能力和你本次的目的。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">总结</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 病，并不是可怕的，可怕的是人心病了；药，不是治病的万能，它仅仅是给人解渴的水；治疗与健康，是在于人的心理。当没有科学依据病源的病，都是一种心病，精神是它的表现。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 常说有病就吃药，那仅仅是针对性的病体治疗，而不是普及与所有病况。如：感冒，是有着存在病体需要药物去化解；咳嗽，是有着感染的病菌需要药物去除杀；头痛，是头部位受到内部或外部的影响而需要药物的麻醉等。然而孩子上网成瘾是因好奇和迷茫的跟众行为所导致的生理和心理上受到影响的网络综合征，是幻觉思想和心理上的依赖症状。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">因为网瘾不是对人体有着直接危害的病体，所以也就没有针对性的病菌需要到药物去解决了。如果是盲目的服药，体内没有药物的消化对象，也就是直接散发在人的肉体内，阻挡着人体功能的正常工作，因而是起着副作用的影响。在此也提醒各位家长，当遇到有关精神和心理上问题的时候，请谨慎行事，避免适得其反，错过最佳“疗期”。</span></p>', '1', null, '1444313655', 'mingyu');
INSERT INTO `think_article` VALUES ('412', '5', '赵老师》生活辅导师', '', '', '../Uploads/1469692170.png', '心理咨询，心理辅导，心理教育，青少年教育', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20160728/1469691495102286.jpg\" title=\"赵老师\" alt=\"赵老师\" width=\"255\" height=\"310\" style=\"width: 255px; height: 310px;\" border=\"0\" vspace=\"0\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">生活教育辅导师</span><br/></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 国家注册二级烹饪师,来自湖南岳阳，毕业于湖南长沙岳麓厨师学校烹饪专业。从事饮食行业三十多年，有着丰富的生活经验，从健康饮食到美味佳肴无所不知，有着独特的“饮食文化”。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 为了满足每一位孩子的健康成长和营养需求，学校聘请赵老师来校担任学校厨师及学生的生活教育辅导一职。独特的厨艺让师生赞叹不已，耐心的教导让学生领悟到了生活的奥妙。</span></p>', '1', null, '1431775112', 'mingyu');
INSERT INTO `think_article` VALUES ('413', '5', '催老师》德育法制教育', '', '', '../Uploads/1444963625.jpg', '', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20151016/1444963550576625.jpg\" title=\"德育法制教育\" alt=\"德育法制教育\" width=\"263\" height=\"322\" style=\"width: 263px; height: 322px;\" border=\"0\" vspace=\"0\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">崔成，男，来自湖南益阳市，中共党员，毕业于湖南司法警官学院刑事侦查专业，在校曾荣获“优秀刑事侦查员”称号。从事法制教官已有三年多教育经验，对法制法律方面有比较深入了解，熟悉青少年的触法犯法的行为心理，掌握法制教育教规，让学生轻松认知法律法规，深受学员的信懒和社会的认可。</span></p>', '1', null, '1429159625', 'mingyu');
INSERT INTO `think_article` VALUES ('414', '5', '李老师》生活老师', '', '', '../Uploads/1444963728.jpg', '生活教育，生活体验，生活指导，生活励志，青少年励志教育', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20151016/1444963707116168.jpg\" title=\"生活老师\" alt=\"生活老师\" width=\"204\" height=\"227\" style=\"width: 204px; height: 227px;\" border=\"0\" vspace=\"0\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">李培清</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 生活老师。来自湖南省岳阳汨罗市，日常工作中做到嘴勤、手勤、脚勤，不怕苦和累，引导学生管好自己的物品，培养其独立生活的能力， &nbsp;使学生养成良好的生少习惯掌握学生在日常生活中的表现，有针对性的进行思想品德教育，做到从生活上关心他们，从思想上教育他们，从学习上帮助他们，使他们感到学校这个大家庭的温暖。</span></p>', '1', null, '1427366928', 'mingyu');
INSERT INTO `think_article` VALUES ('415', '5', '刘老师》德育教育特长辅导', '', '', '../Uploads/1444965793.jpg', '德育教育，励志教育，叛逆教育，早恋教育，特训学校，素质教育', '', '<p><br/></p><p style=\"text-align: center;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20151016/1444965554127731.jpg\" title=\"德育老师，德育教育，励志教育\" alt=\"德育老师，德育教育，励志教育\" width=\"269\" height=\"309\" style=\"width: 269px; height: 309px;\" border=\"0\" vspace=\"0\"/></span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">刘云</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 德育老师兼美术音乐特长辅导师。来自湖南长沙望城，曾于中国人</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">民解放军68332部队英雄一连</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">服役三年，任文书兼班长等</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">职务，并获得“优秀士兵&quot;和</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">“优秀班长”等荣誉奖项；</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">因品行出众调往长沙高炮团</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">预备役服役五年,期间带训成</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">绩突出，荣升“二级士官”</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">军衔。在长沙多所高等院校</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">担任军事教官一职，有着丰</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">富教学经验；刘老师为人师</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">表，开朗幽默，注重学员的</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">日常行为养成和军事化规范</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">及道德素养，效果显著。</span></p>', '1', null, '1442409632', 'mingyu');
INSERT INTO `think_article` VALUES ('416', '39', '励志文章：乔布斯---​关于人生中的点点滴滴怎么串连在一起', '', '', '', '', '', '<p><span style=\"color: rgb(0, 112, 192);\"> &nbsp;&nbsp;我在里德学院(Reed college)待了六个月就办休学了。到我退学前，一共休学了十八个月。那么，我为什么休学？ 　　这得从我出生前讲起。我的亲生母亲当时是个研究生，年轻未婚妈妈，她决定让别人收养我。她强烈觉得应该让有大学毕业的人收养我，所以我出生时，她就准备让我被一对律师夫妇收养。但是这对夫妻到了最后一刻反悔了，他们想收养女孩。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp;所以在等待收养名单上的一对夫妻，我的养父母，在一天半夜里接到一通电话，问他们有一名意外出生的男孩，你们要认养他吗？而他们的回答是当然要。后来，我的生母发现，我现在的妈妈从来没有大学毕业，我现在的爸爸则连高中毕业也没有。她拒绝在认养文件上做最后签字。直到几个月后，我的养父母同意将来一定会让我上大学，她才软化态度。&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　十七年后，我上大学了。但是当时我无知选了一所学费几乎跟史丹佛一样贵的大学，我那工人阶级的父母所有积蓄都花在我的学费上。六个月后，我看不出念这个书的价值何在。那时候，我不知道这辈子要干什么，也不知道念大学能对我有什么帮助，而且我为了念这个书，花光了我父母这辈子的所有积蓄。 　　所以我决定休学，相信船到桥头自然直。当时这个决定看来相当可怕，可是现在看来，那是我这辈子做过最好的决定之一。当我休学之后，我再也不用上我没兴趣的必修课，把时间拿去听那些我有兴趣的课。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp;这一点也不浪漫。我没有宿舍，所以我睡在友人家里的地板上，靠着回收可乐空罐的五先令退费买吃的，每个星期天晚上得走七里的路绕过大半个镇去印度教的HareKrishna神庙吃顿好料。我喜欢HareKrishna神庙的好料。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　追寻我的好奇与直觉，我所驻足的大部分事物，后来看来都成了无价之宝。举例来说：当时里德学院有着大概是全国最好的书法指导。在整个校园内的每一张海报上，每个抽屉的标签上，都是美丽的手写字。因为我休学了，可以不照正常选课程序来，所以我跑去学书法。我学了 serif 与 sanserif 字体，学到在不同字母组合间变更字间距，学到活版印刷伟大的地方。书法的美好、历史感与艺术感是科学所无法捕捉的，我觉得那很迷人。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp;我没预期过学的这些东西能在我生活中起些什么实际作用，不过十年后，当我在设计第一台麦金塔电脑时，我想起了当时所学的东西，所以把这些东西都设计进了麦金塔里，这是第一台能印刷出漂亮东西的计算机。如果我没沉溺于那样一门课里，麦金塔可能就不会有多重字体跟变间距字体了。又因为Windows抄袭了麦金塔的使用方式，如果当年我没这样做，大概世界上所有的个人计算机都不会有这些东西，印不出现在我们看到的漂亮的字来了。当然，当我还在大学里时，不可能把这些点点滴滴预先串在一起，但是这在十年后回顾，就显得非常清楚。我再说一次，你不能预先把点点滴滴串在一起；唯有未来回顾时，你才会明白那些点点滴滴是如何串在一起的。&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　所以你得相信，你现在所体会的东西，将来多少会连接在一块。你得信任某个东西，直觉也好，命运也好，生命也好，或者业力。这种作法从来没让我失望，也让我的人生整个不同起来。</span><br/></p>', '1', null, '1439900165', 'mingyu');
INSERT INTO `think_article` VALUES ('417', '39', '励志文章：乔布斯的---有关爱与失去', '', '', '', '', '', '<p><span style=\"color: rgb(0, 112, 192);\"><br/></span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp; 我的运气很好——年轻时就发现自己爱做什么事。我二十岁时，跟 Steve Wozniak 在我爸妈的车库里开始了苹果计算机的事业。我们拼命工作，苹果计算机在十年间从一间车库里的两个小伙子扩展成了一家员工超过四千人、市价二十亿美金的公司，在那之前一年推出了我们最棒的作品——麦金塔，而我才刚迈入人生的第三十个年头，然后被炒鱿鱼。&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 有几个月，我实在不知道要干什么好。我觉得我令企业界的前辈们失望——我把他们交给我的接力棒弄丢了。我见了创办 HP 的David Packard 跟创办 Intel 的 Bob Noyce，跟他们说我很抱歉把事情搞砸得很厉害了。我成了公众的非常负面示范，我甚至想要离开硅谷。但是渐渐的，我发现，我还是喜爱着我做过的事情，在苹果的日子经历的事件没有丝毫改变我爱做的事。我被否定了，可是我还是爱做那些事情，所以我决定从头来过。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　好吧，当苹果计算机成长后，我请了一个我以为他在经营公司上很有才干的家伙来，他在头几年也确实干得不错。可是我们对未来的愿景不同，最后只好分道扬镳，董事会站在他那边，炒了我鱿鱼，公开把我请了出去。曾经是我整个成年生活重心的东西不见了，令我不知所措。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　当时我没发现，但是现在看来，被苹果计算机开除，是我所经历过最好的事情。成功的沉重被从头来过的轻松所取代，每件事情都不那么确定，让我自由进入这辈子最有创意的年代。 　　接下来五年，我开了一家叫做 NeXT 的公司，又开一家叫做 Pixar 的公司，也跟后来的老婆谈起了恋爱。Pixar 接着制作了世界上第一部全计算机动画电影，玩具总动员，现在是世界上最成功的动画制作公司。然后，苹果计算机买下了NeXT，我回到了苹果，我们在NeXT 发展的技术成了苹果计算机后来复兴的核心。我也有了个美妙的家庭。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp;我很确定，如果当年苹果计算机没开除我，就不会发生这些事情。这帖药很苦口，可是我想苹果计算机这个病人需要这帖药。有时候，人生会用砖头打你的头。不要丧失信心。我确信，我爱我所做的事情，这就是这些年来让我继续走下去的唯一理由。你得找出你爱的，工作上是如此，对情人也是如此。&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　你的工作将填满你的一大块人生，唯一获得真正满足的方法就是做你相信是伟大的工作，而唯一做伟大工作的方法是爱你所做的事。如果你还没找到这些事，继续找，别停顿。尽你全心全力，你知道你一定会找到。而且，如同任何伟大的东西，事情只会随着时间愈来愈好。所以，在你找到之前，继续找，别停顿。</span></p>', '1', null, '1441892998', 'mingyu');
INSERT INTO `think_article` VALUES ('418', '39', '励志故事文章：乔布斯---关于死亡', '', '', '', '', '', '<p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 当我十七岁时，我读到一则格言，好像是「把每一天都当成生命中的最后一天，你就会轻松自在。」这对我影响深远，在过去33年里，我每天早上都会照镜子，自问：「如果今天是此生最后一日，我今天要干些什么？」每当我连续太多天都得到一个「没事做」的答案时，我就知道我必须有所变革了。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp;　　提醒自己快死了，是我在人生中下重大决定时，所用过最重要的工具。因为几乎每件事——所有外界期望、所有名誉、所有对困窘或失败的恐惧——在面对死亡时，都消失了，只有最重要的东西才会留下。提醒自己快死了，是我所知避免掉入自己有东西要失去了的陷阱里最好的方法。人生不带来，死不带去，没什么道理不顺心而为。&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　一年前，我被诊断出癌症。我在早上七点半作断层扫描，在胰脏清楚出现一个肿瘤，我连胰脏是什么都不知道。医生告诉我，那几乎可以确定是一种不治之症，我大概活不到三到六个月了。医生建议我回家，好好跟亲人们聚一聚，这是医生对临终病人的标准建议。那代表你得试着在几个月内把你将来十年想跟小孩讲的话讲完。那代表你得把每件事情搞定，家人才会尽量轻松。那代表你得跟人说再见了。&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　我整天想着那个诊断结果，那天晚上做了一次切片，从喉咙伸入一个内视镜，从胃进肠子，插了根针进胰脏，取了一些肿瘤细胞出来。我打了镇静剂，不醒人事，但是我老婆在场。她后来跟我说，当医生们用显微镜看过那些细胞后，他们都哭了，因为那是非常少见的一种胰脏癌，可以用手术治好。所以我接受了手术，康复了。&nbsp;</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　这是我最接近死亡的时候，我希望那会继续是未来几十年内最接近的一次。经历此事后，我可以比之前死亡只是抽象概念时要更肯定告诉你们下面这些：</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp;　　没有人想死。即使那些想上天堂的人，也想活着上天堂。但是死亡是我们共有的目的地，没有人逃得过。这是注定的，因为死亡简直就是生命中最棒的发明，是生命变化的媒介，送走老人们，给新生代留下空间。现在你们是新生代，但是不久的将来，你们也会逐渐变老，被送出人生的舞台。抱歉讲得这么戏剧化，但是这是真的。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp;　　在我年轻时，有本神奇的杂志叫做 Whole Earth Catalog，当年我们很迷这本杂志。那是一位住在离这不远的Menlo Park 的Stewart Brand 发行的，他把杂志办得很有诗意。那是1960年代末期，个人计算机跟桌上出版还没发明，所有内容都是打字机、剪刀跟拍立得相机做出来的。杂志内容有点像印在纸上的Google，在Google出现之前35年就有了：理想化，充满新奇工具与神奇的注记。 　　Stewart跟他的出版团队出了好几期 WholeEarthCatalog，然后出了停刊号。当时是1970年代中期，我正是你们现在这个年龄的时候。在停刊号的封底，有张早晨乡间小路的照片，那种你去爬山时会经过的乡间小路。在照片下有行小字：求知若饥，虚心若愚。那是他们亲笔写下的告别讯息，我总是以此自许。当你们毕业，展开新生活，我也以此期许你们。<br/></span></p>', '1', null, '1444737808', 'mingyu');
INSERT INTO `think_article` VALUES ('419', '17', '感恩励志教育：你养我长大，我陪你变老！', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://www.tudou.com/v/4p9MLLAsSco/&rpid=329045618&resourceId=329045618_04_05_99/v.swf\" width=\"700\" height=\"500\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_blank\" title=\"感恩励志教育\"><img src=\"/ueditor/php/upload/image/20151019/1445230908139879.jpg\" title=\"感恩励志教育\" alt=\"感恩励志教育\" width=\"611\" height=\"272\" style=\"width: 611px; height: 272px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;&nbsp;青少年感恩励志教育：就是让孩子明事理，学道德，懂感恩，会孝顺。</span></p>', '1', null, '1444481262', 'mingyu');
INSERT INTO `think_article` VALUES ('420', '22', '益阳懵懂的少女被早恋带出了课堂', '', 'http://www.841990053.com/case.php?cid=22', '../Uploads/1484646150.jpg', '益阳特训学校，益阳戒网瘾学校，益阳早恋孩子教育学校，益阳叛逆孩子管教学校', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/case.php?cid=22\" target=\"_self\" title=\"早恋孩子特训学校\"><img src=\"/ueditor/php/upload/image/20170117/1484646018371020.jpg\" title=\"特训学校\" alt=\"特训学校\" width=\"283\" height=\"177\" style=\"width: 283px; height: 177px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">&nbsp; 胡娇，女，15岁，来自湖南省益阳市赫山区，主要存在早恋厌学等问题比较严重，2012年5月13日入校，服从意识较强，转变效果很好，现在家上学，表现良好。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\"> <strong>问题概况</strong></span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">1、离家出走的原因是因为长期依赖在家里受到过分管制，在忍受不了的情况下，走上极端的道路，离家出走。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">2、在外结交男朋友，对感情之事看得淡漠。这一方面是因为家庭原因，父亲的过于管制让女儿变得叛逆，另外，特殊的家庭结构和妈妈的生活作风在女儿心里也有了潜移默化的作用。母亲的爱情婚姻观本身就不正确，先是嫁给比自己大28岁，身体还有残疾的男人，当男人的生意不好的时候便离婚另嫁。贪玩好耍没有家庭祝福和母亲的责任感，而且小时候母亲常带着女儿一起到娱乐场所玩。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">3、小娇有说谎的习惯，其主要原因是父亲管制过严，害怕受到惩罚，逼迫孩子有时撒谎。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">4、小娇比较封闭，内向，防御心理强，是由于特殊的家庭环境造成的，让孩子在同学中缺乏自信心。<br/> <strong>辅导方案</strong></span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">1、正确的爱情观教育和自我保护意识教育。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">2、与家长配合，离异家庭父母双方都要正确的心态，以孩子为重，共同担负起孩子成长的责任。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">3、正确认识父母情感变化对自己的影响。<br/></span></p><p><strong><span style=\"color:#0070c0\">教育效果</span></strong></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">1、学会了如何正确的与人沟通。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">2、懂得了做人的道理,认知能力有所提高。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 112, 192);\">3、对父亲的爱也有了更深的理解。</span></p>', '1', null, '1413719785', 'mingyu');
INSERT INTO `think_article` VALUES ('421', '0', '早恋教育：因一次同学聚会后与同学的朋友相恋', '', '', '', '', '', '<p><strong><span style=\"color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"color: rgb(0, 112, 192);\"> &nbsp; 曹梦琴，女，17岁，来自湖南岳阳平江，家境较好，长相甜美，性格较内向，初一开始，被同学誉为校花，有很多男同学追求， 有较强的虚弱心，同时自制力较差，初一下学期与追求她的一男同学交往，并随同本校的高中生一起逃学出去玩，父母由于工作关系，疏于对她的管教，该生从初二开始彻底辍学。至今为止，已两次怀孕。<br/> <strong>问题概况</strong></span></p><p><span style=\"color: rgb(0, 112, 192);\">青少年早恋已成为很严重的社会问题，促成这一现象的因素有以下几方面：一是青春期性生理和性心理发育的自然本能作用。进入青春期的青少年随着生理的发展开始产生性意识。他们开始敏感的看待男女同学间的交往，他注视着异性同学有关自己的一举一动。逐渐的，在众多男女共同交往中逐渐对群体异性的好感转向对个别异性的依恋，形成一对一交往的行动，即早恋。二是青春期教育的缺乏。青春期如何进行健康的异性交往，如何正确区分友情和爱情，与异性交往的行为规范如何……青少年缺乏这些知识，又无行为规范训练，客观条件往往也造成与异性交往的阻力，缺乏与异性朋友建立纯真的友谊条件。三是青少年独立意识的作用。他们对成人世界盲目追求和向往，以为能恋爱就代表自己的成熟，而忽视了自身心理素质的培养、锻炼和提高，是缺乏理智的表现。四是家庭因素。在这个物欲横流的世界，很多家长一味的追求经济利益而忽视了对子女的教育，缺乏与子女的沟通。<br/> <strong>教育方案</strong></span></p><p><span style=\"color: rgb(0, 112, 192);\">一是对她进行挫折教育。自身的长相带给她很强的优越感，挫折教育为的是使她明白长相的好坏不能决定一个人的命运。对她的弱点进行强化，如生学习成绩较差，与人沟通的能力较弱。</span></p><p><span style=\"color: rgb(0, 112, 192);\">二是尊重、关心、理解孩子，加强情感沟通。作为心理老师，首先，对其优点进行肯定，在生活和学习等方面加强关注度。帮助她提高认识，正视早恋，以及早恋给她的身心带来的伤害。引导她应把精力放在追求远大理想和实现人生价值上，而不宜过早恋爱空耗精力，消磨时光。</span></p><p><span style=\"color: rgb(0, 112, 192);\">三是引导她多于父母沟通，加强情感沟通。四是培养她的兴趣，转移情感注意力。<br/> <strong>转化效果</strong></span></p><p><span style=\"color: rgb(0, 112, 192);\">经过在学校的几个月的学习，她认识到了早恋给自己带来的身心伤害，家庭的优越感和自身的长相并不能觉得自己的能力，应该树立正确的价值观，她决心毕业后参加美容培训班，以后做一个美容化妆师。看到她对自己有个明确的目标，我们也感到欣慰。</span></p>', '0', null, '1445259736', 'mingyu');
INSERT INTO `think_article` VALUES ('422', '22', '早恋教育：因一次同学聚会后与同学朋友相恋', '', '', '../Uploads/1445420041.jpg', '早恋教育，未成年孩子早恋教育学校，少年恋爱教育，教育早恋的学校，孩子早恋了怎么办', ' 由于小琴家庭条件和生活环境相对来说是比较好的，并且小琴长相甜美，但是她的性格却是较内向，有点羞润。刚上初中不久就被大批男生追捧，并被评为所为的“校花”。 ', '<p style=\"text-align: center;\"><strong><span style=\"color: rgb(0, 112, 192);\"><a href=\"http://www.841990053.com\" target=\"_blank\" title=\"早恋厌学教育\"><img src=\"/ueditor/php/upload/image/20151021/1445419649406650.jpg\" title=\"早恋厌学女孩\" alt=\"早恋厌学女孩\" width=\"323\" height=\"206\" style=\"width: 323px; height: 206px;\" border=\"0\" vspace=\"0\"/></a></span></strong></p><p><strong><span style=\"color: rgb(0, 112, 192);\">基本信息</span></strong></p><p><span style=\"color: rgb(0, 112, 192);\"> &nbsp; 曹梦琴，女，16岁，来自湖南岳阳平江县，2013年9月入学，来校得到较好的教育辅导，转变效果明显，毕业回去至今无不良反应。</span></p><p><span style=\"color: rgb(0, 112, 192);\"> <strong>问题概况</strong></span></p><p><span style=\"color: rgb(0, 112, 192);\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 由于小琴家庭条件和生活环境相对来说是比较好的，并且小琴长相甜美，但是她的性格却是较内向，有点羞润。刚上初中不久就被大批男生追捧，并被评为所为的“校花”。 她虽然有着较强的虚弱心，但是她的自控能力却比较差。由于父母忙于工作和家务，因而对她的管教有所松懈，她在学校隐瞒着父母谈恋爱，并有过身孕做了人流。其实</span>造成这一现象的因素有以下几方面：一是青春期孩子性生理和性心理发育的自然交往条件反应；二是缺乏青春期相关思想引导和性健康的教育；三是个别性发育早熟的作用；四是家庭的结构因素等。像小琴这样的情况主要是因为受到了社会和他人的影响而被带入了早恋行为，再加上其自己对异性意识的好奇和渴望，最终抵不住诱惑的她也只好逆来顺受，顺其自然了。<br/> <strong>教育方案</strong></span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 1.心理辅导是解决孩子问题的根本，首先我们的主要<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"恋爱教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">教育方案</span></a>就是寻找孩子不同问题的根源，采取问题分类式针对性心理疏导，让孩子从内心深处解开自身问题之谜，正确认识自己问题的利与弊。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 2.早恋的知识教育是关键，通过恋爱和性健康教育，让她清楚地明白恋爱的真实含义，学会自我认识和自我克制，避免好奇和迷茫的恋爱。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 3.良好的生活心态的她淡化恋爱观念的良药，既是生活教育。一个良好的生活规划可以使人明辨是非和充分利用时间，以良好的生活事物替换她的恋爱思想与行为，慢慢地淡化早恋意识。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 4.亲子教育是解决情感问题的最佳桥梁。由于大部分孩子问题源于父母的管教和关爱方式所引起的，那么父母的举动将是影响孩子生活和学习的领头羊。让其父母改变在家庭中的变现身份和对她的关爱及管教方式，使得亲子之间保持良好的沟通交流，接触亲子之间的情感隔阂。同时也可以让她懂得情的所在，心的所爱，也是让她明白什么是真什么是假，避免迷茫的选择。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 5.培养她的学习兴趣，转移她的恋爱意识。只有把她的学习兴趣培养起来了，她才能把心思放在学习上，同时也是经过学习的专心替换贪玩的意念，从而纠正了她的早恋问题。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 6.实施全能课程的同步开展，让她在“心、理、德、智、文、武、法”等各个领域得到相应的提升，增强他的阅历，改变她的不良思想及行为。帮她树立正确的生活学习观和价值观，建立一个良好的生活学习规划和明确她的奋斗目标，让她有心可想，有事可做等。</span></p><p><span style=\"color: rgb(0, 112, 192);\"> <strong>转化效果</strong></span></p><p><font color=\"#0070c0\">&nbsp; 梦琴经过几个月的心理疏导、行为矫正和其他项目的<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"早恋教育学校\"><span style=\"color: rgb(0, 112, 192);\">教育辅</span><span style=\"color: rgb(0, 112, 192);\">导</span></a>，她积极向上的心态改变了她不良的思想和行为。在生活上能够独立自主；在学习上能够勤问好学；在行为上能够自我克制；在仪表上她是个有礼有节的阳光少女。</font></p><p><font color=\"#0070c0\">&nbsp; 现在的她在家里就是个乖乖女，会主动跟父母沟通并帮助父母做一些家务，有什么问题都会跟父母讲解和寻求帮助，家庭的气氛也热闹了起来。她现在的生活很有规律，自己设有一个工整的生活学习规划表和作息时间，能准时无误的完成相应的事务，经常获得父母的表扬和鼓励，让她在生活和学习上增添不少自信心，使她不胆怯、不怕事的“刘胡兰”。</font></p><p><font color=\"#0070c0\">&nbsp; 在学习上成绩不断提升，虽然不是很好，但是都能保持班级中上等位置。由于她在我们学校通过全能化课程的教育培养，在很多方面是现在班级里最优秀，不论是文是武，都是同学最为羡慕的对象，也是班级的领导者。我们每一次的后期访问听到最多的就是其父母的“感谢”二字了，在一个就是她们<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"早恋教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">幸福</span></a>的笑声，让我们感觉到无比的欣慰。</font></p>', '1', null, '1437311019', 'mingyu');
INSERT INTO `think_article` VALUES ('423', '17', '青少年教育学校：《留守儿童 你并不孤独》', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://www.tudou.com/v/7lo1dFCg4_U/&rpid=329045618&resourceId=329045618_04_05_99/v.swf\" width=\"700\" height=\"500\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp;<span style=\"color: rgb(0, 112, 192);\"> 孩子在成长的路上需要父母的关爱和陪伴，而不是身外的物质满足。很多家长以为努力为孩子创造了物质上的满足就是关爱，就是孩子成长的需求？但是不以为然，当孩子得到了您丰富的物质满足后，那些都是一个个静悄悄的物品，而不是“爱”。人是有感情的动物，而不是冷血动物，那么物质仅仅是身外的装饰，是生存的需求之一；但是对于关爱，是亲切的感情，是发自内心的“爱”。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 如今，不少家长把孩子一生就悄然离开，去所为的赚钱。但是多年以后，连自己的孩子都不认识了，何况是亲子与亲情。虽然您是知足了，但是孩子呢？孩子一出世就找不到了亲情，成了“留守儿童”。当他们慢慢的长大成人，依旧是生活在一个无亲无情的坏境里，一个人倍感孤独；当别人他你爸爸妈妈的时候，他却只好沉默，因为他们不知道自己到底有没有爸爸妈妈。当看到别家的孩子与父母有说有笑、幸福拥抱的时候，他们也只能在心中羡慕，把无情的泪水又咽回了肚子里。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 如果您是孩子的家长，您就应该给他们一点点的爱，不再让他们孤独和寂寞！如果您是孩子的家长，您就应该多花一点时间去陪伴他们成长，不再让他们无亲无情！如果您是孩子的家长，您就应该给予他们一些良好的教育，让他们健康成长、成人成才！</span></p>', '1', null, '1444476060', 'mingyu');
INSERT INTO `think_article` VALUES ('424', '38', '亲子教育：《别以为孩子做不到》', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://www.tudou.com/v/03YqGJMQjOI/&rpid=329045618&resourceId=329045618_04_05_99/v.swf\" width=\"700\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">有时候，不少父母都是比较担心孩子不够坚强、软弱、无知等问题，其实是对孩子的不够信任。父母是孩子的最佳导师，孩子是父母的贴心小棉偶，是相互最近的心灵感应和最强的亲切感，也应该是最信任的。只有您相信了孩子，他才能勇敢的去面对问题和解决问题， 更是能够让孩子磨练成长。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 很多孩子有心理一般都不会跟自己的父母说的话，这就证明了孩子对您还不够信任。这是因为孩子担心把心事跟您说了怕您不能够为他保密或者是怕您会责怪他，怕跟您说了会变成一种负担。如果您平时正常、健康、关怀地与孩子进行良好的交往和沟通，跟您说了事就不要责怪他，那么孩子就会相信您是最亲的人和最安全的保障，也是信任您了。当然亲子之间取得良好的信任之后就不会存在疑虑，不论什么事都可以及时的沟通与互动，不仅仅能让事情得到很好的解决，同时也是建起幸福家庭的基础。</span></p>', '1', null, '1444296457', 'mingyu');
INSERT INTO `think_article` VALUES ('425', '9', '叛逆教育：孩子叛逆打父母怎么办？', '', '', '', '孩子叛逆怎么办，孩子打父母怎么办，叛逆孩子教育学校，教育叛逆孩子的学校', '叛逆孩子问题越来越多，也是令家长最为头疼之事。其实，大部分叛逆的孩子都是跟父母的管教方式有着关键性的作用，父母是孩子最亲切的老师，当然父母的一言一行都是孩子所受影响的。对于这些已经产生了叛逆情绪及行为的孩子，我们必须利用最恰当的办法给予解决，找出因果，对症下药。', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;叛逆孩子问题越来越多，也是令家长最为头疼之事。其实，大部分叛逆的孩子都是跟父母的管教方式有着关键性的作用，父母是孩子最亲切的老师，当然父母的一言一行都是孩子所受影响的。对于这些已经产生了叛逆情绪及行为的孩子，我们必须利用最恰当的办法给予解决，找出因果，对症下药。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">一、叛逆孩子不喜欢唠叨</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 根据叛逆孩子的特性心理，大部分都是不喜欢父母及身边人过于唠叨的。因为您的唠叨言语激发了他的不耐烦心理，让他产生抗拒情绪，从而起身反抗，既是叛逆表现。所以当您孩子是比较叛逆的话，建议不要用带有刺激的言语对孩子与辱骂的方式唠叨；需要寻求有利于孩子听从的良言蜜语跟孩子进行平等友谊的沟通交谈，是可以缓解孩子的叛逆情绪及行为的。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">二、叛逆孩子需要关爱</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 很多叛逆的孩子不是与生俱来的，而是在生活当中缺乏安全感和亲人关爱。像叛逆的孩子出现比较严重的反抗及打父母的行为，大部分都是亲子之间存在一定的情感问题所导致的，那么这个情感问题也就是父母没有满足孩子成长需求的安全感和关爱。安全感也就是父母的为人处事等心理与行为可能是不太理性的，而且会造成二次问题的情况，会对家庭和亲人带来一些戳伤等；关爱也就是平时缺乏关注孩子的点点滴滴，给予不理不睬等态度，或者是当孩子请求而没有得到及时的处理和满足时，孩子的心理会产生对父母的失望。因而存在这些问题之后父母再去对孩子进行命令或指责的时候往往会出现叛逆情绪及行为，所以孩子需要您的同情和关爱。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">三、换种身份和态度去面对</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 当您孩子出现叛逆情绪及行为时，建议您不要拿起传统的管教方式给予解决，需要换一种身份和态度去面对处理。传统的身份往往是长辈，也就是父母亲或领导的身份；传统的心态就是以自我为中心，高级的统领心态，以为孩子是晚辈就必须服从长辈的心态。这样往往会让孩子产生一种无望的压力，感觉自己是井底之蛙，永远都是被掌控在父母亲的手掌里的压抑感，因而想办法挣脱父母亲的掌控，便会出现叛逆情况。所以对于这样的孩子，建议做父母亲的有必要降低一下身份和换一种心态去面对和处理，效果会更好。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">四、学会忍痛割爱，谈判解决</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 叛逆的孩子不能用暴力来解决，打骂体罚的暴力手段只能是让孩子更加反抗和叛逆，而不是能压制孩子，更是不能解决问题。在此，我们青少年教育学校建议您抛弃所为的面子，甩掉肩膀上的重担，语轻气和的坐下来感孩子好好沟通，寻找孩子叛逆的为什么。只有跟孩子沟通交流才能获取问题的根源和解决问题的条件，阻止问题的恶化和蔓延。</span></p>', '1', null, '1441976108', 'mingyu');
INSERT INTO `think_article` VALUES ('426', '33', '如果孩子不愿意来学校接受教育辅导怎么办？', '', '', '', '问题孩子教育学校，怎么管教叛逆孩子', '', '<p><span style=\"font-size: 36px; color: rgb(0, 112, 192);\">问</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">：如果我们的孩子不愿意过来学校接受<a href=\"http://www.841990053.com\" target=\"_blank\" title=\"教育辅导\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">教育辅导</span></a>怎么办？因为孩子在家没有很大的问题，他也是愿意改正自己一些不良问题的，但是就是他的思想有点迷茫，自控能力比较差，有时候心情不好就会发脾气，我们也是担心，如果强行让他来学校他肯定会怨恨我们的，但是如果让他自愿来的话却有点困难，我们也只好顺着他了。</span></p><p><span style=\"font-size: 36px; color: rgb(0, 112, 192);\">答</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">：孩子本身就是存在问题需求<a href=\"http://www.841990053.com/show.php?cid=10&id=114\" target=\"_blank\" title=\"青少年教育\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">教育调整</span></a>，如果家长想让孩子自愿来校接受教育是很难的，因为他知道来这种问题矫正学校是没有在家那么自由和幸福的。如果您真正的是想为孩子好，真的想把孩子的问题矫正好，那么就要按我们专业的矫正教育方案来执行，是可以保证效果的。也就是说，孩子有些问题是需要严格的教育要求才能得以转变的，那么您需要下定决心把孩子送到学校来给予教育辅导的矫正。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 如果您是想顺着孩子的心愿来改正，那可能就要这样拖延的时间越来越长，不但问题得不到减轻，反而问题会越来越严重化，后果不堪设想。当您发现孩子问题在家或顺着无法改变的时候，孩子的问题应该的到了严重期段了，比如说“癌症晚期”。到了这些时候了您在下定决心的时候，是没有人能够帮助到您的，也只能自己处理后事了。那么原本好好的孩子就这样被毁在半路，家长也只能眼睁睁的看着孩子为所欲为，接受所为的命运的安排。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; <a href=\"http://www.841990053.com\" target=\"_blank\" title=\"问题孩子教育学校\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">青少年教育辅导中心</span></a>提醒各位家长：问题孩子不是“病”，谨慎去医院医疗，因为医院除了药物还是服药，对没病的孩子的身心及神经都是有副作用的。当您发现孩子有些不良问题的时候，请给予重视，关注孩子的成长，及时教育矫正，挽救孩子一人，幸福您全家庭。</span></p>', '1', null, '1442837974', 'mingyu');
INSERT INTO `think_article` VALUES ('427', '10', '厌学教育:孩子不愿意上学是什么原因？', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 孩子不愿意上学了是目前最为常见的青少年问题之一，但是很多家长却不以为然，顺其孩子心意，为所欲为。学习是孩子的本分，也是国富民强的根本。法律明确规定：九年义务教育是每一位公民必须执行的青少年教育法之一，也是父母必须遵守和履行孩子上学的基本义务与责任。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">其实大部分孩子厌学的原因并不复杂，只要您稍微关注和做出相应的引导工作，可能他就不会出现厌学到辍学的情况。那么孩子厌学辍学的因素有那些呢?</span></p><p><span style=\"color:#0070c0;font-size:18px\">一、没有得到身边人的鼓励</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 鼓励是每一个人成长不可缺少的动力之一，孩子的成长更是倍加需要。孩子厌学其实也是与这个有关，当孩子们高高兴兴地上学而默默地回家时，您没有关注他是怎么了；当孩子们成绩下滑的时候，您没有给予关注和安慰；当孩子学习压力大的时候，您没有帮助和引导；当孩子努力而没有很大的进步时，您没有及时给予相应的鼓励，让他缺乏前进的动力；当学校要求开展家长会或者亲子活动时，您没有参加，让孩子感觉孤单和无助，甚至是没面子等。因而孩子学习的点点滴滴都是需要家长的关注和鼓励。</span></p><p><span style=\"color:#0070c0;font-size:18px\">二、上网等因素使得成绩低落而厌学</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 特别是当今社会，其他因素影响使得孩子成绩低落的问题比较多，一般都是上网成瘾、成绩差、没有得到鼓励、父母不关注等都是引起孩子厌学的原因之一。因而孩子平时上网的时间及内容要有一个良好的控制和管教，一旦把心思放在网络上，孩子的学习肯定就是跟不上，成绩也随之下滑，同时产生自卑心理后出现厌学情绪。当您碰到孩子有这些问题的时候必须给予关注和帮助，鼓励孩子认真学习，杜绝其他不良行为习惯。</span></p><p><span style=\"color:#0070c0;font-size:18px\">三、学校的教学模式产生的影响</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 如今的全日制学校教学模式大同小别，为什么学校和老师个人的业绩，选优排差的偏见行为，让学生产生憎恨心态，压力逐渐增大，严重影响学生的学习信心。还有很多学校对于叛逆的青少年还会采取打骂体罚手段，成绩不佳、作业没完成或没做好、迟到旷课、上课不听讲及其他问题等情况都是实施打骂或体罚手段来泄气，从而在众多同学面前产生自卑心理和憎恨老师的心态，从而做出反抗或者逃避的行为，也就是厌学辍学的情况。</span></p><p><span style=\"color:#0070c0;font-size:18px\">四、外界不良诱惑影响孩子学习心理</span></p><p><span style=\"color:#0070c0;font-size:18px\">&nbsp; 由于社会形形色色的诱惑力比较多，而孩子的自控能力较弱，抵不住社会巨大的诱惑力，因而学习心不在焉，时刻准备有放弃学习的心理。特别是金钱和自由的诱惑占据的份额较大，孩子每天在学校仅仅靠着丁点零花钱来维持生活，而且各种权限无法自由，使得产生极大的获取和摆脱的渴望，心思也跟着漫游，飘逸他乡了。</span></p>', '1', null, '1442324439', 'mingyu');
INSERT INTO `think_article` VALUES ('428', '5', '石老师》德育教育辅导员', '', '', '../Uploads/1462519429.jpg', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160506/1462519188635235.jpg\" title=\"石辅导员\" alt=\"石辅导员\" width=\"319\" height=\"380\" border=\"0\" vspace=\"0\" style=\"width: 319px; height: 380px;\"/></a></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">石 文</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; &nbsp;德育教育辅导员。曾服役于中国人民解放军陆军四十八集团军，在服役期间曾获军事演习中因表现突出荣获三等功一次并被评选为“优秀士兵”。退役后在教育行业工作多年并充分利用业余时间参与教学心理学习和音乐培训，获有心理咨询师资格证，对青少年心理动向有着独特的教育理念和心理战备，轻松解决学生心理问题。现任湖南青少年励志教育学校德育教育辅导员。</span></p>', '1', null, '1441547415', 'mingyu');
INSERT INTO `think_article` VALUES ('429', '60', '心理疏导：心理缓解操——音乐手语操<视频>', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://share.vrs.sohu.com/my/v.swf&topBar=1&id=81928540&autoplay=false&xuid=s4843a16049564b1&from=page\" width=\"700\" height=\"500\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p>', '1', null, '1444365971', 'mingyu');
INSERT INTO `think_article` VALUES ('430', '19', '被医院诊断为精神分裂症抑郁症的衡阳大学生', '', 'http://www.841990053.com/case.php?cid=19', '../Uploads/1484646405.jpg', '网络综合征治疗，少年戒除网瘾，湖南戒网瘾学校，正规戒网瘾中心，青少年戒网所', '湖南省衡阳市，因 上网成瘾后被父母带到医院检查诊断结果是精神分裂症和抑郁症，给予服药治疗无果后送来我校教育辅导，2015年4月入校，现在成功转好回家。', '<p><span style=\"font-size: 18px;\"><strong><span style=\"color: rgb(0, 112, 192);\">基本信息</span></strong></span><br/></p><p><span style=\"font-size: 18px;\"><strong><span style=\"color: rgb(0, 112, 192);\">&nbsp; </span></strong><span style=\"color: rgb(0, 112, 192);\">冯学员，男，22岁，来自湖南省衡阳市，因上网成瘾后被父母带到医院检查诊断结果是精神分裂症和抑郁症，给予服药治疗无果后送来我校教育辅导，2015年4月入校，现在成功转好回家。</span></span></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=41&id=430\" target=\"_self\" title=\"案例描述\"><img src=\"/ueditor/php/upload/image/20151029/1446114260445163.jpg\" title=\"医院诊断病例描述\" alt=\"医院诊断病例描述\" width=\"498\" height=\"208\" style=\"width: 498px; height: 208px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_blank\" title=\"精神分裂与抑郁症的辅导\"><img src=\"/ueditor/php/upload/image/20151029/1446105256381648.jpg\" title=\"医院治疗药物--精神药物\" alt=\"医院治疗药物--精神药物\" width=\"268\" height=\"104\" style=\"width: 268px; height: 104px;\" border=\"0\" vspace=\"0\"/></a></p><p><strong><span style=\"color:#0070c0;font-size:18px\">问题概况</span></strong></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 冯学员是个大学生，由于在校学习的空闲时间较多，便是上网来消磨时间，一段时间后对网络的依赖逐渐增强，产生了网络综合症---网瘾。后来回家一段时间被父母发现：他整天赖着电脑不放，作息时间颠倒，饮食不规律，甚至有时候不吃饭也一直上网着。以为孩子出现了什么问题，便带着他去医院检查，然而医院既是给的结果是网络综合症、精神分裂症和抑郁症等病例，便按医院的服药治疗方案所服药的精神药物。经过长时间服药后冯学员问题没有得到缓解，反而脾气更加暴躁，思维混乱，自卑自闭和叛逆等问题也随之即来。在无奈之下找到了我们学校。</span></p><p><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">辅导效果</span></strong></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=41&id=430\" target=\"_blank\" title=\"心理教育辅导后的诊断结果\"><img src=\"/ueditor/php/upload/image/20151029/1446105511737478.jpg\" title=\"来我校辅导后的检查结果\" alt=\"来我校辅导后的检查结果\" width=\"486\" height=\"248\" style=\"width: 486px; height: 248px;\" border=\"0\" vspace=\"0\"/></a></p><p><span class=\"edui-editor-imagescale-hand1\" style=\"box-sizing: border-box; margin: -4px 0px 0px -4px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 0px; font-family: &#39;Microsoft YaHei&#39;, simsun, &#39;Helvetica Neue&#39;, Arial, Helvetica, sans-serif; vertical-align: baseline; position: absolute; width: 6px; height: 6px; overflow: hidden; display: block; cursor: n-resize; top: 0px; left: 263px; color: rgb(51, 51, 51); background-color: rgb(60, 157, 208);\"></span><span class=\"edui-editor-imagescale-hand2\" style=\"box-sizing: border-box; margin: -4px 0px 0px -3px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 0px; font-family: &#39;Microsoft YaHei&#39;, simsun, &#39;Helvetica Neue&#39;, Arial, Helvetica, sans-serif; vertical-align: baseline; position: absolute; width: 6px; height: 6px; overflow: hidden; display: block; cursor: ne-resize; top: 0px; left: 526px; color: rgb(51, 51, 51); background-color: rgb(60, 157, 208);\"></span><span class=\"edui-editor-imagescale-hand3\" style=\"box-sizing: border-box; margin: -4px 0px 0px -4px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 0px; font-family: &#39;Microsoft YaHei&#39;, simsun, &#39;Helvetica Neue&#39;, Arial, Helvetica, sans-serif; vertical-align: baseline; position: absolute; width: 6px; height: 6px; overflow: hidden; display: block; cursor: w-resize; top: 124px; left: 0px; color: rgb(51, 51, 51); background-color: rgb(60, 157, 208);\"></span></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=41&id=430\" target=\"_blank\" title=\"辅导后的诊断结果\"><img src=\"/ueditor/php/upload/image/20151029/1446105601464915.jpg\" title=\"辅导后的抑郁症诊断结果\" alt=\"辅导后的抑郁症诊断结果\" width=\"486\" height=\"166\" style=\"width: 486px; height: 166px;\" border=\"0\" vspace=\"0\"/></a></p><p>&nbsp;<span style=\"color: rgb(0, 112, 192);\"> 经过一百多天的针对性心里疏导，冯学员问题慢慢得到缓解。他的性格变得开朗阳光，主动与人沟通交流；自控能力得到提升，暴躁的情绪也随之得到缓解；对于父母，他学会了理解和包容，能够跟父母诉说和请求；认知能力得到明显的提升，知道什么该做和不该做的，关于上网，他表示还会上，但是不会过多地去玩游戏了，只会看看电影和正常的使用上，也不会像以前那么依赖网络。在签订离校协议上，他发誓回去后会好好去找个工作努力赚钱，不会再贪玩和上网，请自己的父母放心。在此我们也感动无比的欣慰，同时也愿他再接再厉，努力创造自己美好的人生。</span></p>', '1', null, '1446114509', 'mingyu');
