INSERT INTO `think_article` VALUES ('508', '24', '励志教育2016年元旦亲子文艺演唱会圆满成功！', '', '', '', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160101/1451644225106968.jpg\" title=\"文艺展现\" alt=\"文艺展现\" width=\"347\" height=\"196\" style=\"width: 347px; height: 196px;\" border=\"0\" vspace=\"0\"/></a><a href=\"http://www.841990053.com/show.php?cid=24&id=508\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160101/1451643662109128.jpg\" title=\"接受奖励\" alt=\"接受奖励\" width=\"321\" height=\"195\" style=\"width: 321px; height: 195px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=508\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160101/1451643700602014.jpg\" title=\"光荣榜优秀学员\" alt=\"光荣榜优秀学员\" width=\"345\" height=\"204\" style=\"width: 345px; height: 204px;\" border=\"0\" vspace=\"0\"/></a><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160101/1451643725551409.jpg\" title=\"文艺表演\" alt=\"文艺表演\" width=\"323\" height=\"203\" style=\"width: 323px; height: 203px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160101/1451643749112743.jpg\" title=\"文艺演唱\" alt=\"文艺演唱\" width=\"342\" height=\"202\" style=\"width: 342px; height: 202px;\" border=\"0\" vspace=\"0\"/></a><a href=\"http://www.841990053.com/show.php?cid=24&id=508\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160101/1451643776219505.jpg\" title=\"歌咏合唱\" alt=\"歌咏合唱\" width=\"325\" height=\"201\" style=\"width: 325px; height: 201px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=508\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160101/1451644530155947.jpg\" title=\"文艺表演\" alt=\"文艺表演\" width=\"33