INSERT INTO `think_article` VALUES ('509', '3', '2016元旦文艺演唱会', '', '', '../Uploads/1451694583.jpg', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160102/1451693818640485.jpg\" style=\"width: 315px; height: 171px;\" title=\"恭贺新年\" width=\"315\" height=\"171\" border=\"0\" vspace=\"0\" alt=\"恭贺新年\"/></a><a href=\"http://www.841990053.com/show.php?cid=3&id=509\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160102/1451693818130515.jpg\" title=\"个人演讲\" width=\"292\" height=\"170\" style=\"width: 292px; height: 170px;\" border=\"0\" vspace=\"0\" alt=\"个人演讲\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=3&id=509\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160102/1451693818100436.jpg\" style=\"width: 307px; height: 182px;\" title=\"优秀学员\" width=\"307\" height=\"182\" border=\"0\" vspace=\"0\" alt=\"优秀学员\"/></a><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160102/1451693818256211.jpg\" title=\"文艺表演\" width=\"300\" height=\"181\" style=\"width: 300px; height: 181px;\" border=\"0\" vspace=\"0\" alt=\"文艺表演\"/></a></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20160102/1451693955137146.jpg\" style=\"width: 307px; height: 196px;\" title=\"文艺演唱\" width=\"307\" height=\"196\" border=\"0\" vspace=\"0\" alt=\"文艺演唱\"/><a href=\"http://www.841990053.com/show.php?cid=3&id=509\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160102/1451693955127279.jpg\" title=\"歌咏合唱\" width=\"300\" height=\"195\" style=\"width: 300px; height: 195px;\" border=\"0\" vspace=\"0\" alt=\"歌咏合唱\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=3&id=509\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160102/1451694193931552.jpg\" style=\"width: 303px; height: 182px;\" title=\"颁奖仪式\" width=\"303\" height=\"182\" border=\"0\" vspace=\"0\" alt=\"颁奖仪式\"/></a><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160102/1451694218431092.jpg\" title=\"快乐元旦\" alt=\"快乐元旦\" width=\"303\" height=\"181\" style=\"width: 303px; height: 181px;\" border=\"0\" vspace=\"0\"/></a></p>', '1', null, '1451647239', 'mingyu');
INSERT INTO `think_article` VALUES ('510', '25', '大学生跨年夜网吧通宵4小时猝死 年仅21岁', '', '', '', '', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20160103/1451806818372052.jpeg\" alt=\"大学生网吧猝死：通宵4小时 突然倒下年仅21岁\" align=\"middle\" border=\"1\" class=\"flag_bigP\" style=\"border: 0px; margin: 0px; padding: 0px; font-size: 0px; color: transparent; width: 366px; height: 219px;\" width=\"366\" height=\"219\"/></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">一大学生网吧猝死：通宵4小时 突然倒下年仅21岁</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　　跨年夜原本是个热热闹闹的时段，然而在广饶路附近的一所网吧里却发生了一起悲剧，岛城一所高校一年年仅21岁的大学生在网吧内玩通宵时突然倒下，最终抢救无效身亡，经诊断为心源性猝死。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　　记者1日上午赶到市立医院时，这名大学生已经被宣布死亡。校方一名工作人员介绍，在校期间，学校是不允许学生晚上离开寝室外出上网的，事发后校方立即联系其家属，目前家属已经赶到青岛。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　　记者联系到了广饶路附近的这家网吧的负责人，据介绍，事情发生在1日凌晨2时30分许，当时这名大学生与其他3名同学在网吧内玩得兴起，突然间这名学生歪倒过去，同学立即对他进行心肺复苏，同时拨打120。据了解，这名大学生是2015年12月31日晚上10点前后来到网吧，到发生意外共四个多小时。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　　医务人员提醒，长时间上网，久坐不动，容易造成血管内栓塞，肺梗塞，特别是在晚上熬夜上网，会导致神经持续兴奋易造成心律紊乱，网友连续上网玩游戏时间不宜过长，一般不要超过4个小时。——来自<a href=\"http://news.sohu.com/20160102/n433277779.shtml\" target=\"_self\">搜狐新闻</a></span></p><p>&nbsp; &nbsp; &nbsp;<span style=\"font-size: 18px; color: rgb(0, 176, 80);\">湖南青少年戒网瘾学校提醒：孩子上网成瘾不可忽视，以小犯大，欲哭无泪。孩子一旦上网成瘾，会引发一系列不良问题，比如是厌学辍学、脾气暴躁、暴力趋向、叛逆心理及行为、以自我为中心、饮食混乱、生活无规律、黑白颠倒、伤害及身心，甚至付出生命的危险等等。</span></p>', '1', null, '1451734900', 'mingyu');
INSERT INTO `think_article` VALUES ('511', '25', '孩子自拍“被绑架”照片欲骗父母钱 曾入室盗窃', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">(记者安然 通讯员赵亭超)弟弟被绑架，“绑匪”找父母要两万元，姐姐知道后赶紧报警。但结果是，弟弟为骗爹妈的钱自我绑架。而且他竟然还曾经多次入室盗窃，如今已因涉嫌盗窃被刑事拘留。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　去年12月22日，席女士匆匆冲进顺义高丽营派出所报案。她说，24岁的弟弟席某被绑架了，还发来了一张他被绑在树上的照片，绑匪向家里要两万。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　民警立即展开工作，可越干越觉得“味儿不对”。调取监控，找与被绑架的席某有过接触的人询问，结果在两天之后，民警在高丽营镇东马各庄村一家网吧内找到了玩儿得昏天黑地的席某。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　席某张嘴喊饿，民警给他端来饭菜，席某狼吞虎咽吃饱后承认：绑架是假的。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　他说，他在顺义这边打工，但是嫌工作累，挣钱少，又迷上了网络游戏，积蓄花光了，没脸回家见父母，也没脸见即将临盆的妻子。左思右想下，他利用手机里的延时拍摄功能，拍了一张“被绑在树上”的照片，假称被绑架了，想从父母手上骗点钱。但是没想到，姐姐竟然报了警。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　办案民警发现：这人的体貌特征怎么和几起入室盗窃案里被监控探头拍下的身影那么像？经过工作，席某承认，他一边设局从家里要钱，一边到高丽营一带的出租房“摸”点钱，维持生计。很短时间里，他已经作案十多起。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　目前，席某因涉嫌盗窃，已被顺义警方依法刑事拘留。——来自<a href=\"http://news.youth.cn/jsxw/201601/t20160108_7506181.htm\" target=\"_self\">中国青年网新闻</a></span></p>', '1', null, '1452257871', 'mingyu');
INSERT INTO `think_article` VALUES ('512', '5', '田老师》心理文化老师', '', '', '../Uploads/1456886955.png', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160302/1456886865502611.png\" title=\"心理文化老师\" alt=\"心理文化老师\" width=\"232\" height=\"312\" style=\"width: 232px; height: 312px;\" border=\"0\" vspace=\"0\"/></a></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">田晓静</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 心理咨询辅导师兼文化辅导老师。来自湖南益阳市，毕业于湖南中医药大学心理学院心理应用学专业，在读心理学研究生。</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">在校期间积极参与西部支教等公益活动，具有一定的教学经验。毕业后致力于专业学习，获得心理咨询师资格证及中级教师资格证。曾在多个心理单位进行相关的心理咨询辅导工作，心理咨询时间达八十个小时以上，辅导心理患者达数百人，有较丰富的个案辅导经验。通过业余时间展开了房树人测验与沙盘游戏的学习与实践，在青少年孩子心理问题上又有着较高的突破，一个个不良问题青少年在这里得到重生。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 田老师性格温和善良、大方开朗、沉着冷静、积极进取、责任心强，她希望通过自己的努力能让学生重获阳光，用积极健康的态度去拥抱生活，健康快乐成长，坚强励志成才。</span></p>', '1', null, '1450570802', 'mingyu');
INSERT INTO `think_article` VALUES ('513', '25', '女儿考研失败怪父母没本事：你们又穷又难看', '', '', '', '', '', '<p><span style=\"color: rgb(0, 112, 192);\">都说狗不嫌家贫，儿不嫌母丑，名校大学毕业考研失败的乖乖女，却迁怒于父母：认为是父母没本事送她出国留学，才导致她的困境；家境殷实的她，本有机会留在国 外发展，因父亲不同意她闯世界，她只好回到南宁发展，因为勤奋、肯干，她深得上司的赏识……她们两个都是学习优秀的女孩，在人生的道路上，同样面临着各种 选择，而她们不同的人生态度，结局也截然不同。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　连日来，本报推出的亲子系列报道，讲述了不同家庭的父母与子女之间的种种困惑。很多父母感慨，自己是如此爱儿女，可儿女偏偏不领情，越来越不懂事。而儿女们则埋怨，父母插手，让他们的生活一团糟。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　透过这些故事，情感教育专家说，不是孩子不肯长大，是父母将孩子呵护在羽翼之下，不希望孩子离开。25岁之后，父母对孩子的影响最好为零。</span></p><p><span style=\"color: rgb(0, 112, 192);\">故事一</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　平凡父母全力供女儿读名校</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　女儿考研失败怪父母没本事</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　倾诉人：阿霞 55岁 退休工人</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　倾诉时间：2016年1月9日</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　新 年了，别人都开开心心的，我这心里憋闷得很，又不敢和别人说。女儿小红生气时，总会说“你们这样的父母怎么会生出我这么优秀的女儿？你看你们两个又穷，长 得又难看，根本不配拥有我这样的女儿”，小红说这些话时，我们夫妻俩真的感觉很伤心，可内心深处又感觉有些对不起她，所以也任由她发脾气。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　我和丈夫都是普通工人，单位不景气，两人早早就退休了。现在我每个月也就1000多元的退休金，小红的爸爸稍高点。我们这样的收入，确实不能给小红想要的生活。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　我们夫妻俩都很普通，丢在人群里根本就没人会注意。而我们的女儿小红就不同，从小就很出众。或许是她接了我和爱人的优点，反正她一点也不像我，也不像爸爸，皮肤白皙、眼睛又黑又亮，长得像个洋娃娃。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　除了长相比较出众，她一直都很听话，而且上进好学，学习成绩一直很好。从小到大，小红基本是被我们夸大的。小红自己读书也很努力，小学考上好初中，初中又考上了南宁三中。我们两口子都没啥文化，也不懂如何辅导她，学习全凭她自己。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　高考时，小红的成绩超出一本分数线不少，很多重点院校她可以随便挑，但最后阴差阳错学了日语。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　大 学的4年时光里，小红过得不太开心，因为她并不喜欢日语。加上同宿舍的女生家庭条件优越，个个都是家中的小公主。大学毕业时，同宿舍的6个女孩，只剩小红 一人留在了国内，其他5个女生全部出国，这样小红内心极度失落。好在小红一心考研，第一年没考上。大学毕业后，小红回到家继续复习，专心考研。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　心情好的时候，小红会很开心给我们做她的拿手菜。还会搂着我说，感谢父母给了她那么多。可更多时候，她比较烦躁。烦躁时，她就会责怪我们，当初高考选学校时，我们没给她好的建议，这导致她过得不开心。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　更 让我们生气又伤心的是，一次她居然说，我们这么平凡，怎么会生出她这么优秀的女儿。她所有的一切，都是她通过自己努力获得的。她这么说，我们也哑口无言， 读书时，我们夫妻俩确实没能力辅导她，唯一能给她的，就是尽自己能力供她读书。而如果她想出国，我们的经济条件又不允许。内心深处，我们真的感觉有些对不 起她。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　去年底考研结束后，她又说，感觉自己还是考不上。就算考上了，也不想去读了，因为日语不是她喜欢的专业。既然不喜欢，又何必再去痛苦煎熬几年。她要离开南宁，去大城市找工作，那里才有她的一片天空。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　现在她天天晚上上网玩电脑，早上又蒙头大睡，我们和她就像生活在两个世界里。有时，很想和亲友商量下，说出去，又怕别人议论，对小红不好。真不知道该怎么办？</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　故事二</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　海归女为父回南宁发展</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　积极工作很快成团队领头人</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　倾诉人：阿静 52岁 公务员</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　倾诉时间：2016年1月10日</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　“明明有能力让女儿在国外发展，却为了自己的私心，非要把女儿‘绑’在自己身边，她爸爸真的很自私。”我一直认为，孩子不是父母的私有财产，孩子长大了，就该让她能飞多高飞多高。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　我 和丈夫都是公务员，家里虽然谈不上富裕，但我们夫妻俩工作非常努力，在各自的领域里都比较优秀，是部门负责人。女儿小君在我们的耳濡目染下，从小就很独 立，不仅学习成绩好，而且热衷公益活动。读大学时，小君直接选择了澳大利亚的一所大学。因为小君的爷爷奶奶和叔叔一家都有澳大利亚的绿卡，小君去澳大利亚 读书，身边会有亲人照顾，不会像其他孩子那样孤单。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　虽然有亲人照顾，在澳大利亚读书期间，小君经常趁着假期打短工，以减轻我们夫妻俩的负担。4年前大学毕业，小君想继续读研，可小君的爷爷奶奶却因为不喜欢国外的生活，非要回国。而小君的爸爸害怕小君读研后会选择留在澳大利亚发展，便坚决不同意小君读研。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　我非常支持小君在澳大利亚读研，因为小君学的是金融专业，她回到国内，一样要靠自己努力。在我看来，既然在国内和国外都要靠自己奋斗，那不如选择在国外奋斗，舞台更广阔。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　因为小君的前途，一时间，我们家到处弥漫着“硝烟”，我和丈夫经常争吵，甚至到了想要离婚的地步。听说父母为此吵闹不休，小君还是顾及爸爸的感受，选择了回国，回到南宁发展。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　小 君的履历比较优秀，顺利进入南宁一家银行工作。内心虽有些许不乐意，可既然选择了，小君便很努力，绝不会消极应对。工作忙碌，小君经常会加班，对此小君也 毫无怨言。正应验了那句老话“你有多少努力，就会有多少回报”，小君的努力很快见到效果，如今小君已经是项目负责人，成为团队的领头人。上司对小君也非常 欣赏，重要的项目会放心交给小君来操作。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　小君的发展虽好，可我内心深处还是期望小君能圆了自己的梦想，去澳大利亚继续深造、发展。</span></p><p><span style=\"color: rgb(0, 112, 192);\">　　看了你们的报道，我真的感觉，现在很多父母都把孩子当成私有财产，从精神上、经济上控制孩子。可孩子们都有自己的思想，自己独立的人格。父母能做的，是帮助他们独立，而不该把他们“绑在”自己的身边，满足自己的情感需求--来自<a href=\"http://news.youth.cn/sh/201601/t20160112_7518550.htm\" target=\"_self\">中国青年网新闻</a></span></p>', '1', null, '1452599817', 'mingyu');
INSERT INTO `think_article` VALUES ('514', '24', '青少年2016元旦感恩励志亲子教育文艺演唱会01', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://static.video.qq.com/TPout.swf?vid=q0181500vn0&auto=0\" width=\"700\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160116/1452928964136410.jpg\" title=\"辞旧迎新\" alt=\"辞旧迎新\" width=\"695\" height=\"335\" style=\"width: 695px; height: 335px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 112, 192);\">视频地址：<a href=\"http://v.qq.com/page/q/n/0/q0181500vn0.html\" _src=\"http://v.qq.com/page/q/n/0/q0181500vn0.html\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">http://v.qq.com/page/q/n/0/q0181500vn0.html</span></a>&nbsp;</span></p>', '1', null, '1451742667', 'mingyu');
INSERT INTO `think_article` VALUES ('515', '17', '励志成长教育饭后运动——散步歌咏<视频>', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://static.video.qq.com/TPout.swf?vid=f01818onode&auto=0\" width=\"680\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p><span style=\"color: rgb(255, 255, 255);\">青少年快乐成长教育，叛逆孩子管教中心，网瘾少年教育学校，问题青少年孩子心理辅导机构，不良习惯孩子转化教育基地，青少年磨练中心，少年行走学校，湖南青少年文武学校，少年孩子少林武术学校，叛逆孩子特殊教育培训基地，网瘾青少年戒网瘾机构，早恋孩子怎么管教，孩子叛逆打父母怎么办，孩子厌学早恋教育中心，心理问题青少年孩子心理教育辅导机构，抑郁孩子辅导中心，叛逆不听话青少年孩子教育学校，特训学校，戒网瘾学校，厌学辍学教育所，叛逆青少年管教所，少年管教所，长沙少年特训营</span></p>', '1', null, '1451819643', 'mingyu');
INSERT INTO `think_article` VALUES ('516', '24', '青少年励志成长教育2016元旦亲子联欢演唱会02', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://static.video.qq.com/TPout.swf?vid=i0181m3aaa9&auto=0\" width=\"680\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160124/1453600283126147.jpeg\" title=\"元旦联欢会\" alt=\"元旦联欢会\" width=\"555\" height=\"247\" style=\"width: 555px; height: 247px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 112, 192);\">视频地址：</span><a href=\"http://v.qq.com/page/i/a/9/i0181m3aaa9.html\" _src=\"http://v.qq.com/page/i/a/9/i0181m3aaa9.html\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">http://v.qq.com/page/i/a/9/i0181m3aaa9.html</span></a><span style=\"color: rgb(0, 112, 192);\">&nbsp;</span></p><p></p>', '1', null, '1451736449', 'mingyu');
INSERT INTO `think_article` VALUES ('517', '22', '安徽池州早恋叛逆少女步入大学', '', 'http://www.841990053.com/case.php', '../Uploads/1484648055.jpg', '安徽特训学校，安徽戒网瘾学校，安徽早恋孩子教育学校，安徽少管所', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><strong>基本信息</strong></span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 周容，女，17岁，来自安徽省池州市，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">于</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">2013</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">年</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">4</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">月</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">21</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">日入校，在校辅导六个月。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 因为父母工作比较繁忙，周容小的时候虽然时常还能在一起生活，但很少有时间沟通交流，导致情感逐渐淡漠。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">上初中三年级的时候，周容结识了一些同性好朋友并通过这好朋友认识一男友后经常逃学一般很少回家，但是父母为了工作养家糊口，对周容疏于管教，使用多少简单粗暴手段，失去了爱的尊严。之后问题出现的却是接连不断，恋爱影响了学习，成绩一落千丈便产生了厌学辍学行为，整天跟着同仁游荡，其父母在无奈之下把她送来了我们青少年励志教育心理辅导校区进行矫正教育。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.对于这种不良的行为问题都是来自一种不良的心理思想，主要还是开展心理辅导，由心理老师一对一进行音乐治疗、内观、森田辅导等多种心理辅导模式进行疏导和引导。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.通过德育教育强化她的不良行为，端正其肢体，培养良好的行为素质，由德育老师以良好的做人做事道德及学习的方式方法。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3.通过生活教育与督导，调整其不良的生活习惯，以规范的生活时钟培养她正确的成长规律，树立良好的生活成长目标。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4.同时开展法制教育、情感教育、亲子教育及感恩励志教育、文化辅导等课程丰富其思想阅历，提升文化素质，健康成长。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">转变效果</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 针对周容问题的情况给其灌输正确的人生观、价值观以及世界观，不像哲学课那样子长篇大论而是从生活到真理。我们倾向于一点点改变学生的不良嗜好而不是强行压制。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">由于她的悟性较高，在与心理老师的探讨中说出自己很多关于自己的想法，也认识到自已思想的片面性，对于恋爱的影响和母亲关系的问题得到了很大的转变。深刻认识到自己的错误，并且积极配合教育辅导和改正，转变效果良好。回家后听从父母的安排，认真学习，于2015年考上了安徽林业科技大学。</span></p>', '1', null, '1452337680', 'mingyu');
INSERT INTO `think_article` VALUES ('518', '41', '{抑郁症心理疏导}江西抚州抑郁症少年走向阳光', '', '', '', '', '', '<p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><br/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 杨志华，男，来自江西抚州，入校年龄15岁半，存在抑郁症、心理症结及社交障碍等问题。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 从小父母离异，属于单亲家庭，让他缺乏父母的关爱和家庭的温暖感，在生活与亲情上更是没有什么优越感，在环境和心理作用下使得患上了抑郁症，心理思想出现了偏差，并至社交问题上也有了障碍。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">由于抑郁症影响一般在情绪上都会有所波动，甚至带动心理认知走向偏差，很容易受外界的影响，特别是不良的或有刺激性的言语会激发他的不想脾气的产生，经常或出现脾气暴躁或者沉默不语的现象。当症状出现的时候，他就会陷入无尽的恐慌之中，如果得不到外界的干预与帮助，就会给他的心理造成极大的负面影响，产生抗拒心理及叛逆行为。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 由于志华存在一定的心理症结，与父母沟通和社交上也出现一些障碍，让父母在家也无法可施，在无奈之下通过咨询朋友后找到了我们青少年励志教育心理辅导校区，时候志华问题得到好转。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">首先，打开心结是我们学校教育辅导的重任，针对性的心理辅导是关键。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">其二，导致问题的产生离不开家庭和父母的关系，在辅导的同时我们也会找出问题的根源，让孩子试着去换位思考，让他学会理解和感恩，懂得理性和宽容。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">其三，由于抑郁导致心理症结和社交障碍，在某种生理上产生不良的新陈代谢，为了打破他的不良习惯，让他在运动中释放自己的情感，发泄内心的压力，活跃思维的同时慢慢消除抑郁症状。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">其四，对于家长方面，我们都会不定时的与家长联系疏通思想，进行孩子在家情况的了解化解问题的探讨交流，寻找最佳的辅导方案。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">其五，在问题辅导顺利的情况下会增加其他成长与学习课程的培养，让他在各个方面同步渐进，共同提升和发现。<br/></span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">转化效果</span></strong></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 经过百日维新的教育辅导，志华心理情况正常，抑郁症状得到化解，走向了阳光交流趋势，能够主动与人交谈。回家后使得家庭和睦幸福了许多，让其父母的身心都有所改善，阳关满面。现在的志华还在文化校园中奋发图强，理想的目标就是南昌大学。在此也祝愿他梦想成真！给父母和自己交上一张圆满的试卷。</span></p>', '1', null, '1448021321', 'mingyu');
INSERT INTO `think_article` VALUES ('519', '21', '益阳叛逆厌学少年的百日励志', '', '', '../Uploads/1453362799.jpg', '益阳教育，青少年励志教育戒网瘾学校', '', '<p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">益阳市叛逆厌学少年管教辅导中心</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 范江南，男，15岁，来自湖南省益阳市赫山区，签订在校辅导六个月协议，毕业至今无不良问题反映。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">长沙益阳青少年叛逆孩子特训学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 江南刚步入初中一年级的时候在学习上比较认真，基本能够跟上老师的上课步伐，成绩名列前茅。但是升上看初中二年级后就出现了转变，上课马虎了事，成绩一降再降，原来是迷上了网络游戏，在家就是上网，影响了作息时间和学习心思。在父母无数次坚持着引导与阻止后上网逐渐减少，但是由于意志力和控制力不理想的他很难断开网络，时不时会到外面上网，厌学情绪不断提升。虽然网瘾不是很严重，当外界干预时还能够主动下机，但是随心所向，学习却不怎么理想，成绩一直在中等线上下徘徊，往下滑的概率总是占上风，让他有了辍学的念想，后来就是时去时不去的，不想上课就会溜去网吧，很晚才会回家，在父母的质问下找出各种借口来掩饰自己的不良事件。眼看就要参加中考升高中，而江南成绩却不见提升，其父母最大的期望眼看就会失去了希望，多次采取拳脚相加的手段却无济于事，无奈下寻求外界的帮助，找到了我们<a href=\"http://www.841990053.com\" target=\"_self\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">青少年励志教育学校</span></a>。</span></p><p><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案与效果</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">湖南益阳叛逆问题孩子青少年戒网瘾学校</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其实，就江南本性而言，并不坏，只是因自身的思想未成熟导致了自控能力较弱，受到了外界环境的影响下出现了思想上的扭曲和行为上的偏差。通过学校专业的心理疏导，让他应学会如何自我调节情绪，在学习又该如何去处理自己的学习观念和网络运用技巧，把控自己的行为。通过亲自教育课程让江南主动与父母联系交谈，畅吐心声，提升了亲子情感，打开了亲子沟通困难问题。以生活教育让他明白现实生活的艰难，理解父母的艰辛和自己成长的不易，学会对父母及她人的理解和宽容，学会换位思考，三思而后行；对读书学习产生新的认识，理解了生活与文化的关系，明白学习的重要性，增强他的学习意识。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 家庭是问题的根源，父母的管教是关键。通过学校咨询了解到家庭情况与江南问题的关系，其父母的管教和关爱上是出现了某些不良效应，通过心理专家对其父母做了些概述和管教改善建议，加上亲子教育的实施，问题得到了好转。现在基本上没有出现亲子情感冲突，在各个方面问题的解决上都学会了冷静和沟通，用和谐的方式处理，让家庭和个人问题尽善尽美。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 现在的江南还在高中继续奋发图强，由于来我校进行了多方面的培养和训练，让他的阅历和能力有着与众不同的优势，在学历制学习环境中可以说是女中豪杰的尖子，当上了班干部和校团干部，获有成就的他不断地积极进取，产生了浓厚的学习兴趣，让他的大学梦不再是可能，而是肯定。</span></p>', '1', null, '1479725259', 'mingyu');
INSERT INTO `think_article` VALUES ('520', '27', '湖北厌学叛逆少女：爸爸，我有话想对您说！', '', '', '', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160123/1453537498323469.jpg\" title=\"湖北厌学叛逆少女给爸爸的一封信\" alt=\"湖北厌学叛逆少女给爸爸的一封信\" width=\"691\" height=\"759\" border=\"0\" vspace=\"0\" style=\"width: 691px; height: 759px;\"/></a></p>', '1', null, '1452943544', 'mingyu');
INSERT INTO `think_article` VALUES ('521', '24', '青少年励志成长教育2016元旦亲子联欢演唱会03', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://static.video.qq.com/TPout.swf?vid=s01822rht5m&auto=0\" width=\"680\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160124/1453600470573140.jpg\" title=\"元旦联欢会\" alt=\"元旦联欢会\" width=\"575\" height=\"253\" style=\"width: 575px; height: 253px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 112, 192);\">视频地址:<a href=\"http://v.qq.com/page/s/5/m/s01822rht5m.html\" _src=\"http://v.qq.com/page/s/5/m/s01822rht5m.html\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">http://v.qq.com/page/s/5/m/s01822rht5m.html</span></a>&nbsp;</span></p>', '1', null, '1451821161', 'mingyu');
INSERT INTO `think_article` VALUES ('522', '17', '青少年行为矫正教育军事素质动作会操', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://static.video.qq.com/TPout.swf?vid=x0182re94tv&auto=0\" width=\"680\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160128/1453984036129116.jpg\" title=\"军事会操军体拳\" alt=\"军事会操军体拳\" width=\"553\" height=\"291\" style=\"width: 553px; height: 291px;\" border=\"0\" vspace=\"0\"/></a></p><p style=\"text-align: center;\"><span style=\"color: rgb(0, 112, 192);\">视频地址：<a href=\"http://v.qq.com/page/x/t/v/x0182re94tv.html\" _src=\"http://v.qq.com/page/x/t/v/x0182re94tv.html\" style=\"color: rgb(0, 112, 192); text-decoration: underline;\"><span style=\"color: rgb(0, 112, 192);\">http://v.qq.com/page/x/t/v/x0182re94tv.html</span></a>&nbsp;</span></p>', '1', null, '1452256489', 'mingyu');
INSERT INTO `think_article` VALUES ('523', '27', '一位中学生给父母写下的十几条建议与要求', '', '', '', '叛逆青少年孩子管教学校，问题青少年励志教育学校', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题孩子的问题，到底是谁的问题？或许不少家长并不知道，其实孩子的问题根源基本都是来自父母和家庭，因为那是孩子的榜样。曾有一位中学生在对父母写下了不少条建议于要求，坦述自己的想法和观点：<br/></span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1、不要我要什么就满足我什么。其实有时我只是想知道我能得寸进尺到什么程度。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2、不要对我大喊大叫。你对我喊叫只会减少我对你的尊敬，同时也教会了我喊叫。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3、不要总是发号施令。假如你向我提出请求，而非发出命令，我会更心甘情愿地去做事。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4、请履行诺言。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">5、不要拿我和任何人比较，如果你认为我好于他人，有人会难过；如果你觉得我不如他人，难过的会是我。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">6、不要一会儿让我做这，一会儿让我做那，使我无所适从。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">7、请让我自力更生，如果万事皆由你替我完成，那么我将来永远也学不会做事。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">8、不要在我面前说谎，也别要我为了你而说谎，这样会使我不再相信你的话。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">9、当你犯了错，请承认错误。你的行为也会教导我承认自己的错误。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">10、当我向你讲述我的问题时，请试着理解并帮助我，爱我就要告诉我。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">11、请象对待朋友一样对待我，虽然我们是一家人，但这并不意味着我们不能成为朋友。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">12、不要让我去做你自己不做的事。我永远不会做你让我做而自己却不做的事情。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">如果您是一位孩子的父母，是否也想到这些问题？当您发现孩子出现了不良的叛逆不听话等问题后时候也思考过引起问题的因素在哪里？其实孩子在成长过程中渴望的不仅仅是关爱和陪伴，更是渴望跟父母做朋友一样的友谊与互助，快乐成长。</span></p>', '1', null, '1453202956', 'mingyu');
INSERT INTO `think_article` VALUES ('524', '29', '新疆家长：建议贵校可以考虑在我们那边建立个分校！', '', '', '', '', '', '<p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 针对于不良问题青少年的管教学校，目前全国各地的教育机构都不太完善，就我孩子问题也咨询了全国各地的，感觉贵校是比较好的一所，不论在教学手段、模式、效果及设施等都是不错的，是值得我认可的励志学校！</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 但是据我所知，当今社会的发展趋势和现况，不良问题青少年孩子逐渐增多，而比较规范的教育学校却比较少，对于这些问题孩子我们也感觉到担忧，毕竟我也是从事教育工作的，但我是心有余而力不足，只能眼睁睁的看着周边的不良问题孩子流浪。目前像这样不良问题孩子在我们那边就存在很多，不少家长对孩子的问题也感到无奈，没办法管教，而又不知像您们励志教育学校的存在，因而也是任由孩子放肆玩耍，没人管教。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 我发现我孩子才来贵校不久就改变了很多，特别是对我们的态度和对问题的看法上有着很大的转变，虽然来的时间没多久，但是我们看到了希望，但是我更大的希望是能够帮助更多的孩子和家庭。虽然我也是从事教育工作这么多年，但是服务的问题和方向不一样，对于这样不良问题孩子我也表示无奈，因为我自己孩子也是出现了不良问题，而我却没能管教好，或许这也是我做为一位母亲和教育工作者的失败吧。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 在对我孩子问题的观察发现，其实我孩子问题受到外界环境影响下导致的，因为他所在的学校和外面结交的朋友都是存在类似的不良问题；并且在跟我孩子交流中得知，我孩子所在的班级大部分学生都是存在相似不良问题，导致了我孩子自控能力较弱和意志力不强就身受影响，被同学带坏的。我孩子本质都比较好，而且从小我们对他的管教也比较严，在各个方面都表现都是比较好的，只是近期出现了偏差，我们也在寻找解决办法，但是取不到较好的效果，因而找到了您们学校，请求到了您们的帮助，发现孩子也在逐渐改变过程中，在此忠心感谢贵校的全体老师！您们辛苦了！</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 看到了孩子的希望的同时我想让您们能够帮助更多的孩子和家庭，让爱心扩散，让问题消失！我想建议贵校能否考虑在我们那建立个青少年励志教育分校，帮助我们那偏远的孩子个家庭！让我孩子回去后都能够接触到好了伙伴，好的朋友，让他一起快乐地成长。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 虽然我们那边有点偏远，但是如今人口也比较多，再加上西部地区正在开发崛起，像这样的不良问题孩子也是比较多的，而却没有几所好的学校来管教他们，该有的教育而他们却没有，原本幸福的家庭却被这些不良问题青少年孩子毁掉了，感觉到可怜，我想帮助他们，更多的是需要您们的帮助，您们是最伟大的，最有能力帮助他们的！在此也希望贵校可以考虑在我们那边或者外地设立分校，帮助全国各地的孩子和家庭，建造和谐社会和强大的国家！谢谢！！！</span></p>', '1', null, '1447473802', 'mingyu');
INSERT INTO `think_article` VALUES ('525', '24', '我青少年励志教育学校与长沙六艺学堂开展文学教育合作', '', '', '', '', '', '<p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 2016年1月31日上午10时许，湖南青少年励志教育学校心理辅导校区与长沙市六艺学堂（文化辅导学校）合作会议在我励志教育学校举行，在会议中双方为青少年教育问题上进行了讨论，对于教育教学问题进行了协商，为了让我们的教育事业做大做强，为了能够更好的服务于青少年孩子的教育需求，让每一位孩子都拥有成功的希望，最终双方达成共识——进行教育教学合作。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 于1月31日上午11时许，湖南青少年励志教育学校与长沙市六艺学堂的授牌仪式隆重举行，双方教育教学合作关系正式成立。即日起长沙六艺学堂将设立为湖南青少年励志教育学校文化辅导校区，我励志教育学校将设立为长沙六艺学堂的励志校区，教育教学合作正式启动。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 出席本次活动的有湖南青少年里教育学校校主任李铮主任、励志教育心理辅导校区校长何新国校长、励志教育学校招生主任潘洪登主任、励志教育学校法制教育崔成老师、励志教育心理辅导校区心理辅导主任米星华主任、励志教育想心理辅导校区心理老师黄满老师，励志教育学校心理辅导校区心理老师邓玉春老师、长沙六艺学堂董事长胡滔先生、六艺学堂青山校区彭稳女士、博才校区何蓓女士、广益校区严冲先生任、黄威先生、长沙音子艺校唐浩先生等。</span></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com\" target=\"_self\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><img src=\"/ueditor/php/upload/image/20160131/1454246875457599.jpg\" title=\"教育合作\" alt=\"教育合作\" width=\"328\" height=\"184\" style=\"width: 328px; height: 184px;\" border=\"0\" vspace=\"0\"/></span></a><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><a href=\"http://www.841990053.com/show.php?cid=24&id=525\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160131/1454246902397613.jpg\" title=\"合作授牌\" alt=\"合作授牌\" width=\"320\" height=\"183\" style=\"width: 320px; height: 183px;\" border=\"0\" vspace=\"0\"/></a></span></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=525\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160131/1454246948231123.jpg\" title=\"教育合作成立\" alt=\"教育合作成立\" width=\"339\" height=\"184\" style=\"width: 339px; height: 184px;\" border=\"0\" vspace=\"0\"/></a><a href=\"http://www.841990053.com\" target=\"_self\"><img src=\"/ueditor/php/upload/image/20160131/1454247048914511.jpg\" title=\"教育教学合作授牌仪式\" alt=\"教育教学合作授牌仪式\" width=\"309\" height=\"183\" style=\"width: 309px; height: 183px;\" border=\"0\" vspace=\"0\"/></a></p>', '1', null, '1454246272', 'mingyu');
