INSERT INTO `think_article` VALUES ('777', '62', '学生学习种菜浇水-叛逆厌学孩子矫正学校不仅仅是文化教育', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU1MjI5NzMzNg==/v.swf\" width=\"630\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p>', '1', null, '1524232406', 'mingyu');
INSERT INTO `think_article` VALUES ('778', '62', '让现实的改变迷途少年---摆脱虚拟事物，体验生活的辛酸，理解父母的不易', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU1MjQ3NjA3Mg==/v.swf\" width=\"600\" height=\"510\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">当今社会，很多孩子为何沉迷于虚拟的网络事物中无法自拔？答案很明显 想想十几年之前为何没有这样的情况，正是电子网络发展带来的利弊，也是生活水平提高与生活观念影响。电子网络发展是必然的，生活水平提高也是必然，但是生活观念的影响是关键，特别是父母。他们觉得自己有能力给予孩子幸福，觉得不应该让孩子和下一代吃苦，结果孩子的毕生精力倾向了虚拟的网络，从而逐渐与现实脱离。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 在这里，我们给孩子找来现实、、、</span></p>', '1', null, '1524272147', 'mingyu');
INSERT INTO `think_article` VALUES ('779', '57', '英语口语交流练习---特训学校是不一样的学习与互动平台', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU1MjUxMjIwNA==/v.swf\" width=\"610\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p>', '1', null, '1524373683', 'mingyu');
INSERT INTO `think_article` VALUES ('780', '57', '感恩教育——敬老院献爱心', '', '', '', '', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180423/1524472902758822.jpg\" style=\"width: 371px; height: 372px;\" title=\"感恩励志教育\" width=\"371\" height=\"372\" border=\"0\" vspace=\"0\" alt=\"感恩励志教育\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180423/1524472902125923.jpg\" style=\"width: 490px; height: 318px;\" title=\"感恩教育\" width=\"490\" height=\"318\" border=\"0\" vspace=\"0\" alt=\"感恩教育\"/></p><p><br/></p>', '1', null, '1514018642', 'mingyu');
INSERT INTO `think_article` VALUES ('781', '3', '快乐的冬天——打雪仗', '', '', '../Uploads/1524734454.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU2NTk5ODQzNg==/v.swf\" width=\"620\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p>', '1', null, '1519636816', 'mingyu');
INSERT INTO `think_article` VALUES ('782', '57', '美术特长指导---兴趣是厌学孩子的最佳成长道路', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU2ODY3NTY4OA==/v.swf\" width=\"610\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">兴趣是孩子成长的最佳导师。特别是厌学孩子，他们厌学是因为找不到学习的方式方法而缺乏信心，从而失去了学习的兴趣，看不到希望而自暴自弃。在这里，我们励志学校为孩子找回兴趣，重塑人生。</span></p>', '1', null, '1524819172', 'mingyu');
INSERT INTO `think_article` VALUES ('783', '27', '江西南昌家长给在特训学校女儿的沟通信', '', '', '', '', '', '<p><br/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180430/1525070904114774.jpg\" style=\"width: 465px; height: 628px;\" title=\"江西特训学校\" width=\"465\" height=\"628\" border=\"0\" vspace=\"0\" alt=\"江西特训学校\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180430/1525070980990591.jpg\" title=\"江西励志学校\" alt=\"江西励志学校\" width=\"464\" height=\"274\" style=\"width: 464px; height: 274px;\" border=\"0\" vspace=\"0\"/></p><p><br/></p>', '1', null, '1525070961', 'mingyu');
INSERT INTO `think_article` VALUES ('784', '57', '快乐是孩子成长的动力---新年零食交流大会---特训学校让孩子在快乐中成长', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU4NTIxMzU4MA==/v.swf\" width=\"550\" height=\"620\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">孩子的教育不仅仅是在教室里，只要是能促进孩子成长的都是教育，而且是无处不在的。生活的点点滴滴都是在影响着孩子的现状，而我们选择的是好的影响。在我们特训学校里，每一句话，每一个动作，每一个角落都是充满着教育，让孩子立体化空间中健康成长。</span></p>', '1', null, '1525487811', 'mingyu');
INSERT INTO `think_article` VALUES ('785', '57', '叛逆孩子成长学校-2018年三十全体师生校园观赏烟花', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU5MDA5NjMzMg==/v.swf\" width=\"620\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">一个非比寻常的春节，是对于这些叛逆网瘾厌学亲情淡漠等不良问题青少年孩子，他们从来没有像这样在外面过年。我校是属于特殊教育培训学校，专业管教问题青少年，我们没有固定的假期和入学时间，所以过年也不例外。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 我们虽然没有假期，但是我们一样可以休息和娱乐，甚至过年过节。今年的春节有着不少孩子和老师在学校过年，师生同吃同住同娱乐，我们就像一家人。</span></p>', '1', null, '1525679675', 'mingyu');
INSERT INTO `think_article` VALUES ('786', '3', '特训学校的真实生活---饮食', '', '', '../Uploads/1525763753.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU5MTYxMTc1Ng==/v.swf\" width=\"640\" height=\"560\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 学校是一个大家庭，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">这里的师生同吃同住二十四小时在一起，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">他们的生活与学习都是携手并进，同甘共苦。吃饭是人类活动的头等大事，而且是一日三餐按时完成，特别是处于成长发育阶段的青少年孩子，他们需要及时补充充足的营养物质来维持身体成长所需的能量，此视频既是问题青少年特训学校里的孩子正在进行午餐。</span></p>', '1', null, '1525762457', 'mingyu');
INSERT INTO `think_article` VALUES ('787', '57', '问题孩子管教心理辅导学校课外放松活动---手语操', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU5NDc4Mjg4NA==/v.swf\" width=\"650\" height=\"560\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <font color=\"#0070c0\"><span style=\"font-size: 18px;\">压力无处不在，生活与学习亦如此。人生不是为了到达终点，而是享受路途的风景。在高压的生活与学习路上，我们应该学会停下脚步解解压，放松一下或许能更好的体会到其中的乐趣。</span></font></p><p><font color=\"#0070c0\"><span style=\"font-size: 18px;\">&nbsp; 手语操是我们问题青少年管教辅导学校给予学生解压放松的活动之一，内容包含了音乐、舞蹈、表演、歌唱、运动等等，不仅仅可以解压放松，而且可以使人心情愉悦和强身健体。</span></font></p>', '1', null, '1525853557', 'mingyu');
INSERT INTO `think_article` VALUES ('788', '3', '长沙青少年素质品德教育培训学校圣诞节', '', '', '../Uploads/1525923600.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU5NjQ5MTUzMg==/v.swf\" width=\"620\" height=\"560\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 快乐是成长的潜力，励志是成长的动机，学习是成长的基础，实践是成长的道路。</span><br/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 圣诞节虽然是西方节日，但是我们一样可以学习，可以参与，重在娱乐。我们特殊教育学校也就借节日之名为学生谋取快乐，助其成长。活动是鼓励学生自导自演，不仅可以锻炼他们的组织能力，还可以增强他们的团结意识，也可以锻炼他们的口才与交际能力和表达表演能力。</span></p>', '1', null, '1525923600', 'mingyu');
INSERT INTO `think_article` VALUES ('789', '11', '孩子早恋怎么办？鼓励与支持也变相的教育', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzYwNTk2NzUyNA==/v.swf\" width=\"620\" height=\"560\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">孩子恋爱是早晚之事，我们不要为了期望而阻止孩子的人生。</span></p>', '1', null, '1526309903', 'mingyu');
INSERT INTO `think_article` VALUES ('790', '3', '宣泄室---叛逆孩子发泄空间', '', '', '../Uploads/1526459346.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzYwOTIzOTk5Mg==/v.swf\" width=\"610\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp;&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">宣泄室是我校专门为叛逆、暴力倾向、脾气暴躁、打架斗殴等等问题比较严重的孩子设立的发泄空间，让他们自行发泄完了我们再进行针对性的心理思想辅导。针对这类孩子我们不能正面跟他们对打，也不能让他们伤害别人，因此在确保安全情况下设立了宣泄室给他们解决暴力行为，从而获得更好的教育效果。</span></p>', '1', null, '1526458616', 'mingyu');
INSERT INTO `think_article` VALUES ('791', '3', '校园生活---零食共享', '', '', '../Uploads/1527487592.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzYxNjQwNTIzNg==/v.swf\" width=\"610\" height=\"556\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"color: rgb(0, 112, 192); font-size: 18px;\">零食是少年孩子生活中最有吸引力的东西，也是他们日常生活中最常见的食品。甚至不少孩子把零食当作主食，而且是随时随地没有规律的，导致影响了正常的饮食规律。众所周知，零食中大部分都是加工食品，附有不少添加品，长期食用对身体是有一定影响的。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 我们都是为了孩子的健康成长，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">因此，对于孩子的饮食需要良好的监督。我校为了纠正孩子的不良习惯，零食只建议家长购买健康食品，送给孩子需由学校统一保管，并且按规定时间统一食用。</span></p>', '1', null, '1527487592', 'mingyu');
INSERT INTO `think_article` VALUES ('792', '3', '叛逆少年学校-零食共享2', '', '', '../Uploads/1527585895.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzYzMzA3NTIyNA==/v.swf\" width=\"610\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\"> 教育可以通过饮食进行，让他们学会团结，懂得互助，学会宽容，懂得感恩。</span></p>', '1', null, '1527585461', 'mingyu');
INSERT INTO `think_article` VALUES ('793', '24', '问题少年学校让叛逆厌学孩子体验劳动与收获的辛酸', '', '', '', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=793\" target=\"_self\" title=\"问题少年学校\"><img src=\"/ueditor/php/upload/image/20180530/1527670788802973.jpg\" style=\"width: 541px; height: 347px;\" title=\"问题少年学校\" width=\"541\" height=\"347\" border=\"0\" vspace=\"0\" alt=\"问题少年学校\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/news.php\" target=\"_self\" title=\"叛逆少年矫正学校\"><img src=\"/ueditor/php/upload/image/20180530/1527670790475501.jpg\" style=\"width: 542px; height: 348px;\" title=\"厌学孩子学校\" width=\"542\" height=\"348\" border=\"0\" vspace=\"0\" alt=\"厌学孩子学校\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.841990053.com/index.php\" target=\"_self\" title=\"厌学孩子矫正学校\"><img src=\"/ueditor/php/upload/image/20180530/1527670792162143.jpg\" style=\"width: 543px; height: 369px;\" title=\"叛逆少年矫正学校\" width=\"543\" height=\"369\" border=\"0\" vspace=\"0\" alt=\"叛逆少年矫正学校\"/></a></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 时代虽然在进步，各种高科技也在不断的发展，但人类却离不开劳动，这就是现实。我们都知道“天下没有免费的午餐”，我们所想要的基本上是没有现成的。</span><br/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 然而如今的孩子却逐渐懒惰，过着衣来伸手饭来张口的幸福生活，不知道现实残酷，劳动的艰辛，生活的不易，甚至还不理解父母的用心良苦，不论黑白对错但是随着自己的心情应付，甚至直接排斥和逆反，跟父母对着干。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 我们为了更好的矫正这类孩子的不良心理和行为问题，根据他们问题情况给予针对性辅导，从生活中的点点滴滴学起做起。心理、思想、文化、兴趣、特长、技能、劳动等等全方位开课，让他们在这里明辨一下酸甜苦辣咸来领悟生活与人生。</span></p>', '1', null, '1527671349', 'mingyu');
INSERT INTO `think_article` VALUES ('794', '27', '株洲家长给在问题少年教育学校里的女儿的沟通信', '', '', '', '叛逆问题孩子教育学校', '专业改变不良问题青少年孩子的特训学校，联系电话188 74250 661 叛逆孩子教育戒网瘾学校，正规网瘾厌学少年全封闭军事化管理学校，最好的厌学早恋青少年心理健康辅导学校，学生励志素质教育学校，调皮的男孩女孩管教中心，孩子不良问题矫正机构青少年管教所，湖南北海江西武汉长沙永州衡阳邵东娄底常德张家界怀化郴州安徽河南岳阳广东福建江苏南昌山东', '<p><br/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">亲爱的女儿</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 您好！</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 你写给爸妈的信妈妈都看到了，妈妈每天都想回信给你，可每次提笔都不知道从何说起，因妈妈有太多太多的话想和我的女儿说，今天是母亲节妈妈又看到了老师发来你写给妈妈的信和你的照片视频，妈妈看了很感动和欣慰！其实老师二三天就把你的照片视频发来给妈妈看一次，妈妈就把你的照片视频收藏起来，也转发给你哥哥看，还久不久把你的照片和视频给家里亲人看看，免得大家担心你，妈妈还把你的视频上传到电脑上，我要保存好视频，让视频见证你每天的变化，让你也为自己去励志学校后的脱变而感到幸运。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 女儿，你知道吗？其实能去励志学校的同学都是幸运的，证明每个同学都是父母的心肝宝贝，只是开始时你们不理解父母罢了，父母把你们送去励志学校只是想好好保护你们，让你们顺利度过少年时代的叛逆期，不想让外面的坏风气影响到你们，不想让你们在美好的年华留下任何遗憾。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 女儿，其实妈妈也很想你，开始时妈妈想你想得好难受，老师说让你和我们都冷静一段时间，让你好好反省一下自己，认清自己身上的不良问题并改掉，让我们过一段时间过来看你。之后在老师发来你的照片视频中见你逐渐适应和改变，妈妈也决定让自己日子过得充实一点进步一点，所以妈妈每天都会早早起来买菜在家煮粥和爸爸吃了再去上班，晚上不管多晚回家都煮夜宵和爸爸吃，我想学炒好菜，等女儿回家时我能炒好吃的菜给女儿吃，让你能感觉到家的温暖。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 前几天有一段新闻说一个刚毕业出来工作一年半的空姐深夜乘滴滴车给变态的司机奸杀了，你看看一个二十一岁的空姐在社会上都被坏人害死了，更何况你一个十三岁的未成年人，就像上次你去东莞你不知道妈妈有多担心，你还小不知道外面的社会有多复杂，外面有些坏人专门在寻找迷途的没有父母保护的少年，他们会恐吓威逼那些骗来的未成年人，打断手脚去乞讨，可把少女卖到深山给没有老婆的老男人做生育工具，可软禁到地下室做坏事帮他们赚钱，那些坏人不会理别人死活的，很多失足少年就是这样毁了自己的人生。那时你去东莞妈妈感觉就像世界末日一样，因你还小在外面根本生存不了，怕遇到了坏人，到那时叫天天不应，叫地地不灵，到你明白后悔时已经迟了。好在妈妈和哥哥对你的放弃，前后只三天就去东莞把你接了回来，还没铸成什么错。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 女儿，妈妈买了零食和信一起快递过去给你，牛奶这快递是不允许寄的，所以妈妈就拿回了家，女儿，你要明白妈妈不是很有钱才给你买零食，你以前买的零食妈妈从不舍得买给自己吃过，因妈妈爱你和爱家人，妈妈愿意把最好的给你们，希望你和家人生活得幸福、快乐！</span></p><p style=\"text-align: right;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 爱的爸爸妈妈</span></p><p style=\"text-align: right;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2018.5.15</span></p><p><span style=\"color: rgb(112, 48, 160);\">叛逆厌学少年管教辅导学校》微电：18874250661 改变一个孩子，成就一个家庭。</span></p>', '1', null, '1529141906', 'mingyu');
INSERT INTO `think_article` VALUES ('795', '24', '欢乐端午节--叛逆孩子在学校过一个特殊的端午节', '', '', '', '', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180618/1529317394109894.jpg\" title=\"叛逆孩子学校端午节\" width=\"507\" height=\"282\" style=\"width: 507px; height: 282px;\" border=\"0\" vspace=\"0\" alt=\"叛逆孩子学校端午节\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180618/1529317394688852.jpg\" style=\"width: 507px; height: 353px;\" title=\"问题青少年教育学校\" width=\"507\" height=\"353\" border=\"0\" vspace=\"0\" alt=\"问题青少年教育学校\"/></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180618/1529317394999757.jpg\" style=\"width: 507px; height: 379px;\" title=\"青少年戒网瘾学校\" width=\"507\" height=\"379\" border=\"0\" vspace=\"0\" alt=\"青少年戒网瘾学校\"/></p><p>&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\"> 今天又是一年一度的端午节，或许不少孩子不知道端午节的来历与意义，但是通过节日的洗礼后会不断给他们积累节日感念。比如昨天的父亲节和今天的端午节，我们问题青少年教育学校让学生自行组织举办了节日的宣读、讲解和表演等活动，又一次给他们敲响节日意识的警钟，他们会在有意无意中领悟并了解了节日与生活息息相关，他们也会在今后的日子里不断回忆与追求，把将来的生活和节日过得越来越好。</span></p>', '1', null, '1529313568', 'mingyu');
INSERT INTO `think_article` VALUES ('796', '26', '叛逆少年透露了心里话——叛逆出走的原因是父亲与我的沟通越来越少了', '', '', '', '', '', '<p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180619/1529401290425670.jpg\" title=\"叛逆少年心声\" alt=\"叛逆少年心声\" width=\"384\" height=\"540\" style=\"width: 384px; height: 540px;\" border=\"0\" vspace=\"0\"/></p><p>&nbsp;<span style=\"color: rgb(0, 112, 192);\"> 感恩父亲，他用那厚实的肩膀撑起了我们的家，虽然以后这个家没有母亲的陪伴，我可能不会过得那么好，但是父亲给予我的关爱远远超过母亲的，所以我异常的感谢父亲。</span></p><p><span style=\"color: rgb(0, 112, 192);\">&nbsp; 母亲是半年前离开的，这个不幸的消息，可谓是对于我这样的小家庭是晴天霹雳，父亲的精神接近崩溃，而我成绩一落千丈，每天要么不是打架闹事，就是整天泡吧，抽烟喝酒更是不学就会。父亲过了半个月，经过家人的劝阻与激励，重新振作却整日不见我人影，沟通问同学朋友，在酒吧门口找到了我，当时的您，第一眼看到我就哭了，这是我这么大第一次看到您哭，您诉苦着求我回家，可是我当时的头脑完全被酒精洗刷了，不管什么亲情友情都置之不理，并很快逃离了您的视线，过了几天我因为酒精的作用引秀心脏病突发，当时人就倒在了地上，神志不清，听朋友说您当时急的来的路都不会走了，我在急救室抢救了两个小时才没有危险，当时我感受到有一个人他一直拿毛巾擦拭我的脸、手，盖被子，拿棉签沾水在我干裂的嘴唇上涂着，第二天我醒了，看着您靠在椅子上，头椅在手上，摇摇欲坠，我突然心一下子酸了，眼睛湿润了，想起了自己以前的那些过错。您可能感觉到我醒了，您就醒了，摸了摸我的脸，满脸担心的问我：“还有什么不舒服的吗？”我摇了摇头，“对不起，爸爸”，我内疚的说。后来父亲就接我出院了，我也回到了学校完成学业，不久后我又一次的跑了出去，原因就是父亲与我的沟通越来越少了。</span></p>', '1', null, '1529401349', 'mingyu');
