DROP TABLE IF EXISTS `think_article`;
CREATE TABLE `think_article` (
  `id` int(8) NOT NULL AUTO_INCREMENT,
  `kinds` int(8) NOT NULL,
  `title` char(200) DEFAULT NULL,
  `retitle` char(100) DEFAULT NULL,
  `url` varchar(200) DEFAULT NULL,
  `pic` varchar(200) DEFAULT NULL,
  `key` char(200) DEFAULT NULL,
  `description` varchar(400) DEFAULT NULL,
  `contents` varchar(8000) DEFAULT NULL,
  `status` tinyint(1) DEFAULT NULL,
  `hits` int(10) NOT NULL,
  `time` int(11) DEFAULT NULL,
  `write` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `kinds` (`kinds`)
) ENGINE=InnoDB AUTO_INCREMENT=404 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of think_article
-- ----------------------------
INSERT INTO `think_article` VALUES ('1', '22', '余老师》行为心理素质辅导员', '余老师》行为心理素质辅导员', '', './Uploads/57e12fd6c24a2.jpg', '', '', '<p style=\"white-space: normal; text-align: center;\"><img src=\"/uploads/image/20160920/1474375584932713.jpg\" title=\"1464151066127232.jpg\" alt=\"余学军.jpg\" width=\"224\" height=\"251\" style=\"width: 224px; height: 251px;\"/></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">余老师</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 行为心理素质辅导员，国家三级心理咨询师，来自湖南省长沙市望城区雷锋镇，毕<span style=\"font-size: 16px;\">业于湖南司法警官学院。曾从事多年的青少年心理健康教育工作，接触过丰富的青少年问题案例，具有丰富家庭教育指导经验和丰富的青少年心理健康辅导和行为素质训练。</span></span></p>', '1', '0', '1474375642', '教育服务中心');
INSERT INTO `think_article` VALUES ('2', '22', '石老师》德育教育辅导员', '石老师》德育教育辅导员', '', './Uploads/57e232feae4b1.jpg', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><a href=\"http://www.841990053.com/\" target=\"_self\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(51, 51, 51); text-decoration: none; background: transparent;\"><img src=\"/uploads/image/20160921/1474441956199968.jpg\" title=\"石辅导员\" alt=\"石辅导员\" width=\"319\" height=\"380\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 380px; width: 319px;\"/></a></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">石 文</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; &nbsp; &nbsp;德育教育辅导员。曾服役于中国人民解放军陆军四十八集团军，在服役期间曾获军事演习中因表现突出荣获三等功一次并被评选为“优秀士兵”。退役后在教育行业工作多年并充分利用业余时间参与教学心理学习和音乐培训，获有心理咨询师资格证，对青少年心理动向有着独特的教育理念和心理战备，轻松解决学生心理问题。现任湖南青少年励志教育学校德育教育辅导员。</span></p>', '1', '0', '1434870725', '戒网瘾学校');
INSERT INTO `think_article` VALUES ('3', '22', '赵老师》生活辅导师', '赵老师》生活辅导师', '', './Uploads/57e232a3564b5.png', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"/uploads/image/20160921/1474441862810933.jpg\" title=\"赵老师\" alt=\"赵老师\" width=\"255\" height=\"310\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 310px; width: 255px;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\"></span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">生活教育辅导师</span><br style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 国家注册二级烹饪师,来自湖南岳阳，毕业于湖南长沙岳麓厨师学校烹饪专业。从事饮食行业三十多年，有着丰富的生活经验，从健康饮食到美味佳肴无所不知，有着独特的“饮食文化”。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 为了满足每一位孩子的健康成长和营养需求，学校聘请赵老师来校担任学校厨师及学生的生活教育辅导一职。独特的厨艺让师生赞叹不已，耐心的教导让学生领悟到了生活的奥妙。</span></p>', '1', '0', '1461136237', '叛逆孩子教育学校');
INSERT INTO `think_article` VALUES ('4', '22', '陶老师》德育兼法制教育', '陶老师》德育兼法制教育', '', './Uploads/57e23248584bd.jpg', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 14px; line-height: 22px; vertical-align: baseline; color: rgb(51, 51, 51);\"><img src=\"/uploads/image/20160921/1474441777520555.jpg\" title=\"德育老师\" alt=\"德育老师\" width=\"259\" height=\"317\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 317px; width: 259px;\"/></span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: 22px; vertical-align: baseline; color: rgb(0, 112, 192);\">陶伟（德育兼法制教育)</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 14px; line-height: 22px; vertical-align: baseline; color: rgb(51, 51, 51);\">&nbsp;&nbsp;</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">毕业于湖南司法警官学校法学专业,</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: 22px; vertical-align: baseline; color: rgb(0, 112, 192);\">能充分发挥军事训练磨练意志、锻炼体魄、矫正不良行为习惯的教育功能，坚持“训练和思想教育相结合，严肃要求和真诚关心相结合”的原则，通过军事训练，让学生体验军人的威严和伟大，使学生的身、心、灵都深受影响。</span></p>', '1', '0', '1437376139', '叛逆孩子教育学校');
INSERT INTO `think_article` VALUES ('5', '22', '梁老师》心理文化辅导', '梁老师》心理文化辅导', '', './Uploads/57e231f60705b.jpg', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"/uploads/image/20160921/1474441697879397.jpg\" title=\"心理辅导老师\" alt=\"心理辅导老师\" width=\"238\" height=\"283\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 283px; width: 238px;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: 25px; font-family: Simsun; vertical-align: baseline; background-color: rgb(242, 242, 242);\"></span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">梁琳</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 心理老师兼文化辅导。毕业于长沙民政学院心理系教育心理学专业，对青少年心理特点、辅导技术有着自己个性化的辅导教育技巧。擅长团体辅导，其自创的“我们一起握握手”“亲子沟通座椅”“校园寻宝&quot;等团体辅导游戏激发青少年心理能量，培养团队归属感、团队意识协作精神等方面有明显效果；她综合运用宣泄疗法体验心理疗法、认知疗法等引导青少年自我成长。</span></p>', '1', '0', '1443337664', '教育服务中心');
INSERT INTO `think_article` VALUES ('6', '22', '袁老师》教育辅导专家', '袁老师》教育辅导专家', '', './Uploads/57e231a8222da.png', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"/uploads/image/20160921/1474441613188479.png\" title=\"1432727404459321.png\" alt=\"06.png\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: auto;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">袁江鑫</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp;&nbsp;国家二级心理咨询师，是永州市某中学退休教师，从事教育工作几十余年，2002年开始心理科目进行学习研究，2006年正式从事心理咨询和辅导工作。曾在学校展开学生的考试前心理疏导工作，得到了学校师生的认可，几年间完成了心理疏导案例上千余人。曾多次接受即将毕业生家长邀请，到各地进行上门服务，针对毕业生考前的压力做出相应的心理辅导工作。退休后被我校聘请加入青少年励志教育心理专家组开展针对青少年问题的心理辅导工作，拯救了诸多孩子与家庭。</span></p>', '1', '0', '1421823967', '教育服务中心');
INSERT INTO `think_article` VALUES ('7', '22', '赵老师》心理、特长辅导', '赵老师》心理、特长辅导', '', './Uploads/57e23139dd9f3.png', '服务优势', '服务优势', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"/uploads/image/20160921/1474441479439526.png\" title=\"1432735548893468.png\" alt=\"07.png\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: auto;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">赵老师</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; 心理老师兼特长辅导。湖南师范大学心理学专业毕业，获有国家注册教师资格证和心理咨询师资格证；获有广州艾芮菲美容化妆职业培训学校美容化妆培训职业资格证等。曾有丰富的全日制中学教学和美容化妆培训教学经验，辅导教育方法独特而富有幽默风趣，让学员学而不厌，教而不烦；能够快速激发学员学习兴趣，释放出深藏的潜能。</span></p>', '1', '0', '1431327858', '励志教育学校');
INSERT INTO `think_article` VALUES ('8', '22', '黄老师》德育心理辅导', '黄老师》德育心理辅导', '', './Uploads/57e230e084d79.jpg', '心理辅导中心', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"/uploads/image/20160921/1474441370106060.jpg\" title=\"黄老师\" alt=\"黄老师\" width=\"272\" height=\"301\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 301px; width: 272px;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192); font-family: inherit; font-size: 18px; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; text-indent: 2em;\">黄 满</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; 德育兼心理老师，</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">曾于中国人民解放军河南军区75223部队教导大队服役四年，四级士官，大专学历，中共党员。 期间，多次获得“优秀班长”、“十佳带兵骨干”等荣誉称号。</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">于老师军事素质过硬，带训经验丰富。动作指导，耐心细致；奖惩分明，严守原则。经过他训练的学生，纪律性、荣誉感都较强，具有良好的团队合作意识。</span></p>', '1', '0', '1429599670', '青少年励志教育学校');
