INSERT INTO `think_article` VALUES ('156', '22', '李老师》心理文化辅导师', '', '', './uploads/57e274b2cbee2.jpg', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"/uploads/image/20160921/1474458763987321.jpg\" title=\"李老师\" alt=\"李老师\" width=\"244\" height=\"299\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 299px; width: 244px;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;<span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">李 洁</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; 心理辅导师。毕业于湖南省衡阳师范学院教育心理学专业，获有教师资格证和心理咨询师资格证。勤奋好学的她通过自己的不懈努力，在校期间曾担任学院心理学会会长，积极组织会员开展校内外青少年的心理辅导工作，帮助有需要的青少年孩子，不断地提升自己的专业知识和心理辅导水平，对于青少年心理健康辅导有独特的见解，不拘泥于某种心理流派，而倡导以孩子为中心来进行心理健康辅导。</span></p>', '1', '0', '1442058722', '叛逆孩子心理辅导学校');
INSERT INTO `think_article` VALUES ('157', '22', '曹老师》心理文化辅导师', '', '', './uploads/57e2754284425.jpg', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><a href=\"http://www.841990053.com/\" target=\"_self\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(51, 51, 51); text-decoration: none; background: transparent;\"><img src=\"/uploads/image/20160921/1474458893117429.jpg\" title=\"曹老师\" alt=\"曹老师\" width=\"281\" height=\"344\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 344px; width: 281px;\"/></a></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">曹 慧</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; 心理文化辅导师。毕业于湖南长沙民政学院心理学院的心理应用学专业，获有注册心理咨询师资格证书和中级教师资格证书。在校期间曾加入爱心校园心理辅导社团，参与爱心公益性心理辅导工作，帮助许多存有心理问题的青少年，并建有“青少年心理咨询团”数千人的QQ群，接受社会各界人士的心理咨询。对于青少年的心理问题</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">有着丰富的心理咨询与辅导经验，掌握了青少年心理发展动向和阶段性问题特征。她为人谦虚善良，热爱于教育与服务事业，富有爱心和耐心，应对不同心理问题的学生有着极强的应变能力，并能果断用各种不同的辅导手法做好学生的心理工作。</span></p>', '1', '0', '1443182037', '心理教育学校');
INSERT INTO `think_article` VALUES ('158', '35', '90后游戏瘾少年8天砸车16辆只偷到100元', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 一名97年出生的“网虫”白天吃住在网吧里，凌晨出来找活干，干的什么“活”呢?砸车偷盗车内物品。只可惜他不“走运”，连续8天先后砸了16辆停放在小区里的车子，只偷走了百元。扬子晚报记者昨天从江宁区人民检察院获悉，该院以盗窃罪对嫌疑人刘某起诉，上月底，江宁区人民法院判处刘某1年零1个月有期徒刑。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; “什么人这么缺德，车子在小区里停着，副驾驶座位的玻璃窗被砸碎了!”今年2月初，家住江宁区天地新城小区的王女士一大早起来准备上班时，看见自己停放在小区的车子车窗玻璃被砸碎了，刚好她停车的地方没有监控。而驾驶室的储物柜被人翻过了，里面只放了10元零钱，还是上次收费站找零的，钱没损失多少，可没有专门保玻璃破碎险，这车窗被砸，保险理赔起来还真挺麻烦。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 接连一个多星期，类似王女士这样的情况，在南京市江宁区不少小区都有发生，这些车主的车内物品损失都不多，最多的也只有40元，少则一包南京烟，但副驾驶座位旁的车窗玻璃被砸，着实让车主们感到头疼。什么人干的呢?由于案 发的大多为老小区，被砸车辆不是处于监控死角，就是因为监控设备老旧成摆设而找不到有力线索。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 2月11日凌晨，随着“哐当”一声，江宁区一处会所门口的一辆车子车窗玻璃再次被砸，会所门口的监控探头终于“看见”了这个嫌疑人，只见他在车子里翻动了一阵后，很快离去，走到了街对面不远处的一个网吧里。事发后警方根据这条监控截屏排查，终于找到了整日吃住在网吧的刘某。警方调查得知，刘某1997年出生，中专文化，从苏北来宁，是无业人员，民警在网吧里找到他后，他对砸车偷盗车内物品的事情供认不讳，并主动交代，因为没钱，他就动歪脑筋砸车偷取车内物品碰运气，他从网上买了锤子、电筒等作案工具，每天凌晨2点左右出动。2月3日至11日，他连续在江宁区的十来家小区先后砸了16辆车子，偷盗储物柜内物品，可都“运气不好”，偷来的大多15元、20元，最多的一次也就40元，还有不少次是无功而返。据警方调查，刘某砸这么多车偷盗加起来总案值也就100元。“最后一次在会所门口砸车被监控拍到，其实那次车内真的啥值钱的东西都没有。”刘某悔恨地说。（<a href=\"http://life.btime.com/life/20160705/n268677.shtml\" target=\"_self\" style=\"color: rgb(0, 112, 192);\">360北京时间新闻网</a>）</span></p>', '1', '0', '1513646062', '少年改造学校');
INSERT INTO `think_article` VALUES ('159', '35', '广州18岁游戏男子为筹钱买装备', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 人民网广州6月29日电 从广州市公安局了解到，广州增城警方近日在“飓风2016”专项行动中，侦破一起入室抢劫案，抓获一名犯罪嫌疑人。据悉，该嫌疑人为18岁广州增城籍男子，因沉迷网游为筹钱买“装备”，屡次行窃并数次入狱，在近期一次入室行窃中持刀伤人后逃跑。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 据了解，6月19日凌晨，增城区新塘镇西洲村一栋出租屋内发生一起入室抢劫案，一名男子在行窃过程中惊醒了睡在屋内的一对母女，该男子为了逃离现场，竟持刀将事主母女砍伤。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 案发后，增城警方立即展开调查。事主向警方反映嫌疑人作案时赤裸着上身，很快民警在出租屋的阳台上找到嫌疑人脱下的衣物。经过调查，衣服主人的身份很快被确定了——嫌疑人张某，一个有数次盗窃前科的青年男子。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 警方通过调取视频监控录像，拼接起了案发当天嫌疑人张某的活动轨迹，最终将线索指向了广东东莞的一间网吧。民警了解到，张某当天上网时没有登记，而是趁包夜的人走了，但时段还没到的时候，直接用别人开好的机器上网，因为凌晨客人也不多，网吧也没有阻止。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 民警进一步掌握到，嫌疑人非常爱玩网游，经常流连于各个网吧。据此，民警全面加强对相关地段网吧的布控。6月28日上午10时许，当嫌疑人张某来到增城区荔城街一间网吧上网时，民警果断展开抓捕，当场将其抓获。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 据嫌疑人张某供述，案发当天，他独自一人乘坐摩托车从东莞中堂来到新塘，想要“弄”点钱后继续打网游。当其走到西洲村一栋出租房楼下时，发现楼房外的水管比较方便攀爬，于是便沿着水管爬到了出租屋的二楼，发现其中一间出租房的阳台门并没有锁，于是就翻到阳台上伺机行窃。由于担心衣服摩擦发出声响，惊醒屋内睡觉的人，他将衬衫和长裤脱在阳台上，并且从厨房拿了一把菜刀放在阳台门口。然而在行窃过程中他还是被屋内的一对母女发现，拉扯中他拿起事先放在阳台门口的菜刀砍伤了母女二人，随后慌乱逃离了现场。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 据办案民警介绍，嫌疑人由于长期沉迷网游，一些认识他的人都说他是个“网痴”，为了买网游装备，甚至不惜实施侵财犯罪，之前已经两次因盗窃罪被捕入狱了，但还是不知悔改。为了逃避打击，他甚至还精心设计了一些伪装，不过这些伎俩终究没能得逞，案发后的第9天，嫌疑人正在敲击键盘的双手被戴上了冰冷的手铐。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">目前，嫌疑人已被警方依法刑事拘留，案件正在进一步审查中。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 警方提醒广大市民群众，夜晚休息时一定要做好防盗措施，关闭门窗，反锁大门，有条件的还应加装防盗网、防盗报警器等设施，水管、煤气管等便于攀爬的位置可以涂上黄油或安装防爬刺。此外，嫌疑人走上犯罪道路跟沉溺网游有直接联系。暑期将至，学生们有更多的时间上网，家长应该及时进行引导，做到适度安全上网，控制上网时间，避免孩子沉溺网络游戏。——<a href=\"http://www.jfdaily.com/guonei/new/201606/t20160629_2329470.html\" target=\"_self\" style=\"color: rgb(0, 112, 192);\">人民网广东频道</a></span></p>', '1', '0', '1513645815', '广东叛逆问题少年教育学校');
INSERT INTO `think_article` VALUES ('160', '28', '男子迷恋网络赌博 4天输光70万公款', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 中新网安阳6月24日电(记者 侯伟胜)河南省林州市一名公司会计迷恋上网络赌博后，仅仅4天时间，便把多年的积蓄和公司的70万元公款输了个精光。在三次自杀未遂后，他向公安机关投案自首。记者24日从林州市警方获悉，该公司会计因涉嫌职务侵占已被刑事拘留。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 郭某今年39岁，是一名公司会计。今年6月14日，他无意间发现了一个网络赌博平台，便投身其中进行赌博。输光积蓄后，他又利用职务之便，于6月14至17日分三次将公司的70万公款全部挪用于网赌，最后却输得精光。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 此时，郭某不知该如何向公司交代，更不知如何来偿还公款。万念俱灰之下，他便想着死了一了百了。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 6月17日，在公司仓库内，郭某选择了上吊自杀。第一次因系绳结松动被摔了下来，除了脖子上留下了一道勒痕，别的毫发未损。第二次悬吊的过程中又不小心从高处摔了下来，造成双脚因此扭伤。第三次他顺着梯子往高处爬的过程中，因为双脚疼痛难忍、举步维艰，不得不中止此举。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 三次自杀均未遂，郭某自知逃脱不了法律的惩处。在亲人的劝说下，他最后选择了向公安机关投案自首。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">目前，郭某已因涉嫌职务侵占犯罪被林州警方依法刑事拘留。——<a href=\"http://www.chinanews.com/sh/2016/06-24/7916445.shtml\" target=\"_self\" style=\"color: rgb(0, 112, 192);\">中国新闻网</a></span></p>', '1', '0', '1466772597', 'admin');
INSERT INTO `think_article` VALUES ('161', '34', '{亲子感恩励志教育}特训学校叛逆孩子的父亲节', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/study.php?cid=9\" target=\"_self\"><img src=\"/uploads/image/20160921/1474462291879139.jpg\" title=\"父亲节演唱会\" alt=\"父亲节演唱会\" width=\"575\" height=\"350\" border=\"0\" vspace=\"0\" style=\"width: 575px; height: 350px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=624\" target=\"_self\"><img src=\"/uploads/image/20160921/1474462291996314.jpg\" title=\"叛逆孩子的父亲节\" alt=\"叛逆孩子的父亲节\" width=\"572\" height=\"373\" border=\"0\" vspace=\"0\" style=\"width: 572px; height: 373px;\"/></a></p><p style=\"white-space: normal;\">&nbsp;&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">父亲节，对于我们来说，这是一个伟大的感恩与孝顺节日，应该积极面对；而对于叛逆的青少年孩子来说，那是一个可怕的节日，想办法逃避现实。</span><br/></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;今天，我们就是把这些叛逆的青少年孩子牵引回正常人的生活轨道，积极面对现实生活与学习，勇敢参与伟大的节日——父亲节。</span></p>', '1', '0', '1466340667', '感恩励志教育学校');
INSERT INTO `think_article` VALUES ('162', '34', '厌学叛逆学员应邀参与外校校园艺术节文艺汇演活动', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=609\" target=\"_self\"><img src=\"/uploads/image/20160921/1474462554276694.jpg\" title=\"校园艺术节\" alt=\"校园艺术节\" width=\"518\" height=\"331\" border=\"0\" vspace=\"0\" style=\"width: 518px; height: 331px;\"/></a><br/></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474462554123156.jpg\" title=\"文艺表演\" alt=\"文艺表演\" width=\"517\" height=\"275\" border=\"0\" vspace=\"0\" style=\"width: 517px; height: 275px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=609\" target=\"_self\"><img src=\"/uploads/image/20160921/1474462554453468.jpg\" title=\"活动结束\" alt=\"活动结束\" width=\"512\" height=\"325\" border=\"0\" vspace=\"0\" style=\"width: 512px; height: 325px;\"/></a></p><p style=\"white-space: normal;\">&nbsp;<span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp;接到当地全日制普通中学学校的活动邀请，于今日安排部分厌学叛逆学员应邀前往参与校园艺术节文艺汇演活动。获得了主办单位的高度重视与支持，学员的表演节目获得观众的一致好评。</span></p>', '1', '0', '1465044945', '校园拓展教育中心');
INSERT INTO `think_article` VALUES ('163', '35', '安徽少年因厌学用刀随机杀人 作案后酣睡现场', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 20px; color: rgb(0, 112, 192);\">&nbsp; 据市场星报报道，2015年8月18日，潜山县城内农贸市场附近一栋三层民房内，发生一起命案。45岁的妇女汪某颈部被割刺死亡，嫌凶为一名16岁的中学生余某某。日前，安庆市中院审结该案，被告人余某某一审被判处无期徒刑。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 20px; color: rgb(0, 112, 192);\">&nbsp; 余某某，男，1999年3月4日出生，系潜山县某高中在校学生，后因出现厌学情绪并一度休学，随之其产生杀人的想法。2015年8月18日18时许，余某某携带事先购买的一把双刃刀从家中出来，伺机杀人。其步行至潜山县梅城镇小河沿5号被害人汪某某(女，45岁)家门口时，看见汪某某家院门及楼房一楼大门敞开，余某某遂进入汪某某家院内，躲在楼房大门门口左侧。汪某某从楼房大门出来时，余某某趁其不备，持双刃刀刺入其右颈处。汪某某俯身倒地，余某某又向汪某某后颈部猛刺数刀，致汪某某死亡。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 20px; color: rgb(0, 112, 192);\">&nbsp; 余某某随即进入汪某某家中，关上大门，从客厅沿着楼梯跑到楼顶，企图沿着同排相连的居民住宅楼楼顶平台逃跑，在寻找出路未果后又返回楼顶平台。余某某自觉疲倦，便在楼顶平台睡觉。次日凌晨5时许，接到报案到达现场的民警，将在楼顶平台上睡觉的余某某抓获，并在其身边查获作案工具双刃刀。——来自<span style=\"text-decoration: underline;\"><a href=\"http://survey.btime.com/dc_baoguangtai/20160529/n176837.shtml\" target=\"_self\" style=\"color: rgb(0, 112, 192);\">新闻网</a></span></span></p>', '1', '0', '1464526719', '青少年管教所');
INSERT INTO `think_article` VALUES ('164', '43', '湖南永州家长：孩子叛逆送去特训学校有用吗？', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 36px;\">&nbsp;问</span><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">：一位来自湖南永州的家长电话联系咨询到了一个很关键的问题，也是每一位家长所关注的问题之一，那就是把叛逆的孩子送去特训学校有没有用？有没有效果？当时听到这句问话我感觉很惊讶，突然之间我感觉到有一种突如其来的压力——教育压力。似乎您也觉得很有专业性的问题，或许您也会问：把孩子送到学校接受教育是好还是坏？那么下面就给您分析一下答案。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;&nbsp;<span style=\"font-size: 36px;\">答</span>：对于这个问题，答案不止一个，请不要急切地找出结论！只有相对的好，没有绝对的好。因为这个问题很有目的性，也很直接，是关系到万事万物的结果好坏；凡事都有两面性，好坏兼容并存，缺一不可。喜欢完美是人的本性，但是也要遵循自然，接受现实的的洗礼。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 此问题非一事之有，生活中也是难免的。就像我们生病了需要治病一样，有中医也有西医，也还有民间的巫术，您相信哪一种疗法？中医是我们本国特有的疗法，既是尊重自然，又关注健康，运用长短互补的关系化疗疾病，效果明显；西医则是西方的先进科学技术疗法，他们逆转乾坤，颠覆自然，以攻克的化学反应来强制攻毒，有效果但会有反作用；而民间巫术则是抓住了中医和西医之间的不良效应妖言惑众，以语言或者行为艺术通过演示蒙蔽患者心理，并无效果。根据分析您会选择哪种疗法呢？相信大部分都会根据需要选择中医或者西医了。同样的道理，我们来分析一下关于所问的教育问题。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 关于教育，有着学历制教育、艺术技能教育、健康成长教育等，面对以上问题则是健康成长教育。健康成长教育本是非学历制非艺术技能类的教育，是归于人的本性教育。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">目前存有的教育机构有青少年特殊教育学校、青少年特训学校、心理辅导机构、青少年管教所等等，而问题则归于青少年特训学校，是属于不良问题青少年孩子的特殊培训学校。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 然而，目前特训学校也是多种多样，鱼龙混杂，让家长面对选择模糊不清，问题重重。根据目前的特训学校出现有几种：励志教育、素质教育、成长教育、特训基地、拓展训练营、心理辅导中心等等青少年教育机构，但是教育的模式手段却有明显的不同。可以分为几大类：</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 一、励志教育：结合心理辅导、行为训练、思想引导、素质礼仪、文化学习、生活培养、艺术技能培训、野外拓展等融为一体化教育，可以让孩子在各个方面和领域获得学习发展；</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 二、素质教育、成长教育、特训基地、拓展训练营：以传统的军事化思想强制关押，多以军事训练为主，以服从命令为天职的封建军人思想强化孩子的服从意识，不听则大或关，压缩了孩子的成长思想和空间，等脱离这种环境后问题就像被压缩的弹簧蹦跳无极；</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 三、心理辅导中心：以心理沟通为主，通过言语或行为去了解问题和作出语言引导，对于自知困惑的孩子有一定帮助，但是对于叛逆不听话的问题孩子无法可施，并且时间短暂，犹如快餐，无法根除问题。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 根据分析，您觉得哪类特训学校有用呢？还是那句话，凡事均有两面性，答案不止一个，只有相对的好，没有绝对的好！</span></p>', '1', '0', '1464181208', '青少年管教所');
INSERT INTO `think_article` VALUES ('165', '35', '少年辍学抢劫被判缓刑 深入帮扶牵出案中案', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 六一儿童节前夕，江苏省人民检察院通报了各地检察机关打击侵害未成年人权益犯罪，保护未成人合法权益的有关情况，并发布了多起典型案例。据介绍，2009年以来，江苏检察机关受理审查起诉未成年人3万余人，对5900余名未成年人不批准逮捕，(附条件)不起诉2400余人，受理未成年人犯罪案件数量呈现逐年减少趋势。但从公布的一些典型案件来看，社会上侵害未成年人权益的违法犯罪行为时有发生，有的非常恶劣，亟待引起全社会的关注。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">案件回放</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">A、17岁少年辍学抢劫被判缓刑</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 2012年11月，徐州某区检察院办理了一起抢劫案。案情很简单，17岁的小路与他人结伙实施抢劫，他们在农贸市场附近殴打了一名被害人并抢走了被害人手机。随着办案进程，检察官心里越发沉甸甸的。小路是一名涉艾孤儿，他的母亲因输血感染艾滋病去世，父亲随后也因病撒手人寰。在校期间，小路的学习成绩一直稳定在年级前十名，但亲人的接连离世对他造成了巨大打击，加上生活无着，小路高二时就被迫辍学。因为在社会上接触不良朋友，他走上了犯罪的道路。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">鉴于小路初犯、从犯、具有悔改表现等情节，法院也采纳了检察机关的量刑建议，判处小路有期徒刑二年，缓刑三年。与此同时，借助检察院“爱立方”青少年维权工作站，检察官对小路进行了心理支持、帮教救助等一系列帮扶，小路得以重返校园。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">B、深入帮扶时牵出“案中案”</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 为了保障小路日常生活来源，检察官多次与民政部门沟通，希望为小路办理“孤儿救助金”。但在民政部门查询时，检察官吃惊地发现，这笔资金已经被他人冒领。循着这条线索，经过4个月侦查，一起贪污救助金的案件浮出水面。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">经查明，2011年至2013年间，吴某(徐州市某区民政局社会事务科原科长)与李某(徐州市某区民政局社会事务科原负责人)等4人交错结伙，在负责审核、申报、发放孤儿救助款过程中，采取虚报冒领、私自截留等手段骗取、侵吞孤儿补助资金25万余元。法网恢恢，4名被告人均被法院以贪污罪判处11年至1年6个月不等的有期徒刑。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">目前，当地经过清理整顿，清退超龄儿童37人，撤销不符合救助条件的主体12人，并将192名符合救助条件的人员纳入救助范围。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">未成年人犯罪近三年逐年减少</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">专家认为，家庭与学校合力教育作用不可忽视</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 江苏省人民检察院还通报，近3年来，在全社会的共同努力下，江苏防治未成年人犯罪工作取得明显成绩，检察机关受理审查起诉的未成年犯罪嫌疑人数量逐年减少，2014年同比下降6.08%，2015年同比下降14.73%，2016年1-4月同比下降11.48%，但未成年人犯罪案件呈现出一些新的特点，值得引起关注。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 据介绍，当前江苏未成年人犯罪中，文化程度低、外来人员作案多、农民和无业者多是一大特点。更值得关注的是，涉及未成年人的侵财类、暴力性犯罪比重较大，毒品犯罪人数在增多，盗窃罪、聚众斗殴罪和寻衅滋事罪总人数占比高达68.9%，是未成年人实施最多的三大罪名。三年中，全省未成年人犯罪人数以及犯盗窃罪、聚众斗殴罪、寻衅滋事罪等人数均呈现减少的总趋势，而未成年毒品犯罪人数却呈相反上升趋势，3年间增长38.4%。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 此外，在未成年人犯罪案件中，共同犯罪案件占到近四分之一，有预谋、有分工的共同犯罪案件不断增多，团伙特征明显，往往会在犯罪前制订计划，周密布置，对作案目标进行踩点或选择。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 对于未成年人犯罪的主要原因，专家认为，未成年人心理、生理发育不成熟，不良文化带来的负面影响，以及家庭、学校监管不到位是最主要的。为了预防未成年人犯罪，检察机关建议，形成家庭与学校的教育合力;加强综合治理，净化社会环境;做好失足未成年人的综合矫治和帮教工作，有效解决和预防失足未成年人再犯罪问题。__来自<a href=\"http://news.btime.com/society/20160527/n173080.shtml\" target=\"_self\" style=\"color: rgb(0, 112, 192);\">新闻网</a></span></p>', '1', '0', '1464354115', '叛逆少年管教中心');
INSERT INTO `think_article` VALUES ('166', '36', '浙江湖州家长：原本叛逆的女儿变乖了很多！', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 孩子叛逆非常难管，但一般都是男孩子，女孩子都是棉袄。而没想到我的孩子恰恰是相反的，小儿子比较乖巧懂事，而大女儿却是带刺的仙人掌，总是惹是生非，没法管教，很无奈。</span><br/></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 孩子小时候还算听话，但是慢慢长大了也就变了，很叛逆。平时说什么她都会顶嘴，骂她也骂，打她反抗。有时候真的是拿他没办法，也就随她去，但是又担心出事。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 所幸一次偶然的机会在网上看到了励志学校，让我看到了希望！</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 通过网络查询了几家特训学校，感觉他们的管教模式基本上是一样的，绝大部分都是以全军事化全封闭式教育，学校面积都比较小，而且他们说是为了安全，在围墙上都缠绕着带刺的铁丝网（相似监狱一样，个人感觉不太好），在一个就是统一的服装，都是像军队的迷彩服，不论男女都是一样的，看起来就像是军队似的。师资感觉也是怪怪的，大部分都是所谓的教官，也就是调皮的退伍青年，应该是没什么文化的。但是这励志教育学校却是有点区别，首先第一眼看起来没什么压抑感，围墙没有什么铁丝网，学生穿的是普通的校服，老师就是随意的便装，挺有亲切感的；再一个就是学校对孩子的管教是透明化的，只是半军事化管教，也是全封闭式的，但是有网络远程监控，不管在哪里都可以通过手机或者电脑看到孩子在学校的情况，而且也可以随时来校看孩子，这让我感觉很踏实，就这几点我毫无犹豫地选择了湖南励志教育学校，同时也给了我预想的效果！</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 虽然孩子开始很不情愿的入校，但学校还是有他们的管教方式，以为这样的安排孩子会憎恨我的，但是没想到过一段时间，孩子在学校就写了一份电子书信，写的都是她自身的问题和忏悔，并表示愿意接受我的安排和学校的教导，这让我情不自禁地流下了眼泪，非常感动！是因为孩子通过学校的心理辅导下认识到了自己的问题，也明白了过错和接受及学会了改变，这也是我想要看到的。在这过程中，我每天都在学校的远程监控中看着孩子在学校的举动，并且通过书信、电话和视频等渠道深入的沟通，不但发现孩子改变了许多，同时也发现我们之间能够正常的沟通交流了，之间的情感加深了！</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 由于工作较忙，这这过程中才去看了孩子两次，但是每一次都让我收获满满，也让流下了感动的热泪。孩子随着时间的变化而变化，接受了学校的各种课程的安排，学会了很多，不仅改变了心态和不良行为习惯，在生活学习及道德礼仪等各个方面有着较大的提升，面对问题也学会了换位思考和理解，也懂得了感恩和励志，让我看到了孩子的成长而松下了口气，感觉很幸福！</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 现在孩子回来这段时间里，却是改变了一个人，也改善了我们一家人。她的生活规律有序，能够独立自理，从不让我像以前那么操心，而且还会给我们做饭和家务以及端茶倒水；对我们的管教也不再那么逆反了，学会了跟我们沟通谈判，感觉孩子真的长大了。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 当然这一切的变化都是来自励志学校老师们的辛勤付出，是他们的心血养育了孩子的改变和我们的幸福！在此忠心感谢励志学校的老师们，您们辛苦了！！！ &nbsp;</span></p>', '1', '0', '1463835789', '叛逆孩子教育中心');
INSERT INTO `think_article` VALUES ('167', '35', '男子天天打游戏暴死屋中 被发现时已生蛆虫', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 22日晚，在市中区后龙窝庄一出租房内，一名年轻小伙被发现死在自己房间内。据了解，小伙已死亡多日，房东发现情况后报了警。有居民猜测小伙的死亡可能与玩电脑游戏过度有关。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 23日上午，在事发地后龙窝庄63号一栋自建楼前，不少居民站在楼下。向上望去，3楼不少身穿蓝色防护服的民警及法医正在一处房屋内检查。据了解，事发地就在这里。“是房东最先发现并报了警，屋里有股臭味，特别难闻。”一位知情居民告诉记者，近日不少邻居反映该栋楼3层有股味儿，便联系房东过来清扫。“我们以为是走廊堆的垃圾变质，房东昨天晚上就来打扫了，听他说到了3层其中一户出租屋时发现了不少蛆，而且屋子的门也打不开了就报了警。”知情居民说。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 据了解，事发地共6层楼，不少房子正在出租，租房的多为学生或者外地打工族。对于出事的小伙，邻居们表示并不太常打交道，“租房子的人经常换，一拨一拨的，我们看了眼花缭乱，他们也是早出晚归，或者就在楼上打游戏，和我们都不太说话。”一位居民说。该居民还说，他印象中这个出事房间内的租户不常出门。“也就20岁左右的小伙，经常听见屋里打电脑的声音，听说他玩游戏一天也不出门，上网有瘾，也可能是长时间打网络游戏导致猝死。”对此，不少邻居表示赞同。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 此时，房东也在现场，但对于此事他并不愿意多说。23日中午，不少在3层居住的租户正在打包行李准备搬家。大伙表示，出事后觉得害怕，准备换个地方暂住。法医对现场勘察完毕后离开，随后小伙的遗体被送往殡仪馆。目前，警方已经联系上小伙家属，死亡原因还在进一步调查中。——<span style=\"text-decoration: underline;\"><a href=\"http://3c.ycwb.com/2016-05/24/content_22099693.htm\" target=\"_self\" style=\"color: rgb(0, 112, 192);\">新闻网</a></span></span></p>', '1', '0', '1464095088', '戒网瘾机构');
INSERT INTO `think_article` VALUES ('168', '44', '单亲父亲暴力教子 12岁男孩不堪打骂离家出走', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 恼羞成怒的韩成把门反锁，再加了一把儿子打不开的旧挂锁后离开。他想吓一下12岁的儿子，没想到，一个小时后他再回家，发现地上的书包、做作业的小方凳消失了，一起消失的，还有儿子。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 这一消失就是3天3夜。截至22日晚上10点，韩成、亲戚朋友和老师仍未能找到孩子。目前，成都武侯区警方已立案调查。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">打骂</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">“又出去耍你给我跪下!”</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 拐过逼仄的楼道，韩成转身往在二楼的家门口走。按照他<span style=\"color: rgb(0, 176, 240);\"></span>的预想，儿子小峰此刻应该正蹲在门口的小板凳上做作业。然而，灯熄着，儿子不在。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 5月17日晚上7点过，他比平常早一个小时收工回家。他曾数次警告儿子:必须在我下班之前回家，不然就要挨打。饭也没来得及做，他出门找儿子，回来路上，他顺手捡了一根干枯树枝。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 9 点，小峰回家了。“爸爸。”小峰小声招呼了声。韩成坐在房间里，手握树枝，还没等儿子说下一句，他便吼道“又出去耍了!把衣服脱了。”这是第一次他让儿子脱衣服挨打。小峰慢慢脱去校服和裤子，浑身上下只剩一条内裤。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 韩成用树枝在小峰背部挥去，“两下就断了。”随后，他又从屋子里拿出小指头细的废旧电线。“你给我跪下!”小峰转头跑到楼下，却被韩成追下楼厉声喝住。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 光着身子，儿子就跪在楼下的小院子里，韩成用电线往他身上抽打，“为啥还要出去耍?”“怎么老是记不住?”韩成说，这么做是想让儿子在邻居面前有羞愧感，“要是下回再不听话就跪公路边，下下回再不听就跪广场上去!”</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">小峰抓住电线，“不出去了，不出去了。”韩成终于听到了儿子承认错误，眼眶里含着眼泪，收了手。后来，两父子回家做饭、吃饭、做作业，睡觉时已快到凌晨1点。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">“你不听话，就背起书包爬!”</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 第二天，18日下午放学时间，韩成没有等到儿子打来的电话。这也是韩成的规矩:每天晚上放学后，要用家里床头的旧手机给爸爸打电话，确认已回家。旧手机是一部老手机，只能打电话和发短信，被一根2米长的绳子绑在床头，“上面有我做的记号，不准解开。”</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">当晚韩成回家后，儿子解释是头晚没睡好，打盹忘记打电话，韩成才没有追究。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 19日早8点，韩成照常送儿子出门，路上他再次敲警钟:放学回来必须给我打电话。如果我的话你不听，你就不要在我这儿待了。然而，当晚7点40分、8点，他两次打家里的手机都没有人接，直到8点10分，小峰才接起电话。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">“你不听我的话，就背起书包爬!”韩成说完这句话，儿子一直没有回答。数秒沉默后，韩成掐掉了电话。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 9点20分，韩成回到家，不见儿子身影，只看到沙发上的书包。他顿时恼怒，抓起书包扔到了门外的墙角下，翻出一把旧挂锁，把家门死死锁住，然后离开。这把挂锁，小峰没有钥匙。韩成说，当时锁门是一时气愤，也是想吓一下儿子。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">一个小时后，韩成回家，发现门口的书包、做作业的小方凳和儿子小峰一起消失了。这一消失，就是3天3夜。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">家境</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">做保洁工父亲独自带大孩子</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 22日，接到韩成寻找儿子的求助电话，华西都市报记者来到他位于成都武侯区的家中。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">这是一处简陋的二层老式居民楼，单间，只有十几平米，父子俩挤一张床。房间凳子上放着一双崭新的蓝色运动鞋。这是韩成给儿子的儿童节礼物，上周就准备好了，“比不起其他当父母的，这双鞋不到一百元。”</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 昏暗灯光下，墙上是一张儿子5岁时的艺术照，钉子上还挂着一些衣物。小峰平时做作业，就在小方凳上铺块硬纸板，父亲则坐在床上。<span style=\"color: rgb(255, 255, 255);\">特训学校</span></span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 这间房每个月租金一百出头，韩成还是觉得贵。42岁的他已经做了十年的家居保洁，一个套二家居保洁，收费80元，要干近4小时。生意好时，一个月工作28天，能挣够4000元，“还有4个半天，必须要陪儿子。”</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">“他妈妈走了10年了，从来没有联系过。”韩成和前妻在儿子1岁多时离婚，自己独自抚养孩子至今。为了多挣钱，把陪伴孩子和沟通的时间省出来干活。小峰有哪些兴趣爱好、成长的烦恼，他根本说不上来。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp;“他就是贪耍。”韩成说，儿子常到同学家玩，用电脑上网，玩别人的智能手机。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 韩成和儿子没有什么情感交流，但也没有太大矛盾。“他只要不按我的条条框框来，就要挨打。”韩成似乎知道自己动手打人不对，但面对儿子的叛逆和“屡教不改”，他却只知道用这样的方式教育，“动手最主要的目的是为了他的学习”。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">寻人</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">3天3夜 父亲难以成眠<span style=\"color: rgb(255, 255, 255);\">四川戒网瘾学校</span></span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 这是孩子第一次离家出走，一开始韩成并没有意识到事情的严重性。直到在孩子要好的小伙伴家、学校和街坊邻居家里都没有找到人，他才慌了。在孩子离家出走一天后，他报了警。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 随后，他找来孩子的证件照，写了寻人启事，在附近的菜市场、大街上贴了30张。他取消了本已经预定好的保洁工作，找亲戚朋友一起满大街找人。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 直到此时，他才想起平时对孩子的那些严厉。他总是提醒他随身携带纸巾、养成饭前洗手饭后擦嘴的习惯、衣着要整洁，“穿得干净才能和同学相处好一点。”儿子个子长不高，他给他买钙片和补锌营养液;练字工具、记忆法书籍、学习机平板电脑，加在一起即使要花掉一整个月工资，他也愿意。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 在记者面前，韩成尽可能表现得轻松，或者他到目前为止都还没有接受，孩子离家出走的残酷事实。最近几晚，他只是眯三四个小时，天一亮就开始找人。在孩子离家3天3夜后，韩成给包括华西都市报在内的多家媒体致电求助，希望帮忙寻找。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; “生活压力大，他一个人很辛苦，也很压抑。”韩成的表弟彭启康对记者说。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">22日，成都好久都未下过如此长时间的雨了。那些贴在墙上、电线杆上的寻人启事，被雨水冲刷得看不见字，有的飘落在水里。采访结束后，韩成送记者离开，经过小巷，他从地上捡起一张模糊不清的寻人启事，眼里不知是雨水还是泪水，滴落在了纸上。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">对话<span style=\"color: rgb(255, 255, 255);\">四川叛逆青少年管教学校</span></span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp;“超常严格，是为了儿子不再当保洁工”<span style=\"color: rgb(255, 255, 255);\">湖南叛逆少年学校</span></span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">记者:孩子的母亲呢?韩成:在娃娃1岁零8个月的时候，他妈妈就和我离了婚，因为性格不合。在10年里，孩子和母亲几乎没有联系过。全靠我一个人抚养孩子。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 记者:你为什么要打孩子?韩成:我是雅安宝兴人，文化程度不高。我找不到其他方式让他改正坏习惯。我晓得打他不好，时间长了，他也麻木了，我也麻木了。他挨打几乎不哭，也不去找亲戚求安慰。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 记者:你对儿子的期望是什么?韩成:我没有太多钱，也不像其他家长一样遗传给儿子高智商。我只希望他长大了不要再像我一样，再做保洁工了。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">记者:你觉得是什么让他决定离家出走?</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 韩成:他走的那晚上，我们面都没有见到。我最后悔把书包放在外面，还把门故意锁了。他肯定觉得我要赶他走，不要他了。我太极端了!</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">记者:若是儿子回来，你对待他会有改变吗?<span style=\"color: rgb(255, 255, 255);\">长沙叛逆孩子学校</span></span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 韩成:教育方式上我太粗暴了，要改。我要多抽时间陪他，多关心他。我现在就担心他离家出走会不会遇到坏人了。我希望好心人能告诉我他在哪里，我去接他。——转自</span><a href=\"http://news.btime.com/society/20160523/n159847.shtml\" target=\"_self\">新闻网</a></p>', '1', '0', '1464008887', '家庭教育中心');
INSERT INTO `think_article` VALUES ('169', '38', '湖南湘西学员演讲稿：做人要有感恩心', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.qsn580.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474463481492154.jpg\" title=\"做人要有感恩心\" alt=\"做人要有感恩心\" width=\"468\" height=\"596\" border=\"0\" vspace=\"0\" style=\"width: 468px; height: 596px;\"/></a></p><p style=\"white-space: normal;\">&nbsp;&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">激发孩子的心灵，活跃孩子的思想，展现他不一样的才华。感恩励志教育是结合素质礼仪、亲子情感、生活才能及成长目标等综合能力的教育与培养。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">每一位孩子入世本是一张白纸，但自从有了教育，这张白纸上添加了满满的文彩。</span></p>', '1', '0', '1461676222', '湖南青少年励志教育学校');
INSERT INTO `think_article` VALUES ('170', '35', '女孩因父母离异焦虑自残:从来不觉得有父母', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 我从来不觉得自己有父亲、母亲。高二女生小童说出心底话。</span><br/></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 高一下学期以来，她从用钢尺的尖角在自己手臂上划痕开始，发展到用刀在手臂上划上一道道血痕，只为以痛感缓解焦虑。一切源于6岁时的父母离异，她成了“找不到”妈妈的孤独孩子。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 很多父母可能不知道，孩子的心其实很脆弱，很容易碎。所有的孩子都对父母有一个理想的期待，只要家还在，只要家的大门敞开，孩子就永远不会走上绝路。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 小童，广州某校高二女生。住校期间，被老师发现左手手臂上布满伤疤和血痕，老师意识到她有自残行为。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">小童非常清瘦，脸色苍白，“这么一个弱小的身躯，是什么原因使她有勇气一次又一次地把刀割向自己?”</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 小童自述，从小学到初中成绩都很好，是老师的宠儿，父母的骄傲，中考时因为发挥失常到了现在的学校，因此进校前就要求自己考到年级第一名。可入校第一次月考她就考砸了，在全年级并不领先，离预定目标相差甚远。老师在月考总结的时候也没表扬她。小童心里倍感失落，感觉老师以后都不再重视自己。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 高一第二学期，学习压力增大，焦虑、无力、入睡困难，食欲不振，小童上课逐渐听不进、听不懂，作业不会做，考试成绩下滑得厉害。为让自己提神，她开始用钢尺的尖角在自己手臂上划痕，微痛的刺激让她有一种特殊的感觉。后来，小童感到焦虑时就用钢尺的尖角在自己手臂上划痕.再后来，钢尺的尖角已不能满足她了，她开始用刀在手臂上划一道道血痕。小童的自残行为已出现快一个学期了。</span></p><p style=\"white-space: normal;\"><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">背景:小学二年级父母离异 家人称她很懂事</span></strong></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 在小童讲自己的情况时，爸爸一直看着她，但小童却没看爸爸一眼。爸爸表示很震惊，他没想到孩子会做出如此极端的举动。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 爸爸说，在小童6岁上一年级的时候，夫妻离异。他是一名公务员，前妻则经营一家小店。离异时，小童妈妈表示自己没有能力照顾女儿，小童便跟随父亲生活。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 小童上小学二年级时，爸爸组织了新家庭，并与现任妻子有了儿子。由于家庭经济状况不是很好，爸爸从小就对小童要求严格。女儿从小也很懂事，觉得挣钱不容易，希望能取得好成绩，让他们感到欣慰。所以，小童很少让爸爸操心，跟继母和弟弟关系都不错。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 爸爸表示，小童妈妈开了一家小店，但从没来看过孩子，也没有打过电话，“离异时就说当自己没生过这孩子，这话我也跟小童说过，小童上初三时有一段时间说要找妈妈，为此还跟奶奶吵架。”爸爸狠狠地教训过小童，女儿中考发挥失常，爸爸说主要是那时的心思都放在“找妈妈”上。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; “最后还是去了她妈妈的小店，她妈妈竟没有认出女儿。我就跟孩子说，这样的妈妈还找什么找?!”爸爸说。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">自述:爸爸奶奶更爱弟弟 好想能被妈妈呵护！</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">我从来不觉得自己有父亲、母亲。父母没分开的时候，觉得那时候家里老是吵架。先是妈妈和奶奶吵，后是妈妈和爸爸吵。上小学一年级，妈妈就搬走了。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">然后，继母就来了，很快，弟弟也出生了。大人们都围着小弟弟转。奶奶总说:“你是姐姐，要懂事，要学会照顾弟弟。”有次弟弟生病时，大家都去医院陪他，我也去，围着弟弟看，妈妈(继母)却说让我安静地坐着边上就当帮忙了，不要添乱。妈妈不凶，从不骂人，但我不敢亲近她。爸爸虽然很凶，会打人骂人，但我反倒不怕。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 但是爸爸更爱弟弟、更爱奶奶，奶奶也更爱弟弟。有一次我生病，大概是三年级。爸爸带我去医院打屁股针，我哭着喊着死活不肯打。护士阿姨也说我都9岁了还怕打针，爸爸很凶地骂我。最后是爸爸强按着我打了针，我只记得自己哭了好久。那是我第一次好想妈妈呵护我。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 初中有一次和同学在班里吵架，被老师批评态度不好，要我道歉，我觉得很委屈。回家告诉爸爸，没想到爸爸却加倍批评我。那晚我躲在被窝里哭了好久，我想有妈妈多好啊。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">我到处打听妈妈的事，奶奶知道了就骂我没良心，竟还记挂从来不理我的亲妈。我顶撞奶奶说，我找妈妈的事不用他们管。奶奶哭着向爸爸告状，爸爸回来狠狠地把我打了一顿。——来自广州日报</span></p>', '1', '0', '1462108485', '教育服务中心');
INSERT INTO `think_article` VALUES ('171', '39', '湘西家长与在校网瘾叛逆厌学儿子的沟通语录', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">亲爱的儿子：</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 我们分别有半个月了，但我感觉却很久很久......坦率地说，送你去励志学校我的内心也很纠结，我也不知道你要经过多久才能理解父母的良苦用心。你信中的看法在我的意料之中，我也可以体会你现在的感受。老师告诉我，你在学校表现挺好，尊敬老师，团结同学，刻苦训练，在这么短的时间内就纠正了低头走路的不良习惯，爸爸为你的进步感到高兴，也希望你继续努力，爸妈会时刻关注你的成长进步的。</span></p><p style=\"white-space: normal; text-align: right;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">爱你的父亲</span></p><p style=\"white-space: normal; text-align: right;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2015.11.07</span></p>', '1', '0', '1443100574', '亲子教育中心');
INSERT INTO `think_article` VALUES ('172', '30', '针对不同类型问题青少年进行一对一心理辅导', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=60&id=576\" target=\"_self\"><img src=\"/uploads/image/20160921/1474463894135993.jpg\" title=\"心理辅导\" alt=\"心理辅导\" width=\"536\" height=\"355\" border=\"0\" vspace=\"0\" style=\"width: 536px; height: 355px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.qsn580.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474463894113091.jpg\" title=\"心理辅导\" alt=\"心理辅导\" width=\"534\" height=\"333\" border=\"0\" vspace=\"0\" style=\"width: 534px; height: 333px;\"/></a></p>', '1', '0', '1437657482', '心理健康教育中心');
INSERT INTO `think_article` VALUES ('173', '38', '广西叛逆学员：在特训教育学校不仅化解了问题，而且还能减肥', '', '', '', '', '', '<p style=\"white-space: normal;\">&nbsp;&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">想起我的成长和生活，真是丰富多彩，有声有色。从小时候生活感觉都是在父母的怀抱里成长，像个不会断奶的孩子。不知道是我自己不会长大，还是父母的过于宠爱，总感觉我是在幸福中堕落。</span><br/></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 在小学爸妈就让我接触了网络，但是那时候只是简单了解，并没有真正意义上的使用网络。平时也就看看电影，更多的看到了爸妈在使用——玩游戏。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 随着思想和身心慢慢成熟，对网络也有浓厚的兴趣，开始从看着爸妈玩游戏到自己尝试玩游戏，觉得越来越有意思，后来还跟爸妈组队和PK，慢慢地我学会了独立——玩游戏。游戏的独立让我获有了成就感，越玩越有劲，成了游戏达人。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 之后上网也就慢慢地成了我生活的一部分，而且占据的份额越来越大，到了网络霸占了我的全部生活后被接到了励志教育学校。到了励志学校我才醒悟过来——我似乎穿越到了另一个世界。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 励志教育学校这环境里深深地影响了我生活，同时也改变了我的生活，让我重新回归了丰富多彩的现实世界！并且也改变了我不应有的问题：网瘾、好吃懒做、叛逆不听话、不想学习、好吃懒做、生活不规律等不良思想及行为。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 首先，我沉迷多年的网络游戏被学校良好的教育化解了，让我走出了网瘾的泥潭！现在我对于网络游戏是不喜不悲，能玩能放，不再受网络的控制，并且健康上网。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其次，把我两百斤的“肥胖”甩掉了，现在我的体重只有一百五十多，感觉生活轻松了很多！</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 再次，励志学校让我回归了正常的生活，不再是以前的黑白颠倒，不再是那么的暴饮暴食，让我生活涉入了更多的精彩！</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 不论怎么样，我都要感谢的我的父母为我做出了一次明智的选择，给我改过自新的机会！同时也非常感谢励志教育学校这个环境和所有的老师以及同学们，不仅让我学到了很多，同时也让我改变了很多！这些影响可能是一般的生活环境和学校没有的，但是能让我经历和获得，并且是能改变我命运的！同时也希望在校的同学们好好表现，认真听从老师教诲并改掉自己的一切不良思想及行为习惯，争取早日回家！在此，我忠心的感谢大家对我的教导和帮助！谢谢您们！！！</span></p><p style=\"white-space: normal; text-align: right;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">杨**</span></p><p style=\"white-space: normal; text-align: right;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2016年3月6日</span></p>', '1', '0', '1513663332', '广西叛逆孩子特训学校');
INSERT INTO `think_article` VALUES ('174', '51', '【厌学】重庆厌学叛逆少年的求学路', '', '', '', '', '', '<p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息<span style=\"font-size: 18px; color: rgb(255, 255, 255);\">重庆特训学校</span></span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 钟重庆（化名），男，14岁，来自重庆市，存在游戏瘾、厌学、叛逆、自理能力差等不良问题，于2012年7月11日被接入校，留校期为一百八十天。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况<span style=\"font-size: 18px; color: rgb(255, 255, 255);\">重庆戒网瘾学校</span></span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 重庆小学到初中第一学期对学习方面的态度很好，学习成绩一直也还不错，但是慢慢地由于经常与一些不爱学习的同学玩耍，有时候逃学上网玩游戏等，使得成绩急剧下滑，然而对学习的态度也出现了变化，产生了厌学情绪，甚至不上课的行为逐渐增多，问题开始恶化。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">经过我们的心理辅导得知，发现重庆对未来完全没有概念，缺乏人生目标，当接触到网络游戏，发现其能给自己带来精神享受和愉悦感而学习是枯燥无味的，心思开始转移到娱乐界，才会把注意力转移到网络游戏上，慢慢地就迷上了上网。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 20px; color: rgb(0, 112, 192);\">辅导与转变<span style=\"font-size: 20px; color: rgb(255, 255, 255);\">重庆叛逆孩子教育学校</span></span></strong><br/></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 首先，我们心理老师与其进行了深入浅出的人生探讨、生活探讨，让他对生活、对人生、对未来有了新的认识，并开始慢慢思考自己的人生。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 第二，通过行为训练看出，重庆从一开始的怕苦怕累不愿意参加，到后来慢慢地参与并从中学会了坚强、勇敢、不怕苦、不怕累、敢于拼搏、敢于挑战，他不会再抱怨，而是会笑着去坦然面对，并一直坚持到最后，让他的意志力、自控能力等获得明显的提升。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 第三，通过与其家人的深入沟通得知，其爸妈为维持家里的生计一直都在忙于工作，在一起的时间较少，很少对重庆进行教育与引导。对此我们建议让其父母须尽可能的抽出时间来教导孩子和关注孩子成长的点滴，并适时的给予正确的引导和帮助，不要让孩子处在精神空虚的状态中，给厌学、网瘾趁虚而入的机会。同时，父母还应在孩子心中树立积极向上、勇敢追求目标的正面形象，从而影响和引导孩子明确自己的人生目标，从而才能保证孩子有源源不断的学习动力。</span></p>', '1', '0', '1513663221', '重庆叛逆少年特训学校');
INSERT INTO `think_article` VALUES ('175', '51', '【厌学】邵阳邵东厌学少年励志考湖大', '', '', '', '', '', '<p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 邓邵阳（化名），男，16岁，来自湖南邵阳邵东，因存在</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">厌学、叛逆、亲情淡漠、离家出走等不良问题于2011年3月6日被接入校，辅导期为半年。 &nbsp; &nbsp; &nbsp;&nbsp;</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong><br/></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 邵阳自从上了初中以后由于学习环境的改变和学习科目多而压力大一时还不能适应，出现了成绩不理想的情况，然而家人并没有帮助他分析原因和采取应对措施，而是责怪邵阳的主观努力程度不够。于是给邵阳施加更多的管制和监督。使处在学习困境中的邵阳感到更加无助和压抑，厌学情绪开始出现，继而才会有后来的叛逆、和母亲吵架、离家出走。</span></p><p style=\"white-space: normal;\"><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">辅导与效果</span></strong></p><p style=\"white-space: normal;\"><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;&nbsp;</span></strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">问题的根源孩子不良的心理思想，但一部分是因受外界因素影响下才恶化的，甚至动作随着心理思想产生了不良的行为，这让孩子的不良问题形成恶性循环，正常管教无法可施。因此，我们针对于这种特殊问题既是采取特殊的教育方式方法，以理论、思想、行为三位一体的模式同时进行，既治标也治本。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1、首先就是心理疏导和引导，让邵阳重新认识自己学习问题根源和成绩下滑这一情况并寻找原因，然后，鼓励和引导其发挥主观能动性，自己想办法解决问题，这也是对邵阳的受挫折能力、以及独立解决问题的能力的培养和提升，重新回归校园。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;2、引导邵阳重新认识母亲的唠叨和管制，首先认识到母亲承担着家庭生活的重担，要对母亲给予一定的理解。同时，还可以建议邵阳与母亲沟通，让母亲了解自己的想法，让母亲看到自己的成长，“妈妈，我已经长大了，您可以不用再事事都为我操心了！”</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3、鼓励邵阳在平时多与母亲沟通交流，可以谈论自己在学校发生的事情，与老师、同学相处的情景，通过向老师或同学寻求帮助解决了哪些在学习上遇到的难题，以及在学习上的进步等等。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">4、邵阳</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">对自己的学习、生活、与母亲的关系有了新的认识。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">5、开始学会理解母亲，对母亲的爱有了更深的感悟；受挫折能力和独立解决问题的能力都得到了极大的提升。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">6、重新找回了学习的自信、学习的动力、学习的目标，立志考上湖南大学。</span></p>', '1', '0', '1448976429', '青少年厌学心理教育中心');
INSERT INTO `think_article` VALUES ('176', '34', '清明节是缅怀先烈的祭祀活动 是青少年孩子的感恩与励志课程', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=561\" target=\"_self\"><img src=\"/uploads/image/20160921/1474464562101137.jpg\" title=\"缅怀先烈是感恩也是孝道\" alt=\"缅怀先烈是感恩也是孝道\" width=\"566\" height=\"319\" border=\"0\" vspace=\"0\" style=\"width: 566px; height: 319px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.qsn580.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474464562753443.jpg\" title=\"为先烈默哀\" alt=\"为先烈默哀\" width=\"566\" height=\"330\" border=\"0\" vspace=\"0\" style=\"width: 566px; height: 330px;\"/></a></p><p style=\"white-space: normal;\">&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;2016年4月4日的今天，是我国一年一度的传统节日——清明节。清明节也叫踏青节，是我国民间传统的祭祀文化，也就是祭祖与扫墓活动的日子。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 今天，举国上下男女老少都在争先恐后地开展祭祖与扫墓活动，铭记古人，缅怀先烈。不论市郊与乡间，又现出了几道新路——孝道。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">其实，清明节不仅仅是一种活动，也是一种文化，更是一种课堂。活动是大人的缅怀，形式是民间的风俗与文化，过程是小孩的学习与课堂。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 上午九时许，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">在我励志教育学校的校园里也敲响了</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">清明课堂钟声，一堂知恩、感恩、缅怀、孝顺和励志等人生课程在由校长亲自带领下，全校师生共同参与了本次课堂。课程以祭奠毛主席和感恩共产党为代表，通过鲜花祭祀、严肃默哀、发表感言等不同形式展开，让在校的青少年孩子也接受了一场民间传统文化的洗礼，让他们了解社会与生活，理解与感恩父母，懂得孝顺与励志。</span></p>', '1', '0', '1459776547', '感恩励志教育学校');
INSERT INTO `think_article` VALUES ('177', '25', '青少年孩子过度使用社交网络或导致社交障碍', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 在当代，一些社交媒体的弄潮儿赚足了人们的眼球，也获得了不菲的经济收益。然而，社交媒体却使更多的人在现实生活中感到了孤独，尤其对青少年的影响更大。据英美等国的多项调查显示，在社交媒体伴随下成长的年轻一代不乏网络社交达人，但更多的人却不爱出家门，缺乏社交能力，有的甚至“不敢接电话或应门”。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　19岁的英国姑娘伊娃·麦克唐纳曾过度沉迷社交媒体，以至于无法在现实生活中结识新的朋友。她对《每日邮报》记者表示，“我的社交能力很差，因此故意把自己和其他人隔离起来，这样就不必与任何人交流。我甚至从不接听电话或应答门铃，因为脑海里总有一个声音告诉我，人们在了解我之前会对我评头论足。”</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　在英国，类似伊娃这样的患有社交障碍的青少年还不是少数。伦敦大学国王学院的研究人员对1000名12岁至17岁的青少年进行了调查，发现其中60%的人有孤独感，三分之一的受调查者感觉自己在朋友中不受欢迎，甚至有5%的人说，放学后从未与小伙伴一起玩耍。此外，调查还发现，女生的孤独感比男生更加强烈。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　负责这项调查的国王学院教授珍妮弗·劳表示，青少年的孤独感可能伴随着焦虑和抑郁，阻碍现实中社交技能的发展。而社交技能在日后的工作中起着非常重要的作用，但却难以教授，只能在青少年时期自然而然地学会。而该调查的委托方，英国国民信托首席执行官迈克尔·莱纳斯认为，这一结果揭露了英国年轻一代的孤独程度和社交的缺乏，将不利于他们将来的幸福和英国未来经济的发展。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　此外，英国学术期刊　《性格与个体差异》　的一篇论文认为，由于社交媒体用户不太可能对自身行为和周围世界进行反思，因此容易养成如自恋、缺乏安全感、强迫症等负面人格。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　目前伊娃正在接受训练以重建与人交往的信心。她说，未来想考入音乐学院学习，成为一名歌手。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　不止在英国，青少年因过度使用社交媒体导致社交障碍乃至抑郁已经成为一个全球性的问题。在美国，匹兹堡大学的一项最新研究表明，社交媒体的重度用户患抑郁症的几率是普通用户的2.7倍。而密歇根大学的研究报告称，社交网络实际上“破坏”了年轻人的幸福感。在日本、中国，类似的报道也数不胜数。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　其实，出于对青少年的保护，美国法律规定，年龄在13岁以下的儿童在申请登录社交网络前，必须获得家长同意。目前，美国几乎所有主流社交媒体服务限定的最小年龄都为13岁。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　欧盟则试图出台更加严格的法律。去年底，欧盟出台新的数据保护法草案，规定只有满16岁的青少年才可以自由使用社交媒体、电子邮件等服务，低于这个年龄的需要在获得父母同意后才能上网。但这一提议立刻遭到了维护青少年利益团体的反对，理由是“13岁到16岁的青少年早已习惯使用社交媒体”。此外，一些网络巨头也游说反对。据悉，该法案的最后文本可能在2016年年中面世，并要等到2018年才有可能成为欧盟成员国各国的法律。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　无论法律如何规定，但在实际生活中，由于社交媒体如此强大，智能手机如此方便，很多青少年很容易在父母的视野之外上网。因此，更重要的是良好习惯的养成。</span></p>', '1', '0', '1459690256', '健康网络教育中心');
INSERT INTO `think_article` VALUES ('178', '25', '《天下故事会》手刃同伴的网瘾少年 疯狂沉迷于网络游戏', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://i7.imgs.letv.com/player/swfPlayer.swf?autoPlay=0&id=2039764\" width=\"660\" height=\"560\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"white-space: normal; text-align: center;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">视频地址：</span><a href=\"http://www.le.com/ptv/vplay/2039764.html#vid=2039764\" _src=\"http://www.le.com/ptv/vplay/2039764.html#vid=2039764\" style=\"color: rgb(0, 112, 192); font-size: 18px;\">http://www.le.com/ptv/vplay/2039764.html#vid=2039764</a><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 父母的疏忽，孩子的悲剧。网瘾少年走向不归路是谁的错？当孩子不想读书倾向于上网时，您是否察觉到孩子已经出现了问题？当您看着孩子上网时间越来越长甚至影响了正常的生活与学习的时候，您是否采取了措施？</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 您的忽视与小视，可能就会带来一种不可挽回的错误，特别是孩子问题。孩子一旦不愿意上学就会弃学辍学，无能无技的孩子也只能依赖父母，无聊之际也只好上网打发时间，但是已经走向了网瘾的圈套，一脚踏进，全身坠崖，后果不堪设想。</span></p>', '1', '0', '1457530384', '戒网瘾教育中心');
INSERT INTO `think_article` VALUES ('179', '28', '生活教育专家：拖延症为什么一定要治？', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 拖延症不知从何时起成了一个特别容易被人们挂在嘴边的词，而随着它出镜率越来越高，它所指代的种种习惯也正在逐步趋于中性。如今的网络上有多少人喜欢直言自己是“重度拖延症患者”或“拖延癌晚期患者”？</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">　但是，这样真的好吗？外国最新研究告诉你，拖延症不再单纯地是一种流行语，它很有可能让你和变成富翁失之交臂!</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　After studying the daily habits of 177 self-made millionaires over the course of five years,Thomas C. Corley found that they avoid one costly habit: procrastination.</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　托马斯C·科利在长达五年的时间里，研究了177个白手起家百万富翁的生活习惯，发现他们都避开了一个代价颇高的习惯：拖延症。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　&quot;It prevents even the most talented individuals from realizing success in life,&quot; he writes in his upcoming book, &quot;Change Your Habits, Change Your Life.&quot;</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;“即使是最有才能的人，若是拖延的话，也无法成功，”他在自己即将出版的《改变习惯，改变生活》这本书中这样写道。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　This goes hand-in-hand with author Napoleon Hill’s assertion that the wealthiest people are also the most decisive people.</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　这与作家拿破仑·希尔的论断——“最富有的人也是最果断的人”——不谋而合。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　Out of the 500 millionaires Hill studied in the early 20th century, &quot;Every one of them had the habit of reaching decisions promptly,&quot; Hill wrote in his 1937 classic, &quot;Think and Grow Rich.&quot;</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　20世纪早期，希尔对500多位百万富翁进行了研究，“他们中每个人都有迅速决策的习惯，”希尔在他1937年的经典作品《思考致富》中这样写道。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　One of the major causes of procrastination is lack of passion, Corley emphasizes: &quot;We simply like to do the things we like to do and we put off the things we do not like to do.&quot;</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　造成拖延的一大主要原因是缺少激情，科利强调说：“我们只喜欢做自己爱做的事，并且推迟做我们不喜欢做的事情。”</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　&quot;According to Gallup, only 13% of employees are ’engaged’ in their jobs, or emotionally invested in their work,&quot; Corley writes.</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　　“据盖洛普民意测验所的数据来看，仅有13%的员工‘专注于’他们的工作，或者在他们的工作中投入情感，”科利写道。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　&quot;Whether you realize it or not, procrastination is a big reason why you are struggling financially in life. It damages your credibility with employers and fellow colleagues at work. It also affects the quality of your work and this affects the business you or your employer receive from customers, clients, and business relationships.&quot;</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　“不管你有没有意识到，拖延是你在生活中遭遇经济危机的一大重要原因。老板和同事都会认为你不可信。这也会影响到你的工作质量，影响到你和你老板从顾客、客户以及有业务关系的人那里得到的生意机会。”</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　The good news is that anyone can overcome procrastination — and it’s simpler than you may think. He also notes that &quot;the voice of procrastination screams just as loud and clear in the minds of those who excel in life as it does in the minds of those who do not.&quot;</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　但是有个好消息：任何人都能克服拖延症——而且这比你想象的还要简单。他还说“不管是有所成就的人，还是一事无成的人，拖延症在他们脑袋里发出的尖叫声是一样大一样清晰的。”</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　How do they silence it? They rely on &quot;to-do&quot; lists to get things done. They create firm deadlines, and they meet with &quot;accountability partners&quot; to ensure they’re sticking to their goals and deadlines, Corley says.</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　他们怎么让这声音安静下来呢?他们借助“待办事项”清单来把事情都做完。他们给自己设定不可更改的截止日期，与“问责伙伴”会面以确保他们在为实现目标而努力并遵守截止日期，科利说。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　Nearly everyone is susceptible to procrastination — but fighting against it can make all the difference.</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">　几乎每个人都易受拖延症的影响——但是与之战斗能够改变一切。（来至</span><a href=\"http://learning.sohu.com/20160328/n442553543.shtml\" target=\"_self\" style=\"font-size: 18px; color: rgb(0, 112, 192);\">搜狐教育</a><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">）</span></p>', '1', '0', '1456666465', '生活健康教育中心');
INSERT INTO `think_article` VALUES ('180', '26', '今日关注：初中未成年孩子早恋引发的悲剧', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://www.v1.cn/player/cloud/cloud_player.swf?&autoPlay=false&showAD=false&id=1005274&videoUrl=http://f01.v1.cn/group1/M00/95/4B/CgoBNlMRyiKANo_iAQbN7eorR4I019.flv\" width=\"649\" height=\"532\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p>', '1', '0', '1456061806', '早恋教育辅导中心');
INSERT INTO `think_article` VALUES ('181', '25', '叛逆青少年混迹社会后酝酿的残忍事件', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://www.v1.cn/player/cloud/cloud_player.swf?&autoPlay=false&showAD=false&id=null&videoUrl=http://f01.v1.cn/group1/M00/59/C6/CgoBNlKs-aiALPpeAzIB1ieHnZw017.flv\" width=\"678\" height=\"520\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"white-space: normal; text-align: center;\"><span style=\"color: rgb(0, 112, 192);\">原视频地址：</span><a href=\"http://link.v1.cn/2013-12-15/899191.shtml\" _src=\"http://link.v1.cn/2013-12-15/899191.shtml\" style=\"color: rgb(0, 112, 192);\">http://link.v1.cn/2013-12-15/899191.shtml</a>&nbsp;</p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 当孩子沉迷游戏之后，会给您带来一些意想不到的“惊喜”。日常生活中，我们会经常发现：孩子出现上网成瘾、厌学不愿读书并且辍学、叛逆顶嘴不听话、脾气暴躁对着干、离家出走夜不归宿、混迹社会吃喝赌玩会飙车、打架斗殴混老大、坑蒙拐骗接着来......等等问题屡现不鲜，但是却被其父母给忽视了，等到了问题上门才知道哭天喊地，为时已晚。咨询电话》18874250661潘老师</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 如果您发现孩子上网时间越来越长，请您及时关注！因为孩子开始产生厌学心理，如不警示，将会辍学。当您发现孩子不愿意上学的时候请给予关注！因为孩子准备叛逆对抗，摆脱您们的管教，随之会混迹社会...诸多问题将会层出不穷，后果不堪设想。</span></p>', '1', '0', '1513662927', '问题孩子心理辅导中心');
INSERT INTO `think_article` VALUES ('182', '44', '孩子为了获取父母的同情将会掩饰自己的过错', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474465381785803.jpg\" title=\"孩子心声\" alt=\"孩子心声\" width=\"512\" height=\"457\" border=\"0\" vspace=\"0\" style=\"width: 512px; height: 457px;\"/></a></p><p style=\"white-space: normal;\">&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;孩子，他们为了满足自己的需求便会哭闹；为了达到自己的目的，他们会变成一个优秀的演员为您演戏。但是您却没有了解他们的真实想法，或许只觉得那些都是孩子正常的变现。<br/></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 当有一天，发现孩子犯了小错时，却给予了忽视，以为孩子还不懂事。但是等到出现了大错的时候，却不知如何管教，认为是孩子的错。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其实，错的不全是孩子，因为他孩子小，需要关爱和教育。爱与教育恰恰是父母的责任与义务。孩子没有成长和犯错误的时候，需要良好的管教与引导；当孩子犯错出现问题的时候，更是需要您采取措施，给予教育，挽回孩子与家庭的幸福！</span></p>', '1', '0', '1456494175', '家庭教育中心');
INSERT INTO `think_article` VALUES ('183', '44', '养父的无奈，孩子的心声：爸爸不要离开我！', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.pptv.com/v/HsgzsGIxKGY9tjo.swf\" width=\"608\" height=\"415\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p style=\"white-space: normal; text-align: center;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">视频地址：</span><a href=\"http://v.pptv.com/show/HsgzsBhib7iayPDdE.html\" _src=\"http://v.pptv.com/show/HsgzsBhib7iayPDdE.html\" style=\"color: rgb(0, 112, 192); font-size: 18px;\">http://v.pptv.com/show/HsgzsBhib7iayPDdE.html</a><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp;</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">当您有勇气生下孩子的时候，您应该做好了抚养准备！</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">当您手接孩子的时候，您已经接受了他（她）所有的义务与责任！</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">当您怀抱孩子的时候，就应该履行您应有的责任与义务！</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">当他们需要的时候，请及时给予关爱和帮助！</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">当他们孤独的时候，请多多陪伴！多一点沟通与交流！</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">当他们走出路的时候，请及时牵住他们的手，指明应该行走的方向！</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">不要丢下他们！因为这里有一种不可抹灭的真情，那就的爱<img src=\"http://img.baidu.com/hi/face/i_f22.gif\"/>！！！</span></p>', '1', '0', '1465739104', '亲子教育学校');
INSERT INTO `think_article` VALUES ('184', '50', '【心理开导】湖北荆州叛逆少年的辛酸历程', '', '', '', '', '', '<p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息 <span style=\"font-size: 18px; color: rgb(255, 255, 255);\">孝感戒网瘾学校</span></span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 张常德（化名），男，14岁，来自湖北省荆州市，因存在游戏瘾、厌学、叛逆、贪玩等等不良问题于2014年3月9日被父母送来我励志成长教育学校进行问题矫正教育。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况<span style=\"font-size: 18px; color: rgb(255, 255, 255);\"> 仙桃戒网瘾中心</span></span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 常德在出现这些不良问题之前比较乖巧听话的，并且在学习方面也算不错，成绩处于中上等，偶尔还获得一些优秀奖状，让家人都对他充满着希望。但是只从步入了初中二年级后思想情绪及行为突然发生转变，上网成瘾、叛逆不听话、厌学辍学、贪玩打架等等不良问题层出不穷，让父母亲百思不得其解。根据我们的分析，张常德的问题根源是来自家庭因素，特别是父母亲的管教方式。首先，常德的</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">爸爸性子急，脾气暴躁，行事冲动等，对孩子的管教既是要么不闻不问，我要么就是以暴力殴打及怒吼等方式，让孩子对其产生恐怖感，同时也学会及养成了类似习惯；再次就是妈妈既是比较宠爱温柔，总是担心这个担心那个，整体对孩子唠叨个不停，让孩子产生厌烦感。其三，在家里无法跟父母亲和谐相处使得常德生活乏味，就寻求发泄之道，开始了网络生活，随之上网成瘾，产生厌学的不良心理；随着网友和同盟好友增多，生活圈也随着扩大，步入了混迹社会道路，辍学情况随即出现，问题不断恶化。在其父亲的强大管教手段下不但没使常德好好转，反而力争摆脱管教，做出离家出走的行为；然而母亲的温柔与宠爱却也收不了常德的良心，拿了钱就走，不愿听半句唠叨，在无奈下决心送来我们湖南青少年励志教育学校进行矫正教育，挽回孩子的成长希望。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导与效果 <span style=\"font-size: 18px; color: rgb(255, 255, 255);\">荆州戒网瘾机构</span></span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1．心理思想教育是给变其问题的根本，通过心理辅导，让常德认识到沉迷于网路暴力性游戏会引起自己在现实生活中产生不良的习性，甚是有些冲动的行为习惯最终可能导致的结果是害人害己，更重要的是耽误了自己美好的前程，影响到了正常的生活和学习。此下去很容易走上违法犯罪道路。常德一旦和别人产生矛盾的时候，不管对方是老师还是父母，都有一种克制不住的冲动和愤恨，想要置对方于死地，看到他人的时候，如果不顺眼，也想狠揍对方一顿。缺乏爱和同情心，无视社会的权威，行为模式倾向于冲动性，攻击性和破坏性。这种性格任其发展下去，有可能形成反社会人格障碍。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.让常德认识到在社会上打打杀杀的生活看起来很风光，但是这种风光是暂时的，不会长久且非常危险。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3.针对常德想问题片面极端的问题，心理老师有意识地教育诱导常德学会站在他人的立场上看待问题，而不是想什么问题都是以自己的喜怒爱好为出发点。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4.教会常德一些控制情绪的方法，发现自己有发怒的冲动时，如何控制自己的情绪，降低内心的愤懑感。</span></p>', '1', '0', '1514292067', '湖北青少年健康辅导中心');
INSERT INTO `think_article` VALUES ('185', '22', '刘老师》德育教育特长辅导', '', '', './uploads/57e2904a1a5c7.jpg', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\"><img src=\"/uploads/image/20160921/1474465833137465.jpg\" title=\"德育老师，德育教育，励志教育\" alt=\"德育老师，德育教育，励志教育\" width=\"269\" height=\"309\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 309px; width: 269px;\"/></span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">刘 云</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 德育老师兼美术音乐特长辅导师。来自湖南长沙望城，曾于中国人</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">民解放军68332部队英雄一连</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">服役三年，任文书兼班长等</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">职务，并获得“优秀士兵&quot;和</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">“优秀班长”等荣誉奖项；</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">因品行出众调往长沙高炮团</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">预备役服役五年,期间带训成</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">绩突出，荣升“二级士官”</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">军衔。在长沙多所高等院校</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">担任军事教官一职，有着丰</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">富教学经验；刘老师为人师</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">表，开朗幽默，注重学员的</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">日常行为养成和军事化规范</span><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">及道德素养，效果显著。</span></p>', '1', '0', '1462110498', '叛逆少年管教中心');
INSERT INTO `think_article` VALUES ('186', '22', '杨老师》心理文化辅导', '', '', './uploads/57e290a352c8c.jpg', '', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"/uploads/image/20160921/1474465898179270.jpg\" title=\"杨老师》心理文化辅导\" alt=\"杨老师》心理文化辅导\" width=\"269\" height=\"366\" border=\"0\" vspace=\"0\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; vertical-align: bottom; max-width: 100%; height: 366px; width: 269px;\"/></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(0, 112, 192); font-family: inherit; font-size: 18px; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; text-indent: 2em;\">杨玉慧</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 15px; padding: 0px; list-style-type: none; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 24px; font-family: 宋体; vertical-align: baseline; text-indent: 2em; color: rgb(65, 65, 65); white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; list-style-type: none; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: inherit; vertical-align: baseline; color: rgb(0, 112, 192);\">&nbsp; 学校心理咨询辅导兼文化老师。毕业于湖北中医药大学心理学院应用心理学专业，获有国家统一注册的教师资格证和心理咨询师资格证，从事教育工作多年，积累了丰富的教学和辅导经验。她心地善良，和蔼可亲，对待学生如亲人好友，辅导过程中不带任何个人情绪，是一位专业性强、责任心重的好老师。</span></p>', '1', '0', '1443189083', '叛逆厌学孩子心理健康教育学校');
INSERT INTO `think_article` VALUES ('187', '34', '湖南著名画家王国祥教授来我综合励志教育校区指导作画', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474466197134788.jpg\" title=\"著名画家来下指导作画\" width=\"581\" height=\"349\" border=\"0\" vspace=\"0\" alt=\"著名画家来下指导作画\" style=\"width: 581px; height: 349px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=538\" target=\"_self\"><img src=\"/uploads/image/20160921/1474466197420420.jpg\" title=\"画家于励志校园作画\" width=\"582\" height=\"314\" border=\"0\" vspace=\"0\" alt=\"画家于励志校园作画\" style=\"width: 582px; height: 314px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474466197107093.jpg\" title=\"大师正挥舞文笔\" width=\"582\" height=\"330\" border=\"0\" vspace=\"0\" alt=\"大师正挥舞文笔\" style=\"width: 582px; height: 330px;\"/></a></p><p style=\"white-space: normal; text-align: left;\"><span style=\"color: rgb(0, 112, 192); font-size: 16px;\">&nbsp; &nbsp; 3月6日讯：系学习雷锋纪念日之后的经典艺术文学之旅，湖南著名画家王国祥接受我校师生的诚挚邀请并亲自前来我综合励志教育学校心理辅导校区进行文学艺术指导且当场作画！</span></p>', '1', '0', '1457272590', '湖南青少年励志教育学校');
INSERT INTO `think_article` VALUES ('188', '34', '三月学雷锋动员大会在综合励志教育心理辅导校区展开', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=24&id=537\" target=\"_self\"></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.qsn580.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474466392314175.jpg\" title=\"向雷锋学习动员大会\" width=\"550\" height=\"283\" border=\"0\" vspace=\"0\" alt=\"向雷锋学习动员大会\" style=\"width: 550px; height: 283px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474466392826683.jpg\" title=\"www.841990053.com\" width=\"550\" height=\"320\" border=\"0\" vspace=\"0\" alt=\"www.841990053.com\" style=\"width: 550px; height: 320px;\"/></a></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp; 学习雷锋日全称学习雷锋纪念日，为每年的3月5日。1963年3月5日，毛泽东“向雷锋同志学习”的题词在人民日报发表，该纪念日由此而来》综合励志成长教育校区对于存有叛逆厌学网瘾早恋亲情淡漠不听话等等不良问题给予矫正教育辅导，大力弘扬雷锋精神，让每一位孩子学会尊敬、包容、帮助、感恩、孝顺等。三月五号我校题为“向雷锋学习动员大会”活动隆重举行，全校师生积极参与其中。</span></p>', '1', '0', '1457186384', '感恩励志教育学校');
INSERT INTO `think_article` VALUES ('189', '51', '【厌学】益阳少年找回了自我，重回校园', '', '', '', '', '', '<p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;曾益阳，男，十四岁，来自湖南省益阳市。他虽然从小就是跟父母在一起生活，但是由于父母平时的工作繁忙，各种应酬也比较多，对他的关爱与管教都是非常有限的，大多是有爷爷奶奶带着，但祖辈们对孩子却比较溺爱，在各个方面一直都是惯着他。管教方式和文化理念的差距及年龄的成长，长江的脾气也逐渐的变得暴躁和冲动，叛逆情绪也随之即来，而他的学习方面却一直不怎么理想，在小学也经常被老师的指责。之后其父母为了摆脱管教问题给他转学到寄宿学校，前期他也愿意去，但是原时间后他变得很懒，不洗澡不洗衣服，在生活上吃喝拉撒，自理能力非常弱，之后和班上同学到外面网吧玩，对学习的心思慢慢地被淡化。在玩方面对朋友看得很重，但就是不听父母的管教，随意迟到旷课，不做作业，不爱读书，想去就去，不想去就是睡觉或到外面上网，厌学情况恶化，网瘾随之即来。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">在校情况</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;懂得如何与父母沟通， 认知自己的错误，重新认定自己的价值观念。懂得学习的重要性并在一定程度的范围内增强了对学习的主动性，认真完成作业，积极完成学业。在励志学校，他学会了如何自理生活，对于个人卫生方面更会严格要求自己。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;他学会思考真正朋友的重要性，会判断损友与益友的区别并与以前的部分不良“朋友”断开关系，再没因为同样的错误耽误学业。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;作为他自己，他也通过自己的努力使自己与父母的关系愈加和谐，连他父母都亲自打电话过来感慨励志学校的教育。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">励志学校本着改变一个孩子，挽救一个家庭，和谐一个社会的宗旨，认真改变每个迷途少年，多少人从这里改变了自己的不足是我们有目共睹的。</span></p><p style=\"white-space: normal;\"><strong><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">辅导效果</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp;来我们青少年励志教育学校经过了一百八十天的特殊训练和教育，曾长江有了明显的给变，不论是在心理思想、行为习惯、生活学习、礼貌礼仪等方面都是有着很大的转变和提升，像换了一个人似的。通过学校的心理疏导和思想的引导，他深刻地认识到自己的不足和缺点，并积极配合学校的教育辅导和改正，让家长和我们共同看到了他的前途和未来，看出了他的希望。</span></p>', '1', '0', '1458568960', '厌学心理辅导学校');
INSERT INTO `think_article` VALUES ('190', '36', '新疆叛逆学生家长：建议您们可以考虑在我们那边建立个分校！', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 针对于不良问题青少年的管教学校，目前全国各地的教育机构都不太完善，就我孩子问题也咨询了全国各地的，感觉贵校是比较好的一所，不论在教学手段、模式、效果及设施等都是不错的，是值得我认可的励志学校！</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 但是据我所知，当今社会的发展趋势和现况，不良问题青少年孩子逐渐增多，而比较规范的教育学校却比较少，对于这些问题孩子我们也感觉到担忧，毕竟我也是从事教育工作的，但我是心有余而力不足，只能眼睁睁的看着周边的不良问题孩子流浪。目前像这样不良问题孩子在我们那边就存在很多，不少家长对孩子的问题也感到无奈，没办法管教，而又不知像您们励志教育学校的存在，因而也是任由孩子放肆玩耍，没人管教。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 我发现我孩子才来贵校不久就改变了很多，特别是对我们的态度和对问题的看法上有着很大的转变，虽然来的时间没多久，但是我们看到了希望，但是我更大的希望是能够帮助更多的孩子和家庭。虽然我也是从事教育工作这么多年，但是服务的问题和方向不一样，对于这样不良问题孩子我也表示无奈，因为我自己孩子也是出现了不良问题，而我却没能管教好，或许这也是我做为一位母亲和教育工作者的失败吧。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 在对我孩子问题的观察发现，其实我孩子问题受到外界环境影响下导致的，因为他所在的学校和外面结交的朋友都是存在类似的不良问题；并且在跟我孩子交流中得知，我孩子所在的班级大部分学生都是存在相似不良问题，导致了我孩子自控能力较弱和意志力不强就身受影响，被同学带坏的。我孩子本质都比较好，而且从小我们对他的管教也比较严，在各个方面都表现都是比较好的，只是近期出现了偏差，我们也在寻找解决办法，但是取不到较好的效果，因而找到了您们学校，请求到了您们的帮助，发现孩子也在逐渐改变过程中，在此忠心感谢贵校的全体老师！您们辛苦了！</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 看到了孩子的希望的同时我想让您们能够帮助更多的孩子和家庭，让爱心扩散，让问题消失！我想建议贵校能否考虑在我们那建立个青少年励志教育分校，帮助我们那偏远的孩子个家庭！让我孩子回去后都能够接触到好了伙伴，好的朋友，让他一起快乐地成长。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 虽然我们那边有点偏远，但是如今人口也比较多，再加上西部地区正在开发崛起，像这样的不良问题孩子也是比较多的，而却没有几所好的学校来管教他们，该有的教育而他们却没有，原本幸福的家庭却被这些不良问题青少年孩子毁掉了，感觉到可怜，我想帮助他们，更多的是需要您们的帮助，您们是最伟大的，最有能力帮助他们的！在此也希望贵校可以考虑在我们那边或者外地设立分校，帮助全国各地的孩子和家庭，建造和谐社会和强大的国家！谢谢！！！</span></p>', '1', '0', '1513662807', '新疆叛逆孩子教育中心');
INSERT INTO `think_article` VALUES ('191', '39', '一位中学生给父母写下的十几条建议与要求', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 问题孩子的问题，到底是谁的问题？或许不少家长并不知道，其实孩子的问题根源基本都是来自父母和家庭，因为那是孩子的榜样。曾有一位中学生在对父母写下了不少条建议于要求，坦述自己的想法和观点：<br/></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1、不要我要什么就满足我什么。其实有时我只是想知道我能得寸进尺到什么程度。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2、不要对我大喊大叫。你对我喊叫只会减少我对你的尊敬，同时也教会了我喊叫。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3、不要总是发号施令。假如你向我提出请求，而非发出命令，我会更心甘情愿地去做事。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4、请履行诺言。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">5、不要拿我和任何人比较，如果你认为我好于他人，有人会难过；如果你觉得我不如他人，难过的会是我。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">6、不要一会儿让我做这，一会儿让我做那，使我无所适从。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">7、请让我自力更生，如果万事皆由你替我完成，那么我将来永远也学不会做事。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">8、不要在我面前说谎，也别要我为了你而说谎，这样会使我不再相信你的话。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">9、当你犯了错，请承认错误。你的行为也会教导我承认自己的错误。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">10、当我向你讲述我的问题时，请试着理解并帮助我，爱我就要告诉我。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">11、请象对待朋友一样对待我，虽然我们是一家人，但这并不意味着我们不能成为朋友。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">12、不要让我去做你自己不做的事。我永远不会做你让我做而自己却不做的事情。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 如果您是一位孩子的父母，是否也想到这些问题？当您发现孩子出现了不良的叛逆不听话等问题后时候也思考过引起问题的因素在哪里？其实孩子在成长过程中渴望的不仅仅是关爱和陪伴，更是渴望跟父母做朋友一样的友谊与互助，快乐成长。</span></p>', '1', '0', '1461247565', '亲子教育中心');
INSERT INTO `think_article` VALUES ('192', '39', '湖北厌学叛逆少女：爸爸，我有话想对您说！', '', '', '', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.qsn580.com/\" target=\"_self\"><img src=\"/uploads/image/20160921/1474466847989515.jpg\" title=\"湖北厌学叛逆少女给爸爸的一封信\" alt=\"湖北厌学叛逆少女给爸爸的一封信\" width=\"469\" height=\"508\" border=\"0\" vspace=\"0\" style=\"width: 469px; height: 508px;\"/></a></p>', '1', '0', '1432822025', '亲子教育学校');
INSERT INTO `think_article` VALUES ('193', '50', '【问题矫正】益阳叛逆厌学少年的百日励志回顾', '', '', '', '', '', '<p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">益阳市叛逆厌学少年管教辅导中心</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 范江南，男，15岁，来自湖南省益阳市赫山区，签订在校辅导六个月协议，毕业至今无不良问题反映。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">长沙益阳青少年叛逆孩子特训学校</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 江南刚步入初中一年级的时候在学习上比较认真，基本能够跟上老师的上课步伐，成绩名列前茅。但是升上看初中二年级后就出现了转变，上课马虎了事，成绩一降再降，原来是迷上了网络游戏，在家就是上网，影响了作息时间和学习心思。在父母无数次坚持着引导与阻止后上网逐渐减少，但是由于意志力和控制力不理想的他很难断开网络，时不时会到外面上网，厌学情绪不断提升。虽然网瘾不是很严重，当外界干预时还能够主动下机，但是随心所向，学习却不怎么理想，成绩一直在中等线上下徘徊，往下滑的概率总是占上风，让他有了辍学的念想，后来就是时去时不去的，不想上课就会溜去网吧，很晚才会回家，在父母的质问下找出各种借口来掩饰自己的不良事件。眼看就要参加中考升高中，而江南成绩却不见提升，其父母最大的期望眼看就会失去了希望，多次采取拳脚相加的手段却无济于事，无奈下寻求外界的帮助，找到了我们<a href=\"http://www.841990053.com/\" target=\"_self\" style=\"color: rgb(0, 112, 192);\">青少年励志教育学校</a>。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案与效果</span></strong><span style=\"font-size: 10px; color: rgb(255, 255, 255);\">湖南益阳叛逆问题孩子青少年戒网瘾学校</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 其实，就江南本性而言，并不坏，只是因自身的思想未成熟导致了自控能力较弱，受到了外界环境的影响下出现了思想上的扭曲和行为上的偏差。通过学校专业的心理疏导，让他应学会如何自我调节情绪，在学习又该如何去处理自己的学习观念和网络运用技巧，把控自己的行为。通过亲自教育课程让江南主动与父母联系交谈，畅吐心声，提升了亲子情感，打开了亲子沟通困难问题。以生活教育让他明白现实生活的艰难，理解父母的艰辛和自己成长的不易，学会对父母及她人的理解和宽容，学会换位思考，三思而后行；对读书学习产生新的认识，理解了生活与文化的关系，明白学习的重要性，增强他的学习意识。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 家庭是问题的根源，父母的管教是关键。通过学校咨询了解到家庭情况与江南问题的关系，其父母的管教和关爱上是出现了某些不良效应，通过心理专家对其父母做了些概述和管教改善建议，加上亲子教育的实施，问题得到了好转。现在基本上没有出现亲子情感冲突，在各个方面问题的解决上都学会了冷静和沟通，用和谐的方式处理，让家庭和个人问题尽善尽美。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 现在的江南还在高中继续奋发图强，由于来我校进行了多方面的培养和训练，让他的阅历和能力有着与众不同的优势，在学历制学习环境中可以说是女中豪杰的尖子，当上了班干部和校团干部，获有成就的他不断地积极进取，产生了浓厚的学习兴趣，让他的大学梦不再是可能，而是肯定。</span></p>', '1', '0', '1513662739', '益阳叛逆少年学校');
INSERT INTO `think_article` VALUES ('194', '52', '【抑郁症】江西抚州抑郁症少年走向阳光', '', '', '', '', '', '<p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">基本信息</span></strong><br/></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 杨志华，男，来自江西抚州，入校年龄15岁半，存在抑郁症、心理症结及社交障碍等问题。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 从小父母离异，属于单亲家庭，让他缺乏父母的关爱和家庭的温暖感，在生活与亲情上更是没有什么优越感，在环境和心理作用下使得患上了抑郁症，心理思想出现了偏差，并至社交问题上也有了障碍。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">由于抑郁症影响一般在情绪上都会有所波动，甚至带动心理认知走向偏差，很容易受外界的影响，特别是不良的或有刺激性的言语会激发他的不想脾气的产生，经常或出现脾气暴躁或者沉默不语的现象。当症状出现的时候，他就会陷入无尽的恐慌之中，如果得不到外界的干预与帮助，就会给他的心理造成极大的负面影响，产生抗拒心理及叛逆行为。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; 由于志华存在一定的心理症结，与父母沟通和社交上也出现一些障碍，让父母在家也无法可施，在无奈之下通过咨询朋友后找到了我们青少年励志教育心理辅导校区，时候志华问题得到好转。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">首先，打开心结是我们学校教育辅导的重任，针对性的心理辅导是关键。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">其二，导致问题的产生离不开家庭和父母的关系，在辅导的同时我们也会找出问题的根源，让孩子试着去换位思考，让他学会理解和感恩，懂得理性和宽容。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">其三，由于抑郁导致心理症结和社交障碍，在某种生理上产生不良的新陈代谢，为了打破他的不良习惯，让他在运动中释放自己的情感，发泄内心的压力，活跃思维的同时慢慢消除抑郁症状。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">其四，对于家长方面，我们都会不定时的与家长联系疏通思想，进行孩子在家情况的了解化解问题的探讨交流，寻找最佳的辅导方案。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">其五，在问题辅导顺利的情况下会增加其他成长与学习课程的培养，让他在各个方面同步渐进，共同提升和发现。<br/></span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">转化效果</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 经过百日维新的教育辅导，志华心理情况正常，抑郁症状得到化解，走向了阳光交流趋势，能够主动与人交谈。回家后使得家庭和睦幸福了许多，让其父母的身心都有所改善，阳关满面。现在的志华还在文化校园中奋发图强，理想的目标就是南昌大学。在此也祝愿他梦想成真！给父母和自己交上一张圆满的试卷。</span></p>', '1', '0', '1437401480', '心理健康教育学校');
INSERT INTO `think_article` VALUES ('195', '51', '【早恋】安徽池州早恋叛逆少女步入大学', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\"><strong>基本信息</strong></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 周容，女，17岁，来自安徽省池州市，</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">于</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">2013</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">年</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">4</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">月</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">21</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">日入校，在校辅导六个月。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">问题概况</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 因为父母工作比较繁忙，周容小的时候虽然时常还能在一起生活，但很少有时间沟通交流，导致情感逐渐淡漠。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">上初中三年级的时候，周容结识了一些同性好朋友并通过这好朋友认识一男友后经常逃学一般很少回家，但是父母为了工作养家糊口，对周容疏于管教，使用多少简单粗暴手段，失去了爱的尊严。之后问题出现的却是接连不断，恋爱影响了学习，成绩一落千丈便产生了厌学辍学行为，整天跟着同仁游荡，其父母在无奈之下把她送来了我们青少年励志教育心理辅导校区进行矫正教育。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">辅导方案</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">1.对于这种不良的行为问题都是来自一种不良的心理思想，主要还是开展心理辅导，由心理老师一对一进行音乐治疗、内观、森田辅导等多种心理辅导模式进行疏导和引导。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">2.通过德育教育强化她的不良行为，端正其肢体，培养良好的行为素质，由德育老师以良好的做人做事道德及学习的方式方法。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">3.通过生活教育与督导，调整其不良的生活习惯，以规范的生活时钟培养她正确的成长规律，树立良好的生活成长目标。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">4.同时开展法制教育、情感教育、亲子教育及感恩励志教育、文化辅导等课程丰富其思想阅历，提升文化素质，健康成长。</span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">转变效果</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 针对周容问题的情况给其灌输正确的人生观、价值观以及世界观，不像哲学课那样子长篇大论而是从生活到真理。我们倾向于一点点改变学生的不良嗜好而不是强行压制。</span><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">由于她的悟性较高，在与心理老师的探讨中说出自己很多关于自己的想法，也认识到自已思想的片面性，对于恋爱的影响和母亲关系的问题得到了很大的转变。深刻认识到自己的错误，并且积极配合教育辅导和改正，转变效果良好。回家后听从父母的安排，认真学习，于2015年考上了安徽林业科技大学。</span></p>', '1', '0', '1445436767', '早恋厌学教育学校');
INSERT INTO `think_article` VALUES ('196', '27', '女儿考研失败怪父母没本事：你们又穷又难看', '', '', '', '', '', '<p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; &nbsp; 都说狗不嫌家贫，儿不嫌母丑，名校大学毕业考研失败的乖乖女，却迁怒于父母：认为是父母没本事送她出国留学，才导致她的困境；家境殷实的她，本有机会留在国 外发展，因父亲不同意她闯世界，她只好回到南宁发展，因为勤奋、肯干，她深得上司的赏识……她们两个都是学习优秀的女孩，在人生的道路上，同样面临着各种 选择，而她们不同的人生态度，结局也截然不同。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　媒体推出的亲子系列报道，讲述了不同家庭的父母与子女之间的种种困惑。很多父母感慨，自己是如此爱儿女，可儿女偏偏不领情，越来越不懂事。而儿女们则埋怨，父母插手，让他们的生活一团糟。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　透过这些故事，情感教育专家说，不是孩子不肯长大，是父母将孩子呵护在羽翼之下，不希望孩子离开。25岁之后，父母对孩子的影响最好为零。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">故事一</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　平凡父母全力供女儿读名校</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　女儿考研失败怪父母没本事</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　倾诉人：阿霞 55岁 退休工人</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　倾诉时间：2016年1月9日</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　新 年了，别人都开开心心的，我这心里憋闷得很，又不敢和别人说。女儿小红生气时，总会说“你们这样的父母怎么会生出我这么优秀的女儿？你看你们两个又穷，长 得又难看，根本不配拥有我这样的女儿”，小红说这些话时，我们夫妻俩真的感觉很伤心，可内心深处又感觉有些对不起她，所以也任由她发脾气。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　我和丈夫都是普通工人，单位不景气，两人早早就退休了。现在我每个月也就1000多元的退休金，小红的爸爸稍高点。我们这样的收入，确实不能给小红想要的生活。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　我们夫妻俩都很普通，丢在人群里根本就没人会注意。而我们的女儿小红就不同，从小就很出众。或许是她接了我和爱人的优点，反正她一点也不像我，也不像爸爸，皮肤白皙、眼睛又黑又亮，长得像个洋娃娃。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　除了长相比较出众，她一直都很听话，而且上进好学，学习成绩一直很好。从小到大，小红基本是被我们夸大的。小红自己读书也很努力，小学考上好初中，初中又考上了南宁三中。我们两口子都没啥文化，也不懂如何辅导她，学习全凭她自己。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　高考时，小红的成绩超出一本分数线不少，很多重点院校她可以随便挑，但最后阴差阳错学了日语。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　大 学的4年时光里，小红过得不太开心，因为她并不喜欢日语。加上同宿舍的女生家庭条件优越，个个都是家中的小公主。大学毕业时，同宿舍的6个女孩，只剩小红 一人留在了国内，其他5个女生全部出国，这样小红内心极度失落。好在小红一心考研，第一年没考上。大学毕业后，小红回到家继续复习，专心考研。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　心情好的时候，小红会很开心给我们做她的拿手菜。还会搂着我说，感谢父母给了她那么多。可更多时候，她比较烦躁。烦躁时，她就会责怪我们，当初高考选学校时，我们没给她好的建议，这导致她过得不开心。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　更 让我们生气又伤心的是，一次她居然说，我们这么平凡，怎么会生出她这么优秀的女儿。她所有的一切，都是她通过自己努力获得的。她这么说，我们也哑口无言， 读书时，我们夫妻俩确实没能力辅导她，唯一能给她的，就是尽自己能力供她读书。而如果她想出国，我们的经济条件又不允许。内心深处，我们真的感觉有些对不 起她。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　去年底考研结束后，她又说，感觉自己还是考不上。就算考上了，也不想去读了，因为日语不是她喜欢的专业。既然不喜欢，又何必再去痛苦煎熬几年。她要离开南宁，去大城市找工作，那里才有她的一片天空。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　现在她天天晚上上网玩电脑，早上又蒙头大睡，我们和她就像生活在两个世界里。有时，很想和亲友商量下，说出去，又怕别人议论，对小红不好。真不知道该怎么办？</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　故事二</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　海归女为父回南宁发展</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　积极工作很快成团队领头人</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　倾诉人：阿静 52岁 公务员</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　倾诉时间：2016年1月10日</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　“明明有能力让女儿在国外发展，却为了自己的私心，非要把女儿‘绑’在自己身边，她爸爸真的很自私。”我一直认为，孩子不是父母的私有财产，孩子长大了，就该让她能飞多高飞多高。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　我 和丈夫都是公务员，家里虽然谈不上富裕，但我们夫妻俩工作非常努力，在各自的领域里都比较优秀，是部门负责人。女儿小君在我们的耳濡目染下，从小就很独 立，不仅学习成绩好，而且热衷公益活动。读大学时，小君直接选择了澳大利亚的一所大学。因为小君的爷爷奶奶和叔叔一家都有澳大利亚的绿卡，小君去澳大利亚 读书，身边会有亲人照顾，不会像其他孩子那样孤单。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　虽然有亲人照顾，在澳大利亚读书期间，小君经常趁着假期打短工，以减轻我们夫妻俩的负担。4年前大学毕业，小君想继续读研，可小君的爷爷奶奶却因为不喜欢国外的生活，非要回国。而小君的爸爸害怕小君读研后会选择留在澳大利亚发展，便坚决不同意小君读研。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　我非常支持小君在澳大利亚读研，因为小君学的是金融专业，她回到国内，一样要靠自己努力。在我看来，既然在国内和国外都要靠自己奋斗，那不如选择在国外奋斗，舞台更广阔。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　因为小君的前途，一时间，我们家到处弥漫着“硝烟”，我和丈夫经常争吵，甚至到了想要离婚的地步。听说父母为此吵闹不休，小君还是顾及爸爸的感受，选择了回国，回到南宁发展。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　小 君的履历比较优秀，顺利进入南宁一家银行工作。内心虽有些许不乐意，可既然选择了，小君便很努力，绝不会消极应对。工作忙碌，小君经常会加班，对此小君也 毫无怨言。正应验了那句老话“你有多少努力，就会有多少回报”，小君的努力很快见到效果，如今小君已经是项目负责人，成为团队的领头人。上司对小君也非常 欣赏，重要的项目会放心交给小君来操作。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　小君的发展虽好，可我内心深处还是期望小君能圆了自己的梦想，去澳大利亚继续深造、发展。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">　　看了你们的报道，我真的感觉，现在很多父母都把孩子当成私有财产，从精神上、经济上控制孩子。可孩子们都有自己的思想，自己独立的人格。父母能做的，是帮助他们独立，而不该把他们“绑在”自己的身边，满足自己的情感需求--来自<a href=\"http://news.youth.cn/sh/201601/t20160112_7518550.htm\" target=\"_self\">中国青年网新闻</a></span></p>', '1', '0', '1452523549', '心理健康教育中心');
