INSERT INTO `think_article` VALUES ('355', '23', '今年五四青年节文艺汇演活动圆满结束', '特训学校新闻 网瘾少年特训学校素质励志成长中心', '', './Uploads/590b1096aa748.jpg', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/show.php?cid=3&id=724\" target=\"_self\" title=\"问题少年特训学校\"><img src=\"/uploads/image/20170504/1493896940743029.jpg\" title=\"问题青少年特训学校\" alt=\"问题青少年特训学校\" width=\"468\" height=\"277\" border=\"0\" vspace=\"0\" style=\"width: 468px; height: 277px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.qsn580.com/\" target=\"_self\" title=\"素质教育励志成长学校\"><img src=\"/uploads/image/20170504/1493896940121904.jpg\" title=\"素质教育学校\" alt=\"素质教育学校\" width=\"470\" height=\"262\" border=\"0\" vspace=\"0\" style=\"width: 470px; height: 262px;\"/></a></p><p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.841990053.com/about.php?cid=3\" target=\"_self\" title=\"叛逆孩子素质特训学校相册\"><img src=\"/uploads/image/20170504/1493896940394472.jpg\" title=\"蜡笔小新 特训学校\" alt=\"蜡笔小新 特训学校\" width=\"469\" height=\"284\" border=\"0\" vspace=\"0\" style=\"width: 469px; height: 284px;\"/></a></p>', '1', '0', '1493897260', '网瘾叛逆少年管教辅导学校');
