INSERT INTO `think_article` VALUES ('388', '23', '打雪仗-快乐成长，励志成才', '', '', './uploads/5ae183ab632d1.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU2NTk5ODQzNg==/v.swf\" width=\"650\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p>', '1', '0', '1524728615', '特训学校');
INSERT INTO `think_article` VALUES ('389', '30', '特长指导—叛逆少年励志学校美术特长辅导课程', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU2ODY3NTY4OA==/v.swf\" width=\"660\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp;&nbsp;<span style=\"color: rgb(0, 112, 192);\">叛逆少年厌学孩子管教学校特长指导课程---想改变孩子现状就必须为孩子寻找出路，不能让她们在迷茫的十字路口打转。兴趣培养与特长的培训不仅可以为孩子找到方向，还会让孩子在学习与生活中获得成就感，提升信心，越战越勇。</span></p>', '1', '0', '1524818134', '改变孩子的学校');
INSERT INTO `think_article` VALUES ('390', '39', '江西南昌厌学叛逆孩子家长给在特训学校女儿的沟通信', '', '', '', '', '', '<p style=\"text-align: center;\"><img src=\"/uploads/image/20180430/1525073146610534.jpg\" title=\"江西戒网瘾学校\" alt=\"江西戒网瘾学校\" width=\"469\" height=\"585\" style=\"width: 469px; height: 585px;\" border=\"0\" vspace=\"0\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/image/20180430/1525073246752841.jpg\" title=\"南昌特训学校\" alt=\"南昌特训学校\" width=\"469\" height=\"284\" style=\"width: 469px; height: 284px;\" border=\"0\" vspace=\"0\"/></p>', '1', '0', '1525073232', '教育江西叛逆孩子的学校');
INSERT INTO `think_article` VALUES ('391', '23', '快乐是孩子成长的动力---新年零食交流会，长沙特训学校让孩子在快乐中成长', '', '', './uploads/5aed61724b532.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU4NTIxMzU4MA==/v.swf\" width=\"560\" height=\"660\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp;&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">专业教育辅导叛逆不听管教、厌学不愿意学习、亲情淡漠沟通困难、夜不归宿离家出走、沉迷虚拟事物自卑自闭、暴力倾向打架斗殴、骄傲自大以自我为中心、自控力意志力比较弱、心理障碍行为不良的问题青少年。</span></p>', '1', '0', '1525505687', '特训学校');
INSERT INTO `think_article` VALUES ('392', '23', '叛逆孩子成长学校-春节校园欣赏烟花', '', '', './uploads/5af00f4abfd45.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU5MDA5NjMzMg==/v.swf\" width=\"650\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 叛逆厌学沉迷游戏问题孩子在特训学校过一个不一样的年，2018春节校园欣赏烟花。</span></p>', '1', '0', '1525681578', '叛逆孩子成长学校');
INSERT INTO `think_article` VALUES ('393', '23', '问题孩子心理辅导学校的生活---餐饮', '', '', './uploads/5af15f8cc98d7.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU5MTYxMTc1Ng==/v.swf\" width=\"650\" height=\"560\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp;&nbsp;</p>', '1', '0', '1525765463', 'admin');
INSERT INTO `think_article` VALUES ('394', '30', '问题孩子管教心理辅导学校学生课外放松活动---手语操', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU5NDc4Mjg4NA==/v.swf\" width=\"660\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">运动是解压的最佳方式，而手语操不仅仅是运动，还纳入了音乐、舞蹈、表演和歌唱与运动结合为一体，让人在运动中感受到美好的音乐，并且手舞足蹈的运动表现出健康快乐之韵。</span></p>', '1', '0', '1525855420', '管教问题孩子的特训学校');
INSERT INTO `think_article` VALUES ('395', '23', '长沙叛逆孩子素质品德教育培训学校圣诞节文艺活动', '', '', './uploads/5af459a56c911.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzU5NjQ5MTUzMg==/v.swf\" width=\"660\" height=\"560\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; &nbsp; 对于孩子来说，逢年过节期盼的就是快乐，对于学校而言，过节就是一种教育。长沙叛逆孩子素质品德教育培训通过节日让学生自行组织自导自演的文艺节目，不仅可以培养组织能力和领导能力，还可以提升他们的口语表达和交际能力，同时还可以培养他们的兴趣爱好，提升学习和生活信心。</span></p>', '1', '0', '1525963467', '长沙青少年素质教育学校');
INSERT INTO `think_article` VALUES ('396', '26', '孩子早恋了怎么办？应该支持还是阻止 还真是送到早恋教育学校', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzYwNTk2NzUyNA==/v.swf\" width=\"650\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 如果孩子早恋了怎么办，或许支持和鼓励更符合青春叛逆期孩子的管教。</span></p>', '1', '0', '1526309951', '早恋问题孩子辅导中心');
INSERT INTO `think_article` VALUES ('397', '24', '叛逆的孩子管教不能简单粗暴-更不能对打', '', '', '', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzYwOTIzOTk5Mg==/v.swf\" width=\"620\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp;&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">宣泄室---让叛逆孩子自行宣泄后我们再理性辅导。</span></p>', '1', '0', '1526457652', '教育叛逆孩子的特训学校');
INSERT INTO `think_article` VALUES ('398', '23', '问题孩子学校生活---零食共享', '', '', './Uploads/5b0bb0426537d.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzYxNjQwNTIzNg==/v.swf\" width=\"620\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp;&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\">零食是生活饮食中的副食品，然而不少孩子却把零食当作主食。由于孩子制止力比较弱，当他们无法控制时候既是影响到正常的饮食规律，长期还会影响身体健康。</span></p><p><span style=\"font-size: 18px; color: rgb(0, 112, 192);\">&nbsp; 我校毕竟是专业纠正孩子不良问题的，因此在校孩子平时是不能随意吃零食，当家长送来的零食都是由学校统一保管统一时间配发食用。</span></p>', '1', '0', '1527492636', '叛逆问题青少年特训学校');
INSERT INTO `think_article` VALUES ('399', '23', '特训学校生活---零食共享2', '', '', './Uploads/5b0d0c2f988be.jpg', '', '', '<p style=\"text-align: center;\"><embed type=\"application/x-shockwave-flash\" class=\"edui-faked-video\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"http://player.youku.com/player.php/sid/XMzYzMzA3NTIyNA==/v.swf\" width=\"620\" height=\"550\" wmode=\"transparent\" play=\"true\" loop=\"false\" menu=\"false\" allowscriptaccess=\"never\" allowfullscreen=\"true\"/></p><p>&nbsp; <span style=\"font-size: 18px; color: rgb(0, 112, 192);\">零食共享是为了让孩子学会团结，懂得互助，学会理解，懂得谦让，学会宽容，懂得感恩。</span></p>', '1', '0', '1527580636', '厌学叛逆少年特训学校');
INSERT INTO `think_article` VALUES ('400', '34', '体验劳动与收获让叛逆厌学奢侈消费孩子改变不良观念', '', '', '', '', '', '<p style=\"text-align: center;\"><a href=\"http://www.qsn580.com/show-5-34-400.html\" target=\"_self\" title=\"少年管教学校\"><img src=\"/uploads/image/20180530/1527679368688680.jpg\" style=\"width: 508px; height: 346px;\" title=\"戒网瘾学校生活\" width=\"508\" height=\"346\" border=\"0\" vspace=\"0\" alt=\"戒网瘾学校生活\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.qsn580.com/news-5-34.html\" target=\"_self\" title=\"戒网瘾学校\"><img src=\"/uploads/image/20180530/1527679368129405.jpg\" style=\"width: 504px; height: 344px;\" title=\"叛逆孩子管教学校 劳动体验\" width=\"504\" height=\"344\" border=\"0\" vspace=\"0\" alt=\"叛逆孩子管教学校 劳动体验\"/></a></p><p style=\"text-align: center;\"><a href=\"http://www.qsn580.com/\" target=\"_self\" title=\"特殊教育学校\"><img src=\"/uploads/image/20180530/1527679370128570.jpg\" style=\"width: 502px; height: 319px;\" title=\"劳动体验 特训学校\" width=\"502\" height=\"319\" border=\"0\" vspace=\"0\" alt=\"劳动体验 特训学校\"/></a></p><p>&nbsp;<span style=\"font-size: 18px; color: rgb(0, 112, 192);\"> 劳动是人类生存与进步发展的基础，而当今社会发展到一定程度后却让不少人变得懒惰，特别是青少年孩子。我校多年专业从事不良问题青少年孩子进行纠正管教辅导，接到各种不良问题孩子，尤其是沉迷网络游戏、叛逆不听话、厌学不愿意上学、混迹社会打架斗殴、贪玩夜不归宿、早恋网恋同性恋、亲情淡漠沟通困难、自卑自闭社交障碍、心理症结行为异常等等。</span></p>', '1', '0', '1527680518', 'admin');
INSERT INTO `think_article` VALUES ('401', '39', '株洲家长给在问题孩子教育学校里的女儿的沟通信', '株洲叛逆孩子教育学校', 'www.qsn580.com', '', '株洲问题少年孩子教育学校', '', '<p><br/></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">亲爱的女儿</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp; 您好！</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp; 你写给爸妈的信妈妈都看到了，妈妈每天都想回信给你，可每次提笔都不知道从何说起，因妈妈有太多太多的话想和我的女儿说，今天是母亲节妈妈又看到了老师发来你写给妈妈的信和你的照片视频，妈妈看了很感动和欣慰！其实老师二三天就把你的照片视频发来给妈妈看一次，妈妈就把你的照片视频收藏起来，也转发给你哥哥看，还久不久把你的照片和视频给家里亲人看看，免得大家担心你，妈妈还把你的视频上传到电脑上，我要保存好视频，让视频见证你每天的变化，让你也为自己去励志学校后的脱变而感到幸运。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp; 女儿，你知道吗？其实能去励志学校的同学都是幸运的，证明每个同学都是父母的心肝宝贝，只是开始时你们不理解父母罢了，父母把你们送去励志学校只是想好好保护你们，让你们顺利度过少年时代的叛逆期，不想让外面的坏风气影响到你们，不想让你们在美好的年华留下任何遗憾。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp; 女儿，其实妈妈也很想你，开始时妈妈想你想得好难受，老师说让你和我们都冷静一段时间，让你好好反省一下自己，认清自己身上的不良问题并改掉，让我们过一段时间过来看你。之后在老师发来你的照片视频中见你逐渐适应和改变，妈妈也决定让自己日子过得充实一点进步一点，所以妈妈每天都会早早起来买菜在家煮粥和爸爸吃了再去上班，晚上不管多晚回家都煮夜宵和爸爸吃，我想学炒好菜，等女儿回家时我能炒好吃的菜给女儿吃，让你能感觉到家的温暖。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp; 前几天有一段新闻说一个刚毕业出来工作一年半的空姐深夜乘滴滴车给变态的司机奸杀了，你看看一个二十一岁的空姐在社会上都被坏人害死了，更何况你一个十三岁的未成年人，就像上次你去东莞你不知道妈妈有多担心，你还小不知道外面的社会有多复杂，外面有些坏人专门在寻找迷途的没有父母保护的少年，他们会恐吓威逼那些骗来的未成年人，打断手脚去乞讨，可把少女卖到深山给没有老婆的老男人做生育工具，可软禁到地下室做坏事帮他们赚钱，那些坏人不会理别人死活的，很多失足少年就是这样毁了自己的人生。那时你去东莞妈妈感觉就像世界末日一样，因你还小在外面根本生存不了，怕遇到了坏人，到那时叫天天不应，叫地地不灵，到你明白后悔时已经迟了。好在妈妈和哥哥对你的放弃，前后只三天就去东莞把你接了回来，还没铸成什么错。</span></p><p><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp; 女儿，妈妈买了零食和信一起快递过去给你，牛奶这快递是不允许寄的，所以妈妈就拿回了家，女儿，你要明白妈妈不是很有钱才给你买零食，你以前买的零食妈妈从不舍得买给自己吃过，因妈妈爱你和爱家人，妈妈愿意把最好的给你们，希望你和家人生活得幸福、快乐！</span></p><p style=\"text-align: right;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 爱的爸爸妈妈</span></p><p style=\"text-align: right;\"><span style=\"color: rgb(0, 112, 192); font-size: 18px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2018.5.15</span></p><p>&nbsp; <span style=\"color: rgb(0, 112, 192);\">&nbsp;教育服务热线》18874250661 潘老师</span></p>', '1', '0', '1529141984', '问题少年教育学校');
INSERT INTO `think_article` VALUES ('402', '34', '端午节这些孩子在问题少年特殊教育学校吃饱喝足快乐过', '', '', '', '', '', '<p style=\"text-align: center;\"><img src=\"/uploads/image/20180618/1529321961133445.jpg\" style=\"width: 559px; height: 359px;\" title=\"问题孩子纠正学校\" width=\"559\" height=\"359\" border=\"0\" vspace=\"0\" alt=\"问题孩子纠正学校\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/image/20180618/1529321961129758.jpg\" style=\"width: 559px; height: 359px;\" title=\"问题少年辅导学校\" width=\"559\" height=\"359\" border=\"0\" vspace=\"0\" alt=\"问题少年辅导学校\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/image/20180618/1529321961923677.jpg\" style=\"width: 559px; height: 369px;\" title=\"问题青少年矫正中心\" width=\"559\" height=\"369\" border=\"0\" vspace=\"0\" alt=\"问题青少年矫正中心\"/></p><p style=\"text-align: center;\"><img src=\"/uploads/image/20180618/1529321962117149.jpg\" style=\"width: 560px; height: 329px;\" title=\"问题孩子纠正机构\" width=\"560\" height=\"329\" border=\"0\" vspace=\"0\" alt=\"问题孩子纠正机构\"/></p><p>&nbsp; &nbsp;<font color=\"#0070c0\"><span style=\"font-size: 18px;\">昨天是一个伟大的节日——父亲节，接着今天又是一个纪念、感恩与美食浓缩的端午节，正符合针对叛逆厌学亲情淡漠不懂感恩的青少年孩子的教育主题，我校顺应着节日的气氛让学生自导自演，昨天举办了感恩父亲的演讲与故事分享会，以及今天庆祝端午文艺演唱活动和名人故事茶话会，在无意中给他们灌输了无限的正能量。</span></font><br/></p>', '1', '0', '1529321584', '问题青少年教育学校');
INSERT INTO `think_article` VALUES ('403', '38', '叛逆出走少年透露心声——原因是父亲与我的沟通越来越少了', '', '', '', '', '', '<p style=\"white-space: normal; text-align: center;\"><a href=\"http://www.qsn580.com/news-5-38.html\" target=\"_self\" title=\"叛逆问题青少年教育学校\"><img src=\"http://www.841990053.com/ueditor/php/upload/image/20180619/1529401290425670.jpg\" title=\"叛逆少年教育学校学员心声\" alt=\"叛逆少年教育学校学员心声\" width=\"384\" height=\"540\" border=\"0\" vspace=\"0\" style=\"width: 384px; height: 540px;\"/></a></p><p style=\"white-space: normal;\">&nbsp;<span style=\"color: rgb(0, 112, 192);\">&nbsp;感恩父亲，他用那厚实的肩膀撑起了我们的家，虽然以后这个家没有母亲的陪伴，我可能不会过得那么好，但是父亲给予我的关爱远远超过母亲的，所以我异常的感谢父亲。</span></p><p style=\"white-space: normal;\"><span style=\"color: rgb(0, 112, 192);\">&nbsp; 母亲是半年前离开的，这个不幸的消息，可谓是对于我这样的小家庭是晴天霹雳，父亲的精神接近崩溃，而我成绩一落千丈，每天要么不是打架闹事，就是整天泡吧，抽烟喝酒更是不学就会。父亲过了半个月，经过家人的劝阻与激励，重新振作却整日不见我人影，沟通问同学朋友，在酒吧门口找到了我，当时的您，第一眼看到我就哭了，这是我这么大第一次看到您哭，您诉苦着求我回家，可是我当时的头脑完全被酒精洗刷了，不管什么亲情友情都置之不理，并很快逃离了您的视线，过了几天我因为酒精的作用引秀心脏病突发，当时人就倒在了地上，神志不清，听朋友说您当时急的来的路都不会走了，我在急救室抢救了两个小时才没有危险，当时我感受到有一个人他一直拿毛巾擦拭我的脸、手，盖被子，拿棉签沾水在我干裂的嘴唇上涂着，第二天我醒了，看着您靠在椅子上，头椅在手上，摇摇欲坠，我突然心一下子酸了，眼睛湿润了，想起了自己以前的那些过错。您可能感觉到我醒了，您就醒了，摸了摸我的脸，满脸担心的问我：“还有什么不舒服的吗？”我摇了摇头，“对不起，爸爸”，我内疚的说。后来父亲就接我出院了，我也回到了学校完成学业，不久后我又一次的跑了出去，原因就是父亲与我的沟通越来越少了。</span></p><p>&nbsp; &nbsp;<span style=\"color: rgb(112, 48, 160);\"> 叛逆问题青少年教育服务热线》188 74250 661 学校面向全国招生</span></p>', '1', '0', '1529405894', '叛逆问题青少年教育学校');

