INSERT INTO `think_config` VALUES ('1', '湖南青少年励志教育叛逆孩子特训学校 长沙不良问题青少年全封闭军事化管教学校 湖北正规青少年心理健康教育辅导机构 河南叛逆少年管教所 江西专业化解网瘾基地 问题孩子特殊教育矫正中心', 'http://www.qsn580.com', './Uploads/5a38c26730cae.jpg', '285412937@qq.com', '湖南仁佳青少年教育服务中心', '叛逆孩子教育学校，正规戒网瘾学校，叛逆孩子管教中心，青少年心理辅导学校，问题青少年教育学校，心理健康教育学校，叛逆孩子学校，湖南戒网瘾学校，问题孩子矫正学校，问题少年学校，特训学校，少年管教所', '问题孩子教育学校是针对不良问题青少年提供心理思想辅导、行为矫正、素质品德教育、感恩励志等教育服务，针对存有“上网成瘾、厌学辍学、叛逆不听话、早恋网恋、亲情淡漠离家出走、暴力趋向打架斗殴、好吃懒做肥胖难减、混迹社会”等等不良问题的青少年孩子进行以“心理疏导、思想引导、行为督导”三位一体化问题纠正教育，同时开设全方位课程，让孩子在“心、德、礼、智、文、武、法、美”等各个领域获得改变提升和健康成长', '彭毅', '188 74250 661', '188 74250 661', '188 74250 661', '553522', '15001862号-1', '贵州省贵阳市高新技术产业园', '<p style=\"text-align: center; white-space: normal;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<a href=\"http://www.qsn580.com/about-6-40.html\" target=\"_self\"><img width=\"813\" height=\"50\" title=\"特训学校 戒网瘾学校 叛逆青少年管教学校\" style=\"width: 813px; height: 50px;\" alt=\"特训学校 戒网瘾学校 叛逆青少年管教学校\" src=\"/uploads/image/20170820/1503192073787911.jpg\" border=\"0\" vspace=\"0\"/></a></p><p><span style=\"color: rgb(0, 0, 0);\">&nbsp;</span><span style=\"color: rgb(191, 191, 191);\">Copyright 青少年教育服务中心 湖南青少年励志教育学校</span><span style=\"color: rgb(0, 0, 0);\">&nbsp;</span>All Rights Reserved 青少年教育ICP备案15001862号-1</p><p style=\"text-align: center;\">网址：<a href=\"http://www.qsn580.com\" _src=\"http://www.qsn580.com\">http://www.qsn580.com</a>&nbsp; &nbsp; &nbsp; &nbsp; 联系报名咨询电话：188 74250 661 潘主任</p><p style=\"text-align: center;\">校址：湖南省长沙市望城乔口加油站附近</p><p><meta name=\"360-site-verification\" content=\"32c95af97ff46e815448837e471a7d11\"/></p>', '<script type=\"text/javascript\">var cnzz_protocol = ((\"https:\" == document.location.protocol) ? \" https://\" : \" http://\");document.write(unescape(\"%3Cspan id=\'cnzz_stat_icon_1260429881\'%3E%3C/span%3E%3Cscript src=\'\" + cnzz_protocol + \"s95.cnzz.com/z_stat.php%3Fid%3D1260429881%26show%3Dpic\' type=\'text/javascript\'%3E%3C/script%3E\"));</script>\r\n\r\n<Script Language=javascript> \r\nfunction key(){ \r\n//if(event.shiftKey){\r\n//window.close();}\r\n//禁止shift\r\nif(event.altKey){\r\nalert(\'改变孩子现状，成就孩子与家庭未来\');}\r\n//禁止alt\r\nif(event.ctrlKey){\r\nalert(\'怀抱只能给孩子一时的温暖，教育才能让孩子一辈子幸福\');}\r\n//禁止ctrl\r\nreturn false;}\r\ndocument.onkeydown=key;\r\nif (window.Event)\r\ndocument.captureEvents(Event.MOUSEUP);\r\n//swordmaple javascript article.\r\n//from www.jx165.com\r\n//function nocontextmenu(){\r\n//event.cancelBubble = true\r\n//event.returnValue = false;\r\n//return false;}\r\nfunction norightclick(e){\r\nif (window.Event){\r\nif (e.which == 2 || e.which == 3)\r\nreturn false;}\r\nelse\r\nif (event.button == 2 || event.button == 3){\r\nevent.cancelBubble = true\r\nevent.returnValue = false;\r\nreturn false;}\r\n}\r\nfunction Click(){ \r\nalert(\'欢迎来访湖南青少年励志教育学校！改变孩子现状，成就家庭未来！\'); \r\nwindow.event.returnValue=false; \r\n} \r\ndocument.oncontextmenu=Click; \r\ndocument.onselectstart=new Function(\"event.returnValue=false;\");\r\n</Script>');

