DROP TABLE IF EXISTS `think_shoper`;
CREATE TABLE `think_shoper` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `kinds` int(11) DEFAULT NULL COMMENT '1为个人，2为企业',
  `realname` varchar(40) DEFAULT NULL COMMENT '联系人姓名',
